INSERT INTO `chinese_infos` VALUES (6393, '齢', '金', 17, 'n', '同“龄”（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (6394, '迟', '金', 18, 'y', '形声。本义慢慢走；。泛指慢；迟,徐行也。--《说文》；迟,缓也。--《广雅》；废彻不迟。--《诗·小雅·楚茨》；行道迟迟。--《诗·邶风·谷风》；迟归有待。--《易·归妹》；陵迟故也。--《荀子·宥坐》；三日断五匹,大人故嫌迟。--《玉台新咏·古诗为焦仲卿妻作》；又如迟徐(缓慢);迟滞(缓慢阻滞；迟钝;反应慢,不灵敏；料事同而迟速不同。--魏源《默觚下》；周少言重迟。--《汉书·杜周传》');
INSERT INTO `chinese_infos` VALUES (6395, '翱', '金', 18, 'y', '展翅飞');
INSERT INTO `chinese_infos` VALUES (6396, '蝉', '金', 18, 'y', '形声。从虫,单声。本义一种昆虫。也称知了；。节肢动物门,昆虫纲,同翅目,蝉科,蝉属；蝉,以旁鸣者。--《说文》。按,尔雅谓之蜩,今苏俗谓之知了。；寒蝉鸣。--《礼记·夏小正》；清风半夜鸣蝉。--宋·辛弃疾《西江月》；又如寒蝉(秋天天冷时的蝉；古代一种极薄的丝织品。以其薄如蝉翼而得名；蝉；连续不断；蝉联陵丘。--左思《吴都赋》');
INSERT INTO `chinese_infos` VALUES (6397, '织', '金', 18, 'y', '制作布帛之总称，又作编织、搜罗，比喻客观事物纷繁交错、思想情绪纷乱纠结。');
INSERT INTO `chinese_infos` VALUES (6398, '镇', '金', 18, 'y', '为左右结构汉字，该字形声。从金，真声。本义:对物体施加压力。');
INSERT INTO `chinese_infos` VALUES (6399, '颛', '金', 18, 'y', '1.愚昧。2.同“专”。');
INSERT INTO `chinese_infos` VALUES (6400, '缯', '金', 18, 'y', '丝织品的总称。〈方〉绑；扎');
INSERT INTO `chinese_infos` VALUES (6401, '镒', '金', 18, 'y', '古代重量单位，一镒合二十两（一说二十四两）');
INSERT INTO `chinese_infos` VALUES (6402, '璨', '金', 18, 'y', '1.美玉。2.鲜明光亮的样子。');
INSERT INTO `chinese_infos` VALUES (6403, '赜', '金', 18, 'y', '精妙；深奥');
INSERT INTO `chinese_infos` VALUES (6404, '储', '金', 18, 'y', '可以做姓，其他意思有积蓄、夸大、欺诳、储君、太子。');
INSERT INTO `chinese_infos` VALUES (6405, '聂', '金', 18, 'y', '附耳私小语也聂北，古代地名；。在今山东省茌平县贾寨乡。');
INSERT INTO `chinese_infos` VALUES (6406, '镕', '金', 18, 'n', '1.熔铸金属的模型，引申为楷模。多 用于人名。2.“鎔”，另见“熔”');
INSERT INTO `chinese_infos` VALUES (6407, '缮', '金', 18, 'y', '1.修补；整治2.抄写');
INSERT INTO `chinese_infos` VALUES (6408, '阙', '金', 18, 'y', '1.过失；疏失2.同“缺”。1.古代皇宫大门前两边供瞭望的楼，泛指帝王的住所2.神庙、陵墓前竖立的石雕。3.姓。');
INSERT INTO `chinese_infos` VALUES (6409, '雟', '金', 18, 'n', '〔子～〕即“子规”，杜鹃鸟。〔越～〕地名，在中国四川省。今作“越西”。');
INSERT INTO `chinese_infos` VALUES (6410, '鄜', '金', 18, 'n', '1.鄜县，旧地名，在陕西。今改称富县。2.姓。');
INSERT INTO `chinese_infos` VALUES (6411, '锁', '金', 18, 'y', '形声；置于可启闭的器物上,以钥匙或暗码(如字码机构、时间机构、自动释放开关、磁性螺线管等)打开的扣件；柄铁折叠环复,如锁上练,引之长丈许。--明·魏禧《大铁椎传》；又如锁管(锁簧插入的锁身);锁孔(锁眼);锁梃儿(锁的销子);上锁；一种用铁环钩连而成的刑具；若见鬼把椎锁绳纆,立守其旁。--汉·王充《论衡·订鬼篇》；又如锁靠(束缚手腕的刑具);锁韁(枷锁和韁绳；链子；俄有赤龙长千余尺,电目血舌,朱鳞火鬣,项掣全锁,锁牵玉柱,千雷万霆,激绕其身。--唐·李朝威《柳毅传》');
INSERT INTO `chinese_infos` VALUES (6412, '镐', '金', 18, 'y', '繁体字：(釒高)，左右结构；(形声。从金，高声。本义：温器。)表示工具、古地名及姓氏；具体详见释意。');
INSERT INTO `chinese_infos` VALUES (6413, '双', '金', 18, 'y', '出自《说文》，指一对。');
INSERT INTO `chinese_infos` VALUES (6414, '环', '金', 18, 'y', '形声。从玉,本义圆形而中间有孔的玉器；环,璧也。--《说文》；肉好若一谓之不。--《尔雅·释器》。李注其孔及边肉大小适等。”；行步则有环珮之声。--《礼记·经解》；孔子佩象环五寸。--《礼记·玉藻》；闻水声,如鸣佩环。--唐·柳宗元《至小丘西小石潭记》；腰白玉之环。--明·宋濂《送东阳马生序》');
INSERT INTO `chinese_infos` VALUES (6415, '饆', '金', 18, 'n', '〔～饠（luó）〕古代的一种夹馅面饼，如“韩约能作樱桃～饠（luó），其色不变。”');
INSERT INTO `chinese_infos` VALUES (6416, '镈', '金', 18, 'n', '1.古代击乐器。形状像钟。2.古代一种锄草用的农具。');
INSERT INTO `chinese_infos` VALUES (6417, '镑', '金', 18, 'n', '英语音译词。埃及、黎巴嫩、苏丹、叙利亚、以色列、英国等国的本位货币均译为镑。');
INSERT INTO `chinese_infos` VALUES (6418, '鏊', '金', 18, 'n', '鏊子，一种摊面食的器具。铁制，平圆，中间稍凸起。');
INSERT INTO `chinese_infos` VALUES (6419, '儭', '金', 18, 'n', '1.布施，施给（僧、尼）。2.衬里。3.古同“衬”，衬托。');
INSERT INTO `chinese_infos` VALUES (6420, '鎞', '金', 18, 'n', '1.旧时妇女插在头发上的一种首饰，即钗2.古代治眼病用的一种器具1.古同“鈚”，犁刃。2.古同“錍”，箭镞。古通“篦”，篦子');
INSERT INTO `chinese_infos` VALUES (6421, '膪', '金', 18, 'n', '见〖囊膪〗（nāngchuài）。');
INSERT INTO `chinese_infos` VALUES (6422, '艟', '金', 18, 'n', '见〔艨艟〕');
INSERT INTO `chinese_infos` VALUES (6423, '繟', '金', 18, 'n', '1.宽松的丝带。2.舒缓；坦然。〔～联〕连绵不断的样子。');
INSERT INTO `chinese_infos` VALUES (6424, '擤', '金', 18, 'n', '按住鼻孔用力出气，使鼻涕排出');
INSERT INTO `chinese_infos` VALUES (6425, '遭', '金', 18, 'n', '形声。从辵，曹声。本义：遇见，碰到。遭字从走从曹。“曹”意为“粮草”。“走”与“曹”联合起来表示“走陆路运送粮草”。本义：走陆路运送粮草。');
INSERT INTO `chinese_infos` VALUES (6426, '踪', '金', 18, 'n', '释义:脚印；留下的痕迹：～迹|失～|行～。跟～。');
INSERT INTO `chinese_infos` VALUES (6427, '鬃', '金', 18, 'n', '马、猪等颈上的长毛');
INSERT INTO `chinese_infos` VALUES (6428, '懤', '金', 18, 'n', '〔～～〕深深的忧愁。');
INSERT INTO `chinese_infos` VALUES (6429, '镏', '金', 18, 'n', '〔镏子〕戒指中国特有的一种镀金法。所镏的金层经久不变。');
INSERT INTO `chinese_infos` VALUES (6430, '鞣', '金', 18, 'n', '用鞣料使兽皮变柔软，制成皮革');
INSERT INTO `chinese_infos` VALUES (6431, '镎', '金', 18, 'n', '人造金属元素，符号Np，原子序数93。银白色，有放射性，由人工核反应获得。可 用作航天、航海及医学等特殊需要的 同位素能源。');
INSERT INTO `chinese_infos` VALUES (6432, '雏', '金', 18, 'n', '释义为幼小的（多指鸟类）：雏鸡；雏燕；育雏。');
INSERT INTO `chinese_infos` VALUES (6433, '鶵', '金', 18, 'n', '古同“雏”。');
INSERT INTO `chinese_infos` VALUES (6434, '镓', '金', 18, 'n', '金属元素，符号Ga（gallium）。银白色，质软。用作制光学玻璃、真空管、半导体等的原料，也用来制高温温度计。');
INSERT INTO `chinese_infos` VALUES (6435, '阕', '金', 18, 'n', '1.终了2.a）歌曲或词一首叫一阕3.姓。');
INSERT INTO `chinese_infos` VALUES (6436, '啮', '金', 18, 'n', '咬（多指鼠、兔等）');
INSERT INTO `chinese_infos` VALUES (6437, '钨', '金', 18, 'n', '金属元素，符号W，原子序数74。钢灰色，在金属中硬度最大，熔点最高。可制灯丝。钨钢 用于火箭及原子能工程。 也 用于制高速切削工具和钻头等。');
INSERT INTO `chinese_infos` VALUES (6438, '镉', '金', 18, 'n', '一种金属元素，用于制合金、釉料、颜料，并用作原子反应堆的中子吸收棒。同“鬲”。古代炊具。');
INSERT INTO `chinese_infos` VALUES (6439, '铠', '金', 18, 'y', '铠甲， 古代作战时穿的护身衣。多 用金属片连缀而成。');
INSERT INTO `chinese_infos` VALUES (6440, '婶', '金', 18, 'n', '叔母。父亲弟弟的妻子；及抱入姆、婶房中。--吴自牧《育子》；又如婶母(婶娘;婶婶。都指叔父之妻);婶太太(对别人叔母的尊称；称呼与父母同辈而年龄较小的已婚妇女；兄、嫂称弟之妻；兄弟,婶子害什么病?--《水浒传》');
INSERT INTO `chinese_infos` VALUES (6441, '觞', '金', 18, 'n', '古代称酒杯');
INSERT INTO `chinese_infos` VALUES (6442, '蟮', '金', 18, 'n', '见〖曲蟮〗。');
INSERT INTO `chinese_infos` VALUES (6443, '幮', '金', 18, 'n', '古代一种似橱形的帐子。');
INSERT INTO `chinese_infos` VALUES (6444, '飕', '金', 18, 'n', '1.风吹（使变干或变冷）2.拟声词。风声3.同“嗖”。');
INSERT INTO `chinese_infos` VALUES (6445, '饩', '金', 18, 'n', '1.谷物；饲料。2.活的牲口；生肉。3.赠送（食物）。');
INSERT INTO `chinese_infos` VALUES (6446, '拧', '金', 18, 'n', '属于左右结构汉字。形声；从扌、宁声。本义:用拇指和另外一至两根指头扭住皮肉用力转动。属多音多义字，可用于多种环境下。');
INSERT INTO `chinese_infos` VALUES (6447, '燹', '金', 18, 'n', '火；野火');
INSERT INTO `chinese_infos` VALUES (6448, '劐', '金', 18, 'n', '1.用刀剪等划开。2.用耕具划开土壤。3.同“耠”。');
INSERT INTO `chinese_infos` VALUES (6449, '窜', '金', 18, 'n', '乱跑；乱逃：流窜、抱头鼠窜。');
INSERT INTO `chinese_infos` VALUES (6450, '秽', '金', 18, 'n', '形声。从禾，岁声。本义：荒废；长满野草） 杂乱');
INSERT INTO `chinese_infos` VALUES (6451, '阋', '金', 18, 'n', '争吵；争斗');
INSERT INTO `chinese_infos` VALUES (6452, '镍', '金', 18, 'n', '金属元素，符号Ni（niccolum）。银白色，质坚韧，延展性强，有磁性，在常温中不跟空气中的氧起作用。用来制特种钢和其他合金、催化剂等，也用于电镀。');
INSERT INTO `chinese_infos` VALUES (6453, '铩', '金', 18, 'n', '1.古代的一种长矛。2.摧残；伤残');
INSERT INTO `chinese_infos` VALUES (6454, '蟓', '金', 18, 'n', '蚕。');
INSERT INTO `chinese_infos` VALUES (6455, '狞', '金', 18, 'n', '反犬旁 犭,组词：狰狞、狞笑、狞恶、狞猫、狞视');
INSERT INTO `chinese_infos` VALUES (6456, '蹙', '金', 18, 'n', '1.急迫；紧迫2.皱；收缩');
