INSERT INTO `chinese_infos` VALUES (6459, '儯', '金', 18, 'n', '〔倰（lèng）～〕很长很长。');
INSERT INTO `chinese_infos` VALUES (6460, '儱', '金', 18, 'n', '见“笼”。〔～倲〕劣。〔～偅（zhòng）〕行动不灵活；走路不稳当。');
INSERT INTO `chinese_infos` VALUES (6461, '儵', '金', 18, 'n', '同“倏”。');
