INSERT INTO `chinese_infos` VALUES (6464, '嚖', '金', 18, 'n', '古同“嘒”。');
INSERT INTO `chinese_infos` VALUES (6465, '嚗', '金', 18, 'n', '1.象声词2.怒声。声。〔～喿〕声音嘈杂。');
INSERT INTO `chinese_infos` VALUES (6466, '嚘', '金', 18, 'n', '1.语未定的样子。2.气逆。');
