INSERT INTO `chinese_infos` VALUES (6468, '𪠸', '金', 18, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (6469, '嚜', '金', 18, 'n', '古通“默”〔～杘〕狡猾多诈。古通“默”');
INSERT INTO `chinese_infos` VALUES (6470, '嚞', '金', 18, 'n', '古同“哲”。');
INSERT INTO `chinese_infos` VALUES (6471, '嚟', '金', 18, 'n', '1.译音用字。《字彙補·口部》2.〈方〉來，來到。粵語。');
INSERT INTO `chinese_infos` VALUES (6472, '嚠', '金', 18, 'n', '古同“浏”。');
INSERT INTO `chinese_infos` VALUES (6473, '嚡', '金', 18, 'n', '〔～里〕方言，哪里，如“伊从～里进去格？”亦作“鞋里”。方言，涩，粗糙');
INSERT INTO `chinese_infos` VALUES (6474, '嚢', '金', 18, 'n', '同“囊”（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (6475, '嚤', '金', 18, 'n', '方言，慢；缓慢');
INSERT INTO `chinese_infos` VALUES (6476, '壝', '金', 18, 'n', '古代祭坛四周的矮墙');
