INSERT INTO `chinese_infos` VALUES (6478, '𪥿', '金', 18, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (6479, '嬼', '金', 18, 'n', '1.寡妇。2.艳丽美好。');
INSERT INTO `chinese_infos` VALUES (6480, '𪨗', '金', 18, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (6481, '屪', '金', 18, 'n', '男性外生殖器。');
INSERT INTO `chinese_infos` VALUES (6482, '巀', '金', 18, 'n', '〔～嶭（niè）〕a.（山）高峻，如“九嵕～嶭（niè），南山峨峨。”b.山名。');
INSERT INTO `chinese_infos` VALUES (6483, '巂', '金', 18, 'n', '1.〔越～〕古郡名。2.姓。1.鸟名，即“子规”。2.古通“规”，指车轮转一周。古地名，在今中国山东省东阿县西。');
INSERT INTO `chinese_infos` VALUES (6484, '廫', '金', 18, 'n', '1.古同“寥”，空虚。2.室内空虚的样子。3.寂静。');
INSERT INTO `chinese_infos` VALUES (6485, '懕', '金', 18, 'n', '同“恹”。');
INSERT INTO `chinese_infos` VALUES (6486, '懖', '金', 18, 'n', '既无知又自以为是。');
INSERT INTO `chinese_infos` VALUES (6487, '懘', '金', 18, 'n', '〔惉～〕见“惉”。');
INSERT INTO `chinese_infos` VALUES (6488, '懪', '金', 18, 'n', '1.烦闷。2.违反，违背。相反，糊涂。');
INSERT INTO `chinese_infos` VALUES (6489, '懫', '金', 18, 'n', '1.偏激、凶狠的怨恨2.阻止；塞满。');
INSERT INTO `chinese_infos` VALUES (6490, '懬', '金', 18, 'n', '1.广阔；广大。2.怨恨。3.古通“旷”，空。');
INSERT INTO `chinese_infos` VALUES (6491, '懮', '金', 18, 'n', '〔懮懮〕愁苦的样子。〔懮受〕体态轻盈的样子。');
INSERT INTO `chinese_infos` VALUES (6492, '懰', '金', 18, 'n', '1.〔～栗〕悲伤；忧伤。2.停留美好');
