INSERT INTO `chinese_infos` VALUES (6495, '擥', '金', 18, 'n', '同“揽”。');
INSERT INTO `chinese_infos` VALUES (6496, '擪', '金', 18, 'n', '1.（用手指）按压。2.压；压抑3.书法执笔法之一。4.拿着。');
INSERT INTO `chinese_infos` VALUES (6497, '擵', '金', 18, 'n', '古同“摩”。');
INSERT INTO `chinese_infos` VALUES (6498, '擶', '金', 18, 'n', '射欹令正。');
INSERT INTO `chinese_infos` VALUES (6499, '擸', '金', 18, 'n', '1.折2.把粮食粗粗地磨1.持，执。2.分理（须发或形似须发的东西）而握持。');
INSERT INTO `chinese_infos` VALUES (6500, '擹', '金', 18, 'n', '古同“摊”，摊开。');
INSERT INTO `chinese_infos` VALUES (6501, '㧰', '金', 18, 'n', '见“擽”。');
INSERT INTO `chinese_infos` VALUES (6502, '攂', '金', 18, 'n', '古均同“擂”。');
INSERT INTO `chinese_infos` VALUES (6503, '攅', '金', 18, 'n', '古同“攒”。');
INSERT INTO `chinese_infos` VALUES (6504, '斔', '金', 18, 'n', '古同“斞”古同“鍾”，古代容量单位，六斛四斗。');
INSERT INTO `chinese_infos` VALUES (6505, '旛', '金', 18, 'n', '同“幡”。');
