INSERT INTO `chinese_infos` VALUES (6973, '镗', '金', 19, 'y', '用镗床切削机器零件上已有的孔眼。同“嘡”。');
INSERT INTO `chinese_infos` VALUES (6974, '鎏', '金', 19, 'y', '1.成色好的金子。2.同“镏（liú）”。');
INSERT INTO `chinese_infos` VALUES (6975, '选', '金', 19, 'y', '形声。从辵,巽声。本义遣送;放遂；选,遣也。--《说文》；其母曰弗去,惧选。”癸卯,针适晋。--《左传·昭公元年》；假借为柬”。挑选,选择；选,一曰择也。--《说文》；圣人,十人曰选。--《白虎通》；不可选也。--《诗·邶风·柏舟》；命乡论秀士,升之司徒曰选士。--《礼记·王制》；昼选男德以象谷明。--《国语·晋语》；器成卒选,则士知胜矣。--《管子·七法》；案谨募选阅材伎之士。--《荀子·王制》');
INSERT INTO `chinese_infos` VALUES (6976, '镘', '金', 19, 'n', '抹墙用的抹子（mǒ·zi）。');
INSERT INTO `chinese_infos` VALUES (6977, '锚', '金', 19, 'n', '一般指船锚，是锚泊设备的主要部件。铁制的停船器具，用铁链连在船上，抛在水底或岸上，可以使船停稳。古代的锚是一块大石头，或是装满石头的篓筐，称为”碇“。碇石用绳系住沉入水底，依其重量使船停泊。后来有木爪石锚，即在石块两旁系上木爪，靠重量和抓力使船停泊。中国南朝已有关于金属锚的记载。中国古代帆船使用四爪铁锚，这种锚性能优良，至今在舢板和小船上仍有使用。');
INSERT INTO `chinese_infos` VALUES (6978, '镆', '金', 19, 'n', '古代宝剑名，常跟“干将”并说，泛指宝剑。也作莫邪。');
INSERT INTO `chinese_infos` VALUES (6979, '宠', '金', 19, 'y', '本义：家养的无毒蛇。转义：1.供养。2.溺爱。');
INSERT INTO `chinese_infos` VALUES (6980, '谮', '金', 19, 'n', '诬陷；中伤');
INSERT INTO `chinese_infos` VALUES (6981, '鲳', '金', 19, 'n', '也叫镜鱼、平鱼。鱼类。体侧扁，长可达40厘米，银白色。是上等食 用鱼，中国沿海均产。');
INSERT INTO `chinese_infos` VALUES (6982, '牍', '金', 19, 'n', '指古时书写用的木片，同时也是一种乐器，现在已不经常使用此字。');
INSERT INTO `chinese_infos` VALUES (6983, '兽', '金', 19, 'n', '象形。小篆字形。上象耳,中象头,下象足踩地。兽本是禽兽的总称。古另有玣字,指捕捉禽兽的活动。二字通用。本义禽兽的总称；四足的哺乳动物,多指野兽；兽,守备者。--《说文》；四足而毛谓之兽。--《尔雅·释鸟》；兽毛虫总号。--《释兽释文》。按,兽中可畜养者则曰兽。；兽,狐狼之属。--《周礼·庭氏》注；大兽公之。--《周礼·兽人》；搏兽于敖。--《诗·小雅·车攻》；猛兽步高岗。--曹操《却东西门行》；兽同足者而俱行。--《战国策·齐策》；山有猛兽。--《淮南子·说山训》');
INSERT INTO `chinese_infos` VALUES (6984, '躁', '金', 19, 'n', '形声，从足，喿声兼表字义，足是人的脚，而脚的作用是使人能够走动，喿是出现众多的事物，合起来的意思是生出要走动的众多事物。本意：出现众多的事物要动。《唐韵》《集韵》《韵会》《正韵》则到切，音灶。《说文》作趮，疾也。今俗别作躁。非是。《释名》躁，燥也。物燥乃动而飞扬也。');
INSERT INTO `chinese_infos` VALUES (6985, '襜', '金', 19, 'n', '1.围裙。2.〔襜褕〕短外衣。');
INSERT INTO `chinese_infos` VALUES (6986, '鹊', '金', 19, 'n', '形声。从鸟,昔声。字本从隹,隶变作鸟。见《说文》舃字注。本义喜鹊；维鹊有巢,维鸠居之。--《诗·召南·鹊巢》；鹊始巢。--《礼记·月令·季冬》；明月别枝惊鹊。--宋·辛弃疾《西江月》诗；鸦科的鹊属和若干其他属的许多鸟之一,它与松鸦亲缘关系密切,但有一长而分等级的尾,体羽通常为黑色和白色；维鹊有巢,维鸠居之。--《诗·召南》');
INSERT INTO `chinese_infos` VALUES (6987, '犊', '金', 19, 'n', '小牛');
INSERT INTO `chinese_infos` VALUES (6988, '蹴', '金', 19, 'n', '1.踢2.踏');
INSERT INTO `chinese_infos` VALUES (6989, '鲭', '金', 19, 'n', '鱼，身体呈梭形而侧扁，鳞圆而细小，头尖，口大。种类很多，常见的有鲐、马鲛等。鱼跟肉合在一起的菜。');
INSERT INTO `chinese_infos` VALUES (6990, '璹', '金', 19, 'n', '1.玉器。2.玉名。');
INSERT INTO `chinese_infos` VALUES (6991, '摅', '金', 19, 'n', '1.表示；发表2.腾跃；奔腾。');
INSERT INTO `chinese_infos` VALUES (6992, '颠', '金', 19, 'n', '其本义是头顶。');
INSERT INTO `chinese_infos` VALUES (6993, '镛', '金', 19, 'y', '古乐器，奏乐时表示节拍的大钟。');
INSERT INTO `chinese_infos` VALUES (6994, '镖', '金', 19, 'n', '旧式武器，形状像矛的头，投掷出去杀伤敌人');
INSERT INTO `chinese_infos` VALUES (6995, '擞', '金', 19, 'n', '见〖抖擞〗。用通条插到炉灶里抖动，使炉灰掉下去');
INSERT INTO `chinese_infos` VALUES (6996, '惩', '金', 19, 'n', '1.处罚：~一警百，~恶扬善，严~不贷。2.警戒：~前毖后');
INSERT INTO `chinese_infos` VALUES (6997, '鄯', '金', 19, 'n', '鄯善（Shànshàn），地名，在新疆。');
INSERT INTO `chinese_infos` VALUES (6998, '鲰', '金', 19, 'n', '1.小鱼。2.形容小。');
INSERT INTO `chinese_infos` VALUES (6999, '镚', '金', 19, 'n', '原指清代末年所发行的无孔的小铜币。现在的小形硬币 也叫镚子或镚 儿 。');
INSERT INTO `chinese_infos` VALUES (7000, '馐', '金', 19, 'n', '美味的食品');
INSERT INTO `chinese_infos` VALUES (7001, '鏖', '金', 19, 'n', '艰苦而激烈地战斗');
INSERT INTO `chinese_infos` VALUES (7002, '锘', '金', 19, 'n', '金属元素，符号No（nobelium）。有放射性，由人工核反应获得。');
INSERT INTO `chinese_infos` VALUES (7003, '膻', '金', 19, 'n', '像羊肉的气味1.袒露。2.胸中');
INSERT INTO `chinese_infos` VALUES (7004, '馊', '金', 19, 'n', '食物因变质而发出酸臭味');
INSERT INTO `chinese_infos` VALUES (7005, '镂', '金', 19, 'n', '雕刻');
INSERT INTO `chinese_infos` VALUES (7006, '骚', '金', 19, 'n', '形声。从马,蚤声。本义刷马；骚,摩马。--《说文》。段玉裁注人曰搔,马曰骚,其意一也。摩马,如今人之刷马。；骚动;动乱；骚,扰也。--《说文》。按,谓马扰动也。；骚,动也。--《尔雅》；徐方绎骚。--《诗·大雅·常武》；南阳骚动。--《后汉书·李通传》。注骚,亦动也。；九年而王室始骚。--《国语·郑语》');
INSERT INTO `chinese_infos` VALUES (7008, '臊', '金', 19, 'n', '像尿或狐狸的气味：～气。腥～。狐～。；（形声。从肉（月），声。本义：腥臊）');
INSERT INTO `chinese_infos` VALUES (7009, '碹', '金', 19, 'n', '1.桥梁、涵洞、巷道等工程建筑的弧形部分。2.用砖、石等筑成弧形。');
INSERT INTO `chinese_infos` VALUES (7010, '醯', '金', 19, 'n', '1.醋。2.酰（xiān）的旧称。');
INSERT INTO `chinese_infos` VALUES (7011, '铧', '金', 20, 'y', '犁的主要部件之 一。起掘土作用。');
INSERT INTO `chinese_infos` VALUES (7012, '镡', '金', 20, 'y', '姓。姓。1.古代剑柄的顶端部分。2.古代兵器，似剑而小。');
INSERT INTO `chinese_infos` VALUES (7013, '镤', '金', 20, 'y', '金属元素，符号 Pa（protactinium）。银白色，有放射性。');
INSERT INTO `chinese_infos` VALUES (7014, '镨', '金', 20, 'y', '金属元素，符号 Pr（praseodymium）。是一种稀土元素。银白色。用来制特种合金、有色玻璃、陶瓷、搪瓷，也用作催化剂。');
INSERT INTO `chinese_infos` VALUES (7015, '聍', '金', 20, 'y', '见〖耵聍〗 。');
INSERT INTO `chinese_infos` VALUES (7016, '钟', '金', 20, 'y', '形声。左形,右声。鐘本义古代打击乐器,青铜制；钟,乐钟也。--《说文》。杨树达曰钟者,可捶之物。；钟,铃也。--《广雅·释器》；垂之和钟。--《礼记·明堂位》；钟音之器也。--《左传·昭公二十一年》；大钟鼎。--《淮南子·本经》；细钧有乐,钟兑音也。--《国语·周语》；声如洪钟。--宋·苏轼《石钟山记》');
INSERT INTO `chinese_infos` VALUES (7017, '襦', '金', 20, 'y', '1.短衣；短袄。2.小孩的围嘴儿。');
INSERT INTO `chinese_infos` VALUES (7018, '译', '金', 20, 'y', '把一种语言文字依照原义改变成另一种语言文字');
INSERT INTO `chinese_infos` VALUES (7019, '续', '金', 20, 'y', '有连接、继续的意思。');
INSERT INTO `chinese_infos` VALUES (7020, '释', '金', 20, 'y', '是形声字，釆为形，睾为声。釆表示辨别、区别。释的本意是分离、分解，特指详细地说明情况、解释。可用作动词或名词。用作动词时多用于“解说；消除；放开”的意思；用作名词时泛指佛教。');
INSERT INTO `chinese_infos` VALUES (7021, '铴', '金', 20, 'y', '1.〔～锣〕小铜锣。2.（鐋）古代磨木使平的石制器具。');
INSERT INTO `chinese_infos` VALUES (7022, '馨', '金', 20, 'y', '散布得远的香气');
INSERT INTO `chinese_infos` VALUES (7023, '触', '金', 20, 'y', '该字的基本字义是抵、顶，如：抵触。“羝羊触藩，赢其角”。或作碰，撞；遇着解意。因某种刺激而引起感情变化，如：感触。忽有所触。');
INSERT INTO `chinese_infos` VALUES (7024, '霰', '金', 20, 'y', '空中降落的白色不透明的小冰粒，常呈球形或圆锥形。多在下雪前或下雪时出现。有的地区叫雪子（xuězǐ）、雪糁（xuěshēn）。');
INSERT INTO `chinese_infos` VALUES (7025, '鳃', '金', 20, 'n', '多数水生动物的呼吸器官，用来吸收溶解在水中的氧。鱼鳃主要生在头部两侧。古同“葸”，害怕。');
INSERT INTO `chinese_infos` VALUES (7026, '鳋', '金', 20, 'n', '古书上说的一种像鳣的鱼。');
INSERT INTO `chinese_infos` VALUES (7027, '馓', '金', 20, 'n', '〔馓子〕 一种油炸的面食，细条相连，拧成各种花样。');
INSERT INTO `chinese_infos` VALUES (7028, '锎', '金', 20, 'n', '金属元素，符号Cf（californium）。有放射性，由人工核反应获得。用作实验室的中子源。');
INSERT INTO `chinese_infos` VALUES (7029, '鐏', '金', 20, 'n', '1.戈柄下端的圆锥形金属套2.古同“樽”，古代的酒杯3.古书上说的一种农具。4.姓。');
INSERT INTO `chinese_infos` VALUES (7030, '缱', '金', 20, 'n', '〔缱绻〕形容情意缠绵，难舍难分。绻（quǎn）。');
INSERT INTO `chinese_infos` VALUES (7031, '驺', '金', 20, 'n', '1.古代给贵族掌管车马的人。2.姓。');
INSERT INTO `chinese_infos` VALUES (7032, '镪', '金', 20, 'n', '古称成串的钱。〔镪水〕具有强烈腐蚀性的浓硝酸、浓盐酸等的俗称。如浓硝酸称为硝镪水，浓盐酸称为盐镪水。');
INSERT INTO `chinese_infos` VALUES (7033, '鏻', '金', 20, 'n', '一类具有R4ＰＸ通式的含磷有机化合物的总称（Ｒ为烃基，Ｘ为羟基等）。');
INSERT INTO `chinese_infos` VALUES (7034, '镣', '金', 20, 'n', '意思是古代称美好的银子。');
INSERT INTO `chinese_infos` VALUES (7035, '謦', '金', 20, 'n', '〔謦欬〕轻轻咳嗽。借指谈笑。欬（kài）。');
INSERT INTO `chinese_infos` VALUES (7036, '黥', '金', 20, 'n', '1.在脸上刺上记号或文字并涂上墨，古代用作刑罚，后来也施于士兵，以防逃跑。2.在人体上刺上带颜色的文字、花纹或图形。');
INSERT INTO `chinese_infos` VALUES (7037, '鏸', '金', 20, 'n', '1.锐。2.三棱矛。大鼎。古代侍臣所执的兵器。');
INSERT INTO `chinese_infos` VALUES (7038, '鐉', '金', 20, 'n', '1.门钩。2.门框上承受门枢的铁环。');
INSERT INTO `chinese_infos` VALUES (7039, '鐍', '金', 20, 'n', '1.箱子上安锁的环形钮。2.锁3.锁闭。4.喻枢要。5.戳伤。');
INSERT INTO `chinese_infos` VALUES (7040, '镢', '金', 20, 'n', '镢头，刨土的工具。');
INSERT INTO `chinese_infos` VALUES (7041, '蝾', '金', 20, 'n', '两栖动物，外形像蜥蜴，头扁，表皮粗糙，背面黑色，腹面红黄色，四肢短，尾侧扁。生活在水中，卵生。吃小动物。');
INSERT INTO `chinese_infos` VALUES (7042, '鐎', '金', 20, 'n', '1.刁斗，古代军用炊具，三足，有柄，夜间用来敲击报更2.温酒器。');
INSERT INTO `chinese_infos` VALUES (7043, '锏', '金', 20, 'n', '古代兵器，金属制成，长条形，有四棱，无刃，上端略小，下端有柄。嵌在车轴上的铁条，可以保护车轴并减少摩擦。');
INSERT INTO `chinese_infos` VALUES (7044, '繻', '金', 20, 'n', '1.彩色的丝织品。2.古代一种用帛制的通行证。');
INSERT INTO `chinese_infos` VALUES (7045, '蠕', '金', 20, 'n', '虫类爬行的样子;微动貌；蠕虫类慢慢爬行的样子～动。蠕虫如绦虫、蛔虫、蚯蚓等。');
INSERT INTO `chinese_infos` VALUES (7046, '铹', '金', 20, 'n', '金属元素，符号Lr（lawrencium）。有放射性，由人工核反应获得。');
INSERT INTO `chinese_infos` VALUES (7047, '鐇', '金', 20, 'n', '1.宽刃斧。2.一种形似铲的工具。3.铲除。4.铁椎。5.化学元素“钒”的旧译。');
INSERT INTO `chinese_infos` VALUES (7048, '犨', '金', 20, 'n', '1.牛喘息的声音。也指牛鸣。2.突出来的。');
INSERT INTO `chinese_infos` VALUES (7049, '酬', '金', 20, 'n', '酬，本意是“劝酒”，指主人再次给客人敬酒，引申为酬谢；又引申为应付和赠答，由此又引申为劳动报酬。');
INSERT INTO `chinese_infos` VALUES (7050, '镫', '金', 20, 'n', '1.古代盛肉食的器皿。2.同“灯”，指油灯。挂在鞍子两旁供脚蹬的东西，多用铁制成');
INSERT INTO `chinese_infos` VALUES (7051, '镄', '金', 20, 'n', '金属元素，符号Fm（fermium）。银白色，化学性质活泼，有放射性，由人工核反应获得。');
INSERT INTO `chinese_infos` VALUES (7052, '鰆', '金', 20, 'n', '〔～鱼〕体长而侧扁，银灰色，有暗色横纹或斑点，鳞细小或无鳞，口大，吻尖。生活于海洋，常成群作远程洄游。性凶猛，捕食小鱼。亦称“马鲛”。');
INSERT INTO `chinese_infos` VALUES (7053, '璺', '金', 20, 'n', '陶瓷、玻璃等器具上的裂痕');
INSERT INTO `chinese_infos` VALUES (7054, '谵', '金', 20, 'n', '多说话。今多指说胡话');
INSERT INTO `chinese_infos` VALUES (7055, '孀', '金', 20, 'n', '指寡妇');
INSERT INTO `chinese_infos` VALUES (7056, '镦', '金', 20, 'n', '　冲压金属板，使其改变形状。矛戟柄末的平底金属套。');
INSERT INTO `chinese_infos` VALUES (7057, '骟', '金', 20, 'n', '割掉马、牛等的睾丸或卵巢');
INSERT INTO `chinese_infos` VALUES (7058, '镌', '金', 21, 'y', '雕刻');
INSERT INTO `chinese_infos` VALUES (7059, '镯', '金', 21, 'n', '镯子，套在手腕或脚腕上的环状装饰品');
INSERT INTO `chinese_infos` VALUES (7060, '属', '金', 21, 'n', '形声。从尾,蜀声。尾与身体相连。；种类。亦特指牲类；忠之属也,可以一战。--《左传·庄公十年》；土地平旷,屋舍俨然,有良田美池桑竹之属。--晋·陶渊明《桃花源记》；又如金属;属禽(分别飞禽的种类；亲属；是疾易传染,遘者虽戚属,不敢同卧起。--清·方苞《狱中杂记》');
INSERT INTO `chinese_infos` VALUES (7061, '铁', '金', 21, 'y', '从金从臷声。臷通迭。本义:化学元素。符号。一种很重的可锻、有延展性和有磁性的、主要是两价和三价的金属元素,纯铁为银白色,但在潮湿空气中容易生锈。');
INSERT INTO `chinese_infos` VALUES (7062, '燨', '金', 21, 'n', '古同“爔”。');
INSERT INTO `chinese_infos` VALUES (7063, '铛', '金', 21, 'n', '读时，可作为名词使用，意为金属制作的物品，如锒铛（锁系囚人的铁索）、“足履革屣,耳悬金铛”（女子的耳饰）；也可作为象声词使用，指撞击金属器物等发出的声音。时，指烙饼或做菜用的平底浅锅，如：饼铛；也可指温器，如：酒铛、茶铛。');
INSERT INTO `chinese_infos` VALUES (7064, '骖', '金', 21, 'y', '古代指驾在车辕两旁的马。');
INSERT INTO `chinese_infos` VALUES (7065, '蛩', '金', 21, 'y', '古指蟋蟀。也指蝗虫。');
INSERT INTO `chinese_infos` VALUES (7066, '镭', '金', 21, 'y', '金属元素，符号Ra（radium）。银白色，质软，有放射性。用来治疗恶性肿瘤，镭盐和铍粉的混合制剂可制成中子源。');
INSERT INTO `chinese_infos` VALUES (7067, '随', '金', 21, 'y', '基本释义是跟从、顺从的意思。可以作为姓氏。春秋时期诸侯国名。');
INSERT INTO `chinese_infos` VALUES (7068, '鐾', '金', 21, 'y', '把刀的刃部在布、皮、石头等上面反复摩擦几下，使锋利');
INSERT INTO `chinese_infos` VALUES (7069, '险', '金', 21, 'y', '⑵可能遭受的灾难 ；⑶可能发生灾难的；⑷要隘，不易通过的地方；⑸存心狠毒；⑹几乎，差一点');
INSERT INTO `chinese_infos` VALUES (7070, '铎', '金', 21, 'y', '古代宣布政教法令时或有战事时用的大铃');
INSERT INTO `chinese_infos` VALUES (7071, '攒', '金', 23, 'n', '形声。从手，赞声。本义：积蓄。；形声。从手，赞声。本义：聚。形声。从木，赞声。参见“欑”。');
INSERT INTO `chinese_infos` VALUES (7072, '鹚', '金', 21, 'n', '见  〔鸬鹚〕');
INSERT INTO `chinese_infos` VALUES (7073, '谴', '金', 21, 'n', '表示责备，斥责。');
INSERT INTO `chinese_infos` VALUES (7074, '衬', '金', 21, 'n', '作名词，一般指内衣，或附在衣裳某一部分里面的纺织品。词性变化可作动词，意思指在里面再托上一层或搭配上别的东西。“衬”也是一种写作方法，主要方法有反衬，衬托等。');
INSERT INTO `chinese_infos` VALUES (7075, '镱', '金', 21, 'n', '金属元素，符号Yb，原子序数70。是稀土元素之 一。银白色金属，有延展性，质软。是重要的发光材料敏化剂。可制特种合金。');
INSERT INTO `chinese_infos` VALUES (7076, '隧', '金', 21, 'n', '（形声。从阜，遂声。阜指土山，从阜的字有的与建筑有关。本义：墓道，古墓中运送棺材到墓室的通道）');
INSERT INTO `chinese_infos` VALUES (7077, '镰', '金', 21, 'n', '形声。从金,廉声。本义农具名。即镰刀；镰,锲也。--《说文》；刈钩自关而西或谓之镰。--《方言五》；又如佩镰;两刃镰;开镰收割的季节,村里人都很忙；锋利的棱角；凡箭镞胡合嬴者四镰。--《方言九》');
INSERT INTO `chinese_infos` VALUES (7078, '邃', '金', 21, 'n', '1.深远（指空间或时间）2.精深');
INSERT INTO `chinese_infos` VALUES (7079, '鸧', '金', 21, 'n', '〔鸧鹒〕 也作仓庚。即 “黄鹂”');
INSERT INTO `chinese_infos` VALUES (7080, '嗫', '金', 21, 'n', '〔嗫嚅〕想说话而又吞吞吐吐不敢说出来的样子。嚅（rú ）。');
INSERT INTO `chinese_infos` VALUES (7081, '鏴', '金', 21, 'n', '1.古代有金黄色装饰的车。2.化学元素“镥”的旧译。');
INSERT INTO `chinese_infos` VALUES (7082, '躇', '金', 21, 'n', '见〖踌躇〗。');
INSERT INTO `chinese_infos` VALUES (7083, '虿', '金', 21, 'n', '蝎子一类的有毒的虫');
INSERT INTO `chinese_infos` VALUES (7084, '搀', '金', 21, 'n', '意思是指用手轻轻架住对方的手或胳膊。另外同“掺”，不过用得较少。');
INSERT INTO `chinese_infos` VALUES (7085, '麝', '金', 21, 'n', '1.哺乳动物，外形像鹿而小，无角，前腿短，后腿长，善于跳跃，尾巴短，毛黑褐色或灰褐色。雄麝的犬齿很发达，肚脐和生殖器之间有腺囊，能分泌麝香。也叫香獐子。2.麝香的简称。');
INSERT INTO `chinese_infos` VALUES (7086, '羼', '金', 21, 'n', '掺杂');
INSERT INTO `chinese_infos` VALUES (7087, '鄹', '金', 21, 'n', '1.古地名。在今山东曲阜东南。2.周朝国名。即邹。');
INSERT INTO `chinese_infos` VALUES (7088, '韂', '金', 21, 'n', '马鞍子下面垫的东西。');
INSERT INTO `chinese_infos` VALUES (7089, '呲', '金', 21, 'n', '〔～儿（cīr）〕方言，斥责，申斥，如“我被爸爸～儿（cīr）了一顿”。同“龇”。');
INSERT INTO `chinese_infos` VALUES (7090, '骢', '金', 21, 'y', '毛色青白相间的马。');
INSERT INTO `chinese_infos` VALUES (7091, '忏', '金', 21, 'n', '1.悔过2.旧时僧尼道士代人拜祷忏悔时念的经文或仪式');
INSERT INTO `chinese_infos` VALUES (7092, '儹', '金', 21, 'n', '同“攒1”。');
INSERT INTO `chinese_infos` VALUES (7093, '骁', '金', 22, 'y', '1.好马。2.勇猛矫健');
INSERT INTO `chinese_infos` VALUES (7094, '镬', '金', 22, 'n', '古代的大锅');
INSERT INTO `chinese_infos` VALUES (7095, '袭', '金', 22, 'y', '形声。从衣,从两龙省声。本义死者穿的衣服,衣襟在左边；袭,左衽袍也。--《说文》。按,凡敛死者,左衽,不纽。；陈袭事于房中,西领南上,不緋。--《仪礼·士丧礼》。郑玄注袭事,谓衣服也。；又如袭衣(尸衣;也指古代行礼时穿在裼衣外面的上衣);袭玩(衣着器用；姓；袭；衣一套为一袭；赐衣被一袭。--《汉书·昭帝纪》。注一称也。犹今一副。；乃命留五时衣各一袭。--《后汉书·东平宪王苍传》；袈衣一袭,止用一两。--《三宝太监西洋记通俗演义》');
INSERT INTO `chinese_infos` VALUES (7096, '镔', '金', 22, 'y', '镔铁，精炼的铁。');
INSERT INTO `chinese_infos` VALUES (7097, '隰', '金', 22, 'y', '1.低湿的地方。2.新开垦的田。3.姓。');
INSERT INTO `chinese_infos` VALUES (7098, '鉴', '金', 22, 'y', '形声。从金,监声。本义古代用来盛水或冰的青铜大盆；鑑,大盆也,一曰鑑诸,可以取明水于月。--《说文》。徐灏曰鑑,古祇作堅,从皿以盛水也。其后范铜为之,而用以照形者,亦谓之鑑,声转为镜。”；鑑谓之镜。--《广雅》；借明于鑑以炤之。--《淮南子·主术》；我心匪鑒。--《诗·邶风·柏舟》；卫灵公有妻三人,同鑑而浴。--《庄子》；春始治鑑。--《周礼》');
INSERT INTO `chinese_infos` VALUES (7099, '铸', '金', 22, 'y', '该字的基本字义是指把金属熔化后倒在模子里制成器物，如：铸铁、铸造、铸件、熔铸、铸币。');
INSERT INTO `chinese_infos` VALUES (7100, '踯', '金', 22, 'n', '〔踯躅〕徘徊不前。躅（zhú ）。');
INSERT INTO `chinese_infos` VALUES (7101, '摛', '金', 22, 'n', '舒展；散布');
INSERT INTO `chinese_infos` VALUES (7102, '冁', '金', 22, 'n', '笑的样子');
INSERT INTO `chinese_infos` VALUES (7103, '骣', '金', 22, 'n', '骑马不加鞍辔（pèi）');
INSERT INTO `chinese_infos` VALUES (7104, '躔', '金', 22, 'n', '1.兽的足迹。2.天体运行。');
INSERT INTO `chinese_infos` VALUES (7105, '慑', '金', 22, 'n', '恐惧；使害怕');
INSERT INTO `chinese_infos` VALUES (7106, '癣', '金', 22, 'n', '由真菌(尤其是发癣菌属和小孢子菌属)所引起的人类和家畜皮肤、毛发或趾(指)甲的接触性传染病,特征为皮肤有环形脱色斑,覆以疱疹及鳞屑；其中是多豪鱼…可以已白癣。--《山海经》');
INSERT INTO `chinese_infos` VALUES (7107, '镲', '金', 22, 'n', '钹（bó），一种打击乐器。');
INSERT INTO `chinese_infos` VALUES (7108, '撺', '金', 22, 'n', '1.抛掷。2.匆忙地做3.发怒');
INSERT INTO `chinese_infos` VALUES (7109, '蹰', '金', 22, 'n', '见  〔踟蹰〕');
INSERT INTO `chinese_infos` VALUES (7110, '仇', '金', 23, 'y', '当读时是深切的怨恨的意思：当读时是姓氏读音，古时同“逑”');
INSERT INTO `chinese_infos` VALUES (7111, '铄', '金', 23, 'y', '1.熔化（金属）2.销毁。3.同“烁”。');
INSERT INTO `chinese_infos` VALUES (7112, '镥', '金', 23, 'n', '金属元素，符号Lu（lutetium）。是一种稀土元素。银白色，质软。用于核工业。');
INSERT INTO `chinese_infos` VALUES (7113, '镳', '金', 23, 'n', '1.马嚼子的两端露出嘴外的部分2.同“镖”。');
INSERT INTO `chinese_infos` VALUES (7114, '纤', '金', 23, 'y', '细微，细小)(细致而详尽)(天然丝或化纤粗细的程度)');
INSERT INTO `chinese_infos` VALUES (7115, '鱚', '金', 23, 'n', '〔～鱼〕体呈圆筒形，长约十余厘米，银灰色，眼大，口小，吻长而尖，背鳍有硬刺。栖息在近海沙底。');
INSERT INTO `chinese_infos` VALUES (7116, '鹇', '金', 23, 'n', '见〖白鹇〗。');
INSERT INTO `chinese_infos` VALUES (7117, '鼷', '金', 23, 'n', '〔鼷鼠〕小家鼠。');
INSERT INTO `chinese_infos` VALUES (7118, '鳟', '金', 23, 'n', '鳟鱼，鱼类。银白色，背略带黑色。');
INSERT INTO `chinese_infos` VALUES (7119, '鸶', '金', 23, 'n', '见  〔鹭鸶〕');
INSERT INTO `chinese_infos` VALUES (7120, '颥', '金', 23, 'n', '颅骨之 一，即颞（niè）骨。');
INSERT INTO `chinese_infos` VALUES (7121, '髑', '金', 23, 'n', '〔髑髅〕死人的头 骨；骷髅。髅（lóu）。');
INSERT INTO `chinese_infos` VALUES (7122, '黪', '金', 23, 'n', '1.浅青黑色2.昏暗。');
INSERT INTO `chinese_infos` VALUES (7123, '髓', '金', 23, 'n', '人体骨髓、脊髓和脑髓的总称。由肾的精气与饮食精微所化生，有充养骨骼、补脑等作用。属奇恒之腑。');
INSERT INTO `chinese_infos` VALUES (7124, '銎', '金', 23, 'n', '斧子上安柄的孔。');
INSERT INTO `chinese_infos` VALUES (7125, '脏', '金', 23, 'n', '该字zang时字义是玷污，不干净或不纯洁的意思或指语言不文明,骂人；字义的是指身体内部器官的总称。');
INSERT INTO `chinese_infos` VALUES (7127, '鳝', '金', 23, 'n', '鳝鱼，通常指黄鳝。');
INSERT INTO `chinese_infos` VALUES (7128, '鑫', '金', 24, 'y', '财富兴盛（金多）。多用于商店的牌号或人名。');
INSERT INTO `chinese_infos` VALUES (7129, '谗', '金', 24, 'n', '在别人面前说某人的坏话');
INSERT INTO `chinese_infos` VALUES (7130, '瓒', '金', 24, 'y', '1.质地不纯的玉。2.古代祭祀时用于灌酒的一种玉器。');
INSERT INTO `chinese_infos` VALUES (7131, '瓉', '金', 24, 'n', '同“瓒”。');
INSERT INTO `chinese_infos` VALUES (7132, '骤', '金', 24, 'n', '基本意义为（马）快跑，急、疾速，突然等。骤既可以作形容词又可以作副词，常用词组有骤然，即突然，忽然的意思。');
INSERT INTO `chinese_infos` VALUES (7133, '蚕', '金', 24, 'n', '形声。本义一种能吐丝结茧的昆虫；遍身罗绮者,不是养蚕人。--宋·张俞《蚕妇》；又如蚕舍(蚕屋。蚕房);蚕精(蚕神);蚕蜕(蚕眠期所脱的皮；蚕事。养蚕的工作；罗敷善蚕桑。--《乐府诗集·陌上桑》');
INSERT INTO `chinese_infos` VALUES (7134, '矗', '金', 24, 'n', '可以做形容词，也可以动词用，字义的意思是直立。');
INSERT INTO `chinese_infos` VALUES (7135, '嘱', '金', 24, 'n', '本义：嘱托，叮嘱。');
INSERT INTO `chinese_infos` VALUES (7136, '谶', '金', 24, 'n', '迷信的人指将来要应验的预言、预兆');
INSERT INTO `chinese_infos` VALUES (7137, '钥', '金', 25, 'y', '门直闩。上穿横闩下插地上的直木户钥，自关而东，陈、楚之间谓之键，自关而西谓之钥。——《方言》枢要，重要之处又半里，有村在山北之麓，其名曰金村，乃是站之当钥者。——《徐霞客游记》2.，用于“锁钥(比喻做好一件事的关键，也比喻军事要地)”。');
INSERT INTO `chinese_infos` VALUES (7138, '镶', '金', 25, 'n', '形声。从金，从襄，襄亦声。“金”指“金属（铸造）”，“襄”意为“包裹”、“包容（异物）”。“金”与“襄”联合起来表示“在金属铸造过程中预留空间，以容纳玉石等其他材质的装饰物”。本义：在陶泥模型中预留暗道和空位，以待铜水浇注冷却后形成容纳装饰物的空间');
INSERT INTO `chinese_infos` VALUES (7139, '蹑', '金', 25, 'n', '1.踩；插进2.放轻（脚步）3.追踪');
INSERT INTO `chinese_infos` VALUES (7140, '齄', '金', 25, 'n', '古同“齇”。');
INSERT INTO `chinese_infos` VALUES (7141, '蹿', '金', 25, 'n', '1.向上、向前猛跳2.喷射；喷发');
INSERT INTO `chinese_infos` VALUES (7142, '鑶', '金', 25, 'n', '铃声。');
INSERT INTO `chinese_infos` VALUES (7143, '镵', '金', 25, 'n', '1.古代一种铁制的刨土工具。2.刺（cì）');
INSERT INTO `chinese_infos` VALUES (7144, '璛', '金', 25, 'n', '古同“玊”，琢玉的工人。');
INSERT INTO `chinese_infos` VALUES (7145, '赃', '金', 25, 'n', '形声。左形,右声。本义赃物；以赃获罪,没其先居之财。--《列子·天瑞》；又如赃仗(赃证);赃派(栽赃诬陷);栽赃(坐赃。把赃物或违禁物暗放在别人处,诬告他犯法);窝赃;追赃;分赃;退赃;销赃;赃品(赃物);赃银(赃款；赃；贪污;行贿；纳贿曰赃。--《广韵》；南阳太守韩昭坐赃,下狱死。--《后汉书·质帝纪》；所在虏掠,赃各千万计,并斩之。--《魏书·世祖纪》');
INSERT INTO `chinese_infos` VALUES (7146, '鲿', '金', 25, 'n', '1.鲿科鱼类的通称。2.毛鲿鱼，石首鱼科的一种。');
INSERT INTO `chinese_infos` VALUES (7147, '衅', '金', 25, 'n', '繁体“衅”。《说文》：“衅，血祭也。象祭灶也。从爨省，从酉。酉，所以祭也。从分，分亦声。”粤语（三声）和孕的粤语读音相同。基本字义1.古代用牲畜的血涂器物的缝隙：～钟。～鼓。2.缝隙，感情上的裂痕，争端：～隙。～端（争端）。挑～。寻～。3.以香熏身：～浴。');
INSERT INTO `chinese_infos` VALUES (7148, '镧', '金', 25, 'n', '金属元素，符号La（lanthanum）。是一种稀土元素。银白色，质软，在空气中容易氧化。用于制备钐、铕和镱，镧的化合物用来制光学玻璃、高温超导体等。');
INSERT INTO `chinese_infos` VALUES (7149, '骥', '金', 26, 'y', '1.好马2.比喻贤能。');
INSERT INTO `chinese_infos` VALUES (7150, '跹', '金', 26, 'n', '见  〔蹁跹〕');
INSERT INTO `chinese_infos` VALUES (7151, '镊', '金', 26, 'n', '夹取毛发、细刺及其他细小东西的器具，一般用金属制成，通称“镊子”。');
INSERT INTO `chinese_infos` VALUES (7152, '酾', '金', 26, 'n', '“酾1”shī的又音。1.又shāi2.滤（酒）。3.斟（酒）。4.疏导（河渠）。');
INSERT INTO `chinese_infos` VALUES (7153, '馋', '金', 26, 'n', '形声。从食)声。本义贪嘴,贪食；贪图;贪羡；馋看见好吃的食物就忍不住想吃；专爱吃好的～猫、～嘴。羡慕；贪。；【馋涎欲滴】馋得口水都快滴下来了。形容馋到了极点。也比喻对某种东西极想占有。；馋（饞）贪吃，专挑好的吃～嘴。贪官嘴～。贪，羡慕眼～手痒。');
INSERT INTO `chinese_infos` VALUES (7154, '镩', '金', 26, 'n', '1.一种凿冰工具，头部尖，有倒钩2.用冰镩凿（冰）');
INSERT INTO `chinese_infos` VALUES (7155, '趱', '金', 26, 'n', '赶（路）；快走（多见于早期白话）');
INSERT INTO `chinese_infos` VALUES (7156, '瞩', '金', 26, 'n', '注视');
INSERT INTO `chinese_infos` VALUES (7157, '钻', '金', 27, 'y', '表示动词，表用尖的物体在另一物体上转动或者穿过的意思，表名词，意为打孔的工具或者钻石。');
INSERT INTO `chinese_infos` VALUES (7158, '銮', '金', 27, 'y', '1.铃铛2.皇帝车驾上有銮铃，借指皇帝的车驾3.姓。');
INSERT INTO `chinese_infos` VALUES (7159, '颞', '金', 27, 'n', '也叫颞颥（rú ）。头部两侧靠近耳朵上方的部位。');
INSERT INTO `chinese_infos` VALUES (7160, '黩', '金', 27, 'n', '1.玷污。2.轻率；轻举妄动');
INSERT INTO `chinese_infos` VALUES (7161, '锣', '金', 27, 'n', '一种乐器，铜制，像盘，用槌子敲打出来|带卷边的青铜圆盘；鸣锣聚众。--《广东军务记》');
INSERT INTO `chinese_infos` VALUES (7162, '凿', '金', 27, 'n', '◎ 穿孔，挖掘：～孔。～井。～通。◎ 器物上的孔，是容纳枘（榫头）的。◎ 明确，真实：～～。证据确～。');
INSERT INTO `chinese_infos` VALUES (7163, '灥', '金', 27, 'n', '三泉。古同“泉”。下雨而泉水出。');
INSERT INTO `chinese_infos` VALUES (7164, '灨', '金', 27, 'n', '同“赣1”。');
INSERT INTO `chinese_infos` VALUES (7165, '犫', '金', 27, 'n', '古同“犨”。');
