INSERT INTO `chinese_infos` VALUES (8796, '荔', '木', 12, 'y', '草名。即马蔺”。又名马荔”。一种多年生草本植物，须根长而坚硬，叶片狭线形，花蓝色。花及种子可入药，叶可造纸，根可制刷子。古人对大雪节气有“鴠鸟不鸣，虎始交，荔挺生”的描述。');
