INSERT INTO `chinese_infos` VALUES (8802, '雅', '木', 12, 'y', '者，古正也。──《白虎通·礼乐》。按：说文疋下古文，以为诗大雅字。疋字隶体似正，故傅会训正，其实古文借疋为谞，后又借雅为谞也。风，讽也。雅，谞也，颂，诵也。此四始之本字。');
INSERT INTO `chinese_infos` VALUES (8803, '闳', '木', 12, 'y', '1.巷门。2.宏大。3.姓。');
INSERT INTO `chinese_infos` VALUES (8804, '稀', '木', 12, 'y', '汉字稀读音为；，有以下几个意思：；事物中间距离远、空隙大，与“密”相对，～疏。～落。～客。依～。；浓度小，含水分多的，与“稠”相对：～薄。～料。～释。；少：～少。～罕。～奇。古～之年。；用在“烂”、“松”等形容词前面，表明程度深：～烂。～碎。～松。');
INSERT INTO `chinese_infos` VALUES (8805, '荒', '木', 12, 'y', '字从艹，从巟；，巟亦声。“艹”指茂密的野草。“巟”指“沼泽地”。“艹”与“巟”联合起来表示“长满野草的沼泽地”。本义：长满野草的沼泽地。。');
INSERT INTO `chinese_infos` VALUES (8806, '厦', '木', 12, 'y', '或。该字读作时的字义是指大屋子、房子后面的突出的部分。读作时字义是指地名，在中国福建省的厦门。');
INSERT INTO `chinese_infos` VALUES (8807, '统', '木', 12, 'y', '形声。从糸,充声。本义丝的头绪；统,纪也。--《说文》；茧之性为丝,然非得工女煮以热汤而抽其统纪。--《淮南子·泰族训》；又如统绪(头绪；世代相继的系统；求其统类。--《荀子·解蔽》；又如皇统(世代相传的帝系);道统;传统;统承(继承统绪);统系(系统);统贯(系统;条贯);统嗣(帝统的嗣续关系；纲纪,准则；忠信以为质,端悫以为统。--《荀子·臣道》');
INSERT INTO `chinese_infos` VALUES (8808, '茼', '木', 12, 'y', '〔茼蒿〕一年生或二年生草本植物。叶长形，有香气。嫩茎、叶可食用。');
INSERT INTO `chinese_infos` VALUES (8809, '棣', '木', 12, 'y', '1.〔～棠〕落叶灌木，花黄色。果实黑色，供观赏。2.同“弟”〔～～〕文雅安闲的样子，如“威仪～～”。通');
INSERT INTO `chinese_infos` VALUES (8810, '第', '木', 12, 'y', '形声。从竹,弟声。本写作弟。本义次第,次序；。有时也用于数字前表示次序；第,次也。--《小尔雅·广诂》；书称题亦言第,因其第次也。--《释名·释书契》。按,用于书册次弟字,故又加竹。；楚国第。--《左传·哀公十六年》。释文次第也。；然臣辈惜程艺天下第一。--宋·王傥《唐语林·雅量》；此印者才毕,则第二板已具。--宋·沈括《梦溪笔谈·活板》；又如等第(名次等级);第次(等级;次第);第老的(排行最小的);第三本《诗经》(指宋朱熹编注的《诗经传》');
INSERT INTO `chinese_infos` VALUES (8811, '迦', '木', 12, 'y', '用于译音，也用于专名。');
INSERT INTO `chinese_infos` VALUES (8812, '杰', '木', 12, 'y', '从人从桀。才智出众的人。；形声。从人,桀声。杰为杰的俗字。本义:才智出众的人。；五人曰茂，十人曰选，百人曰俊，千人曰英，倍英曰贤，万人曰杰,万杰曰圣。——《白虎通圣人引辨名记》命太尉赞杰俊。——《礼记·月令》字义：1.才能出众的人：俊～。英雄豪～；2、特异的，超过一般的：～作。～出人才。佳～。在我国古书典籍中均有“杰”字出现。');
INSERT INTO `chinese_infos` VALUES (8813, '草', '木', 12, 'y', '形声。从苃,早声。小篆苃,象两棵草形,是草的本字。今草字系假借字,原是皂的本字。《说文》草,草斗,栎实也(栎实栎树的荚果,即皂角)。借为草木之；草以后,则另造皂字来代替。本义栎实；草本植物的总称；苃,百卉也。从二屮。会意。经传皆以草为之『书多以屮为之。--《说文》；大草不生。--《吕氏春秋·任地》。注草,秽也。；草食者羶。--《吕氏春秋·本味》；草苴比而不芳。--《楚辞·悲回风》。注生曰草。；苔痕上阶绿,草色入帘青。--唐·刘禹锡《陋室铭》');
INSERT INTO `chinese_infos` VALUES (8814, '策', '木', 12, 'y', '通常指计谋、计策等。在中国古代科举中，策指的是“策问”、“对策”。它是殿试考试的主要内容，“策问”与“对策”就成为出题与应试的两个部分，“策问”一般是以“皇帝的口吻”发问的，其内容主要是治国安邦、国计民生的政治大事。而士子们在应试的过程中便针对“策问”的内容作出回答，也就是所谓的“对策”。说的通俗一点，“策”某种程度上就相当于时事论文。');
INSERT INTO `chinese_infos` VALUES (8815, '寓', '木', 12, 'y', '出处：“又北三百八十里，曰虢山，其上多漆，其下多桐椐。其阳多玉，其阴多铁。伊水出焉，西流注于河。其兽多橐驼，其鸟多寓，状如鼠而鸟翼，其音如羊，可以御兵。');
INSERT INTO `chinese_infos` VALUES (8816, '植', '木', 12, 'y', '物，谷类、花草、树木等的统称。如：植保（植物保护）；植株（包括根、茎、叶等部分的植物体）；动植物。');
INSERT INTO `chinese_infos` VALUES (8818, '间', '木', 12, 'y', '参与；由于缝隙的距离都十分短近，因此又引申为抄近路；由缝隙、间隙引申为人与人或国与国之间的嫌隙、隔阂等');
INSERT INTO `chinese_infos` VALUES (8819, '栈', '木', 12, 'n', '从木从戋。牲口棚，马棚。；形声。从木,戋声。本义:牲口棚。；栈,棚也。——《说文》。按,栅者,竖编之,棚者,横编之。汉字，可做名词或动词。古代本义为牲口棚，现已引申出多个含义。(3)又如:皂栈(马房中的栅栏和方格木条)(4)古代用竹木条横排编成车箱的轻便车子');
INSERT INTO `chinese_infos` VALUES (8820, '椒', '木', 12, 'y', '本意是指花椒，芸香科植物，落叶灌木或小乔木。该文字在杜牧的《阿房宫赋》等文献均有记载。');
INSERT INTO `chinese_infos` VALUES (8821, '蛟', '木', 12, 'y', '蛟龙，古代传说中一种能发洪水的龙。');
INSERT INTO `chinese_infos` VALUES (8822, '绛', '木', 12, 'y', '深红色。');
INSERT INTO `chinese_infos` VALUES (8823, '尧', '木', 12, 'y', '古人名。传说中的中国 古代帝王。号陶唐氏，史称唐尧。他死后通过禅让制度由舜继位。');
INSERT INTO `chinese_infos` VALUES (8824, '极', '木', 12, 'y', '是汉语通用规范一级字（常用字） [；]  。极和它的繁体原是音义都不同的两个字，繁体极始见于秦代篆书，形声兼会意字，从木亟声，亟兼表义。本义指房屋的正梁，又叫脊檩，是最高处的檩。“极”由此有最高处的意义。引申指程度最高的，如“极品”“极佳”。转指最顶端的，北极、南极，指地球的南北两端。简体极始见于春秋《石鼓文》，形声字，从木及声，《说文解字》解释为放在驴背上用以载物的驮架，后成为“极”的简化字。');
INSERT INTO `chinese_infos` VALUES (8825, '雁', '木', 12, 'y', '形声。从隹,从人,厂声,隹,短尾鸟。厂,石山之涯岩,也有表意作用。本义鸟名；鸟类的一属,形状略像鹅,颈和翼较长,足和尾较短,羽毛淡紫褐色,善于游泳和飞行；雁,雁鸟也。--《说文》；纳采纳吉,请期皆用雁。--《仪礼·士昏礼》；下大夫相见以雁。--《仪礼·士相见礼》；朔风飘胡雁,惨澹带沙砾。--唐·杜甫《遣兴五首》');
INSERT INTO `chinese_infos` VALUES (8826, '珺', '木', 12, 'y', '一种美玉。');
INSERT INTO `chinese_infos` VALUES (8827, '开', '木', 12, 'y', '基本字义为：启，张，把关闭的东西打开。同义词：启；反义词：关、闭、谢、落。');
INSERT INTO `chinese_infos` VALUES (8828, '嵇', '木', 12, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (8829, '棚', '木', 12, 'y', '用竹木搭成架子，上面覆盖席、布等做成的遮蔽风雨日光的东西或简陋的小屋：天～。帐～。窝～。～车。');
INSERT INTO `chinese_infos` VALUES (8830, '凯', '木', 12, 'y', '军队得胜回来奏的乐曲。衍义：又用作姓 ');
INSERT INTO `chinese_infos` VALUES (8831, '几', '木', 12, 'y', '常用汉字。有和两个常用来询问数量多少，表大概，一般表示整数。');
INSERT INTO `chinese_infos` VALUES (8832, '茵', '木', 12, 'y', '本义：车垫子。茵还有衬垫；褥子等意思。《秦风·小戎》等均有相关记载。');
INSERT INTO `chinese_infos` VALUES (8833, '景', '木', 12, 'y', '本义指日光。 [2]  “风景”本指风与日光，后凝固成复合词，指景色。有光才能成影，“景”引申出阴影的“影”。阴影的“影”本来就用“景”表示。景也有大土堆的意思，又指大。北京“景山”就取此义。');
INSERT INTO `chinese_infos` VALUES (8834, '集', '木', 12, 'y', '常见汉字。有聚在一起、集市、集子、集姓等意思。另外，集是数学中集合的简称，同时也是控制论中的一个概念。同时，集也是一种游戏。简单而益智，一个集能发小波,两个集能发中波,三个集能发大波.大部分中小学生都玩过集.也对集有各自的理解地名用字，如：张表集、店集镇、大李集等。');
INSERT INTO `chinese_infos` VALUES (8835, '莒', '木', 12, 'n', '1.莒县，地名。在山东。2.周朝国名。在今山东莒县。');
INSERT INTO `chinese_infos` VALUES (8836, '棋', '木', 12, 'y', '形声。从木,其声。本义古时通称博奕的子为棋；棊,博棊也。--《说文》；五簙或谓之棊。--《方言》；故行棊者。--《淮南子·泰族》。注谓六博也。；休与之山,其上有石焉,名曰帝台之棋。--《山海经·中山经》。注;博棋也。；尧造围棊,以教子丹朱,或云舜以子商均愚,故作围棊教之。--《博物志》；山出棋置。--《史记·货殖列传》');
INSERT INTO `chinese_infos` VALUES (8837, '森', '木', 12, 'y', '《说文》中说“森，木多貌。”可见，“森”的本义是树木茂密的样子。后引申为众多。又由本义引申为阴森，幽暗的样子。');
INSERT INTO `chinese_infos` VALUES (8838, '荃', '木', 12, 'y', '古书上说的一种香草。');
INSERT INTO `chinese_infos` VALUES (8839, '贯', '木', 12, 'y', '本义为古代穿钱的绳索（把方孔钱穿在绳子上，每一千个为一贯）。引申为“穿，通，连” 、古同“惯”，习惯、原籍，出生地 、又用作姓');
INSERT INTO `chinese_infos` VALUES (8840, '栋', '木', 12, 'y', '是形声字，木为形，东为声。；。本意是指屋子正中最高处的东西向横木，引申为担负重任的人或事物。');
INSERT INTO `chinese_infos` VALUES (8841, '邱', '木', 12, 'y', '1.姓。2.“丘”3.的异体字。');
INSERT INTO `chinese_infos` VALUES (8842, '棉', '木', 12, 'y', '会意。从木,从帛。植物名；木棉,也叫攀枝花、英雄树；棉,木棉,树名。《吴录》云其实如酒杯,中有绵如蚕绵,可作布。--《广韵》');
INSERT INTO `chinese_infos` VALUES (8843, '阮', '木', 12, 'y', '也叫阮咸。拨弦乐器。相传因西晋阮咸善弹这种乐器而得名。琴身木制、圆形，颈较长，四根弦。用拨子或假指甲弹奏。现经改革，分小阮、中阮、大阮、低阮四种。常用于伴奏及合奏。');
INSERT INTO `chinese_infos` VALUES (8844, '期', '木', 12, 'y', '形声。从月,其声。金文从日,其声。战国古文从日,丌声。日、月都有表时间的意思。本义约会,约定；期,会也。--《说文》。段注会者,合也,期者,邀约之意,所以为会合也。；归妹愆期。--《易·归妹》；若旬虚期于月津。--《管子·侈靡》。注匝一月曰期。；而不能期月守也。--《礼记·中庸》；叔孙旦而立期焉。--《左传·昭公二十三年》；与老人期,后何也?--《史记·留侯世家》；与友期行。--《世说新语·方正》；又；期日中。；期我决斗。--明·魏禧《大铁椎传》');
INSERT INTO `chinese_infos` VALUES (8845, '欺', '木', 12, 'y', '形声。从欠，其声。“欠”与出气、说话有关。本义：欺骗；施诈。');
INSERT INTO `chinese_infos` VALUES (8846, '皋', '木', 12, 'y', '1.水边的高地，岸2.沼泽，湖泊号呼；呼告');
INSERT INTO `chinese_infos` VALUES (8847, '筌', '木', 12, 'y', '捕鱼的竹器');
INSERT INTO `chinese_infos` VALUES (8848, '荦', '木', 12, 'y', '明显');
INSERT INTO `chinese_infos` VALUES (8849, '络', '木', 12, 'y', '经络是人体的重要组成部分。');
INSERT INTO `chinese_infos` VALUES (8850, '祁', '木', 12, 'y', '1.指安徽祁门2.指湖南祁阳3.姓。');
INSERT INTO `chinese_infos` VALUES (8851, '乔', '木', 12, 'y', '会意。从夭,从高省,高亦声。夭,象人(大)行走的样子。本义:高；高耸。多用以形容树木乔,高而曲也。——《说文》乔，繁体乔字从夭从高。乔字属于夭字族。在夭字族里，夭字都是声符兼义符。夭字族汉字都与身形丰满、肥壮之义有关。乔就是“向上圆曲凸起”。而且乔还是一种姓氏。');
INSERT INTO `chinese_infos` VALUES (8852, '茜', '木', 12, 'y', '1.茜草，多年生攀缘草本植物。茎方形，有倒刺。根黄红色，可提取染料，也可供药用。2.红色音译用字。多用于外国女子名。');
INSERT INTO `chinese_infos` VALUES (8853, '茗', '木', 12, 'y', '原指某种茶叶，今泛指喝的茶');
INSERT INTO `chinese_infos` VALUES (8854, '笋', '木', 12, 'y', '形声。从竹,尹声。本义竹笋；竹子初从土里长出的嫩芽,味鲜美,可以做菜,也叫竹笋；箂,竹胎也。--《说文》。字亦作笋。；绚笋及蒲。--《诗·大雅·韩奕》；苞笋抽节。--左思《吴都赋》；如筴竹笋,有二月生者,有三四月生者,有五月方生者谓之晚筴。--宋·沈括《梦溪笔谈》；又如笋玉(喻指女人的手指);笋剥(像剥笋似地脱掉,剥光);笋枯(笋干);笋鞭(竹的地下茎);笋尖(笋的尖嫩部分);笋衣(笋壳；竹的青皮；敷重笋席。--《书·顾命》');
INSERT INTO `chinese_infos` VALUES (8855, '茹', '木', 12, 'y', '1.吃2.姓。');
INSERT INTO `chinese_infos` VALUES (8856, '耜', '木', 12, 'y', '1.古代的一种农具，形状像现在的锹。2.古代跟犁上的铧相似的东西。');
INSERT INTO `chinese_infos` VALUES (8857, '喆', '木', 12, 'y', '同“哲”，多用于人名。');
INSERT INTO `chinese_infos` VALUES (8858, '犇', '木', 12, 'n', '同“奔跑的奔”。');
INSERT INTO `chinese_infos` VALUES (8859, '琯', '木', 12, 'n', '古代乐器，用玉制成，六孔，像笛。');
INSERT INTO `chinese_infos` VALUES (8860, '桠', '木', 12, 'n', '丫杈');
INSERT INTO `chinese_infos` VALUES (8861, '闶', '木', 12, 'n', '〔闶阆〕〈方〉也叫闶阆子。建筑物中空廓的部分。阆（láng）。高大。');
INSERT INTO `chinese_infos` VALUES (8862, '答', '木', 12, 'n', '的本义是以竹补篱，答谢，报答。答也是回族姓。回族中的答姓，据始修于清嘉庆五年（；800年）的《答氏宗谱》记载，现居于湖北钟祥、两阳等地的回族，其始祖“原籍西域，自元世祖至元癸已（；293年），讳刺辛与刺海祖，同孛可入，献大珠，赐价不售，帝嘉其廉，命姓答氏”。');
INSERT INTO `chinese_infos` VALUES (8863, '茯', '木', 12, 'n', '〔茯苓〕真菌的一 种。形似瓜、拳、瓦罐等。表面淡黑色或紫褐色，内部粉白色。多寄生在松树根上。可供药用，主治小 便不利、水肿等。');
INSERT INTO `chinese_infos` VALUES (8864, '等', '木', 12, 'n', '具有多重含义。同时也有很多流行歌曲以此为名。等韵学研究的对象，通常二名不分。它是以音节表为主要方式对汉语字音进行分析的一门学科，是汉语音韵学的一个分支。');
INSERT INTO `chinese_infos` VALUES (8865, '棵', '木', 12, 'n', '木，从果，果亦声。“果”指“果实”。“木”与“果”联合起来表示“已经初次结果的果树”、“正式列入统计数字的成熟果树”。本义：正式列入统计数字的成熟期果树。引申义：（成熟）植物的植株单位。');
INSERT INTO `chinese_infos` VALUES (8866, '钪', '木', 12, 'n', '金属元素，符号Sc（scandium）。是一种稀土元素。银白色，质软。用来制特种玻璃、轻质耐高温合金等。');
INSERT INTO `chinese_infos` VALUES (8867, '荇', '木', 12, 'n', '多年生草本植物，叶子略呈圆形，浮在水面，根生在水底，花黄色，蒴果椭圆形。茎可以吃，全草入药。');
INSERT INTO `chinese_infos` VALUES (8868, '晷', '木', 12, 'n', '1.日影。比喻时间2.日晷。');
INSERT INTO `chinese_infos` VALUES (8869, '悻', '木', 12, 'n', '〔悻悻〕怨恨、愤怒的神态');
INSERT INTO `chinese_infos` VALUES (8870, '茴', '木', 12, 'n', '茴香；茴,茴香。--《玉篇》；蘹香,北人呼为茴香,声相近也。--《本草纲目》引苏颂《图经本草》');
INSERT INTO `chinese_infos` VALUES (8871, '筒', '木', 12, 'n', '形声。从竹,同声。本义:竹筒,粗大的竹管');
INSERT INTO `chinese_infos` VALUES (8872, '傀', '木', 12, 'n', '一般是指木偶戏里的木头人');
INSERT INTO `chinese_infos` VALUES (8873, '喟', '木', 12, 'n', '叹气');
INSERT INTO `chinese_infos` VALUES (8874, '贶', '木', 12, 'n', '赠送');
INSERT INTO `chinese_infos` VALUES (8875, '皖', '木', 12, 'y', '安徽的别称。');
INSERT INTO `chinese_infos` VALUES (8876, '椁', '木', 12, 'n', '古代套在棺材外面的大棺材');
INSERT INTO `chinese_infos` VALUES (8877, '椭', '木', 12, 'n', '形声。从木,隋声。本义长圆形,狭长；其方圆锐椭不同,盛水各异,其于灭火钧也。--《淮南子》；椭；指某些长圆形的容器；椭,车笭中椭椭器也。--《说文》');
INSERT INTO `chinese_infos` VALUES (8878, '喉', '木', 12, 'n', '喉头；喉,咽也。--《说文》；喉咽者,量肠之府也。--《后汉书·马融传》；断其喉。--唐·柳宗元《三戒》');
INSERT INTO `chinese_infos` VALUES (8879, '猓', '木', 12, 'n', '〔～然〕古书上指一种长尾猿。');
INSERT INTO `chinese_infos` VALUES (8880, '棱', '木', 12, 'y', '是一个木字旁汉字，指的是物体上的条状突起，或不同方向的两个平面相连接的部分。');
INSERT INTO `chinese_infos` VALUES (8881, '筏', '木', 12, 'n', '是一个字，基本意思是用竹、木等平摆着编扎成的水上交通工具。');
INSERT INTO `chinese_infos` VALUES (8882, '聒', '木', 12, 'n', '声音嘈杂，使人厌烦');
INSERT INTO `chinese_infos` VALUES (8883, '珶', '木', 12, 'n', '玉名。〔～瑭〕玉名。亦作“瑅瑭”。');
INSERT INTO `chinese_infos` VALUES (8884, '筅', '木', 12, 'n', '炊帚（多指用竹子做的）。');
INSERT INTO `chinese_infos` VALUES (8885, '棼', '木', 12, 'n', '纷乱');
INSERT INTO `chinese_infos` VALUES (8886, '棍', '木', 12, 'n', '能够转动,用以缠绕线、弦的旋纽；棍,转也。谓箜篌上转绳也。--唐·玄应《一切经音义》；棍；棍棒；是那一个实丕丕将着粗棍敲,打的来痛杀杀精皮掉。--《元曲选·纪君祥·赵氏孤儿》；又如大棍;齐眉棍;三节棍;二节棍;大梢子棍;手捎子棍；品行很坏的人；山儿,我如今放你去,若拿得这两个棍徒,将功折罪;若拿不得,二罪俱罚。--元·康进之《李逵负荆》');
INSERT INTO `chinese_infos` VALUES (8887, '筐', '木', 12, 'n', '指竹子或柳条等编成的盛东西的器具。如：筐子，土筐，抬筐。');
INSERT INTO `chinese_infos` VALUES (8888, '羟', '木', 12, 'n', '〔～基〕化学名词，由氢和氧两种原子组成的一价原子团。也叫氢氧基。羊名。《说文·羊部》');
INSERT INTO `chinese_infos` VALUES (8889, '控', '木', 12, 'n', '告发；控告；指出罪恶：～诉∣指～∣被～∣。2控制；驾驭：遥～。；3.受控：弓不再～。 ④投：～于地。 ⑤使身体或身体的一部分处于失去支撑的状态：腿都～肿了∣枕头掉了。');
INSERT INTO `chinese_infos` VALUES (8890, '蛞', '木', 12, 'n', '1.〔～蝓〕软体动物，身体像蜗牛，但没有壳，吃蔬菜或瓜果的叶子，对农作物有害。亦称“蜒蚰”；俗称“鼻涕虫”。2.〔～蝼〕古书上指“蝼蛄”。');
INSERT INTO `chinese_infos` VALUES (8891, '驭', '木', 12, 'n', '1.驾驭2.统率；控制');
INSERT INTO `chinese_infos` VALUES (8892, '喈', '木', 12, 'n', '1.〔～～〕a.声音和谐，如“鼓钟～～”；b.鸟声，如“鸡鸣～～”。2.疾速的样子');
INSERT INTO `chinese_infos` VALUES (8893, '街', '木', 12, 'n', '指居民区、城镇中交通功能较完善、两边有房屋的、比较宽阔的道路，通常指开设商店的地方：如：街道，大街小巷。');
INSERT INTO `chinese_infos` VALUES (8894, '结', '木', 12, 'y', '从糸从吉。糸，表示与线丝有关；吉，将兵器安放在兵器架上，不用，以减少战争，使人民没有危难，为吉。本义：用线、绳、草等条状物、将兵器束或编织起来。结，用绳、线、皮条等绾成的疙瘩。');
INSERT INTO `chinese_infos` VALUES (8895, '傜', '木', 12, 'n', '同“徭”。');
INSERT INTO `chinese_infos` VALUES (8896, '饫', '木', 12, 'n', '饱。');
INSERT INTO `chinese_infos` VALUES (8897, '笔', '木', 12, 'y', '会意。从竹,从聿。聿),是笔的本字,小篆象以手执笔。古时毛笔笔杆都是以竹制成,故从竹。简化字笔,从竹从毛会意,指旧时用的毛笔。此字最早见于北齐；隽修罗碑,是六朝时的俗字。也见于《集韵》。本义毛笔；筽,秦谓之筽。从聿从竹。--《说文》。按,此秦制字。秦以竹为之,加竹。；史载笔,士载言。--《礼记·曲礼》；不能竟书而搁笔。--清·林觉民《与妻书》');
INSERT INTO `chinese_infos` VALUES (8898, '筋', '木', 12, 'n', '会意。从肉,从力,从竹。因为竹多筋,故从竹。本义附着在骨上的韧带；筋,肉之力也。--《说文》；诸筋者,皆属于节。--《素问·五藏生成论》；强者在内而摩其筋。--《考工记·弓人》；劳其筋骨。--《孟子·告子下》；筋骨之强。--《荀子·劝学》；先折筋骨。--清·方苞《狱中杂记》；筋骨尽脱。--清·方苞《左忠毅公逸事》');
INSERT INTO `chinese_infos` VALUES (8899, '茶', '木', 12, 'y', '茶树；茶叶。由茶树的嫩叶加工制成,供泡取饮料用；前月浮梁买茶去。--唐·白居易《琵琶行(并序)》；又如茶市(茶叶市场；用茶叶泡制、烹制或煎制而成的饮料；唐人煎茶,用姜用盐。--苏轼《东坡志林》；日高人渴漫思茶。--宋·苏轼《浣溪沙》');
INSERT INTO `chinese_infos` VALUES (8900, '悸', '木', 12, 'n', '因害怕而心跳得厉害');
INSERT INTO `chinese_infos` VALUES (8901, '荩', '木', 12, 'n', '1.〔荩草〕一年生细弱草本植物。生于山野地区，除供放牧外，它的汁液可作黄色染料，纤维可作造纸原料。2.忠');
INSERT INTO `chinese_infos` VALUES (8902, '惊', '木', 12, 'y', '形声。从马,敬声。本义马受惊；驚,马骇也。--《说文》；襄子至桥而马惊。--《战国策·赵策一》；又如惊嘶(马受惊而嘶叫);惊尘(车马疾驶所扬起的尘土);惊镳(惊马；惊动;震惊；宫庭震惊。--《楚辞·招魂》；其生若惊。--《吕氏春秋·慎大》；震惊百里。--《易·震卦》。郑注惊之言警戒也。；皆惊忙。--《乐府诗集·木兰诗》；月出惊山鸟。--唐·王维《鸟鸣涧》；惊问信之。--明·魏禧《大铁椎传》');
INSERT INTO `chinese_infos` VALUES (8903, '阱', '木', 12, 'n', '该字的主要字义是指捕野兽用的陷坑：陷阱。');
INSERT INTO `chinese_infos` VALUES (8904, '哟', '木', 12, 'n', '助词，用在句末表示祈使的语气。用在歌词中做衬字。');
INSERT INTO `chinese_infos` VALUES (8905, '最', '木', 12, 'y', '从冃,从取。本义:冒犯夺取。《说文》犯而取也。《广韵》极也。');
INSERT INTO `chinese_infos` VALUES (8906, '犍', '木', 12, 'n', '指犍牛犍为（Qiánwéi），地名，在四川。');
INSERT INTO `chinese_infos` VALUES (8907, '棕', '木', 12, 'n', '释义：1. 植物名，棕榈、棕树；2. 棕色。');
INSERT INTO `chinese_infos` VALUES (8908, '椎', '木', 12, 'n', '敲打东西的器具；敲打。如“椎心泣血”。；，椎骨(大多指构成高等动物背部中央骨柱的不规则骨)。如“脊椎”。');
INSERT INTO `chinese_infos` VALUES (8909, '棒', '木', 12, 'n', '形声。从木,奉声。本义棍,杖；人马逼战,刀不如棒。--《魏书·尔朱荣传》；又如棒糖;棍棒;棒打鸳鸯(以强硬手段,破坏他人的夫妇关系,或指阻挠男女的婚事；棒；用棍棒打；其或迟违,则赤棒棒之。--《北齐书·琅琊王(萧)俨传》');
INSERT INTO `chinese_infos` VALUES (8910, '窖', '木', 12, 'n', '收藏东西的地洞：冰窖；白菜窖；地窖。2指物品贮存在地洞里：把白菜窖起来。');
INSERT INTO `chinese_infos` VALUES (8911, '茱', '木', 12, 'n', '〔茱萸〕灌木或小乔木。果实供药用。另有吴茱萸，灌木，花蕾供药用。');
INSERT INTO `chinese_infos` VALUES (8912, '茭', '木', 12, 'n', '喂牲口的干草。');
INSERT INTO `chinese_infos` VALUES (8913, '跏', '木', 12, 'n', '〔跏趺〕佛教徒修行的一种坐法。盘腿而坐，脚背放在股上。趺（fū）。');
INSERT INTO `chinese_infos` VALUES (8914, '棹', '木', 12, 'n', '1.划船的一种工具，形状和桨差不多。2.划船3.船同“桌”。');
INSERT INTO `chinese_infos` VALUES (8915, '绞', '木', 12, 'n', '把住物体的两头拧：绞干；绞毛巾。2勒死；吊死：绞刑；绞架。；3.把绳索一端系在轮上，转动轮子，使系在另一端的物体移动：绞盘；绞车。④费心思：绞尽脑汁。⑤量词：一绞纱');
INSERT INTO `chinese_infos` VALUES (8916, '袷', '木', 12, 'n', '同“夹”。〔～袢〕无领大衣；中国维吾尔和塔吉克等民族的对襟长袍。古代交叠于胸前的衣领。');
INSERT INTO `chinese_infos` VALUES (8917, '痉', '木', 12, 'n', '〔痉挛〕指骨骼肌、平滑肌等局部紧张，较长时间收缩。常由于中枢神经系统受刺激，肌肉本身受束缚、损伤或寒冷引起。如腓肠肌痉挛、胃痉挛等。');
INSERT INTO `chinese_infos` VALUES (8918, '荈', '木', 12, 'n', '茶的老叶，即粗茶。');
INSERT INTO `chinese_infos` VALUES (8919, '硬', '木', 12, 'n', '基本语义是指坚固，结实。可作动词，副词，形容词，放到相关组词语境中其义非常丰富。');
INSERT INTO `chinese_infos` VALUES (8920, '距', '木', 12, 'n', '义为“包罗万象”、“任何事物”。“足”与“巨”联合起来表示“观察者的足趾尖与任何物体的直线间隔。');
INSERT INTO `chinese_infos` VALUES (8921, '犋', '木', 12, 'n', '牵引犁、耙等农具的畜力单位，能拉动一种农具的畜力叫一犋，有时是一头牲口，有时是两头或两头以上。');
INSERT INTO `chinese_infos` VALUES (8922, '楮', '木', 12, 'n', '1.也叫榖。通称构树。落叶乔木。叶似桑叶而粗糙，果圆球形，熟时红色。树皮可造纸，叶可作猪饲料。2.纸的代称');
INSERT INTO `chinese_infos` VALUES (8923, '篅', '木', 12, 'n', '〈方〉用竹篾编成的盛粮食的器物。类似囤。');
INSERT INTO `chinese_infos` VALUES (8924, '荑', '木', 12, 'n', '除去田地里的野草1.植物初生的叶芽。2.稗子一类的草。');
INSERT INTO `chinese_infos` VALUES (8925, '椅', '木', 12, 'n', '木名。即山桐子；另见譹；椅；(形声。从木,奇声。；椅子,有靠背的坐具；椅,坐具后有倚者,今人俗呼椅子。--《正字通》；又如椅杌(椅子和凳子);椅背(椅披。椅子的靠背；轿子；皇帝乘椅,自便殿举至西便门。--《辽史》');
INSERT INTO `chinese_infos` VALUES (8926, '棰', '木', 12, 'n', '1.短木棍。2.用棍子打。3.同“箠”。4.同“槌”。');
INSERT INTO `chinese_infos` VALUES (8927, '掘', '木', 12, 'n', '挖：掘土；挖掘；发掘。');
INSERT INTO `chinese_infos` VALUES (8928, '掩', '木', 12, 'n', '原意指遮蔽、遮盖，也有关、闭之意，或指乘人不备而袭击或捉拿。');
INSERT INTO `chinese_infos` VALUES (8929, '茈', '木', 12, 'n', '茈湖口（Zǐhúkǒu），地名，在湖南。见〖凫茈〗。');
INSERT INTO `chinese_infos` VALUES (8930, '茨', '木', 12, 'y', '1.用茅草和芦苇盖屋，也指用茅草盖的屋。2.蒺藜。');
INSERT INTO `chinese_infos` VALUES (8931, '喀', '木', 12, 'n', '形容呕吐、咳嗽等的声音。');
INSERT INTO `chinese_infos` VALUES (8932, '掎', '木', 12, 'n', '1.拉住；拖住。引申为牵制。2.通“倚（yǐ）”。支撑。');
INSERT INTO `chinese_infos` VALUES (8933, '枨', '木', 12, 'n', '触动');
INSERT INTO `chinese_infos` VALUES (8934, '窘', '木', 12, 'n', '表示难堪；觉得应付不了，窘况，窘态毕露，出于《离骚》。');
INSERT INTO `chinese_infos` VALUES (8935, '啾', '木', 12, 'n', '1.〔～～〕象声词，形容动物细小的叫声，如“～～鸟鸣”。2.〔～唧〕小声，如“秋虫～唧”。');
INSERT INTO `chinese_infos` VALUES (8936, '茺', '木', 12, 'n', '〔茺蔚〕即“益母草”');
INSERT INTO `chinese_infos` VALUES (8937, '讵', '木', 12, 'n', '1.文言副词。难道；岂。表示反问2.文言连词。如果');
INSERT INTO `chinese_infos` VALUES (8938, '厩', '木', 12, 'n', '马棚。泛指牲口棚');
INSERT INTO `chinese_infos` VALUES (8939, '掬', '木', 12, 'n', '1.两手捧（东西）。笑容可～（笑容露出来，好像可以用手捧住，形容笑得明显）。憨态可～。2.姓。');
INSERT INTO `chinese_infos` VALUES (8940, '椐', '木', 12, 'n', '古书上说的一种小树。即灵寿木。多肿节，古时以为手杖。');
INSERT INTO `chinese_infos` VALUES (8941, '殛', '木', 12, 'n', '杀死');
INSERT INTO `chinese_infos` VALUES (8942, '棘', '木', 12, 'n', '朿”是“刺”的本字。两个“朿”字并排立着，表示棘树多刺，是矮小而成丛莽的灌木。本义：丛生的小枣树');
INSERT INTO `chinese_infos` VALUES (8943, '戢', '木', 12, 'n', '1.收敛；收藏2.停止');
INSERT INTO `chinese_infos` VALUES (8944, '迭', '木', 12, 'n', '1.更换；轮流2.屡次3.及');
INSERT INTO `chinese_infos` VALUES (8945, '胳', '木', 12, 'n', '形声。从肉，从各，各亦声。“肉”指身体、肢体。');
INSERT INTO `chinese_infos` VALUES (8946, '掐', '木', 12, 'n', '动词，用指甲按或截断：掐算。掐花。掐尖儿。动词，割断，截去：掐头去尾。动词，用手的虎口及手指紧紧握住：一把掐住。动词，拇指与另一手指捏合的动作：掐进一个量');
INSERT INTO `chinese_infos` VALUES (8947, '覃', '木', 12, 'y', '姓。1.延及。2.深');
INSERT INTO `chinese_infos` VALUES (8948, '棺', '木', 12, 'n', '形声。从木,官声。本义棺材,装殓尸体的器具)。亦称棺木,棺函,棺柩,棺衬；棺,榇也,所以掩尸。孝经郑注周尸为棺。--《说文》；棺之为言完,所以藏尸令完全也。--《白虎通》；阳作木棺,有虞氏用瓦棺。--《古文考》');
INSERT INTO `chinese_infos` VALUES (8949, '袼', '木', 12, 'n', '〔袼褙〕用布或纸裱糊成的厚片，用来做布鞋或书套等物。');
INSERT INTO `chinese_infos` VALUES (8950, '诂', '木', 12, 'n', '用当代的话解释古代文献词语的意义');
INSERT INTO `chinese_infos` VALUES (8951, '胱', '木', 12, 'n', '含有二硫键（—S—S—）的氨基酸，广泛存在于毛、发、骨、角中。');
INSERT INTO `chinese_infos` VALUES (8952, '雇', '木', 12, 'n', '出钱让人为自己做事：～工。～主。～用。～员。～佣。～请。；租赁交通运输工具：～车。');
INSERT INTO `chinese_infos` VALUES (8953, '酤', '木', 12, 'n', '1.薄酒；清酒。2.买（酒）。3.卖（酒）。');
INSERT INTO `chinese_infos` VALUES (8954, '栖', '木', 12, 'y', '多为停留的意思。');
INSERT INTO `chinese_infos` VALUES (8955, '球', '木', 12, 'y', '本义为玉的一种，特指玉磬。引申义为球形物体。');
INSERT INTO `chinese_infos` VALUES (8956, '蛐', '木', 12, 'n', '1.〔～～儿儿〕蟋蟀的俗称。2.〔～蟮〕即“蚯蚓”。');
INSERT INTO `chinese_infos` VALUES (8957, '诎', '木', 12, 'n', '1.缩短。2.言语迟钝。3.同“屈”。4.姓。');
INSERT INTO `chinese_infos` VALUES (8958, '椑', '木', 12, 'n', '〔椑柿〕古书上说的一种植物。果实似柿而小，色青黑，可以制柿漆。古代盛酒的器皿。');
INSERT INTO `chinese_infos` VALUES (8959, '轱', '木', 12, 'n', '〔～辘〕ａ．车轮，如“车～辘”；ｂ．滚动，转（zhuǎn）动，如“当心，别从坡上～辘下去”。均亦作“轱轳”、“毂辘”（“辘”、“轳”均读轻声）。');
INSERT INTO `chinese_infos` VALUES (8960, '觚', '木', 12, 'n', '1.古代一种盛酒器具。2.古代用来写字的木简3.棱角。');
INSERT INTO `chinese_infos` VALUES (8961, '荏', '木', 12, 'n', '1.即白苏。参见“苏”2.软弱');
INSERT INTO `chinese_infos` VALUES (8962, '秆', '木', 12, 'n', '基本意思是指用盾牌抵挡毒辣阳光以保护庄稼');
INSERT INTO `chinese_infos` VALUES (8963, '掮', '木', 12, 'n', '用肩扛（东西）');
INSERT INTO `chinese_infos` VALUES (8964, '茛', '木', 12, 'n', '1.野葛，现叫钩吻。藤本植物。根和叶有剧毒。2.毛茛，多年生草本植物。茎叶有毛，花黄色，植株有毒。');
INSERT INTO `chinese_infos` VALUES (8965, '嵌', '木', 12, 'n', '形声。本义山谷深的样子；张开的样子；金人仡仡其承钟虚兮,嵌岩岩其龙鳞。--《文选·扬雄·甘泉赋》；嵌岩窦穴。--宋·陆游《过小孤山大孤山》');
INSERT INTO `chinese_infos` VALUES (8966, '茸', '木', 12, 'y', '公梅花鹿或公马鹿头上一对带细毛的嫩角。');
INSERT INTO `chinese_infos` VALUES (8967, '给', '木', 12, 'n', '有和两种读音，常用作动词、介词和助词。表示交付、送与、替，为、被，表示遭受、把，将等；还表示供应、富裕，充足、敏捷意。');
INSERT INTO `chinese_infos` VALUES (8968, '猊', '木', 12, 'n', '见〔狻猊〕');
INSERT INTO `chinese_infos` VALUES (8969, '椋', '木', 12, 'n', '〔椋鸟〕鸟类。种类很多。羽毛大部分为灰褐色，喙、足橙红色。食种子和昆虫。有的善于模仿别的鸟叫。如八哥、灰椋鸟等。');
