INSERT INTO `chinese_infos` VALUES (11101, '鳒', '木', 21, 'n', '鱼，体侧扁，长卵圆形，一般两眼都在身体的左侧，也有在右侧的，上方的眼睛靠近头顶，有眼的一侧黄褐色，无眼的一侧白色。生活在海洋中。');
