INSERT INTO `chinese_infos` VALUES (11104, '鳑', '木', 21, 'n', '鱼，体侧扁，卵圆形，银灰色，多带橙黄色或蓝色斑纹，背鳍和臀鳍较长。生活在淡水中，吃水生植物，卵产在蚌壳里。');
