INSERT INTO `chinese_infos` VALUES (11109, '䲢', '木', 21, 'n', '鱼，身体近圆筒形，头大而阔，眼小，下颌突出，有两个背鳍，青灰色，有褐色斑纹。常栖息在海底，捕食小鱼等。种类很多，常见的有鱼䲢、青䲢等。');
