INSERT INTO `chinese_infos` VALUES (11124, '鶷', '木', 21, 'n', '〔～𪆰〕ａ．百舌鸟，似伯劳而较小。ｂ．白头乌，一种鸟，似乌鸦而比乌鸦大，颈项白色。');
