INSERT INTO `chinese_infos` VALUES (11127, '鹡', '木', 21, 'n', '鸟，背部羽毛颜色纯一，中央尾羽比两侧的长，停息时尾上下摆动。生活在水边，吃昆虫等。种类较多，常见的有白鹡鸰。');
