INSERT INTO `chinese_infos` VALUES (11166, '蔼', '木', 22, 'y', '和气，态度好，笑语蔼然。来源《聊斋志异·王大》，');
INSERT INTO `chinese_infos` VALUES (11167, '苏', '木', 22, 'y', '形声。从苃,稣声。从苃,与植物有关。本义植物名,即紫苏；的种子。可以入药、榨油；柴草；樵苏脂烛,莫非种植之物也。--《颜氏家训》；须状下垂的饰物；金车玉作轮,踯躅青骢马,流苏金缕鞍。--《玉台新咏·古诗为焦仲卿妻作》');
INSERT INTO `chinese_infos` VALUES (11168, '苹', '木', 22, 'y', '形声。从苃,平声。本义浮萍；苹,无根浮水而生者。--《说文》；蒿的一种。即青蒿；呦呦鹿呜,食野之苹。--《诗·小雅·鹿鸣》；又如苹藻主(妻子);苹敬(礼金；同蘯。见苹果；通輟。兵车的一种；苹车之萃。--《周礼·春官·车仆》');
INSERT INTO `chinese_infos` VALUES (11169, '蕲', '木', 22, 'y', '1.求。2.指蕲州（旧州名，州治在今湖北蕲春南）。3.姓。');
INSERT INTO `chinese_infos` VALUES (11170, '龚', '木', 22, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (11171, '邝', '木', 22, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (11172, '蘅', '木', 22, 'y', '〔蘅芜〕一种香草。');
INSERT INTO `chinese_infos` VALUES (11173, '芦', '木', 22, 'y', '形声。从苃,眗声。本义草名。芦菔；芦菔,似芜菁。今作萝卜；芦,芦菔也。--《说文》。段注今之萝卜也。；芜菁,共紫华者谓之芦菔。--《方言》');
INSERT INTO `chinese_infos` VALUES (11174, '孽', '木', 22, 'y', '该字的主要字义是恶因，恶事，邪恶，又指奴隶社会、封建社会多妻制下指妾及其子女。');
INSERT INTO `chinese_infos` VALUES (11175, '龉', '木', 22, 'n', '见  〔龃龉〕');
