INSERT INTO `chinese_infos` VALUES (11359, '鼘', '木', 22, 'n', '〔～～〕鼓声，如“～～不已，声以发之。”');
INSERT INTO `chinese_infos` VALUES (11360, '鼜', '木', 22, 'n', '古代查夜时击的鼓');
INSERT INTO `chinese_infos` VALUES (11361, '鼲', '木', 22, 'n', '鼠的一种，体小，背部灰色，腹部白色，尾毛蓬松。毛皮柔软如绒，可作衣物。俗称“灰鼠”。');
INSERT INTO `chinese_infos` VALUES (11362, '鼳', '木', 22, 'n', '古书上说的一种大兽，形状像鼠，长着马蹄，重千余斤。');
INSERT INTO `chinese_infos` VALUES (11363, '鼵', '木', 22, 'n', '古书上指一种与鵌鸟同穴而居的鼠。似家鼠而小，色黄，尾短，尾毛蓬松。亦称“兀鼠”。');
INSERT INTO `chinese_infos` VALUES (11364, '齂', '木', 22, 'n', '鼾声。');
INSERT INTO `chinese_infos` VALUES (11365, '齫', '木', 22, 'n', '古同“齳”。');
INSERT INTO `chinese_infos` VALUES (11366, '𫜲', '木', 22, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (11367, '龢', '木', 22, 'n', '同“和”。');
INSERT INTO `chinese_infos` VALUES (11368, '芗', '木', 23, 'y', '1.古书上指用以调味的香草。2.同“香”。');
INSERT INTO `chinese_infos` VALUES (11369, '兰', '木', 23, 'y', '形声。从苃,阑声。本义兰草,即泽兰；蘭,香草也。--《说文》；衡兰芷若。--《汉书·司马相如传》；兰槐之根是为芷。--《荀子·劝学》');
INSERT INTO `chinese_infos` VALUES (11370, '蘩', '木', 23, 'n', '古书上指白蒿（一种草本植物）。');
INSERT INTO `chinese_infos` VALUES (11371, '驿', '木', 23, 'n', '驿站，旧时供来往送公文的人或出差官员中途换马或暂住的地方。现多 用于地名。');
INSERT INTO `chinese_infos` VALUES (11372, '蘖', '木', 23, 'n', '树木砍去后又长出来的新芽，泛指植物由茎的基部长出的分枝');
INSERT INTO `chinese_infos` VALUES (11373, '蓦', '木', 23, 'n', '突然');
INSERT INTO `chinese_infos` VALUES (11374, '蛊', '木', 23, 'n', '古代传说把许多毒虫放在器皿里使互相吞食，最后剩下不死的毒虫叫蛊，用来放在食物里害人。');
INSERT INTO `chinese_infos` VALUES (11375, '鹬', '木', 23, 'n', '鸟，体色暗淡，嘴细长，腿长，趾间没有蹼。常在浅水边或水田中吃小鱼、贝类、昆虫等，是候鸟。种类较多。');
INSERT INTO `chinese_infos` VALUES (11376, '蔹', '木', 23, 'n', '多年生蔓生藤本植物。分白蔹、赤蔹等。白蔹的根可供药 用。');
INSERT INTO `chinese_infos` VALUES (11377, '藿', '木', 23, 'n', '豆类作物的叶子。');
INSERT INTO `chinese_infos` VALUES (11379, '痈', '木', 23, 'n', '皮肤和皮下组织的化脓性炎症，病原体是葡萄球菌，多发生在背部或项部，症状是局部红肿，形成硬块，表面有许多脓疱，有时形成许多小孔，呈筛状，非常疼痛，常引起发热、寒战等，严重时并发败血症。');
INSERT INTO `chinese_infos` VALUES (11380, '椤', '木', 23, 'n', '见〖黄波椤〗。');
INSERT INTO `chinese_infos` VALUES (11381, '验', '木', 23, 'n', '意为察看、查考，如验血、考验。还表示预期的效果，如效验、灵验。');
INSERT INTO `chinese_infos` VALUES (11382, '藓', '木', 23, 'n', '1.苔藓植物的一类。属于这一类的植物，茎和叶子都很小，绿色，没有根，生在阴湿的地方。2.（Xiǎn）姓。');
INSERT INTO `chinese_infos` VALUES (11383, '鳜', '木', 23, 'n', '1.〔～鯞〕即“鳑鲏”。2.（鱖）1.〔～鱼〕体侧扁，性凶猛，生活在淡水中，味鲜美。是中国特产。亦作“桂鱼”；有些地区称“花鲫鱼”。2.（鱖）');
INSERT INTO `chinese_infos` VALUES (11384, '鹪', '木', 23, 'n', '鸟，体长约10厘米，羽毛赤褐色，略有黑褐色斑点，尾羽短，略向上翘。多在灌木丛中活动，吃昆虫等。');
INSERT INTO `chinese_infos` VALUES (11385, '鹫', '木', 23, 'n', '鸟类。体形大。如秃鹫、兀鹫等。喙钩曲，视力强，腿部有羽毛。嗜食动物尸体， 也捕食小动物。');
INSERT INTO `chinese_infos` VALUES (11386, '癯', '木', 23, 'n', '瘦');
INSERT INTO `chinese_infos` VALUES (11387, '蘧', '木', 23, 'n', '1.惊喜的样子。2.姓。');
INSERT INTO `chinese_infos` VALUES (11388, '蠲', '木', 23, 'n', '积存（多见于早期白话）。');
INSERT INTO `chinese_infos` VALUES (11389, '鹰', '木', 24, 'y', '形声。从鸟。本义鹰属的各种鸟；种鸟；鹰翅疾如风,鹰爪利如锥。--唐·白居易《放鹰》');
INSERT INTO `chinese_infos` VALUES (11390, '黉', '木', 24, 'n', '古代的学校。');
INSERT INTO `chinese_infos` VALUES (11391, '酿', '木', 24, 'n', '形声。从酉，从襄，襄亦声。“襄”意为“包裹”、“包容（异物）”。“酉”与“襄”联合起来表示“在谷物中间放置酒曲”、“用谷物包裹酒曲”。本义：用谷物包裹酒曲的办法造酒。');
INSERT INTO `chinese_infos` VALUES (11392, '赣', '木', 24, 'y', '1.赣江。2.江西的别称。3.古又同“贡（gòng）”。');
INSERT INTO `chinese_infos` VALUES (11393, '霭', '木', 24, 'y', '云气；雾气');
INSERT INTO `chinese_infos` VALUES (11394, '篱', '木', 24, 'n', '用竹、苇、树枝等编成的围墙屏障：篱笆（“笆”读轻声）。');
INSERT INTO `chinese_infos` VALUES (11395, '攥', '木', 24, 'n', '握；握住');
INSERT INTO `chinese_infos` VALUES (11396, '羁', '木', 24, 'n', '1.马笼头2.拘束；束缚3.停留（在外地）；寄居他乡');
INSERT INTO `chinese_infos` VALUES (11397, '搅', '木', 24, 'n', '调拌；掺和：搅拌；搅匀；搅和。2打扰；扰乱：搅扰；搅乱；胡搅蛮缠。【搅和】；混合；掺杂。2扰乱。；本来很好的一件事，都被他搅和了。【搅扰】；扰乱、影响别人，使人感到厌烦。；妈妈正在看书，别去搅扰她。');
INSERT INTO `chinese_infos` VALUES (11398, '衢', '木', 24, 'n', '四通八达的大路');
INSERT INTO `chinese_infos` VALUES (11399, '鞯', '木', 24, 'n', '马鞍子下面的垫子');
INSERT INTO `chinese_infos` VALUES (11400, '蓠', '木', 24, 'n', '见  〔江蓠〕');
INSERT INTO `chinese_infos` VALUES (11401, '魇', '木', 24, 'n', '梦中惊叫，或梦中觉得被什么东西压住不能动。');
INSERT INTO `chinese_infos` VALUES (11402, '簖', '木', 24, 'n', '插在河水里的竹栅栏，用来阻挡鱼、虾、螃蟹，以便捕捉');
INSERT INTO `chinese_infos` VALUES (11403, '鲎', '木', 24, 'n', '1.节肢动物，头胸部的甲壳略呈马蹄形，腹部的甲壳呈六角形，尾部呈剑状，生活在浅海中。俗称鲎鱼。2.虹。');
INSERT INTO `chinese_infos` VALUES (11404, '龋', '木', 24, 'n', '牙齿被腐蚀而残缺。');
INSERT INTO `chinese_infos` VALUES (11405, '腭', '木', 24, 'n', '口腔的上壁。前部由骨和肌肉构成，叫硬腭，后部由结缔组织和肌肉构成，叫软腭。');
INSERT INTO `chinese_infos` VALUES (11406, '萝', '木', 25, 'n', '通常指某些能爬蔓的植物');
INSERT INTO `chinese_infos` VALUES (11407, '观', '木', 25, 'y', '看，察看：视觉。对事物的看法，分为心观与眼观。观：道教徒的活动场所《说文》曰：“观，谛视也。”。《庄子·人间世》：“观者如市。”。《易·系辞下》：“仰则观象于天，俯则观法于地。”');
INSERT INTO `chinese_infos` VALUES (11408, '榄', '木', 25, 'n', '木名。橄榄的省称；榄（欖）［橄榄］见橄。');
INSERT INTO `chinese_infos` VALUES (11409, '缵', '木', 25, 'n', '继承。');
INSERT INTO `chinese_infos` VALUES (11410, '鲚', '木', 25, 'n', '鱼，体小侧扁，头小而尖，尾尖而细，银白色。生活在海洋中，有的春季或初夏到河中产卵。种类较多，常见的有凤鲚、刀鲚等。');
INSERT INTO `chinese_infos` VALUES (11411, '箩', '木', 25, 'n', '一种用框架和纱网材料组成的器具，传统农业用于分离谷物与微小尘土，可以漏掉灰尘和碎末工具。箩【动词】用箩筛或滤；。如：箩面；箩柜（有脚装置的箩面柜子）');
INSERT INTO `chinese_infos` VALUES (11412, '蘸', '木', 25, 'n', '意思是用物体沾染液体。');
INSERT INTO `chinese_infos` VALUES (11413, '髋', '木', 25, 'n', '组成骨盆的大骨，左右各一，形状不规则，是由髂骨、坐骨和耻骨合成的。通称胯骨。');
INSERT INTO `chinese_infos` VALUES (11414, '躜', '木', 26, 'n', '向上或向前冲。');
INSERT INTO `chinese_infos` VALUES (11415, '蠼', '木', 26, 'n', '〔蠼螋〕昆虫。体扁平狭长，黑褐色，尾部像夹子。多生活在潮湿的地方。螋（sōu）。');
INSERT INTO `chinese_infos` VALUES (11416, '阄', '木', 26, 'n', '为赌胜负或决定事情而抓取的揉成团或卷起的做好记号的纸片');
INSERT INTO `chinese_infos` VALUES (11417, '谳', '木', 27, 'n', '审判定案');
INSERT INTO `chinese_infos` VALUES (11418, '颧', '木', 27, 'n', '眼眶下面，两颊上面突起的部分。');
INSERT INTO `chinese_infos` VALUES (11419, '酽', '木', 27, 'n', '（汁液）浓；味厚');
INSERT INTO `chinese_infos` VALUES (11420, '戆', '木', 28, 'n', '〈方〉傻；笨；鲁莽憨厚而刚直。');
INSERT INTO `chinese_infos` VALUES (11421, '棂', '木', 28, 'n', '旧式窗户的格子');
INSERT INTO `chinese_infos` VALUES (11422, '笾', '木', 28, 'n', '古代祭祀或宴会时盛果实、干肉等的竹编食器。');
INSERT INTO `chinese_infos` VALUES (11423, '麣', '木', 31, 'n', '山羊。');
INSERT INTO `chinese_infos` VALUES (11425, '籲', '木', 32, 'n', '见“吁2”。');
INSERT INTO `chinese_infos` VALUES (11426, '灪', '木', 32, 'n', '1.〔～䃶〕高峻，如“澎濞～䃶。”2.〔～滃（wěng）〕大水茫茫的样子。');
INSERT INTO `chinese_infos` VALUES (11427, '龖', '木', 32, 'n', '1.双龙2.龙腾飞的样子。');
INSERT INTO `chinese_infos` VALUES (11428, '卜', '水', 2, 'y', '占卜。古代一种用火灼龟甲，观其裂纹以预测吉凶的行为。2主持占卜的官。；3.推测，估计。④选择。⑤赐予。⑥验证，证实。⑦姓。⑧象声词。见“卜卜”。3⑨萝卜');
INSERT INTO `chinese_infos` VALUES (11429, '八', '水', 2, 'y', '象形。甲骨文象分开相背的样子『字部首之一。从八的字多与分解、分散、相背有关。本义相背分开；八,别也。象分别相背之形。--《说文》。段玉裁注今江、浙俗语以物与人谓之八,与人则分别矣。；八；七加一的和；八,数也。--《玉篇》；陈浪簋。--《诗·小雅·伐木》；地理以八制。--《管子·五行》。注少阴之数。；八者,维纲也。--《大戴礼记·本命》');
INSERT INTO `chinese_infos` VALUES (11430, '勹', '水', 2, 'n', '古同“包”，裹。');
INSERT INTO `chinese_infos` VALUES (11431, '乜', '水', 2, 'n', '姓。1.〔乜斜〕2.斜着眼睛看。3.因为困倦眼睛睁不开。');
INSERT INTO `chinese_infos` VALUES (11432, '凡', '水', 3, 'y', '基本信息汉字：凡凡字骨刻文演变：引自，丁再献、丁蕾《东夷文化与山东·骨刻文释读》十九章第二节，中国文史出版社20；2年2月版；');
INSERT INTO `chinese_infos` VALUES (11433, '子', '水', 3, 'y', '有多种意义和用法，还可用于古人表字，如孔子。');
INSERT INTO `chinese_infos` VALUES (11434, '下', '水', 3, 'y', '1.位置在低处的，跟“上”相反：~面，山~。2.由高处到低处：~山，~楼。3.方面，方位：两~里都同意。4.减除。5.用：~功夫。6.攻克，攻陷：连~数城。7.退让。8.用在名词后面。9.用在动词后面。；0.量词，指动作的次数：打十几~。1.（动物）生产：猫~小猫了。；2.少于某次数：不~三百人。');
INSERT INTO `chinese_infos` VALUES (11435, '亡', '水', 3, 'n', '读时，有伤亡，逃走的意思，读时，有过去，没有的意思。');
INSERT INTO `chinese_infos` VALUES (11436, '片', '水', 4, 'y', '词性可以作为名词、动词、形容词及量词。');
INSERT INTO `chinese_infos` VALUES (11437, '勿', '水', 4, 'n', '甲骨文的勿是会意字，右边像弓，旁边两小撇表示弓弦振动的样子勿的本意为引弓拨弦，后假借为表示劝阻、禁止，做否定词。勿也表示一般否定，相当于“不”。另一说法有据可查，《说文》载：“州里所建旗象其柄有三游杂帛幅半异所以趣民故遽称勿勿凡勿之属皆从勿”，由此看，勿字是象形字。');
INSERT INTO `chinese_infos` VALUES (11438, '方', '水', 4, 'y', '象形。下从舟省,而上有竝头之象。故知并船为本义。本义:并行的两船;泛指并列;并行。；并船也。象两舟省、緫头形。凡方之属皆从方。四个角都是90度直角的四边形或六个面都是方形的立体；正～形，长～形。');
INSERT INTO `chinese_infos` VALUES (11439, '比', '水', 4, 'y', '二人为从，反从为比，相背而北。相随而从，相对而比，相背而北，相转而化。〈动〉；会意。从二匕,匕亦声。甲骨文字形,象两人步调一致,比肩而行。它与“从”字同形,只是方向相反。《说文》:“二人为从,反从为比。”本义:并列;并排。；');
INSERT INTO `chinese_infos` VALUES (11440, '反', '水', 4, 'y', '其本义是（将物体）翻转过来的意思。');
INSERT INTO `chinese_infos` VALUES (11441, '分', '水', 4, 'y', '分”有两个基本读音，即和，分别有各自的释义和用法。');
INSERT INTO `chinese_infos` VALUES (11442, '文', '水', 4, 'y', '从玄从爻。天地万物的信息产生出来的现象、纹路、轨迹，描绘出了阴阳二气在事物中的运行轨迹和原理。故文即为符。上古之时，符文一体。古者伏羲氏之王天下也，始画八卦，造书契，以代结绳（爻）之政，由是文籍生焉。——《尚书序》依类象形，故谓之文。其后形声相益，即谓之字。——《说文》序》仓颉造书，形立谓之文，声具谓之字。——《古今通论》；象形。甲骨文此字象纹理纵横交错形。“文”是汉字的一个部首。本义：花纹；纹理。；文，；念为：、等，从字面意思上就可以理解为文章、文字，与古今中外的各个文学著作中出现的各种文字字形密不可分。古有甲骨文、金文、小篆等，今有宋体、楷体等，都在这一方面突出了“文”的重要性。古今中外，人们对于“文”都有自己不同的认知，从大的方面来讲，它可以用于表示一个民族的文化历史，从小的方面来说它可用于用于表示单独的一个“文”字，可用于表示一段话，也可用于人物的姓氏。');
INSERT INTO `chinese_infos` VALUES (11443, '不', '水', 4, 'y', '其本义为名词，指未经缔结构造而直接使用的树杈上的原始巢居；后引申为禁止、不要，还可表示不是、非等，以上读作bù；“不”借为“否”时，读fǒu。');
INSERT INTO `chinese_infos` VALUES (11444, '毋', '水', 4, 'y', '本义是不要，表示禁止，引申也用作一般否定词。');
INSERT INTO `chinese_infos` VALUES (11445, '夫', '水', 4, 'y', '从一从大。擎天承大之人也。天、大，普世价值也。《说文》“夫，丈夫也。从大，一以象簪也，周制以八寸为尺，十尺为丈，人长八尺，故曰丈夫。”；象形。甲骨文字形,像站着的人形(大),上面的“一”,表示头发上插一根簪,意思是成年男子,是个丈夫了。古时男子成年束发加冠才算丈夫,故加“一”做标志。本义:成年男子的通称。；夫,丈夫也。——《说文》。按,夫从一大,会意兼象形。周制以八寸为尺,十尺为丈,人长八尺,故曰丈夫。旧时称成年男子：渔夫。农夫。万夫不当之勇；旧时称服劳役的人：夫役。拉夫；夫子；丈夫；量词，指一农夫所受之一百亩耕地；文言发语词、文言助词、指示代词，相当于“这”或“那”；夫妻。');
INSERT INTO `chinese_infos` VALUES (11446, '互', '水', 4, 'y', '是象形字，像绞绳子的工具，中间像人手推、握以进行操作的样子。互的本意是指一种绞绳子的工具，引申为交错。又引申为彼此、相互，即表示动作交互进行，用作副词。在《说文》中，互是“䇘”的古文。');
INSERT INTO `chinese_infos` VALUES (11447, '水', '水', 4, 'y', '象形。甲骨文字形。中间像水脉,两旁似流水。水是汉字的一个部首。从水的字,或表示江河或水利名称,或表示水的流动,或水的性质状态。本义以雨的形式从云端降下的；液体,无色无味且透明,形成河流、湖泊和海洋,分子式为2,是一切生物体的主要成分；水,准也。--《说文》；水,准也。准,平也。天下莫平于水。--《释名》；积阴之寒气为水。--《淮南子·天文》；五行一曰水。--《书·洪范》；凡平原出水为大水。--《左传·桓公元年》；水曰清滌。--《礼记·曲礼》；冰,水为之,而寒于水。--《荀子·劝学》');
INSERT INTO `chinese_infos` VALUES (11448, '巴', '水', 4, 'y', '盼望；期望：巴望；巴不得早点放学。2粘贴；附在别的物体上的东西：饭巴锅了；锅巴。；3.靠近；挨着：前不巴村，后不巴店。④词尾，读轻声：尾巴；嘴巴；哑巴。⑤古国名，在今重庆全境、四川东部、湖北西部、贵州北部、湖南西北部一带。⑥姓。⑦蛇。');
INSERT INTO `chinese_infos` VALUES (11449, '毛', '水', 4, 'y', '有多种意思，指动植物的皮上所生的丝状物、鸟类的羽毛、像毛的东西、东西上长的霉、粗糙、不纯净的、做事粗心、惊慌、发怒、指货币贬值等，还是量词、姓氏。');
INSERT INTO `chinese_infos` VALUES (11450, '幻', '水', 4, 'y', '惑乱；幻,相诈惑也。--《说文》；民无或胥,诪张为幻。--《书·无逸》；又如幻惑(迷惑；奇异地变化；幻；假象;虚幻,虚无；皆幻也。--清·薛福成《观巴黎油画记》');
INSERT INTO `chinese_infos` VALUES (11451, '户', '水', 4, 'y', '是象形字1.据甲骨文字解释：象形。甲骨文字形，象门（门）字的一半。汉字部首之一。从“户”的多与门户有关。本义：单扇门。2.据小篆旧字形（繁体字）解释：象形。字从丿从出头的尸。“丿”读为“撇”，“丿”与“尸”联合起来表示“进驻后不再移动”、“入住后不再搬家”。3据隶定字形解释：象形。字从丶从尸。“丶”读为“主”，“丶”为宾主的意思，“丶”与“尸”联合起来表示“进驻后不再移动”、“入住后不再搬家”。说明：根据甲骨文字进行解说危险性很大，因为甲骨文字与隶定字形差异巨大，近代学者所做的甲-隶比对有出错的可能。若隶定字形可以解释，建议尽量不要再拉来甲骨文、金文、大小篆文了，因为这里是普及性解说，而非专业的小学研究。为尊重前面编辑者的劳动，特将两种解释并列于此。');
INSERT INTO `chinese_infos` VALUES (11452, '父', '水', 4, 'y', '从又举杖。家长率教者。矩也。；对有才德的男子的美称,多附缀于表字后面维师尚父,时维鹰扬。——《诗·大雅》三月,公及邾仪父盟于眜。——《春秋》余弟安国平父,安上纯父。——王安石《游褒禅山记》；对老年男子的尊称纵江东父老怜而王我,我何面目见之?——《史记·项羽本纪》父知之乎?(父:老先生)——《史记·张释之冯唐列传》(3)又如:渔父;田父(4)通“甫”。开始强梁者不得其死,吾将以为教父。——《老子·四十二章》(5)又如:教父(教戒的开始)(6)天。古人以为天地生万物,故称天为“父”“父”字非常古老，其意义变化也非常大。在甲骨文中，“父”是一个人手里拿着一柄石斧，其最初意义便是“斧”，这个意义后来写作“斧”，“父”字不再有此义。持有石斧是力量与勇敢的象征，所以“父”字意思引申为持斧之人，也就是值得敬重的人，这个意义后来与“甫”字通用，现代汉语中不常用。');
INSERT INTO `chinese_infos` VALUES (11453, '匹', '水', 4, 'y', '量词（指整卷的绸或布；指马、骡等）。2.单独：～夫（．一个人，泛指平常人，如“国家兴亡，～～有责”；．指无学识、无智谋的人，如“～～之勇”）。3.相当，相敌，比得上：～敌。～配。4.姓：匹姓，匹娄姓。');
INSERT INTO `chinese_infos` VALUES (11454, '壬', '水', 4, 'y', '1.天干的第九位。2.（Rén）姓。');
INSERT INTO `chinese_infos` VALUES (11455, '化', '水', 4, 'y', '古字为匕。会意。甲骨文,从二人,象二人相倒背之形,一正一反,以示变化。本义变化,改变；匕,变也。--《说文》。徐灏曰匕化古今字。；知变化之道。--《易·系辞传》。虞注在阳称变,在阴称化,四时变化。荀注春夏为变,秋冬为化,坤化为物。；和故百物化焉。--《礼记·乐记》；若欲其化也。--《周礼·柞氏》。注犹生也。；状态而实无别而为异者谓之化。--《荀子·正名》。注化者改旧形之名。；胜败若化。--《国语·晋语》。注言转化无常也。；因时而化。--《吕氏；化；放于名词或形容词后，〈表〉变成或使成为某种性质或状态绿～。革命～。科学～。现代～。工业～。；《化学》');
INSERT INTO `chinese_infos` VALUES (11456, '卅', '水', 4, 'y', '数目。三十');
INSERT INTO `chinese_infos` VALUES (11457, '丬', '水', 4, 'n', '古同“爿”。用作偏旁。');
INSERT INTO `chinese_infos` VALUES (11458, '攴', '水', 4, 'n', '1.轻轻地击打。2.古同“扑”，戒尺。');
INSERT INTO `chinese_infos` VALUES (11459, '歹', '水', 4, 'n', '坏，恶，跟“好”相反：歹徒、歹人、为非作歹。有时组字时有不好，不幸，甚至死亡的意思，比如“殃”字。【歹毒】阴险狠毒。【例句】歹毒的敌人竟放火烧了村庄，令数百口人无家可归。');
INSERT INTO `chinese_infos` VALUES (11460, '爿', '水', 4, 'n', '1.劈成片的竹木等2.a）田地一片叫一爿。b）商店、工厂等一家叫一爿。');
INSERT INTO `chinese_infos` VALUES (11461, '北', '水', 5, 'y', '方位名。与南相对；人坐立皆面明背暗,故以背为南北之北。--朱骏声《说文通训定声》；万物负阴而抱阳。--《老子》。王力按,山北为阴,山南为阳,老子的话等于说万物负背而抱南。；然则北通巫峡。--宋·范仲淹《岳阳楼记》；南声函胡,北音清越。--宋·苏轼《石钟山记》；沛公北向坐。--《史记·项羽本纪》；又北向。--明·归有光《项脊轩志》');
INSERT INTO `chinese_infos` VALUES (11462, '弗', '水', 5, 'y', '“不”的同源字，不之深也。语出《公羊传》《吕氏春秋》《史记》《韩非子》等。吴语方言常用字，同古意，表示不。');
INSERT INTO `chinese_infos` VALUES (11463, '穴', '水', 5, 'n', '洞，窟窿；2、人体可以进行针灸的部位，多为神经末梢密集或较粗的神经干经过的地方；3、旧艺人称在市场或广场上表演时所占的一块地方。');
INSERT INTO `chinese_infos` VALUES (11464, '疋', '水', 5, 'n', '同“匹”。古同“雅”，《尔雅》亦作《尔疋》。脚。');
INSERT INTO `chinese_infos` VALUES (11465, '布', '水', 5, 'y', '形声。从巾,父声。本义麻布；(古时无棉布；坤为布。--《易·说卦》；抱布贸丝。--《诗·卫风·氓》；女有余布。--《孟子》；果布辐凑而常然。--左思《吴都赋》；商祝免袒,执功布入。--《仪礼·既夕礼记》；许子必织布而后衣乎?--《孟子·滕文公》；又如布衣(平民,老百姓。古时老百姓穿麻布衣服,所以称平为布衣);布槽(布制的马槽);布褐(粗布衣服;又指平民);布总(古代丧服,以麻布束发；棉、麻、苎、葛等织物的通称；足缠白布。--明·魏禧《大铁椎传》');
INSERT INTO `chinese_infos` VALUES (11466, '半', '水', 5, 'y', '会意。从八,从牛。八是分解的意思;牛大,易于分割,所以取牛会意。本义一半,二分之一；半,物中分也。--《说文》；今汉有天下太半。--《汉书·高帝纪上》；士卒食半。--《汉书·项籍传》；先帝创业未半而中道崩殂。--诸葛亮《出师表》；半匹红绡一丈绫。--唐·白居易《卖炭翁》；身已半入,只露尻尾。--《聊斋志异·狼三则》');
INSERT INTO `chinese_infos` VALUES (11467, '弘', '水', 5, 'y', '1.大（现多用“宏”）：～愿。～图。～业。～谋。恢～（a．宽阔、广大，如“气度～～”；b．发扬，如“～～士气”。亦作“恢宏”）。2.扩充，光大：～扬。3.姓。');
INSERT INTO `chinese_infos` VALUES (11468, '末', '水', 5, 'y', '《说文》：“末，木上曰末。从木，一1.树梢。木～。～大必折。2.泛指物的端、梢、尖端，跟\"本\"相对：～梢。秋毫之～。3.最后，终了，跟\"始\"相对：～尾。～了。岁～。4.碎屑，不重要的事：粉～。药～儿。茶叶～。切勿本～倒置。5.轻微，不足道（旧时用作自谦）：～议。～将愿往。');
INSERT INTO `chinese_infos` VALUES (11469, '付', '水', 5, 'y', '本义“给予”的意思，含有多个引申意思。“付”姓通“傅”,“傅”与“付”同祖同宗，在当代“付”是“傅”字的白字。这是历史的动荡及文化的不济所至。而被认定是姓，只不过是建国后文化落后及文字改革未成熟阶段，人们滥用，使得许多家庭的姓氏的白字甚至错字应用于户籍、档案等方面，因而成了惯用字。');
INSERT INTO `chinese_infos` VALUES (11470, '兄', '水', 5, 'y', '是指兄弟之间，年纪较大的一个，也叫大哥，兄长。');
INSERT INTO `chinese_infos` VALUES (11471, '目', '水', 5, 'y', '是象形字。甲骨文和小篆字形。象眼睛形,外边轮廓象眼眶,里面象瞳孔。小篆处理为线条。先秦时期多用“目”,两汉以后,用眼逐渐多起来。“目”具有书面语色彩。本义:眼睛。引自，丁再献、丁蕾《东夷文化与山东·骨刻文释读》十九章第二节，；象形。甲骨文和小篆字形。；观看，注视，(3)递眼色，使眼。甲金文象人眼目，金文或有将目形竖起，本义是眼睛。参见「臣」。');
INSERT INTO `chinese_infos` VALUES (11472, '包', '水', 5, 'y', '一般指装东西的容器，包装等。计算机用语中，“包”是/协议通信传输中的数据单位，一般也称“数据包”。');
INSERT INTO `chinese_infos` VALUES (11473, '乎', '水', 5, 'y', '是语尾的叹词。字形采用“兮”作边旁，像声音向上越扬的形状。');
INSERT INTO `chinese_infos` VALUES (11474, '丕', '水', 5, 'y', '中原地区的发音，人名中使用，字辈排行，徐州李氏一支（十一世名人徐州状元李蟠、雍正时期李卫）中第二十四世有这个字辈。《说文》：“丕，大也。从一，不声。”按：金文丕不从一，不、丕原为一字，后分化');
INSERT INTO `chinese_infos` VALUES (11475, '白', '水', 5, 'y', '从丿从日。〈形〉根据隶定字形解释。会意。字从丿，从日。“丿”意为“不”，写在“日”的左上角，表示“在前的不算（日出前的天色不算）”，转义为“开始（从日出开始）”。“日”指太阳。“丿”与“日”联合起来表示“从日出开始（到日落前）的天色”。本义：日出与日落之间的天色。；(象形。甲骨文字形,象日光上下射之形,太阳之明为白,从“白”的字多与光亮、白色有关。本义:白颜色；古人用以代表西方、秋季、金、肺等。常作名词、形容词使用，意为白色，白色的。');
INSERT INTO `chinese_infos` VALUES (11476, '母', '水', 5, 'y', '象形。甲骨文字形，女字加上两乳，象母亲有乳之形。本义:母亲。；母亲，同今。也有特指乳母。动词有哺育，抚养的意思。也指本源，根本。雌性的：多指动物，同今。相比较的事物中的重者，大者，粗者。反之，小者，轻者，细者也可称为“子”。');
INSERT INTO `chinese_infos` VALUES (11477, '皮', '水', 5, 'y', '生物学上意为动物或植物体表面的一层组织——皮层组织，广义指表面。皮也是一种中国的姓氏。');
INSERT INTO `chinese_infos` VALUES (11478, '玄', '水', 5, 'y', '其本义是赤黑色，赤黑色颜色较为模糊，由此引申出深奥，玄妙等意思。玄为悬的古字，表示由绳悬挂，又引申出天空，幽远等意。');
INSERT INTO `chinese_infos` VALUES (11479, '仨', '水', 5, 'y', '仨，作为数量词：不仅表示“三个”，且是三个不同的个体；“仨”中既含有数词“三”，又含有量词“个”；它所代表的“三个”是内涵不同的三个东西。仨中所含的“三个”，既有不同性，又有统一性。作为代词：表示三维直角坐标系的I象限；表示直角三菱锥或直角四面体。作为名词：表示三角关系；表示万物的内核');
INSERT INTO `chinese_infos` VALUES (11480, '矛', '水', 5, 'y', '兵器名，是古代用来刺杀敌人的进攻性武器，是战争中常用兵器。长柄，有刃，用以刺敌。始于周代，或周代以前，来历亦甚悠久，惟当时战术未精，各种兵刃使用之法，亦极简单，非若后之武术，以繁取胜，以多矜奇也。矛的使用方法大多是用双手握柄，以直刺或戮为主的战斗使用方法。');
INSERT INTO `chinese_infos` VALUES (11481, '皿', '水', 5, 'n', '名词，指碗、碟、杯、盘一类用器的统称，如：器皿；皿金（金属器皿）；皿卷（清代科举，顺天乡试监生的试卷）；皿器（盛物用具的统称）等。字象盛食物之器具。《说文》：「皿，饭食之用器也。象形。与豆同意。凡皿之属皆从皿。读若猛。」徐锴改作「饮食之用器」，可从。');
INSERT INTO `chinese_infos` VALUES (11482, '禾', '水', 5, 'y', '嘉谷。二月开始生长，八月成孰，处四季之中，得阴阳之和，所以称它为禾（和）');
INSERT INTO `chinese_infos` VALUES (11483, '必', '水', 5, 'y', '意为：一定，决定，肯定，固执，由甲骨文演变而来。词性较多，可作：名词，动词，副词，连词使用。');
INSERT INTO `chinese_infos` VALUES (11484, '匝', '水', 5, 'n', '帀的俗字，环绕一周也。周，环绕一周叫一匝');
INSERT INTO `chinese_infos` VALUES (11485, '民', '水', 5, 'y', '会意。字从尸从氏。“尸”意为“身体不动弹”，引申为“不迁徙”。“氏”意为“国族”、“族”。“尸”与“氏”联合起来表示“本地常住人口”、“土著”。古者四民：有士民，有商民，有农民，有工民。《注》德能居位曰士；辟土植谷曰农；巧心劳手成器物曰工；通财货曰商。——《谷梁传·成元年》可用作姓氏。');
INSERT INTO `chinese_infos` VALUES (11486, '平', '水', 5, 'y', '1.不倾斜，无凹凸，像静止的水面一样：～地。～面。～原。2、均等：～大雪纷飞。抱打不～。公～合理。3、与别的东西高度相同，不相上下：～列。～局。～辈。4、安定、安静：～安。～服。5、治理，镇压：～定。6、抑止（怒气）：他把气～了下去。7、和好、讲和：“宋人及楚人～”。8、一般的，普通的：～民。～庸。～价。～凡。9、往常，一向：～生（．从来；．终身）。～素。；0、汉语四声之一：～声。～仄。1.姓。；2、〔～假名〕日本文所用的草书字母。；3、古同“评”，评议。');
INSERT INTO `chinese_infos` VALUES (11487, '匆', '水', 5, 'y', '本字为悤。会意，心急促（心）如火燎（囱）的意思。急急追逐。字形采用“心、囱”会义，“囱”也作声旁。');
INSERT INTO `chinese_infos` VALUES (11488, '仫', '水', 5, 'n', '〔仫佬族〕中国少数民族之一。人口16万（1990年）。主要分布在广西壮族自治区北部。有本民族语言，多通汉语文和壮语。建有罗城仫佬族自治县。');
INSERT INTO `chinese_infos` VALUES (11489, '夯', '水', 5, 'n', '砸地基用的工具');
INSERT INTO `chinese_infos` VALUES (11490, '弁', '水', 5, 'n', '1.古代男子戴的一种帽子。2.低级武职的旧称3.古又同姓卞的“卞”。');
INSERT INTO `chinese_infos` VALUES (11491, '卟', '水', 5, 'n', '化学音译用字。如卟吩。');
INSERT INTO `chinese_infos` VALUES (11492, '叵', '水', 5, 'n', '文言副词。不可');
INSERT INTO `chinese_infos` VALUES (11493, '閟', '水', 5, 'n', '1.古同“闭”2.掩蔽3.止；尽4.幽静5.古通“秘”，便秘6.古通“祕”（ａ．神秘。ｂ．秘密）。7.慎重；珍重');
INSERT INTO `chinese_infos` VALUES (11494, '叭', '水', 5, 'n', '指将食物扒入口中的声音。');
INSERT INTO `chinese_infos` VALUES (11495, '氕', '水', 5, 'n', '氢的一种稳定同位素，符号为1Ｈ。质量数１。它的原子由一个质子和一个电子组成，是氢的主要成分。占自然界中氢的总量的99.984%。');
INSERT INTO `chinese_infos` VALUES (11496, '血', '水', 6, 'n', '是指事字，血在古文字中像祭祀的器皿中盛着牲畜血的样子。血意为血液，特指祭祀时向神灵敬献的牲畜血，引申为有共同祖先的，又比喻刚强、热烈。');
INSERT INTO `chinese_infos` VALUES (11497, '米', '水', 6, 'y', '其本义指谷物和其他植物子实去壳后的子实，假借指长度单位。');
INSERT INTO `chinese_infos` VALUES (11498, '休', '水', 6, 'y', '休的本义是在树荫下乘凉歇息，后引申为歇息。由歇息引申为停顿，停止；安适的，欢愉的。由停顿，停止引申为取消，离弃；不，不要。');
INSERT INTO `chinese_infos` VALUES (11499, '伐', '水', 6, 'n', '含义：；砍：伐树。砍伐。2征讨：讨伐。伐罪（征讨有罪的人）。口诛笔伐。；3.自夸：伐善（夸自己的好处）。伐智。不矜不伐。');
INSERT INTO `chinese_infos` VALUES (11500, '帆', '水', 6, 'y', '张帆行驶；不枉故人书,无因帆江水。--韩愈《除官赴阙》；帆；挂在船桅上利用风力使船前进的布篷；孤帆远影碧空尽,唯见长江天际流。--李白《黄鹤楼送孟浩然之广陵》；又如扬帆;帆力(帆受风时的推动力);帆势(帆篷张挂之势);帆楫(船帆和桨；靠帆推进的船舶；两岸青山相对出,孤帆一片日边来。--唐·李白《望天门山》');
INSERT INTO `chinese_infos` VALUES (11501, '汀', '水', 6, 'y', '汀：水边平地，小洲。汀洲；绿汀；汀线（海岸被海水侵蚀而成的线状痕迹）。');
INSERT INTO `chinese_infos` VALUES (11502, '回', '水', 6, 'y', '《唐韵》戸恢切《集韵》《韵会》《正韵》胡隈切，𠀤音洄。《说文》从囗，中象回转之形。《徐锴曰》浑天之气，天地相承。天周地外，隂阳五行，回转其中也。');
INSERT INTO `chinese_infos` VALUES (11503, '份', '水', 6, 'y', '[名]整体中的一部分：股～；等～；～额');
INSERT INTO `chinese_infos` VALUES (11504, '伏', '水', 6, 'y', '趴，脸向下，体前屈：～卧。～案读书。2.低下去：此起彼～。3.屈服，承认错误或受到惩罚：～法。～输。～辩（伏罪的供状，亦作“伏辨”）。4.使屈服：降～。降龙～虎。5.隐藏：～兵。埋～。6.农历划分夏季最炎热的三个阶段：～日。～旱。～暑。～天。7.姓。');
INSERT INTO `chinese_infos` VALUES (11505, '名', '水', 6, 'y', '最基本的释义是指人或事物的称谓，或做某事时用来作依据的称号。');
INSERT INTO `chinese_infos` VALUES (11506, '向', '水', 6, 'y', '象形。从宀,从口。“宀”表示房屋,甲骨文中像屋墙,有窗户之形。本义:朝北开的窗户。；繁体字：向曏简介：对着，朝着，与“背”相对：向背、向北。目标，意志所趋：志向。方向。偏袒，袒护：偏向。近，临：向晚。秋天漠漠向昏黑。从前：向日。向者。从开始到现在：向例。一向。姓。');
INSERT INTO `chinese_infos` VALUES (11507, '冰', '水', 6, 'y', '会意。从仌,从水。金文作仌。金文字形表示水凝成冰后,体积增大,表面上涨(上拱)形。《说文》冻也,象水凝之形。小篆繁化,增加水变成。从仌从水；的会意字,于是仌就专用作部首。本义水冻结而成的固体；冰,水坚也。--《说文》；履霜坚冰至。--《易·坤》；迨冰未泮。--《诗·邶风·匏有苦叶》；冰者,阴之盛而水滞者也。--《汉书·五行志》；冰,水为之。--《荀子·劝学》；冰解而冻释。--《管子·五行》；公徒释甲,执冰而踞。--《左传·昭公二十五年》');
INSERT INTO `chinese_infos` VALUES (11508, '行', '水', 6, 'y', '象形。本义道路；行,道也。--《说文》；行有死人。--《诗·小雅·小弁》；遵彼微行。--《诗·豳风·七月》；又如行苇(路旁芦苇);行阡(道路);行神(路神);行程(路程);行街(大路；直排为行,横排为列；左右陈行,戒我师旅。--《诗·大雅·常武》；凌余阵兮躐余行。--《楚辞·九歌·国殇》；奉读书,五行并下。--《后汉书·应奉传》；军队编制,二十五人为一行；郑伯使卒出貑,行出犬鸡。--《左传·隐公十一年》；队伍,军队；陈胜、吴广皆次当行。--《史记·陈涉世家》');
INSERT INTO `chinese_infos` VALUES (11509, '妃', '水', 6, 'y', '1.帝王的妻，位次于皇后；亦指太子、王、侯的妻：～子。～嫔。\r\n\r\n2.古代对神女的尊称：天～。宓～。\r\n\r\n3.古同“绯”，粉红色。');
INSERT INTO `chinese_infos` VALUES (11510, '收', '水', 6, 'y', '本义为捕，即捕取罪人；引申指没收，又引申指收藏、收取，还可引申为所收赋敛');
INSERT INTO `chinese_infos` VALUES (11511, '仿', '水', 6, 'y', '效法，照样做：～效。～古。～冒。～宋（印刷字体的一种，亦称“仿宋体”）。');
INSERT INTO `chinese_infos` VALUES (11512, '泛', '水', 8, 'y', '指漂浮 泛彼柏舟。——《诗·邶风·柏舟》 泛舟于河。——《国语·晋语》。 一曰泛齐。——《周礼·酒正》');
INSERT INTO `chinese_infos` VALUES (11513, '刑', '水', 6, 'y', '对犯罪的处罚：～罚。～法。～律。～事。执～。服～。2.特指对犯人的体罚：～讯。受～。～具。');
INSERT INTO `chinese_infos` VALUES (11514, '印', '水', 6, 'y', '会意。甲骨文字形。左是手爪,右象跪着的人,合起来表示用手按人使之跪拜。本义官印；印,执政所持信也。--《说文》；玺者,印也。印者,信也。--蔡邕《独断》；印,验也。--《苍颉篇》；玺谓之印。--《小尔雅》。按,古上下通曰玺。秦以来,天子诸侯王称玺,独以玉;列侯至二千石曰章,千石至四百石曰印。；每字为一印。--宋·沈括《梦溪笔谈·活板》');
INSERT INTO `chinese_infos` VALUES (11515, '百', '水', 6, 'y', '会意。从一,从白。白假借为百。本义数词；百,十十也。--《说文》；律中百钟。--《淮南子·时则》；虽人有百手,手有百指,不能指其一端。--《虞初新志·秋声诗自序》；又如百辆之迎(用隆重仪式迎娶新妇);百日(一百天;人死后的一百天,请僧道诵经或作道场);百户(一百户人家;统兵百人之官；概数。言其多,许多的,众多的；千禄百福。--《诗·大雅·假乐》；将军百战死,壮士十年归。--《乐府诗集·木兰诗》；猿则百叫无绝。--吴均《与朱元思书》');
INSERT INTO `chinese_infos` VALUES (11516, '亥', '水', 6, 'y', '字最早可见于甲骨文，经过多次演变，变成如今的书写形式。主要用于干支纪年。');
INSERT INTO `chinese_infos` VALUES (11517, '好', '水', 6, 'y', '多音字，读时作形容词泛指一切美好的事物，或同意、应允。读时作动词，表示喜欢的意思，乃是中国最常用的。');
INSERT INTO `chinese_infos` VALUES (11518, '牟', '水', 6, 'y', '取，谋求；牛叫声；姓。牟姓。地名，中牟县读mu。牛气口出为牟，羊气上出为芈。');
INSERT INTO `chinese_infos` VALUES (11519, '合', '水', 13, 'y', '合，亼口为合。亼，从入一，三合也。十口相传是古，三口相同为合。');
INSERT INTO `chinese_infos` VALUES (11520, '冱', '水', 6, 'n', '1.冻2.闭塞。');
INSERT INTO `chinese_infos` VALUES (11521, '仳', '水', 6, 'n', '〔～离〕夫妻分离。特指妇女被遗弃而离去。〔～倠〕ａ．古丑女名；ｂ．容貌丑。');
INSERT INTO `chinese_infos` VALUES (11522, '凼', '水', 6, 'n', '〈方〉水 坑；田地里沤肥的小坑');
INSERT INTO `chinese_infos` VALUES (11523, '缶', '水', 6, 'n', '1.古代一种大肚子小口儿的瓦器。2.古代一种瓦质的打击乐器。');
INSERT INTO `chinese_infos` VALUES (11524, '牝', '水', 6, 'n', '雌性的（指鸟兽，跟“牡”相对）');
INSERT INTO `chinese_infos` VALUES (11525, '纟', '水', 6, 'n', '同“糸”。用作偏旁。俗称“绞丝旁”。');
INSERT INTO `chinese_infos` VALUES (11526, '犯', '水', 6, 'n', '可组成犯罪、犯愁、犯病等动词词组，也可组成罪犯等名词词组。');
INSERT INTO `chinese_infos` VALUES (11527, '乒', '水', 6, 'n', '中文象声词，常用于拟声。');
INSERT INTO `chinese_infos` VALUES (11528, '凶', '水', 6, 'n', '在心理学上是对深部感觉的一种描述。凶恶之人一般明眼人一眼就可以看出，其面部大多鹅毛月样凹陷，脸较长，下唇较上唇凸出，双眼较凹，面皮较黑，眼睛较小但是很有神，一般人较瘦。凶的反面是“善”。');
INSERT INTO `chinese_infos` VALUES (11529, '危', '水', 6, 'y', '引申指危险、不安全的；还引申指端正的、挺直的；又引申指伤害；也指人快去世了，如：生命垂危。| 出自人在厓上，自卪止之也。——《说文》');
INSERT INTO `chinese_infos` VALUES (11530, '汁', '水', 6, 'n', '液汁。字形采用“水”作边旁，“十”是声旁。混有某种物质的水：～液。墨～。果～。胆～。脑～');
INSERT INTO `chinese_infos` VALUES (11531, '扒', '水', 6, 'n', '之一：；，抓、拆、脱、剥削，如“扒车”“扒屋”“扒衣服”“扒皮”。；，用工具使东西聚拢或散开，偷，如“扒草”“扒窃”。它既是一种动作，又是一种烹饪技法。');
INSERT INTO `chinese_infos` VALUES (11532, '糸', '水', 6, 'n', '1.细丝。2.幺。3.微小。4.量词，丝的二分之一。古同“丝”。');
INSERT INTO `chinese_infos` VALUES (11533, '迄', '水', 6, 'n', '到，至：～今为止。；始终（用于“未”或“无”前）：～未成功。～无音信。；文言副词。终究；一直。');
INSERT INTO `chinese_infos` VALUES (11534, '汐', '水', 7, 'y', '夜晚上涨的潮水。参见〔潮汐〕');
INSERT INTO `chinese_infos` VALUES (11535, '希', '水', 7, 'y', '1.少：～少。～罕。～奇。～有。～世。2.盼望：～望。～冀。～图（心里盘算达到某种目的）。～求（．希望得到；．希望和要求）。');
INSERT INTO `chinese_infos` VALUES (11536, '佛', '水', 7, 'y', '从人从弗。人表意，表示看不清楚；弗表声，有\"不正而使其正义“之义。佛，表示看不清楚而想看清楚之意。');
INSERT INTO `chinese_infos` VALUES (11537, '汛', '水', 7, 'y', '汛,洒也。--《说文》。按,此字经传多误作汛滥之汛。；汛埽反道。--《礼记·郊特性》；汛拚席。--《管子·弟子职》；况尽汛扫前圣数千载功业,专用己之私,而能享佑者哉?--《文选·扬雄·剧秦美新》；又如汛扫(洒扫);汛逐(清扫驱除；防守；大军汛黄河而角其前,荆州下宛、叶而犄其后。--《三国演义》');
INSERT INTO `chinese_infos` VALUES (11538, '冷', '水', 7, 'y', '可用作形容词，动词，常表示温度低；寂静，不热闹；生僻；不热情，不温和；不受欢迎的，没有人过问的；突然，意料以外的等意思，也是中国姓氏之一。');
INSERT INTO `chinese_infos` VALUES (11539, '况', '水', 7, 'y', '是形声字，即从水。本义为寒冷。后用于姓氏');
INSERT INTO `chinese_infos` VALUES (11540, '忘', '水', 7, 'n', '会意兼形声。从心,从亡,亡亦声。本义忘记,不记得；忘,不识也。--《说文》；德音不忘。--《诗·郑风·有女同车》；中年病忘。--《列子·周穆王》。释文不记事也。；忘路之远近。--晋·陶渊明《桃花源记》；宠辱偕忘。--宋·范仲淹《岳阳楼记》；忘身于外。--诸葛亮《出师表》');
INSERT INTO `chinese_infos` VALUES (11541, '江', '水', 7, 'y', '形声。从水,工声。本义长江的专称；江,江水也。犊；江之永矣。--《诗·周南·汉广》；滔滔江汉。--《诗·小雅·四月》；江上几人在,天涯孤棹还。--温庭筠《送人东归》；江间波浪兼天涌,塞上风云接地阴。--杜甫《秋兴八首》');
INSERT INTO `chinese_infos` VALUES (11542, '妤', '水', 7, 'y', '见〔婕妤〕');
INSERT INTO `chinese_infos` VALUES (11543, '别', '水', 7, 'y', '会意。从冎,从刀。冎,《说文》剔人肉置其骨也。别的小篆形体,是一个表示用刀剔骨头的会意字。本义分解；别,分解也。--《说文》；桀之力,别觡伸钩。--《淮南子·主术训》；又如别割(分割);别产(分割家产;分家);别国(从一国分化而成的国家;现常指别的国家；分离;分开;分出；岷山导江,东别为沱。--《书·禹贡》；久之,颇晓其别蓍布卦意。--《汉书·张禹传》；有别券者几何家?--《管子·问》');
INSERT INTO `chinese_infos` VALUES (11544, '亨', '水', 7, 'y', '1.通达，顺利2.姓。古同“烹”，煮。');
INSERT INTO `chinese_infos` VALUES (11545, '步', '水', 7, 'y', '一前一后两个脚印，为一步。多指脚步，主要用于动词，走路。步，计量单位，历代不一：周代以八尺为一步，秦代以六尺为一步。止，为半步。尺（尺骨），以人体小臂长为一尺。半步（止）是三尺，即一腿之距。');
INSERT INTO `chinese_infos` VALUES (11546, '即', '水', 7, 'y', '表示就，立刻，现在常用作连词，同“则”。那就。常用词组“即使”。同时即字还有动词、名词、副词、介词的不同用法。');
INSERT INTO `chinese_infos` VALUES (11547, '汲', '水', 7, 'y', '1.从下往上打水2.姓。');
INSERT INTO `chinese_infos` VALUES (11548, '孚', '水', 7, 'y', '1.使人信服。例 2.古又同“孵（fū）”。');
INSERT INTO `chinese_infos` VALUES (11549, '贝', '水', 7, 'y', '海介虫也。居陆名猋，在水名蜬。象形。《埤雅》贝以其背用，故谓之贝。');
INSERT INTO `chinese_infos` VALUES (11550, '孝', '水', 7, 'y', '从爻从子。效也，子承爻（天地万物变动、生生不息的规律）也，善事父母也。顺於道，顺天之经；循於伦，循地之义。《礼·祭统》；形声。从爻从子。本义:尽心奉养并尊敬父母。；读作。本意是上一代与下一代融为一体。《说文解字》解释篆体孝字云：“善事父母者。从老省，从子，子承老也。”孝字写的就是老人与子女的关系。《诗经》中有这么一段话：“父兮生我，母兮鞠我，拊我蓄我，长我育我，顾我复我，出入腹我。欲报之德，昊天罔极。”所谓「百善孝为先」，反映中华民族极为重视孝的观念。因此孝对我们来说极为重要。');
INSERT INTO `chinese_infos` VALUES (11551, '戒', '水', 7, 'y', '会意。小篆字形,上面是戈,下面象两只手(即廾)。两手持戈,表示戒备森严。本义警戒,戒备；戒,警也。--《说文》；岂不日戒。--《诗·小雅·采薇》；以三年之戒。--《礼记·曾子问》；基浇能戒之。--《左传·哀公元年》；息民不戒。--《国语·吴语》；怵然为戒。--《庄子·养生主》；戒之慎勿忘。--《玉台新咏·古诗为焦仲卿妻作》；知足以自戒。--唐·魏征《谏太宗十思疏》');
INSERT INTO `chinese_infos` VALUES (11552, '汜', '水', 7, 'y', '汜河，水名，在河南。');
INSERT INTO `chinese_infos` VALUES (11553, '呆', '水', 7, 'n', '痴;傻；呆霸王调情遭苦打。--《红楼梦》');
INSERT INTO `chinese_infos` VALUES (11554, '罕', '水', 7, 'y', '古字为“爳”。形声。小篆字形，从网，干声。本义为捕鸟用的长柄小网。出自《说文》。');
INSERT INTO `chinese_infos` VALUES (11555, '每', '水', 7, 'n', '从屮母声。艸盛上出也。；形声。小篆字形,从屮,母声。屮,草木初生。一说“每”为“母”的异体字。本义:草旺盛生长)。；每,草盛上出也。——《说文》舆人诵曰:原田每每。——《左传·僖公二十八年》。杜注:“晋军美盛,若原田之草每每然。”');
INSERT INTO `chinese_infos` VALUES (11556, '汝', '水', 7, 'y', '1.人称代词。你2.姓。');
INSERT INTO `chinese_infos` VALUES (11557, '含', '水', 7, 'y', '释义：1.东西放在嘴里，不咽下也不吐出；2、藏在里面；包含；3、带有某种意思、情感等，不完全表露出来。');
INSERT INTO `chinese_infos` VALUES (11558, '芈', '水', 7, 'y', '1.羊的叫声。2.姓。');
INSERT INTO `chinese_infos` VALUES (11559, '汕', '水', 7, 'y', '〔汕头〕市名。位于广东省东部，临南海。人口81万（1997年）。是粤东和闽南的门户，中国经济特区之一。');
INSERT INTO `chinese_infos` VALUES (11560, '忙', '水', 7, 'n', '形声。从心,亡声。本义急迫,慌忙；出门看火伴,火伴皆惊忙。--《木兰诗》；又如急忙(心里着急,行动加快);忙丢丢(急急忙忙);忙不择价(匆忙出卖东西,就卖不到好价钱);忙迫(仓皇迫促;忙碌紧张);忙手忙脚(做事时心慌忙乱的样子);忙促(匆忙急促；;忙碌而紧张);忙碌碌(匆忙急迫貌);忙忙迭迭(十分匆忙的样子；事情多,繁忙；田家少闲月,五月人倍忙。--白居易《观刈麦》');
INSERT INTO `chinese_infos` VALUES (11561, '尾', '水', 7, 'y', '引自《东夷文化与山东·骨刻文释读》释。');
INSERT INTO `chinese_infos` VALUES (11563, '妞', '水', 7, 'y', '女孩子');
INSERT INTO `chinese_infos` VALUES (11564, '兵', '水', 7, 'y', '会意。从廾,从斤。甲骨文字形,上面是斤,是短斧之类;下面是廾双手),象双手持斤。本义兵器,武器；兵,械也。--《说文》；掌五兵。--《周礼·司兵》。司农注戈、殳、戟、酋矛、夷矛也。；陈五兵。--《谷梁传·庄公二十五年》。注矛、戟、钺、榡、弓矢也。；谓五方之兵,东矛、南弩、西戈、北铩、中央剑也。--《匡谬正俗》；离为戈兵。--《易·说卦》；修尔车马,弓矢戎兵。--《诗·大雅·抑》；古之兵,戈、矛、弓、矢而已矣。--《荀子·议兵》');
INSERT INTO `chinese_infos` VALUES (11565, '妙', '水', 7, 'y', '音庙。神妙也。神也者，妙万物而为言者也。尤妙之人，含精于内，外无饰姿。——《刘劭人物志》');
INSERT INTO `chinese_infos` VALUES (11566, '甫', '水', 7, 'y', '在田里长得壮健的新苗。男子之美偁也。甫，在甲骨文字形：在田里长出了新苗；即圃的本字。从金文开始分出两个字：一个是加了囗成圃；一个是由田变为用，成甫。；象形。甲骨文字形。象田中有菜苗之形。金文字形。变为从田父声的形声字。本义:苗。；中国古代对男子的美称。多用于表字之后');
INSERT INTO `chinese_infos` VALUES (11567, '孛', '水', 7, 'y', '古书上指光芒四射的彗星。1.同“勃”。2.姓。');
INSERT INTO `chinese_infos` VALUES (11568, '伯', '水', 7, 'y', '是形声字亻为形，白为声。伯字属于白字族。在白字族里，白字都是声符兼义符。白字族汉字都与“空无一物”之义有关。伯的本义是“没有兄长的男子”、“老大”。现在一般称呼比父亲年长一些的男子。');
INSERT INTO `chinese_infos` VALUES (11569, '伴', '水', 7, 'n', '本义：同在一起而能互助的人');
INSERT INTO `chinese_infos` VALUES (11570, '形', '水', 7, 'y', '中国常用汉字。常用词有：形象、形体、形容、图形等。1.实体：～仪（体态仪表）。～体。～貌。～容。～骸。～单影只。～影相吊。2.样子：～状。～式。～态。～迹。地～。情～。3.表现：～诸笔墨。喜～于色。4.对照，比较：相～见绌。5.状况，地势：～势。6.古同“型”，模子。7.古同“刑”，刑罚。');
INSERT INTO `chinese_infos` VALUES (11571, '池', '水', 7, 'y', '形声。从水,也声。本义水停积处；水塘；池,停水曰池。--《广韵》；穿地畜水,圆者曰池,方者曰塘。--《中文大辞典》；池之竭矣。--《诗·大雅·召》；或饮于池。--《诗·小雅·无羊》；毋漉陂池。--《礼记·月令》。注穿地通水曰池。；掌沟渎浍池之禁。--《周礼·雍氏》。注谓陂障之水道也。；囿有林池。--《国语·周语》；有良田美池桑竹之属。--晋·陶渊明《桃花源记》；鸟宿池边树,僧敲月下门。--唐·贾岛《题李凝幽居》');
INSERT INTO `chinese_infos` VALUES (11572, '弧', '水', 7, 'y', '一般可解释为：；平面角的一种量度单位,其大小等于角所对的弧长被半径除的商,；弧度等于；80=57.3【弧度】；一种持续的耀眼亮光,有时具有辉光弧线的外貌,在电路断开时形成【弧光】；（3）一种电灯,当电流在两根白炽碳精棒或金属电极之间通过时引起电弧,从而发光,电极周围的气体处在大气压下【弧光灯】；（4）弧菌属的任何一种细菌,菌体略呈弧形,有鞭毛【弧菌】；（5）是圆或椭圆一部分的形状【弧形】。');
INSERT INTO `chinese_infos` VALUES (11573, '宏', '水', 7, 'y', '基本字义是广大，博大：～大。～伟。～图。～愿。～论。～丽。～亮。～观。宽～。又为百家姓之一：宏姓。');
INSERT INTO `chinese_infos` VALUES (11574, '庇', '水', 7, 'n', '基本信息汉字结构：半包围结构造字法：形声简体');
INSERT INTO `chinese_infos` VALUES (11575, '尿', '水', 7, 'n', '本以为小便，即人或动物体内由肾脏产生从尿道排泄出来的液体；引申为动词，指排出小便。');
INSERT INTO `chinese_infos` VALUES (11576, '吥', '水', 7, 'n', '唝吥（Gòngbù），柬埔寨地名，今作贡布。');
INSERT INTO `chinese_infos` VALUES (11577, '伻', '水', 7, 'n', '1.使；令。2.使者。');
INSERT INTO `chinese_infos` VALUES (11578, '弝', '水', 7, 'n', '弓的中部，射箭时握弓的地方。');
INSERT INTO `chinese_infos` VALUES (11579, '妘', '水', 7, 'n', '姓。');
INSERT INTO `chinese_infos` VALUES (11580, '否', '水', 7, 'n', '或。该字读作时字义是表示不同意，不认可的意思。读作时字义是不好，坏，恶的意思。');
INSERT INTO `chinese_infos` VALUES (11581, '呋', '水', 7, 'n', '化学音译用字。如 呋喃。');
INSERT INTO `chinese_infos` VALUES (11582, '汗', '水', 7, 'y', '原指人体在运动或其他因素下通过皮表毛孔排出的液体。互联网常用它表示对他人说的话无以言对的意思。');
INSERT INTO `chinese_infos` VALUES (11583, '吧', '水', 7, 'n', '本义：嘴唇粘合在食物或食器上。转义：结束（人或动物吃完食物，嘴部离开食物或食器）。');
INSERT INTO `chinese_infos` VALUES (11584, '夿', '水', 7, 'n', '奤夿屯（Hǎbātún），地名，在北京。');
INSERT INTO `chinese_infos` VALUES (11585, '妨', '水', 7, 'n', '解释1.阻碍，伤害：～碍。～害。无～。不～。何～。2.迷信的人指某人或某物对人不利，俗称相剋：～主。～家。');
INSERT INTO `chinese_infos` VALUES (11586, '妣', '水', 7, 'n', '称死去的母亲');
INSERT INTO `chinese_infos` VALUES (11587, '吡', '水', 7, 'n', '1.古通“比”，比较。2.诋毁。诋毁，斥责');
INSERT INTO `chinese_infos` VALUES (11588, '彷', '水', 7, 'n', '走来走去，犹疑不决，不知往哪个方向去见〖仿佛〗（彷彿）。');
INSERT INTO `chinese_infos` VALUES (11589, '佊', '水', 7, 'n', '邪；不正。');
INSERT INTO `chinese_infos` VALUES (11590, '吠', '水', 7, 'n', '动词，常被解释为狗的叫声，出自《说文》中“吠，犬鸣也”');
INSERT INTO `chinese_infos` VALUES (11591, '污', '水', 7, 'n', '污本作“污”，本意是停积不流的水，引申为脏，不洁净。现代汉语已将“污”归为“污”的异体字。');
INSERT INTO `chinese_infos` VALUES (11592, '皀', '水', 7, 'n', '稻谷的香气。粒；一粒。');
INSERT INTO `chinese_infos` VALUES (11593, '尨', '水', 7, 'n', '〔～茸〕蓬乱的样子，如“狐裘～茸。”1.多毛的狗。2.杂色古同“龙”。1.古通“庞”，高大2.姓。');
INSERT INTO `chinese_infos` VALUES (11594, '汊', '水', 7, 'n', '分支的小河；河水叉出的地方');
INSERT INTO `chinese_infos` VALUES (11595, '屁', '水', 7, 'n', '肠道产生但从肛门突然排出的臭气；屁股,臀部；常用来骂人或指斥诗文、言语的荒谬；比喻没用的、微不足道的事物；屁从肛门排出来的臭气。');
INSERT INTO `chinese_infos` VALUES (11596, '伾', '水', 7, 'n', '〔伾伾〕形容有力气。');
INSERT INTO `chinese_infos` VALUES (11597, '囫', '水', 7, 'n', '〔～囵〕整个的、完全不缺，如“～囵吞枣”（喻学习上不加分析，不作消化理解，含糊笼统地接受下来）。');
INSERT INTO `chinese_infos` VALUES (11598, '佝', '水', 7, 'n', '〔～偻〕由于缺乏维生素Ｄ引起钙、磷代谢障碍导致的骨骼发育不良。俗称“小儿软骨病”。');
INSERT INTO `chinese_infos` VALUES (11599, '汞', '水', 7, 'n', '本义一种有毒的银白色一价和二价重金属元素,它是常温下唯一的液体金属,游离存在于自然界并存在于辰砂、甘汞及其他几种矿中。常常用焙烧辰砂和冷凝汞蒸气的方法制取；汞,它主要用于科学仪器(电学仪器、控制设备、温度计、气压计)及汞锅炉、汞泵及汞气灯中；汞槽；汞弧；汞柱；汞（鋟）金属化学元素。符号。俗称\"水银\"它是易流动的银白色液体，能溶解金、银、锡、钾、钠等。蒸气有剧毒。它广泛用于科学测量仪器中，以及用于制镜、温度计、气压计1.水银灯及汞整流器等。');
INSERT INTO `chinese_infos` VALUES (11600, '吩', '水', 7, 'n', '义：用作连绵词“吩咐”。相当于嘱咐：口头指派或命令。');
INSERT INTO `chinese_infos` VALUES (11602, '阜', '水', 8, 'y', '1.土山。2.（物资）多3.姓。');
INSERT INTO `chinese_infos` VALUES (11603, '房', '水', 8, 'y', '形声。从户,方声。上古边室皆用单扇门(即户),庙门大门才用双扇门,故房从户。本义正室左右的住室；古代堂中间叫正室,两旁的叫房；房,室在傍者也。--《说文》；房,室旁夹室也。--《六书故》。段曰凡堂之内,中为正室,左右为房,所谓东房、西房也。桂馥曰古者宫室之制,前堂后室。前堂之两头有夹室,后室之两旁有东西房；。；记宰夫宴出自东房。--《仪礼·乡食礼》；在东房。--《书·顾命》；鄐子登,妇人笑于房。--《左传·宣公十七年》');
INSERT INTO `chinese_infos` VALUES (11604, '彼', '水', 8, 'y', '本义：与所据相对者。衍义：引申指“他、对方”。如：知己知彼、彼此。衍义：又用作姓');
INSERT INTO `chinese_infos` VALUES (11605, '汰', '水', 8, 'n', '淘洗；作热汤,于大盆中浸豆黄。良久,淘汰,挪去黑皮,漉而蒸之。--《齐民要术》；淘汰；洗涤、清洗；总为设教令,明赏罚,磨治洗汰,其俗一变。--《新唐书·马总传》；挑选；汰；波涛；汰,波也。--《广雅》');
INSERT INTO `chinese_infos` VALUES (11606, '非', '水', 8, 'y', '像鸟的翅膀左右展开，相背的样子。引申为“相背”、与是相背，不是也。〈动〉；指事。金文作“兆”,像“飞”字下面相背展开的双翅形,双翅相背,表示违背。本义:违背;不合。；非,违也。从飞,下翅取其相背也。——《说文》非刀匕是共。——《礼记·檀弓》。皇氏注:“不也。”非礼也。——《礼记·礼运》。注:“犹失也。”傲主人,非礼也;以贵骄人,非道也。——《世说新语·简傲》表示否定的一个词语。有时表示超乎寻常。也是一种逻辑算法，常在计算机中以“非门”或“二极管”的形式存在。表示为：。');
INSERT INTO `chinese_infos` VALUES (11607, '和', '水', 8, 'y', '和谐;协调。；和,相应也。--《说文》；和,谐也。--《广雅》；音声相和。--《老子》；乐从和。--《国语·周语下》；倡予和女。--《诗·郑风·?；鸣鹤在阴,其子和之。--《易·中孚卦》；与讴谣乎相龢。--《文选·王褒·洞箫赋》；和五声。--《吕氏春秋·慎行论》');
INSERT INTO `chinese_infos` VALUES (11608, '沉', '水', 8, 'y', '拥有多重词性。主要指“在水里往下落”、“程度深”等意思。');
INSERT INTO `chinese_infos` VALUES (11609, '沂', '水', 8, 'y', '1.沂河，水名，发源于山东，流入江苏。2.姓。');
INSERT INTO `chinese_infos` VALUES (11610, '宓', '水', 8, 'y', '安静1.古通“伏”。2.姓。');
INSERT INTO `chinese_infos` VALUES (11611, '放', '水', 8, 'y', '从方从攴；形声。从攴,方声。本义:驱逐,流放。；汉字，形声。从攴，从方，方亦声。“方”义为“城邦国家”。“攴”与“方”联合起来表示“以国家的名义将罪犯驱逐出境”、“攻克敌方城邑后将其全体居民强制驱离”。本义：驱逐，流放。');
INSERT INTO `chinese_infos` VALUES (11612, '汨', '水', 8, 'y', '〔汨罗江〕洞庭湖水系河流之一。在湖南东北部。战国时期楚国诗人屈原投此江殉节。');
INSERT INTO `chinese_infos` VALUES (11613, '版', '水', 8, 'y', '形声。从片,反声。片,意思是破开的木片或草片。从片的字大都与木板有关。本义筑墙的夹板；版,判也。--《说文》。按,判木也。；傅说举于版筑之间。--《孟子·告子下》；五板(版)而堵。--《公羊传·定公十二年》；缩版以载。--《诗·大雅·绵》；又指版筑的土墙；朝济而夕设版焉。--《左传·僖公三十年》；又如版筑；供建筑或其他使用的木板◇作板；牍。古时书写用的木简,后也指书籍；修业不息版。--《管子·宙合》');
INSERT INTO `chinese_infos` VALUES (11614, '沛', '水', 8, 'y', '多指水势湍急，行动迅疾的样子');
INSERT INTO `chinese_infos` VALUES (11615, '门', '水', 8, 'y', '象形。甲骨文字形,象门形。门是汉字的一个部首。本义双扇门,门；閠,闻也。从二户相对,象形。按,一扇曰户,两扇曰门。又,在堂室曰户,在宅区域曰门。；五祀,门以闭藏自固也。--《白虎通》；门常闭。--《墨子·号令》；门虽设而常关。--陶渊明《归去来辞》');
INSERT INTO `chinese_infos` VALUES (11616, '沌', '水', 8, 'y', '见〖混沌〗。沌河，水名，在湖北。沌口（Zhuànkǒu），沌阳（Zhuànyáng），地名，都在湖北武汉。');
INSERT INTO `chinese_infos` VALUES (11617, '没', '水', 8, 'y', '不领有，不具有，不存在、或不及，不到，有多个以“没”开头的短语。读时的本意是沉没水中，还有消失不见，覆灭、败亡、没收、隐没、衰败等含义。在古文和方言中还有多种含义。没，在粤语中“没”通“冇”，日常使用“冇”字，所以粤语中基本不会出现“没”字。');
INSERT INTO `chinese_infos` VALUES (11618, '函', '水', 8, 'y', '钱币术语，钱的方孔或者圆孔叫做函，也可叫做好、穿；公文文体。');
INSERT INTO `chinese_infos` VALUES (11619, '汽', '水', 8, 'n', '油是一种具有挥发性、可燃的烃类混合物液体，可用作燃料，尤用于内燃机。通常是由天然气和石油制品组成的混合物（如天然汽油、直馏汽油、裂化汽油、烷基化合物）。');
INSERT INTO `chinese_infos` VALUES (11620, '扮', '水', 8, 'n', '形声。从手,分声。(蝦))本义握,并；打扮,装扮；鲁智深、武松扮作行脚僧。--《征四寇传》');
INSERT INTO `chinese_infos` VALUES (11621, '妹', '水', 8, 'y', '该字的主要字义。《说文》：“妹，女弟也。从女，未声。”');
INSERT INTO `chinese_infos` VALUES (11622, '孟', '水', 8, 'y', '形声。字从子，皿声，莫更切。古音在十部，读如芒。尔雅。孟，勉也。此借孟为猛。');
INSERT INTO `chinese_infos` VALUES (11623, '沁', '水', 8, 'y', '1.（香气、液体等）渗入或透出2.头向下垂3.往水里放。');
INSERT INTO `chinese_infos` VALUES (11624, '虎', '水', 8, 'y', '象形。金文字形象以虎牙、虎纹为特征的虎形。本义老虎；虎,山兽之君。--《说文》；阚如虓虎。--《诗·大雅·常武》；虎狼之心。--《史记·项羽本纪》；苛政猛于虎。--唐·柳宗元《捕蛇者说》；曹公豺虎也。--《资治通鉴》；气吞万里如虎。--宋·辛弃疾《永遇乐·京口北固亭怀古》');
INSERT INTO `chinese_infos` VALUES (11625, '岸', '水', 8, 'y', '形声。从山,从厂,干声。厂(山崖),意思为水边高起之地。本义河岸；淇则有岸。--《诗·卫风·氓》；三尺之岸,而虚车不能登也。--《荀子·宥坐》；两岸猿声啼不住,轻舟已过万重山。--唐·李白《早发白帝城》；后泛指靠近水边的陆地；岸芷汀兰。--宋·范仲淹《岳阳楼记》；又如沿岸;起岸(把货物从船上搬运到岸上；比喻高位；诞先登于岸。(诞发语词,无义。)--《诗·大雅·皇矣》；台阶；襄岸夷涂。--张衡《西京赋》');
INSERT INTO `chinese_infos` VALUES (11626, '帛', '水', 8, 'y', '丝织物的总称');
INSERT INTO `chinese_infos` VALUES (11627, '沆', '水', 8, 'y', '形容水面辽阔。');
INSERT INTO `chinese_infos` VALUES (11628, '汪', '水', 8, 'y', '形声。字从水从王，王亦声。“王”意为“众所归往（者）”。“水”与“王”联合起来表示“众水所归往”。');
INSERT INTO `chinese_infos` VALUES (11629, '盲', '水', 8, 'y', '有形容词词性，动词词性和名词词性。作形容词时有瞎，看不见东西，对事物不能辨认等意思。');
INSERT INTO `chinese_infos` VALUES (11630, '状', '水', 8, 'y', '用具体的动物“犬”表示事物各具形态。');
INSERT INTO `chinese_infos` VALUES (11631, '冈', '水', 8, 'y', '不仅有很多的意思，例如：从山，网声；也是数学上的角度单位。');
INSERT INTO `chinese_infos` VALUES (11632, '沏', '水', 8, 'y', '（用开水）冲；泡');
INSERT INTO `chinese_infos` VALUES (11633, '沙', '水', 8, 'y', '非常细碎的石粒：～子。～石。风～。～尘。～砾（沙和碎石块）。～漠。～丘。～滩。～洲（江河里由泥沙淤积成的陆地）。～暴。～浴。～疗。～鸥（文学上指栖息岸边沙地的鸥一类的水鸟）。');
INSERT INTO `chinese_infos` VALUES (11634, '沈', '水', 8, 'y', '“沈”读shěn，用于姓氏，又是西周诸侯国名；“沉”读chén，表示沉没等义。“渖”最早的字形是篆书 [3]  ，本义是汁液，现代汉语多用于地名，读shěn');
INSERT INTO `chinese_infos` VALUES (11635, '卦', '水', 8, 'y', '是古代用来占卜的工具，它是象征自然现象和人事变化的一套符号。');
INSERT INTO `chinese_infos` VALUES (11636, '沅', '水', 8, 'y', '1.沅江，水名，发源于贵州，流入湖南。2.姓。');
INSERT INTO `chinese_infos` VALUES (11637, '抛', '水', 8, 'y', '随着时代的不断的和谐的发展，抛，也可以以形容词的词性出现再我们的日常生活中，此时，它泛指一种态度，一种思想，一种世界观。目前，此种词性尚属于生僻用法具体来讲，只能用于形容人或者特定人的行为。比如说某人或者某人的行为很抛。起源来于打牌时候三缺一，大家很期望那个人来的时候，那个人说：天气冷，不出来。自此，抛成为那个人的这种行为或者他身上做出的某种行为的形容词。现在可引申用于很多的人。');
INSERT INTO `chinese_infos` VALUES (11638, '府', '水', 8, 'y', '形声。从广,表示与房屋有关,付声。本义府库,府藏。古时国家收藏文书或财物的地方；府,文书藏也。--《说文》；开府库,出币帛。--《淮南子·时则》；在府言府。--《礼记·曲礼》；籍吏民,封府库,而待将军。--《史记·项羽本纪》；以问公卿,亦以为虚费府帑。--《汉书·匈奴传》；遇有水旱疾疫,则开仓廪悉府库以赈之。--清·洪亮吉《治平篇》');
INSERT INTO `chinese_infos` VALUES (11639, '冼', '水', 8, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (11640, '攽', '水', 8, 'n', '发给；分给。');
INSERT INTO `chinese_infos` VALUES (11641, '忻', '水', 8, 'y', '1.同“欣”。2.忻州，地名。在山西中部偏北。');
INSERT INTO `chinese_infos` VALUES (11642, '物', '水', 8, 'n', '属于常用字。本意是指杂色牛，引申指毛色、杂色，这些意义只见于上古。因杂色含有众多的意思，故引申为万事万物。另一意说，物本意指万物，引申指具体的物品，还特指自己意外的人、事、物，多指众人。');
INSERT INTO `chinese_infos` VALUES (11643, '奉', '水', 8, 'y', '同时又是一族古姓。');
INSERT INTO `chinese_infos` VALUES (11644, '武', '水', 8, 'y', '会意。从止,从戈。据甲骨文,人持戈行进,表示要动武。本义勇猛;猛烈；楚庄王曰,夫文止戈为武。又曰,夫武禁暴戢兵保大定功安民和财者也。--《左氏春秋·宣公十二年》；武大为于大君。--《易·履》；孔武有力。--《诗·郑风·羔裘》；诚既勇兮又以武。--《楚辞·九歌·国殇》；又如英武(英俊威武);威武(武力;权势;力量强大);武色(凶猛的神色);武车(威猛的兵车);武健(勇武刚健);武断乡曲(以威势主断曲直；武；指干戈军旅之事；德不厚而行武。--《韩非子·五蠹》');
INSERT INTO `chinese_infos` VALUES (11645, '雨', '水', 8, 'y', '雨,从云层中降向地面的水；雨,濡物者也。--《管子·形势解》；积土成山,风雨兴焉。--《荀子》；又如雨过天晴(像雨后晴空般的蓝色);雨打梨花(喻指零乱不堪的狼狈情景);雨打鸡(喻浑身湿淋淋的状态);雨毛(细雨);雨泣(泪流如雨；比喻朋友；常时车马之客,旧,雨来;今,雨不来。--唐·杜甫《秋述》；比喻教导之言,教泽；泽雨无偏,心田受润。--南朝梁·简文帝《上大法颂表》');
INSERT INTO `chinese_infos` VALUES (11646, '牧', '水', 8, 'y', '会意。甲骨文字形。从牛,从攴,表示手拿棍棒牧牛(羊)。本义放牧牲畜；掌牧六牲。--《周礼·牧人》；谁抜牧圉。--《左传·僖公二十八年》。注牛曰牧。；周宣王之牧正。--《列子·黄帝》。注养禽兽之长也。；牧人乃梦。--《诗·小雅·无羊》；郊外谓之牧。--《尔雅》；使牧羝。--《汉书·李广苏建传》；权汉节牧羊。；郴州荛牧儿。--唐·柳宗元《童区寄传》');
INSERT INTO `chinese_infos` VALUES (11647, '秉', '水', 8, 'y', '释义拿着，持：～烛。');
INSERT INTO `chinese_infos` VALUES (11648, '沐', '水', 8, 'y', '有6种基本含义：1.洗头发：～浴。栉风～雨（喻辛苦奔波，饱经风雨）。2.润泽，或受润泽：～恩。3.古代官员休假：～日。休～。4.整治：助之～椁。5.米汁。6.姓。');
INSERT INTO `chinese_infos` VALUES (11649, '汩', '水', 8, 'y', '〔汩汩〕水流的声音；水流 的样子。');
INSERT INTO `chinese_infos` VALUES (11650, '弦', '水', 8, 'y', '会意。左为弓,右为丝。弓弦是用丝做成的。本义弓弦；弦,弓弦也。从弓,象丝轸之形。--《说文》；无待韦弦。--《文选·王文宪集序》；故佩弦以自急。--《韩非子·观行》；引弦而战。--《淮南子·人间训》；抚弦登陴。--南朝梁·丘迟《与陈伯之书》；又如弦箭(弦上之箭);弦栝(弓弦和箭末扣弦处；弓弩；乐器上的肠线或金属丝；弦而鼓之。--明·刘基《郁离子·千里马篇》');
INSERT INTO `chinese_infos` VALUES (11652, '扶', '水', 8, 'y', '字义：帮助、援助、用手按着或把持着、搀，用手支持人或物，使不倒。也可以作姓。');
INSERT INTO `chinese_infos` VALUES (11653, '享', '水', 8, 'y', '享的古字写作“亯”，始见于商代甲骨文及商代金文，其古字形像祭祀祖先的宗庙 [2]  。在卜辞中，“亯”多用于指向神灵祖先进献食物，后泛指贡献。又转指鬼神享用祭品，再引申为拥有或享受。由享祭又引申为宴享，指给客人提供食物。');
INSERT INTO `chinese_infos` VALUES (11654, '并', '水', 8, 'y', '倂，隷作并。并，同并。；并”，读音有；常用意思为合在一起，一齐，并排着。有动词、副词、连词等的性质。读作时为山西省太原市的简称。在围棋上也可以指在原有棋子同一线上紧挨着下子。');
INSERT INTO `chinese_infos` VALUES (11655, '帔', '水', 8, 'y', '古代披在肩背上的服饰，妇女用的帔绣着各种花纹');
INSERT INTO `chinese_infos` VALUES (11656, '幷', '水', 8, 'n', '同“并”。同“并”。');
INSERT INTO `chinese_infos` VALUES (11657, '命', '水', 8, 'y', '会意。从口从令。表示用口发布命令。本义指派;发号；命,使也。--《说文》。朱骏声按在事为令,在言为命,散文则通,对文则别。令当训使也,命当训发号也。；出君下臣名曰命。--汉·蔡邕《独断》；语下命者,制令也。--《贾子·礼容》；凡尊者之言曰命。--《文选·闲居赋序》注；维君子命。--《诗·大雅·卷阿》；某固辞不得命。--《仪礼·燕礼》；命田舍东郊。--《吕氏春秋·孟春纪》。注命,令也。；命夸娥氏二子负二山。--《列子·汤问》');
INSERT INTO `chinese_infos` VALUES (11658, '或', '水', 8, 'y', '从口（人口）从一（疆界）从戈（守卫城池）。或和国是古今字，本义邦国、疆域；借指某人、有的。；会意。甲骨文字形从囗(人口),从一（疆域）从戈(以戈守之)。表示以戈卫国。本义:国家。用本义时读有人,有的——泛指人或事物古文字中，“国”（；国）字从口从或，究竟是“口”还是“或“为原形，众说纷纭。马叙伦先生主张口为国之原字，......日本学者后藤朝太郎则主张“或”为国（国）之原字。他认为加以口者，系秦汉以后之事。并于《国家学会》杂志第二十七卷第六号上发表论文，说之如下：“国（国）字作口，乃秦汉以来之事，周代春秋战国时代，只有或字。”按：或为戈与口之合体，后汉许慎于《说文解字》中亦曰，或者，邦也。');
INSERT INTO `chinese_infos` VALUES (11659, '汶', '水', 8, 'y', '〔～水〕水名，在中国山东省。亦称“大汶河”。〔～～〕心中昏暗不明，如“人又谁能以身之察察，受物之～～者乎？”');
INSERT INTO `chinese_infos` VALUES (11660, '汾', '水', 8, 'y', '汾河，水名，在山西。');
INSERT INTO `chinese_infos` VALUES (11661, '氛', '水', 8, 'y', '形声。从气,分声。本义凶气,古时迷信说法指预示吉凶的云气,多指凶气；楚氛甚恶。--《左传·襄公二十七年》；台不过望氛祥。--《国语·楚语》。注凶气为氛。；氛邪岁增。--《汉书·元帝纪》。注恶气也。；环顾国内,贼氛方炽。--孙文《序》；又如氛慝,氛邪(邪恶之气);氛厉(祸害之气);氛霓(凶气,妖气);氛杂(杂乱之气);氛秽(邪恶肮脏之气);氛妖(妖气；泛指雾气;云气；氛雾冥冥。--《礼记·月令》');
INSERT INTO `chinese_infos` VALUES (11662, '幸', '水', 8, 'y', '1.意外地得到成功或免去灾害。2、福气。3、高兴。4、希望。5、宠爱。6、指封建帝王到达某地。7、姓。8、牵走。');
INSERT INTO `chinese_infos` VALUES (11663, '味', '水', 8, 'y', '形声。从口,未声。本义滋味；味,滋味也。--《说文》；以五味五谷五药养其病。--《周礼·疾医》；味者,气之父母也。--《鹖冠子·泰录》；五味六和十二食。--《礼记·礼运》；一镬之味。--《吕氏春秋·察今》；其味皆苦。--明·刘基《诚意伯刘文成公文集》；味亦苦。；味苦而微辛。；又如味如嚼蜡(比喻诗文枯燥,读后令人心情不舒);入味(有滋味);味口(犹胃口。指食欲);味欲(对美味的嗜好);口味(饮食品的滋味；食物；食不二味,坐不重席。--《韩非子》');
INSERT INTO `chinese_infos` VALUES (11664, '朋', '水', 8, 'y', '同门、同师为朋；同志为友。按此划分，朋为同学，友则是同道。');
INSERT INTO `chinese_infos` VALUES (11665, '沃', '水', 8, 'y', '从水从芺。形声。从水,芺声。本义:把水从上浇下；');
INSERT INTO `chinese_infos` VALUES (11666, '表', '水', 8, 'y', '含义有：外表(如仪表；地名(如张表；计时工具(如手表)。会意。从毛，从衣，“毛”又兼作声符。小篆字形，衣字中间加个毛字。古人穿皮衣，毛朝外面，所以“表”从“毛”。本义：衣服上裘的有毛的这一面。');
INSERT INTO `chinese_infos` VALUES (11667, '批', '水', 8, 'y', '形声。从手,比声。本义反手打；引申为打、攻击；贵；引申为刺入；依乎天理,批大鄐,导大竨。--《庄子》');
INSERT INTO `chinese_infos` VALUES (11668, '爸', '水', 8, 'y', '本义：亲生之父。如：《广雅·释亲》：“爸，父也。”');
INSERT INTO `chinese_infos` VALUES (11669, '协', '水', 8, 'y', '是会意字，十,表示众多，办表示同力，合起来表示众人同力。本义：和睦;融洽，指共同合作，和洽；也常用于表示帮助，辅助。');
INSERT INTO `chinese_infos` VALUES (11670, '忸', '水', 8, 'n', '〔忸怩〕形容不大方或不好意思的样子。');
INSERT INTO `chinese_infos` VALUES (11671, '咐', '水', 8, 'n', '意思是嘱托；安排。');
INSERT INTO `chinese_infos` VALUES (11672, '扭', '水', 8, 'n', '动词。一般意义上指身体的某个部位，如手、头、腰等进行动作上的位移。也可指使用力量让物体改变之前的形状或者状态。');
INSERT INTO `chinese_infos` VALUES (11673, '汴', '水', 8, 'n', '1.河南开封的别称。2.姓。');
INSERT INTO `chinese_infos` VALUES (11674, '汹', '水', 8, 'n', '形容浪花大，水流急。');
INSERT INTO `chinese_infos` VALUES (11675, '屄', '水', 8, 'n', '阴门的俗称。');
INSERT INTO `chinese_infos` VALUES (11676, '姆', '水', 8, 'y', '有名词词性。属于女子部，现多用作指保姆，也指伯母等。');
INSERT INTO `chinese_infos` VALUES (11677, '把', '水', 8, 'n', '解释：拿起。（形声。字从手，从巴，巴亦声。“巴”意为“附着”、“黏着”、“匍匐”。“手”与“巴”联合起来表示“手掌附着在柄部表面”。本义：握持；执）');
INSERT INTO `chinese_infos` VALUES (11678, '沘', '水', 8, 'n', '1.沘江，水名，在云南。2.沘河，水名，在安徽。');
INSERT INTO `chinese_infos` VALUES (11679, '呼', '水', 8, 'y', '通过口、鼻把气体排出体外：呼吸；呼气。2大声叫喊：呼喊；欢呼；前呼后拥。；3.叫；召唤：呼唤；招呼；呼风唤雨。④拟声词，形容刮风、出气等声音：呼哧；呼噜；呼呼地刮着北风。⑤姓。');
INSERT INTO `chinese_infos` VALUES (11680, '呵', '水', 8, 'n', '字从口从可。“可”字意为“肩挑、担荷”。“口”与“可”合起来表示“肩挑重物时发出的喘气声”。本义：（负重）喘气。');
INSERT INTO `chinese_infos` VALUES (11681, '侔', '水', 8, 'n', '等；齐');
INSERT INTO `chinese_infos` VALUES (11682, '汔', '水', 8, 'n', '1.干涸。2.接近；庶几。');
INSERT INTO `chinese_infos` VALUES (11683, '姅', '水', 8, 'n', '指女性月经、生育、小产等。');
INSERT INTO `chinese_infos` VALUES (11684, '咂', '水', 8, 'n', '1.用嘴唇吸2.咂嘴。3.仔细辨别（滋味）');
INSERT INTO `chinese_infos` VALUES (11685, '侅', '水', 8, 'n', '1.〔奇～〕非常；特殊。2.东西在喉间卡住。');
INSERT INTO `chinese_infos` VALUES (11686, '沚', '水', 8, 'n', '水中的小块陆地。');
INSERT INTO `chinese_infos` VALUES (11687, '畀', '水', 8, 'n', '给；给以');
INSERT INTO `chinese_infos` VALUES (11688, '狃', '水', 8, 'n', '拘泥，因袭；习惯了不愿改变');
INSERT INTO `chinese_infos` VALUES (11689, '瓝', '水', 8, 'n', '小瓜。');
INSERT INTO `chinese_infos` VALUES (11690, '汳', '水', 8, 'n', '古河名，又称汴水，在中国今河南省。');
INSERT INTO `chinese_infos` VALUES (11691, '抃', '水', 8, 'n', '鼓掌，表示欢喜');
INSERT INTO `chinese_infos` VALUES (11692, '忭', '水', 8, 'n', '欢喜；快乐');
INSERT INTO `chinese_infos` VALUES (11693, '咆', '水', 8, 'n', '指猛兽怒吼。');
INSERT INTO `chinese_infos` VALUES (11694, '扳', '水', 8, 'n', '用手将物体反向移动。');
INSERT INTO `chinese_infos` VALUES (11695, '庖', '水', 8, 'n', '1.厨房2.厨师');
INSERT INTO `chinese_infos` VALUES (11696, '劾', '水', 8, 'n', '揭发别人的罪状');
INSERT INTO `chinese_infos` VALUES (11697, '殁', '水', 8, 'n', '死');
INSERT INTO `chinese_infos` VALUES (11698, '孢', '水', 8, 'n', '〔～子〕某些低等动物和植物在无性繁殖或有性生殖中产生的脱离亲本后能直接或间接发育成新个体的单细胞或少数细胞组成的繁殖体。亦作“胞子”。');
INSERT INTO `chinese_infos` VALUES (11699, '忿', '水', 8, 'n', '心绪散乱。心中乱麻一团。情绪糟糕。');
INSERT INTO `chinese_infos` VALUES (11700, '佫', '水', 8, 'n', '同“𢓜”。至；到达。1.《廣韻》下各切，入鐸，匣。2.姓。');
INSERT INTO `chinese_infos` VALUES (11701, '泐', '水', 8, 'n', '1.石头依纹理裂开。2.书写3.同“勒（lè）”。');
INSERT INTO `chinese_infos` VALUES (11702, '卑', '水', 8, 'n', '字义：1.低下，低劣：～鄙。～下。～劣。～微。～怯。～恭。地势～湿。～以自牧（保持谦虚的态度以提高自己的修养）。2、古同“俾”，使。中医解释：仲景十二脉之一。');
INSERT INTO `chinese_infos` VALUES (11703, '呸', '水', 8, 'n', '表示唾弃或斥责');
INSERT INTO `chinese_infos` VALUES (11704, '狒', '水', 8, 'n', '〔狒狒〕哺乳 动物。身体像猴，口吻突出像狗，毛粗，多灰褐色。常成群觅食。多产于非洲。');
INSERT INTO `chinese_infos` VALUES (11705, '郃', '水', 8, 'n', '1.郃阳（Héyáng），地名，在陕西。今作合阳。2.姓。');
INSERT INTO `chinese_infos` VALUES (11706, '勉', '水', 9, 'y', '力量不够而尽力做的意思，如勉强2.努力，如勉励');
INSERT INTO `chinese_infos` VALUES (11707, '泊', '水', 9, 'y', '本义：停船靠岸。如：泊船、泊位（航运上指港区能停靠船泊的位置）、停泊。衍义：引申指“停留”。衍义：引申指“无依无靠”。衍义：引申指“安静”。 ');
INSERT INTO `chinese_infos` VALUES (11708, '沔', '水', 9, 'y', '1.沔水，水名，汉水的上游，在陕西，古代也指整个汉水。2.姓。');
INSERT INTO `chinese_infos` VALUES (11709, '屏', '水', 9, 'y', '退避;隐退；侯生乃屏人间语。--《史记·魏公子列传》；又如屏居(辞官隐居,不问世事);屏语(避开他人,私下交谈);屏伏(隐退,避匿);屏移(退隐；隐藏；尔不许我,我乃屏璧与圭。--《书·金縢》');
INSERT INTO `chinese_infos` VALUES (11710, '盼', '水', 9, 'y', '形声。从目,分声。本义眼睛黑白分明的样子)。喻美目流转；盼,目黑白分也。--《说文》；美目盼兮。--《诗·卫风·硕人》。传盼,白黑分。；盼,美目也。--《字林》；又如盼刀(旧时相术用语。凶恶的目光);盼瞩(流转的目光；盼；顾;看；盼,视也。--《广雅》；祇宜近盼天都,明日登莲顶。--《徐霞客游记·游黄山记》');
INSERT INTO `chinese_infos` VALUES (11711, '勃', '水', 9, 'y', '突然，忽然：勃然（.突然，如“勃勃大怒”；.兴起的样子，如“勃勃作色”）。');
INSERT INTO `chinese_infos` VALUES (11712, '便', '水', 9, 'y', '造字本义为：排屎排尿，从排便缓解生理内急的含义引申出，顺、利、宜、安诸义。会意：从人，从更，人顺（变）更而安。但“便”字中的部首“更”，在金文大篆中的写法明显不同，应有不同起源与意义，大篆便字中的“更”部明显有排便的象形意义。发展到小篆时两者写法才一致。');
INSERT INTO `chinese_infos` VALUES (11713, '毗', '水', 9, 'y', '1.毗连2.辅助。');
INSERT INTO `chinese_infos` VALUES (11714, '抹', '水', 9, 'y', '擦：～桌子。按着向下移动、除去：2.，涂：涂～。～粉（喻美化或掩饰）。～黑（喻丑化）。～子（瓦工用来抹灰泥的器具。亦称“抹刀”）。揩，擦：～拭。哭天～泪。除去，勾掉，不计在内：～煞。轻微的痕迹：“林梢一～青如画”。一～余晖。3.，把和好了的泥或灰涂上后弄平：～墙。～石灰。紧靠着绕过去：～头。～身。用手指轻按，奏弦乐指法的一种。');
INSERT INTO `chinese_infos` VALUES (11715, '沫', '水', 9, 'y', '本义：液体形成的许多细泡 衍义：引申指“唾沫” 衍义：又用作姓');
INSERT INTO `chinese_infos` VALUES (11716, '某', '水', 9, 'y', '代指一些模糊不清的实物，或者是自称。');
INSERT INTO `chinese_infos` VALUES (11717, '波', '水', 9, 'y', '水面振荡起伏的运动。 2.物理学上指振动在物质中的传播能量递进的一种形式：电～。光～。超声～。～长。～段。～速。～谱。');
INSERT INTO `chinese_infos` VALUES (11718, '泮', '水', 9, 'y', '1.融解。2.岸。3.泮池，古代学宫前的水池。清代称考取秀才为入泮。');
INSERT INTO `chinese_infos` VALUES (11719, '玟', '水', 9, 'n', '同“珉”。玉的纹理。');
INSERT INTO `chinese_infos` VALUES (11720, '泌', '水', 9, 'n', '从生物体内产生出某种物质，流质由细孔渗透出来');
INSERT INTO `chinese_infos` VALUES (11721, '泯', '水', 9, 'y', '消灭；丧失');
INSERT INTO `chinese_infos` VALUES (11722, '炬', '水', 9, 'y', '用作名词，火炬，火把；用作动词，火烧');
INSERT INTO `chinese_infos` VALUES (11723, '拂', '水', 9, 'n', '意思是轻轻擦过；掸去；甩动；抖。；古同“弼”，是辅助的意思。');
INSERT INTO `chinese_infos` VALUES (11724, '侯', '水', 9, 'y', '从人从厂从矢，象张布；矢在其下。箭（矢）向山崖（厂）射去，本义：箭靶；后加上了“人”，有“射侯”之礼，寓意能力高强的男子，引申为有本事的人、又用作爵位名称。〈名〉；象形。从人,从厂,象张布,矢在其下。甲骨文字形,象射侯张布著矢之形。本义:箭靶。；。以兽皮或画上兽形的为之矦,春饷所射矦也。——《说文》终日射侯。——《诗·齐风·猗嗟》射有张布谓之矦。——《小尔雅·广器》');
INSERT INTO `chinese_infos` VALUES (11725, '巷', '水', 9, 'y', '从共从邑。邑中所共也。城中的胡同，人们共同使用的道路。〈名〉；。直为街,曲为巷;大者为街,小者为巷〈名〉；矿坑里的通道。如:平巷;煤巷;风巷');
INSERT INTO `chinese_infos` VALUES (11726, '香', '水', 9, 'y', '会意字。据小篆,从黍,从甘。黍表谷物;甘表香甜美好。本义五谷的香；香,芳也。春秋传曰香稷馨香。--《说文》。朱骏声曰按,谷与酒臭曰香。；黍稷靡馨。--《左传·僖公五年》；卬盛于豆,于豆于盛,其香始升。--《诗·大雅·生民》；有飶其香,邦家之光。--《诗·周颂·载芟》；稻花香里说丰年。--宋·辛弃疾《西江月》；淅玉炊香粳。--韩愈孟郊《城南联句》；泛指好闻的气味；二十里中香不断,青羊宫到浣花溪。--陆游《梅花绝句》');
INSERT INTO `chinese_infos` VALUES (11727, '计', '水', 9, 'y', '本义是算账、总计、计算。引申为打算、谋划等义。');
INSERT INTO `chinese_infos` VALUES (11728, '盈', '水', 9, 'y', '形声。字从皿，从夃，夃亦声。“夃”义为“连续添加”。“夃”与“皿”联合起来表示“往器皿中连续添加”。本义：盛满容器。说明：《说文》“盈，满器也”的“满”是动词，意为“连续盛水直到溢出为止”。');
INSERT INTO `chinese_infos` VALUES (11729, '红', '水', 9, 'y', '或形声。字从糸从工，工亦声。“糸”指彩虹的色条系列。“工”为“虹”省。“糸”与“工”联合起来表示“彩虹的最外层（表层）色条”。');
INSERT INTO `chinese_infos` VALUES (11730, '祊', '水', 9, 'n', '1.古代在宗庙门内举行的祭祀2.古代在宗庙门内设祭的地方3.中国春秋邑名，今山东省费县东南。古通“方”，指四方之祭。');
INSERT INTO `chinese_infos` VALUES (11731, '姥', '水', 9, 'y', '老年妇女的俗称：未至十余里，有一客姥，居店卖食。（《世说新语》）2通“母”。；3.婆，丈夫的母亲：便可白公姥。。；同“老”。2外祖母。');
INSERT INTO `chinese_infos` VALUES (11732, '封', '水', 9, 'y', '读作密闭，使跟外面隔绝');
INSERT INTO `chinese_infos` VALUES (11733, '泳', '水', 9, 'y', '意思：在水里游动。');
INSERT INTO `chinese_infos` VALUES (11734, '泓', '水', 9, 'y', '1.水深而广。2.清水一道或一片叫一泓');
INSERT INTO `chinese_infos` VALUES (11735, '泫', '水', 9, 'y', '水珠滴下的样子（多指眼泪）');
INSERT INTO `chinese_infos` VALUES (11736, '负', '水', 9, 'y', '从人从贝，有所恃也。负字的本意解析：负，其正体字为负，其上为一个刀字，意为持刀抢劫；其下为一个贝字，贝就是古代的钱，在此代表东西。');
INSERT INTO `chinese_infos` VALUES (11737, '泔', '水', 9, 'y', '〔泔水〕淘米、洗刷锅 碗等用过的水。有的地区叫潲水。');
INSERT INTO `chinese_infos` VALUES (11738, '皇', '水', 9, 'y', '是“煌”的本字。王，既是声旁也是形旁，表示最高统帅。皇，金文（发光的帽子）（王，最高统帅），是的变形，是“冒”的省略，表示金属护盔；在护盔上加三点，发光的帽子就是王冠，代表最高统帅。造字本义：头戴金制闪光冠冕的帝王。有的金文误将与闪光的王冠写成“白”。篆文误将金文的“白”写成“自”。隶书又将篆文的“自”写成“白”是。当“皇”的“闪光”本义消失后，再加“火”另造“煌”代替。在远古冷兵器时代，军人所使用的武器，代表军人的级别与地位：身在前线用小型战斧作战的叫“兵”*；身在将帅身边使用大型战斧的高级警卫叫“士”*；使用特大战斧的将帅叫“王”*。竹制武器叫“不”*；带刃的木制武器叫“帝”*；文治天下的叫“君”*；头戴金冠之王叫“皇”。〈形〉；：皇，大也。从自。自，始也。始皇者，三皇，大君也。自，读若鼻，今俗以始生子为鼻子。——《说文解字》；皇，大。上帝，天也。——《传》。又三皇伏羲，神农，黄帝是也。');
INSERT INTO `chinese_infos` VALUES (11739, '后', '水', 9, 'y', '会意。象人之形,施令以告四方,古之,从一口,发号者君后也。按从坐人,从口,与君同意。本义君主,帝王；后,继君体也。--《说文》；班瑞于群后。--《书·舜典》；允王维后。--《诗·周颂·时迈》；后王命冢宰。--《礼记·内则》；昔我先世后稷。--《国语·周语》；皇皇后帝。--《左传·文公二年》；夏后皋之墓也。--《左传·僖公三十二年》；又如后王(君王;天子);后帝(天帝;上帝);后夷(上古有穷国的国君);后辟(君主;帝王；帝王的妻子；天子之妃曰后。--《礼记·曲礼》');
INSERT INTO `chinese_infos` VALUES (11740, '厚', '水', 9, 'y', '会意。从厂,表示与山石有关。本义地壳厚。与薄相对；谓地盖厚。--《诗·小雅·正月》；穷高极远而测深厚。--《礼记·乐记》。注深厚,山川也。；厚将崩。--《左传·隐公元年》；见于厚土。--《素问·王常政大论》；又如厚土(覆土厚);厚地(大地);厚坤(大坤);厚厚(大地；扁平物体上下两面之间距离大的。与薄相对；刀刃者无厚。--《庄子·养生主》');
INSERT INTO `chinese_infos` VALUES (11741, '妍', '水', 9, 'y', '美丽');
INSERT INTO `chinese_infos` VALUES (11742, '沿', '水', 9, 'y', '本义：顺着，照着。如：沿岸（靠近江、河、湖、海一带的地区）、沿海（靠海的一带）、沿街、沿途、沿线。 衍义：引申指“因袭相传”。');
INSERT INTO `chinese_infos` VALUES (11743, '保', '水', 9, 'y', '主要含义是看守住，护着不让受损害或丧失：～卫。～管。～健。～障。～密。明哲～身。');
INSERT INTO `chinese_infos` VALUES (11744, '衍', '水', 9, 'y', '是会意字。由氵包裹在行中，表示水在江中流的意思。音，音同演。本意水流入海，引申为富足。又由漫步引申为平坦；由漫衍引申为多余的。');
INSERT INTO `chinese_infos` VALUES (11745, '抱', '水', 9, 'y', '形声。从手，包声。本义：用手臂围住。。公然抱茅入竹去。——唐·杜甫《茅屋为秋风所破歌》遂相与一抱而别。——清·梁启超《谭嗣同传》犹抱薪救火。——宋·苏洵《六国论》');
INSERT INTO `chinese_infos` VALUES (11746, '泱', '水', 9, 'y', '深广；弘大。');
INSERT INTO `chinese_infos` VALUES (11747, '狐', '水', 9, 'y', '形声。从犬,瓜声。本义狐狸；朕狐疑。--《汉书·文帝纪》。注狐性多疑,每渡冰河,且听且渡,故言疑者称狐疑。；小狐汔济。--《易·未济》；狐鸣呼曰。--《史记·陈涉世家》；狐裘不暖锦衾薄。--唐·岑参《白雪歌送武判官归京》；狐凭鼠伏。--《广东军务记》');
INSERT INTO `chinese_infos` VALUES (11748, '眉', '水', 9, 'y', '指眼上方的弓或嵴，亦指生长在这条嵴上的眉毛或指书页上端的空白，也借指美女。眉也是古州名，故地在今四川省眉山县。甲金文从「目」从；，；象眼睛上的眉毛之形，本义是眉毛。金文在「目」上加一长横画，是为眉，再於横画上加毛形，眉毛的意思更为突出。古人考虑到单独画出眉毛难以辨认，故连带画出眼睛。甲骨文或从「卩」，把人身也画出来。');
INSERT INTO `chinese_infos` VALUES (11749, '泰', '水', 9, 'y', '汉字泰的含义：1.平安，安定：～适（幽闲安适）。～安。～然处之。2、佳，美好：～运。否极～来。3、极：～西（旧指欧洲）。4、骄纵，傲慢：～侈（骄纵奢侈）。骄～。5、通：天地交～。');
INSERT INTO `chinese_infos` VALUES (11750, '沼', '水', 9, 'n', '（形声。字从水，从召，召亦声。本义：水池，积水的洼地。上古时期，池和沼都表水池，塘在中古时期才表示水池。一说圆曰池，曲曰沼）沼，小池也。——《一切经音义》引《说文》于沼于沚。——《诗·召南·采蘩》鱼在于沼，亦匪克乐。——《诗·小雅·正月》龟龙在宫沼。——《礼记·礼运》涧溪沼沚之毛。——《左传·隐公三年》王立于沼上。——《孟子》射沼滨之高鸟。——《淮南子·原道》茅覆环堵亭，泉添方丈沼。——唐·白居易《过骆山人野居小池》');
INSERT INTO `chinese_infos` VALUES (11751, '美', '水', 9, 'y', '会意字。金文字形，从羊，从大。本义:肥美。古人养羊肥大为美。再等等，把羊养肥了再吃，指代:很好吃，味美甘甜。');
INSERT INTO `chinese_infos` VALUES (11752, '风', '水', 9, 'y', '形声。从虫,凡声。风动虫生故字从虫。本义空气流动的自然现象;尤指空气与地球表面平行的自然运动；飈,八风也。东方曰明庶风,东南曰清明风,南方曰景风,西南曰凉风,西方曰闾阖风,西北曰不周风,北方曰广莫风,东北曰融风。风动虫生,故虫八日而化。--《说文》；立春条风。--《易·通卦验》；东北条风。--《广雅·释天》；距日冬至四十五日,条风至。--《淮南子·天文》；条风居东北维,主出万物条之。言条治万物而出之。--《史记·律书》；今日不鴓(飈)?--《殷虚文字甲编》');
INSERT INTO `chinese_infos` VALUES (11753, '法', '水', 9, 'y', '从氵从廌从去，以水之平、廌触不直者去之。后简为法。；会意。从“水”,表示法律、法度公平如水;从“廌”,即解廌,神话传说中的一种神兽,据说,它能辨别曲直,在审理案件时,它能用角去触理曲的人。基本义:刑法;法律;法度。；同基本义解廌（獬豸）具有很高的智慧，懂人言知人性。它怒目圆睁，能辨是非曲直，能识善恶忠奸，发现奸邪的官员，就用角把他触倒，然后吃下肚子。它能辨曲直，又有神羊之称，它是勇猛、公正的象征，是司法“正大光明”“清平公正”的象征。法是国家的产物。是指统治者(统治集团，也就是政党,包括国王、君主)，为了实现统治并管理国家的目的，经过一定立法程序，所颁布的一切规范的总称。20；4年；2月；9日，由国家语言资源监测与研究中心、商务印书馆等主办的“汉语盘点20；4”年度字词今揭晓，“法”被列年度国内字。');
INSERT INTO `chinese_infos` VALUES (11754, '昴', '水', 9, 'y', '星名。二十八宿之一。');
INSERT INTO `chinese_infos` VALUES (11755, '冒', '水', 9, 'y', '会意。小篆字形。上为帽子,下边是眼睛。本义帽子；帽的古字。帽子；著黄冒。--《汉书·儁不疑传》；白纱冒者,视朝、听讼、宴见宾客之服也。--《新唐书》；又如冒絮(头巾；古代殓尸的布囊,由上下两截合成；冒缁质,长与手齐。--《仪礼》；冒与芼古字通。芼,菜也；肥狗之和,冒以山肤。--枚乘《七发》；通瑁。天子所执之玉；天子执冒四寸,以朝诸侯。--《周礼·考工记·玉人》；冒；戴帽；山东之卒被甲冒胄以会战。--《战国策》');
INSERT INTO `chinese_infos` VALUES (11756, '治', '水', 9, 'y', '从水从台（胎的本字）。自水的初始处、基础、细小处开始，以水的特征为法，进行的修整、疏通，是为治。治，本义水名，出自泰山。治，一般做动词，亦可作名词或形容词。有管理、惩办、医疗、从事研究等意思。旧称地方政府所在地，如府治、治所等。还可作姓氏。');
INSERT INTO `chinese_infos` VALUES (11757, '玫', '水', 9, 'y', '〔～瑰〕.美玉；.落叶灌木，枝上有刺，花有紫红色、白色等多种，香味很浓，可做香料，花和根可入药。');
INSERT INTO `chinese_infos` VALUES (11758, '泗', '水', 9, 'y', '1.鼻涕。2.泗河，水名，在山东。');
INSERT INTO `chinese_infos` VALUES (11759, '泠', '水', 9, 'y', '1.清凉2.姓。');
INSERT INTO `chinese_infos` VALUES (11760, '泉', '水', 9, 'y', '象形。甲骨文字形。象水从山崖泉穴中流出的样子。本义泉水；泉,人原也。象水流出成川形。字亦作洤。--《说文》；如彼泉流。--《诗·小雅·小昮》；泉之竭矣。--《诗·大雅·召昮》。传泉水从中以益者也。；泉涓涓而始流。--晋·陶渊明《归去来兮辞》；酿泉为酒。--宋·欧阳修《醉翁事记》；泉香而酒洌。；泉而茗者。--明·袁宏道《满井游记》；有泉可汲。--明·顾炎武《复庵记》');
INSERT INTO `chinese_infos` VALUES (11761, '扁', '水', 9, 'y', '有两个含义：一.，；物体平而薄：扁豆。扁担。扁圆。扁铲。；古同“匾”，匾额。二.，小，如“扁舟(小船)”。');
INSERT INTO `chinese_infos` VALUES (11763, '河', '水', 9, 'y', '形声。从水,可声。本义黄河；河,河水出敦煌塞外昆仑山,发原注海。--《说文》；导河积石。--《书·禹贡》；河阳之北。--《列子·汤问》；三豕涉河。--《吕氏春秋·慎行论》；又如河堧(黄河河边之地);河湟(黄河与湟水);河朔(泛指黄河北岸的地方);河洛(黄河与洛水);河堑(黄河沿岸的壕沟；河流的通称；南方无河也,冀州凡水大小皆谓之河。--《汉书·司马相如传》注；国破山河在,城春草木深。--杜甫《春望》；韩信钓河曲。--《后汉书·郦炎传》');
INSERT INTO `chinese_infos` VALUES (11764, '沱', '水', 9, 'y', '1.沱江，水名，是长江的支流，在四川中部。2.〈方〉可以停泊船只的水湾。多用于地名，如朱家沱、唐家沱（均在四川）。');
INSERT INTO `chinese_infos` VALUES (11765, '飞', '水', 9, 'y', '象形。小篆字形,下面象展开的双翼,上面象鸟首。本义鸟飞；飛,鸟翥也。象张翼之形。--《说文》；飞鸟遗之音。--《易·小过》；明珠弹于飞肉。--《太玄·唐》。注飞肉,禽鸟也。；西塞山前白鹭飞,桃花流水鳜鱼肥。--唐·张志和《渔歌子》；鸟倦飞而知还。--晋·陶渊明《归园田居》；鸢飞戾天者。--吴均《与朱元思书》；又如飞凫(飞翔的凫鸟);飞翰(飞翔的小鸟);飞鸿(飞翔中的鸿雁；泛指飞翔；飞龙在天。--《易·乾》；彗孛飞流。--《汉书·天文志》');
INSERT INTO `chinese_infos` VALUES (11766, '品', '水', 9, 'y', '会意,从三口。口代表人,三个表多数,意即众多的人。本义众多；品,众庶也。--《说文》；(3；又如品人(众多的人);品事(诸多事项);品物(各类特定范围之物);品供(各种供品);品象(众人);品庶(众人百姓);品极(众多；(4；颜色淡。如品绿；(5；齐一,相同；品,齐也。--《广雅》；品其百笾。--《国语·周语中》；品；〈名〉；(事物的种类；五品不逊。--《虞书》。传,谓五常。郑注父母兄弟子也。；厥贡惟金三品。--《书·禹贡》；果、肴非远方珍异,食非多品。--宋·司马光《训俭示康》；又如货品(货物或货物的品种);品汇(事物的品种类别);品搭(按品类等级搭配);品流(品类;流别);品例(种类);品件(花色品种);用品(应用的物品);珍品(珍贵的物品);品物(物品;东西；(3；品性;品格。如人品(人的品质);品诣(品行);品概(品格;气节);品地(品质;本色;官职);品格儿(相貌;形体);品极(人品的高低；(4；等级；(5；又如品从(等级);上品；(6；中国封建社会的一种官阶、爵阶；皇上超擢品卿衔军机章京。--清·梁启超《谭嗣同传》；(7；又如品子(品官之子弟);品次(位列;品级);品制(等级制度);品命(官阶;品位);品著(指不同品位的服饰);品从(正从。指古代官吏的正品与从品);品爵(品级爵位);品令(盐官吏的格令；(8；格调；诗之品有九。--《沧浪诗话》；(9；标准,基准。如品式(标准;法式);品度(标准,法度；(；0；法式,法则；乡有俗,国有法。…品有所成,故曰人不一事。--《管子》；(；乐器构件名。指月琴、琵琶等乐器上的弦枕木；(；2；古地名。唐宋皆有品州,约在今四川省宜宾地区境内；(；3；姓；品；演奏乐器。如品竹(吹奏管乐器);品竹弹丝(吹拉弹唱);品弄(吹奏);品统法(弦乐曲调的弹奏技法);品箫弄笛(吹奏洞箫和笛子；评价,衡量；于病中犹作《汉晋春秋》,品评卓逸。--《世说新语·文学》；品其名位,犹不失下曹从事。--《资治通鉴》；(3；又如品度(品评打量);品择(品评选择);品藻(品评;评论);品酌(衡量,斟酌);品选(评选);品头题足(亦作品头论足”。指无聊的人随意议论妇女的容貌体态；(4；品尝品评茶味；品尝；细致地辨别滋味;试图[味道]；膳夫授祭,品尝食,王乃食。--《周礼·膳夫》。郑玄注品者,每物皆尝之。”；品尝菜肴；品德；[]品格德行；被一种个人的品德所激励；品等；质量等级；所生产之木材由发展局丈量及评定品等；品第；评出级别之高低；品第；指门第、等级；品服；古代表示官吏等级的、型制颜色不同的公服；品格；[]∶品性;性格；烈士所具有的品格；[]∶指文学、艺术作品的质量和风格；(3；∶物品的质量、规格；品红；比大红浅的红色；品级；[]∶古代官吏的级别；∶各种产品、物品的质量级别；品鉴；欣赏鉴定；品鉴艺术品；品节；[]品德节操；品酒；喝酒;尝酒的味道；品蓝；略带红的蓝色；品类；品种;类别;指各种物体；品绿；ǜ；[]像青竹那样的绿色；品貌；[]∶人的品行、相貌；品貌俱佳；我的这个贤婿,才学又高,品貌又好。--清·吴敬梓《儒林外史》；∶人的个性和容貌；品名；产品或商品的名称；品茗；品茶;喝茶；品目；物品的名称、类别；品目繁杂；品牌；商品牌号,商标；品评；评价;评论；他开始品评油画；品色；品种;品类；这个茶店的茶叶品色多样；品题；评论人物,定其高下；好共核论乡党人物,每月辄更其品题,故汝南俗有月旦评焉。--《后汉书·许劭传》；品头论足；－；指有些闲人过多地仔细评论女性相貌,也比喻在细节上随便批判；众情颠倒,品头论足,纷纷如狂。--《聊斋志异》');
INSERT INTO `chinese_infos` VALUES (11767, '奂', '水', 9, 'y', '1.文采鲜明。2.盛；多。');
INSERT INTO `chinese_infos` VALUES (11768, '宦', '水', 9, 'n', '本义是做奴隶主或帝王的奴仆。');
INSERT INTO `chinese_infos` VALUES (11769, '拭', '水', 9, 'n', '从手，从式，式亦声。“式”指“程序”、“规范”。“手”与“式”联合起来表示“按照一定的程序或规范来清理”。');
INSERT INTO `chinese_infos` VALUES (11770, '染', '水', 9, 'y', '把东西放在颜料里使着色；感受疾病或沾上坏习惯或接触到什么。');
INSERT INTO `chinese_infos` VALUES (11771, '洗', '水', 9, 'y', '形声。从水,先声。据甲骨文,上为足形,下为水形。本义用水洗脚；洗,洒足也。--《说文》；洗去足垢。--《论衡·讥日》；王方踞床洗。--《汉书·黥布传》。注濯足也。；沛公方踞床,使两女子洗足。--《史记·高祖本纪》；假借为洒。用水涤除污垢；水在洗东。--《仪礼·乡饮酒礼》。注承盥洗者,弃水器也。；上闻后宫欢笑,问其故,左右以贵妃三日洗禄儿对。--《资治通鉴·唐玄宗天宝十载》；晴雪所洗。--明·袁宏道《满井游记》');
INSERT INTO `chinese_infos` VALUES (11772, '贞', '水', 9, 'y', '会意。从卜,从贝(甲骨文作鼎,后省改为贝)。鼎本是食器,这里表火具,即用火具而卜。本义占卜；貞,卜问也。从卜,贝以为贽。会意。京房说,鼎省声。--《说文》；季冬,陈玉,以贞来岁之恶。--《周礼·春官·天府》。郑玄注问事之正曰贞。；凡国大贞,卜立君,卜大封。--《周礼》；又如贞龟(占卜。古人灼龟甲以卜);贞吝(卜问不吉,其事难行);贞卜(占卜;卜问；贞；假借为正、为定。端方正直；元、亨、利、贞。--《易·乾》');
INSERT INTO `chinese_infos` VALUES (11773, '叛', '水', 9, 'n', '指背叛过去的立场或集团而投靠敌对一方。');
INSERT INTO `chinese_infos` VALUES (11774, '泡', '水', 9, 'n', '古水名。又名丰水；泡,泡水,出山阳平乐,东北入泗。--《说文》；鼓起而松软的东西；泡；盛大；泡,盛也。--《方言》二；又如泡溲(水盛大的样子；泡〈量〉；用于屎尿或涕泪；相当于番、回；顺便去会制台,着实恭维一泡。--《文明小史》');
INSERT INTO `chinese_infos` VALUES (11775, '疱', '水', 9, 'n', '皮肤上长的像水泡的小疙瘩。 也指凸出皮肤表面的火疱或脓疱。');
INSERT INTO `chinese_infos` VALUES (11776, '怖', '水', 9, 'n', '畏惧；非常害怕：恐怖；阴森恐怖。');
INSERT INTO `chinese_infos` VALUES (11777, '哞', '水', 9, 'n', '形容牛叫的声音。');
INSERT INTO `chinese_infos` VALUES (11778, '泣', '水', 9, 'n', '“泣”：是形声字，“眼泪”的意思');
INSERT INTO `chinese_infos` VALUES (11779, '咪', '水', 9, 'y', '象声词，形容猫叫声或唤猫等。');
INSERT INTO `chinese_infos` VALUES (11780, '拍', '水', 9, 'n', '一般是指动词拍打，如拍手。也可以用于摄影，如拍摄。甚至可以作拍马屁来进行嘲讽。');
INSERT INTO `chinese_infos` VALUES (11781, '沭', '水', 9, 'n', '沭河，水名。发源于山东南部，流至江苏北部入海。');
INSERT INTO `chinese_infos` VALUES (11782, '哺', '水', 9, 'n', '指喂不会取食的幼儿：～乳。～养。～育；口里含着的食物：“一饭三吐～”。');
INSERT INTO `chinese_infos` VALUES (11783, '拇', '水', 9, 'n', '指足的大趾或手的中指。');
INSERT INTO `chinese_infos` VALUES (11784, '泅', '水', 9, 'n', '浮水');
INSERT INTO `chinese_infos` VALUES (11785, '眇', '水', 9, 'n', '1.原指一只眼睛瞎，后来也指两只眼睛瞎。2.渺小；微小。');
INSERT INTO `chinese_infos` VALUES (11786, '窆', '水', 9, 'n', '埋葬。');
