INSERT INTO `chinese_infos` VALUES (11795, '抿', '水', 9, 'n', '1.用小刷子蘸水或油抹（头发等）2.（嘴、耳朵、翅膀等）稍稍合拢；收敛3.嘴唇轻轻地沾一下碗或杯子，略微喝一点');
INSERT INTO `chinese_infos` VALUES (11796, '柒', '水', 9, 'n', '七的大写；壹、贰、叁、肆、伍、陆、柒、捌、玖、拾,字书皆有之。--宋·陆游《老学庵笔记》');
INSERT INTO `chinese_infos` VALUES (11797, '哌', '水', 9, 'n', '化学音译用字。如哌嗪。');
