INSERT INTO `chinese_infos` VALUES (11815, '怙', '水', 9, 'n', '1.依靠；仗恃。2.坚持');
INSERT INTO `chinese_infos` VALUES (11816, '拊', '水', 9, 'n', '1.拍2.古又同“抚慰”的“抚”。');
INSERT INTO `chinese_infos` VALUES (11817, '卸', '水', 9, 'n', '会意。本义解马卸车。卸,舍车解马也。--《说文》；卸,卸马去鞍。--《广韵》；空烦卸马鞍。--杜甫《携酒高亦同过用寒字》；又如卸车;卸鞍；解除;解下。亦指褪下;脱下；你快卸了装束,归顺天恩。--《西游记》；功高马卸黄金甲。--唐·黄滔《李相公》');
INSERT INTO `chinese_infos` VALUES (11818, '泄', '水', 10, 'n', '同泻，也表示疏散、宣泄等意思。');
INSERT INTO `chinese_infos` VALUES (11819, '罘', '水', 9, 'n', '芝罘（Zhīfú），山名，又岛名，都在山东。');
INSERT INTO `chinese_infos` VALUES (11820, '玢', '水', 9, 'y', '1.玉名。2.玉的纹理。见〔赛璐玢〕');
INSERT INTO `chinese_infos` VALUES (11821, '疤', '水', 9, 'n', '形声。字从疒，从巴，巴亦声。“巴”指“附着”、“匍匐”。“疒”与“巴”联合起来表示“一层结痂附着在柔软皮肤的表面”。本义：皮外伤形成的硬质结痂层。)');
INSERT INTO `chinese_infos` VALUES (11822, '油', '水', 9, 'n', '基础意义：形声。字从水，从由，由亦声。“由”意为“滑动”。“水”指“汁水”、“液体”。“水”与“由”联合起来表示“润滑的液体”。本义：润滑的（动植物）汁液。');
INSERT INTO `chinese_infos` VALUES (11823, '沾', '水', 9, 'n', '意思是浸湿。');
INSERT INTO `chinese_infos` VALUES (11824, '毖', '水', 9, 'n', '谨慎小心');
INSERT INTO `chinese_infos` VALUES (11825, '拔', '水', 9, 'y', '字义：抽出、拉出、吸出、选取、提升、超出、高出、改变、夺取军事上的据点、把东西放在凉水里使变凉等意思。');
INSERT INTO `chinese_infos` VALUES (11826, '泖', '水', 9, 'n', '水面平静的小湖。也用于地名，如泖桥（在上海）。');
INSERT INTO `chinese_infos` VALUES (11827, '沲', '水', 9, 'n', '见〔淡沲〕同“沱”。');
INSERT INTO `chinese_infos` VALUES (11828, '哈', '水', 9, 'y', '通常指张口呼气：如打哈欠，哈一口气。也作象声词，形容爽朗的笑声。姓。');
INSERT INTO `chinese_infos` VALUES (11829, '泵', '水', 9, 'n', '音译字。吸入和排除流体的机械。能提升、输送或压缩流体；泵吸入和排出流体的机械。能把流体抽出或压入容器，也能把液体提送到高处。如水泵。又叫唧筒。');
INSERT INTO `chinese_infos` VALUES (11830, '沸', '水', 9, 'n', '开，滚，液体受热到一定温度时，内部发生气泡，表面翻滚，变成蒸气：～点。～水。～涌。～腾（亦喻事物蓬勃发展或情绪高涨）。～反盈天（形容人声喧闹，乱成一片）。人声鼎～。；波涌的样子：～郁（．翻涌的样子；．愤懑不平的样子）。');
INSERT INTO `chinese_infos` VALUES (11831, '昧', '水', 9, 'n', '形声。从日,未声。从日,与光线有关。本义昏暗不明；昧,冥也。--《广雅·释诂四》；时甲子昧爽。--《书·牧誓》；日昧昧其将暮。--《楚辞·九章·怀沙》；君昧爽而栉冠。--《荀子·哀公》。注昧,闇;爽,明也。谓初晓尚暗之时。；神非其所宜而行之则昧。--《淮南子·原道》；虽昧必亮。--《后汉书·苏竟传》；愚者昧于成事。--《战国策·赵策》；惟党人之偷乐兮,路幽昧以险隘。--《楚辞·离骚》；轻身而昧大义。--清·方苞《左忠毅公逸事》');
INSERT INTO `chinese_infos` VALUES (11832, '很', '水', 9, 'n', '表示达到一个很高的程度或达到一个相当的范围。');
