INSERT INTO `chinese_infos` VALUES (11835, '虻', '水', 9, 'n', '昆虫，体长椭圆形，头阔，触角短，复眼大，黑绿色，口吻粗，腹部长大。生活在田野杂草中，雄的吸植物的汁液或花蜜，雌的吸人和动物的血液。幼虫生活在泥土、池沼、稻田中，吃昆虫、草根等。种类很多，如牛虻。');
