INSERT INTO `chinese_infos` VALUES (12411, '茘', '水', 9, 'n', '〔～枝〕ａ．常綠喬木，果實球形，果實外殼有瘤狀突起，熟時紫紅色，果肉白色半透明，多汁，味甜美；ｂ．這種植物的果實。均亦稱“離枝”、“丹荔”。');
