INSERT INTO `chinese_infos` VALUES (13425, '郲', '水', 10, 'n', '1.古国名，中国春秋时被齐所灭。故地在今山东省龙口市莱子城一带。亦作“莱”。2.古地名，中国春秋时属郑，在今河南省荥阳县东厘城旧址。3.姓。');
INSERT INTO `chinese_infos` VALUES (13426, '郳', '水', 10, 'n', '1.中国周代诸侯国名，在今山东省滕州市东（一说在枣庄市西北）。2.姓。');
