INSERT INTO `chinese_infos` VALUES (14862, '迈', '水', 20, 'y', '抬起腿来跨步：迈步。迈进。老：老迈。年迈。远行：远迈。豪放：豪迈。超过：“则三王可迈，五帝可越”。英里（用于机动车行车速度）：每小时八十迈。迈克尔杰克逊：迈克尔杰克逊的中文简称，迈克尔杰克逊，美国流行音乐之王，是世界上最伟大的艺术家。');
