INSERT INTO `chinese_infos` VALUES (14868, '濒', '水', 20, 'y', '【释义】①紧靠（水边）：濒海|濒湖。②临近（某种不好的境地）：濒死|濒危|濒行。【濒临】 #bīnlín 紧接；临近。〖例句〗我国濒临太平洋，有很多的水产资源。');
