INSERT INTO `chinese_infos` VALUES (15186, '鶔', '水', 20, 'n', '〔鶝（fú）～〕见“鶝1”。');
INSERT INTO `chinese_infos` VALUES (15187, '鶕', '水', 20, 'n', '古同“鹌”。');
INSERT INTO `chinese_infos` VALUES (15188, '鹙', '水', 20, 'n', '秃鹙， 古书上说的 一种水鸟。头上没有毛，性贪暴，喜食蛇。');
INSERT INTO `chinese_infos` VALUES (15189, '𫛸', '水', 20, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (15190, '鶙', '水', 20, 'n', '古同“鶗”。古同“鶗”。');
INSERT INTO `chinese_infos` VALUES (15191, '鶛', '水', 20, 'n', '雄鹌鹑。');
INSERT INTO `chinese_infos` VALUES (15192, '鶝', '水', 20, 'n', '〔～鶔（róu）〕古代传说中的一种鸟。形状像喜鹊而尾巴较短。很灵活，能躲过射向自己的箭，并将其衔住，从嘴中吐出反射人。〔～𪂉〕鸟名。即戴胜鸟。');
INSERT INTO `chinese_infos` VALUES (15193, '鶞', '水', 20, 'n', '〔鳻（fén）～〕见“鳻2”。');
INSERT INTO `chinese_infos` VALUES (15194, '鶟', '水', 20, 'n', '〔～鶦（hú）〕白头翁（一种鸟）。');
INSERT INTO `chinese_infos` VALUES (15195, '鶠', '水', 20, 'n', '凤凰的别称。');
INSERT INTO `chinese_infos` VALUES (15196, '鹖', '水', 20, 'n', '古书上说的一种善斗的鸟。');
INSERT INTO `chinese_infos` VALUES (15197, '鶢', '水', 20, 'n', '〔～鶋（jū）〕古书上说的一种海鸟。');
INSERT INTO `chinese_infos` VALUES (15198, '鶣', '水', 20, 'n', '〔～𪅃〕轻捷，如“～𪅃燕居。”古同“扁”，姓。');
INSERT INTO `chinese_infos` VALUES (15199, '鶤', '水', 20, 'n', '1.大鸡2.〔～鸡〕凤凰的别称。3.古书上说的一种像天鹅的大鸟');
INSERT INTO `chinese_infos` VALUES (15200, '𫛷', '水', 20, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (15201, '鶨', '水', 20, 'n', '古书上说的一种勾嘴鸟。亦称“痴鸟”。古同“雉”，野鸡。');
INSERT INTO `chinese_infos` VALUES (15202, '䴗', '水', 20, 'n', '古书上指伯劳。');
INSERT INTO `chinese_infos` VALUES (15203, '鶫', '水', 20, 'n', '◎ 斑鶇。秋天從北方南來的候鳥。◎ 日本地名用字。');
INSERT INTO `chinese_infos` VALUES (15204, '鷌', '水', 20, 'n', '一种小鸟，即“黄雀”。');
INSERT INTO `chinese_infos` VALUES (15205, '麘', '水', 20, 'n', '古同“香”。');
INSERT INTO `chinese_infos` VALUES (15206, '麙', '水', 20, 'n', '古同“羬”，细角羚羊');
INSERT INTO `chinese_infos` VALUES (15207, '麚', '水', 20, 'n', '公鹿。');
INSERT INTO `chinese_infos` VALUES (15208, '麛', '水', 20, 'n', '1.幼鹿2.泛指幼兽');
INSERT INTO `chinese_infos` VALUES (15209, '黁', '水', 20, 'n', '香气');
INSERT INTO `chinese_infos` VALUES (15210, '黤', '水', 20, 'n', '深黑色');
INSERT INTO `chinese_infos` VALUES (15211, '鼰', '水', 20, 'n', '“鼳”的訛字。');
INSERT INTO `chinese_infos` VALUES (15212, '齚', '水', 20, 'n', '古同“齰”，啃，咬');
INSERT INTO `chinese_infos` VALUES (15213, '齛', '水', 20, 'n', '羊反刍。');
INSERT INTO `chinese_infos` VALUES (15214, '齝', '水', 20, 'n', '牛反刍。');
INSERT INTO `chinese_infos` VALUES (15215, '齞', '水', 20, 'n', '齿露唇外');
INSERT INTO `chinese_infos` VALUES (15216, '䶮', '水', 20, 'n', '人名 用字。五代南汉主刘䶮自造的名字。');
INSERT INTO `chinese_infos` VALUES (15217, '瀵', '水', 21, 'y', '1.由地底喷出 的泉水。2.瀵水，水名。在山西。');
INSERT INTO `chinese_infos` VALUES (15218, '霸', '水', 21, 'y', '形声。从月。；假借为伯。指古代诸侯联盟的盟主；霸者,伯也,行方伯之职。--《白虎通》；管仲以其君霸。--《孟子·公孙丑上》；霸者,长也。言为诸侯之长。--《孟子·离娄·丁音》；五霸不同法而霸。--《商君书·更汉》；又如春秋五霸;霸王请客;强留(歇后语；依仗权势或实力横行一方的人；薛家系金陵一霸。--曹雪芹《红楼梦》');
INSERT INTO `chinese_infos` VALUES (15219, '藩', '水', 21, 'y', '1.篱笆2.屏障；保卫。3.封建王朝分封的属地或属国');
INSERT INTO `chinese_infos` VALUES (15220, '邈', '水', 21, 'y', '遥远');
INSERT INTO `chinese_infos` VALUES (15221, '轰', '水', 21, 'n', '拟声词，形容巨大的声响：轰鸣；轰隆；轰响。');
INSERT INTO `chinese_infos` VALUES (15222, '瀼', '水', 21, 'n', '瀼河，水名，在河南。瀼渡河（Ràngdù Hé），水名，在重庆。');
INSERT INTO `chinese_infos` VALUES (15223, '辩', '水', 21, 'n', '说明是非或争论真假。');
INSERT INTO `chinese_infos` VALUES (15224, '飙', '水', 21, 'y', '暴风');
INSERT INTO `chinese_infos` VALUES (15225, '澜', '水', 21, 'y', '本意指波浪，大波为澜。组词波澜、狂澜、巨澜、澜倒波随（犹言随波逐流，比喻言行无标准）、澜澳（水曲）、推波助澜。');
INSERT INTO `chinese_infos` VALUES (15226, '攘', '水', 21, 'n', '从扌（手）从襄。推手使前也。古让字。引申之使人退让亦用此字。本义：推让。“手”与“襄”联合起来表示“用手接过对方的物品，接着用手递回去”、“用手握住对方伸过来的手并反推回去”。');
INSERT INTO `chinese_infos` VALUES (15227, '鹤', '水', 21, 'y', '形声。从鸟,隺声。本义鹤科各种类鸟的统称,皆为大型涉禽；,羽毛更紧密,部分秃顶,嘴较钝并在近中央处有较大鼻孔,后趾向上抬起；鹤,似鹄长喙。--《广韵》；鹤鸣于九皋。--《诗·小雅·鹤鸣》；腾群鹤于瑶光。--《楚辞·刘向·九叹·远游》');
INSERT INTO `chinese_infos` VALUES (15228, '黯', '水', 21, 'n', '黑；阴暗');
INSERT INTO `chinese_infos` VALUES (15229, '灂', '水', 21, 'n', '〔～～〕象声词，雨声或水声，如“霰雨～～。”1.用漆涂合。2.眼睛昏蒙〔瀺～〕见“瀺”。');
INSERT INTO `chinese_infos` VALUES (15230, '瀿', '水', 21, 'n', '水暴溢。');
INSERT INTO `chinese_infos` VALUES (15231, '瀷', '水', 21, 'n', '1.雨后地面的积水2.水流急。3.水名。');
INSERT INTO `chinese_infos` VALUES (15232, '猕', '水', 21, 'n', '见下 。');
INSERT INTO `chinese_infos` VALUES (15233, '醺', '水', 21, 'n', '酒醉');
INSERT INTO `chinese_infos` VALUES (15234, '瀹', '水', 21, 'n', '1.煮2.疏通（河道）。');
INSERT INTO `chinese_infos` VALUES (15235, '潋', '水', 21, 'n', '1.形容水满或满而溢出2.形容水波流动');
INSERT INTO `chinese_infos` VALUES (15236, '鳍', '水', 21, 'n', '形声。从鱼,耆声。本义鱼类和某些其他水生动物的类似翅或桨的附肢；。如鳍棘(支撑鱼鳍薄膜的棘刺状硬骨。又叫鳍鬣；鳍鱼类及其它水生脊椎动物的运动器官。一般由薄膜、柔软分节的\"鳍条\"和坚硬不分节的\"鳍棘\"构成背～。腹～。尾～。');
INSERT INTO `chinese_infos` VALUES (15237, '护', '水', 21, 'y', '从言从蒦。言，言语；蒦，以手抓获盗贼。合起来的意思是：以言语劝慰使之不受侵害。1.使不受侵犯和损害：2.掩蔽，包庇。');
INSERT INTO `chinese_infos` VALUES (15238, '襮', '水', 21, 'n', '1.表露2.外表。');
INSERT INTO `chinese_infos` VALUES (15239, '骠', '水', 21, 'n', '1.勇猛2.马快跑的样子。马的一种。全身淡黄栗色，鬃、尾等长毛部分近于白色，现叫银河马。也指全身黄栗色的马，通称黄骠马。');
INSERT INTO `chinese_infos` VALUES (15240, '霹', '水', 21, 'n', '意思是雷电轰击。');
INSERT INTO `chinese_infos` VALUES (15241, '鼙', '水', 21, 'n', '击乐器。古时军队中用的小鼓。');
INSERT INTO `chinese_infos` VALUES (15242, '瀐', '水', 21, 'n', '古同“瀸”。');
INSERT INTO `chinese_infos` VALUES (15243, '膘', '水', 21, 'n', '字从肉从票，票亦声。“票”意为“掠过”、“轻拂”。“肉”与“票”联合起来表示“饱含油脂的肥肉”。本义为饱含油脂的肥肉。说明：动物油脂能在汤水表面漂浮。“膘”实际上是指“动物油脂”。');
INSERT INTO `chinese_infos` VALUES (15244, '缬', '水', 21, 'n', '有花纹的丝织品。');
INSERT INTO `chinese_infos` VALUES (15245, '骅', '水', 22, 'y', '〔骅骝〕赤色的好马。骝（liú ）。');
INSERT INTO `chinese_infos` VALUES (15246, '边', '水', 22, 'y', '从辶（辵）从从臱,山崖的边沿。〈名〉；(形声。从辵,臱声。本义:山崖的边缘；边,行垂崖也。——《说文》边,垂也。——《尔雅》');
INSERT INTO `chinese_infos` VALUES (15247, '鳗', '水', 22, 'n', '〔鳗鲡〕 也叫白鳝。鱼类。蛇形，体长可达1米，鳞已退化。中国沿海及江湖中均有分布。');
INSERT INTO `chinese_infos` VALUES (15248, '穰', '水', 22, 'y', '1.同“瓤”。2.丰盛');
INSERT INTO `chinese_infos` VALUES (15249, '欢', '水', 22, 'y', '形声。从欠,雚声。本义喜悦,高兴；欢,喜乐也。--《说文》；君子不尽人之欢。--《礼记·曲礼》；夫妇不得不髈。--《荀子·大略》；若果养乎?予果欢乎?--《庄子·至乐》；许子而大欢,彼将知君利之也。--《韩非子·说林上》；而大国与之懽。--《战国策·秦策》；饮酒则欢乐。--《庄子·渔父》；今日之欢。--《汉书·李广苏建传》；众宾欢也。--宋·欧阳修《醉翁亭记》；欢动一城。--明·高启《书博鸡者事》；奉一日之欢。--清·侯方域《壮悔堂文集》');
INSERT INTO `chinese_infos` VALUES (15250, '藻', '水', 22, 'y', '泛指生长在水中的植物，亦包括某些水生的高等植物。藻类是隐花植物的一大类，无根、茎、叶等部分的区别，有叶绿素可以自己制造养料，种类很多，海水和淡水里都有，极少数可生活在陆地的阴湿地方。海藻。小球藻。藻类植物。也指华丽的文彩、文辞：藻思（多采的文思）。藻丽。藻绘（文采）。藻井（中国传统建筑物天花板上一方一方的彩画）。藻镜（指品评鉴别。亦称“藻鉴”）。辞藻。藻饰。古代帝王冕上系玉的五彩丝绳：玉藻。');
INSERT INTO `chinese_infos` VALUES (15251, '灌', '水', 22, 'n', '浇地，指注入液体这个动作，有丛生的树木义。同时灌还是古代祭祀的一种仪式，斟酒浇地降神。本义灌水因符合互联网开放自由而被众网友所喜爱，灌水成为论坛最火的行为。');
INSERT INTO `chinese_infos` VALUES (15252, '响', '水', 22, 'y', '多指声音，主要指听觉上对声音强弱感到的轻重程度。');
INSERT INTO `chinese_infos` VALUES (15253, '霁', '水', 22, 'y', '1.雨后或雪后转晴2.怒气消散');
INSERT INTO `chinese_infos` VALUES (15254, '沣', '水', 22, 'y', '1.沣河，水名，在陕西。2.沣水（Fēngshuǐ），地名，在山东。');
INSERT INTO `chinese_infos` VALUES (15255, '潆', '水', 22, 'n', '〔潆洄〕水流回旋的样子。');
INSERT INTO `chinese_infos` VALUES (15256, '鳖', '水', 22, 'n', '形声。从鱼,敝声。本义甲鱼,一种爬行动物,俗称团鱼；北各地,多栖于池沼、河沟、稻田中,肉味鲜美,一般认为是补品；江汉之鱼鳖鼋鼍为天下富。--《墨子·公输》');
INSERT INTO `chinese_infos` VALUES (15257, '艨', '水', 22, 'n', '〔艨艟〕也作蒙冲。古代的一种战船。');
INSERT INTO `chinese_infos` VALUES (15258, '滠', '水', 22, 'n', '滠水，水名，在湖北，流入长江。');
INSERT INTO `chinese_infos` VALUES (15259, '瓤', '水', 22, 'n', '本义：带瓜子的瓜肉。瓤形声。从瓜，从襄，襄亦声。“襄”意为“包裹”、“包容”。“瓜”与“襄”联合起来表示“包容了瓜子的瓜肉”。');
INSERT INTO `chinese_infos` VALUES (15260, '獾', '水', 22, 'n', '狗獾、猪獾等的统称。');
INSERT INTO `chinese_infos` VALUES (15261, '霾', '水', 22, 'n', '空气中因悬浮着大量的烟、尘等微粒而形成的混浊现象，能见度小于10千米。通称阴霾。');
INSERT INTO `chinese_infos` VALUES (15262, '飨', '水', 22, 'n', '1.用酒食款待人。也泛指对人提供某些东西2.古又同“享”。');
INSERT INTO `chinese_infos` VALUES (15263, '鳔', '水', 22, 'n', '1.某些鱼类体内可以胀缩的囊状物。里面充满氮、氧、二氧化碳等混合气体。收缩时鱼下沉，膨胀时鱼上浮。有的鱼类的鳔有辅助听觉或呼吸等作用。也叫鱼鳔，有的地区叫鱼白。2.鳔胶。3.用鳔胶粘上。');
INSERT INTO `chinese_infos` VALUES (15264, '鳕', '水', 22, 'n', '鳕鱼，体稍侧扁，头大，尾小，下颌有一根须，背部灰褐色，有许多小黑斑，有三个背鳍，腹部灰白色。肝可制鱼肝油。也叫鳘鱼。');
INSERT INTO `chinese_infos` VALUES (15265, '耱', '水', 22, 'n', '1.耢（lào）2.用耱平整土地。');
INSERT INTO `chinese_infos` VALUES (15266, '携', '水', 22, 'n', '形声。从手,隽声。本义提着；(把地上的物品举提起来；携,提也。--《说文》；如璋如圭,如取如携。--《诗·大雅·板》；又如携壶;携带(提挈,照顾);携酒牵羊;携杖；拿着,持；闲携杖,漫出门,官槐满路叶纷纷。--清·孔尚任《桃花扇》；又如携盘;携帚；牵挽;挽扶；相携于道。--《淮南子·览冥》；吾与汝并肩携手。--清·林觉民《与妻书》');
INSERT INTO `chinese_infos` VALUES (15267, '鳘', '水', 22, 'n', '鳕鱼的通称。');
INSERT INTO `chinese_infos` VALUES (15268, '滩', '水', 23, 'y', '分为海滩（有管理的又称海水浴场）、湖滩、河滩，是由海水搬运积聚的沉积物——沙或石砾，堆积而形成的岸，滩可依沙的粗细分为砾滩（石滩）、沙滩。');
INSERT INTO `chinese_infos` VALUES (15269, '变', '水', 23, 'y', '从丝从言从攴。本义：弹琴时手指在不同琴弦间移动。〈动〉；形声。从攴,娈声。本义:变化,改变。；变,更也。——《说文》变,易也。——《小尔雅》基本意思是形容性质状态或情形和以前不同，更改，变调，变动。还有以这个字为题的诗歌，图书，歌曲以及音乐唱片。又做“変”，“変”为日本汉字');
INSERT INTO `chinese_infos` VALUES (15270, '鲼', '水', 23, 'n', '鱼类。体 扁平，呈菱形。胸鳍前部伸延至吻部。尾细长，常具尾刺。种类很多。分布于热带和亚热带海洋中。');
INSERT INTO `chinese_infos` VALUES (15271, '鬟', '水', 23, 'n', '妇女梳的环形的发髻');
INSERT INTO `chinese_infos` VALUES (15272, '禳', '水', 23, 'n', '向鬼神祈祷消除灾殃。');
INSERT INTO `chinese_infos` VALUES (15273, '洒', '水', 23, 'y', '“洒”被人们赋予了一种新的含义——理睬，一般用作否定，如“不洒你”的意思就是“（我）不想理你”，在部分地区较常见。');
INSERT INTO `chinese_infos` VALUES (15274, '鷩', '水', 23, 'n', '赤雉，即“锦鸡”');
INSERT INTO `chinese_infos` VALUES (15275, '霉', '水', 23, 'n', '形声。字从雨，从每，每亦声。连绵的雨天中有机物自发生长并蔓延在其表面的细丝状物质。本义：有机物因生菌而质变如：霉显（受潮霉烂）；霉黑（物受潮湿而发霉变青黑）；霉干菜（芥菜的茎叶，加香料盐渍，封在瓦器中，使发霉后晒干而成）');
INSERT INTO `chinese_infos` VALUES (15276, '鬓', '水', 24, 'n', '本意是指脸旁靠近耳朵的头发，耳际之发，宋代苏轼的《江城子·密州出猎》和唐代杜甫的《登高》有记载。');
INSERT INTO `chinese_infos` VALUES (15277, '躞', '水', 24, 'n', '见〔蹀躞〕。');
INSERT INTO `chinese_infos` VALUES (15278, '髌', '水', 24, 'n', '1.膝盖骨。2.古代一种除去膝盖骨的酷刑。');
INSERT INTO `chinese_infos` VALUES (15279, '颦', '水', 24, 'n', '皱眉');
INSERT INTO `chinese_infos` VALUES (15280, '辔', '水', 24, 'n', '驾驭牲口用的嚼子和缰绳');
INSERT INTO `chinese_infos` VALUES (15281, '蚌', '水', 24, 'n', '形声。从虫,丰声。本义一种软体动物。有的能产珍珠；离为蚌。--《易·说卦》；蚌方出曝,而鹬啄其肉,蚌合而其喙。--《战国策·燕策》；巨蝝函蛛。--《文选·左思·南都赋》；民食果菰蝝蛤腥臊恶臭。--《韩非子·五蠹》；又如蚌蛤(蚌与蛤。长者曰蚌,圆者曰蛤);蚌壳(蚌的外壳；美珠；蚌,美珠。--《类篇·虫部》');
INSERT INTO `chinese_infos` VALUES (15282, '灏', '水', 25, 'y', '1.水势大。2.〔灏溔〕水无边际的样子。溔（yǎo）。');
INSERT INTO `chinese_infos` VALUES (15284, '鹱', '水', 25, 'n', '鸟，身体大，嘴的尖端略呈钩状，趾间有蹼。会游泳和潜水，生活在海岸边，吃鱼类和软体动物等。种类很多，常见的有白额鹱。');
INSERT INTO `chinese_infos` VALUES (15285, '蛮', '水', 25, 'n', '先秦非华夏民族的泛称之一﹐秦汉至魏晋南北朝为南方少数民族的泛称。后也有指南方汉族的意思。字面上有粗野，凶恶，不通情理的意思。');
