INSERT INTO `chinese_infos` VALUES (15287, '襻', '水', 25, 'n', '1.用来扣住纽扣的套2.形状或功用像襻的东西3.用绳子、线等使分开或分散的东西连在一起');
INSERT INTO `chinese_infos` VALUES (15288, '灞', '水', 25, 'n', '灞河，水名，在陕西。');
INSERT INTO `chinese_infos` VALUES (15289, '漯', '水', 25, 'n', '〔漯河〕地名。在河南中部。京广铁路经此。漯河，水名。在山东。');
INSERT INTO `chinese_infos` VALUES (15290, '蘼', '水', 25, 'n', '〔蘼芜〕古书上指芎䓖的苗。');
INSERT INTO `chinese_infos` VALUES (15291, '湾', '水', 26, 'y', '形声。从水，弯声。本义：河水弯曲处。量词：用于水或水面，相当于“处”。');
INSERT INTO `chinese_infos` VALUES (15292, '滦', '水', 27, 'n', '〔滦河〕华北地区水资源较丰富的河流之 一。发源于河北省丰宁满族自治县境内，向北流入内蒙 古自治区，再转回河北省，最后成为昌黎、乐亭两县界河入渤海。长877千米。');
INSERT INTO `chinese_infos` VALUES (15293, '戅', '水', 28, 'n', '同“戆”。同“戆”。');
INSERT INTO `chinese_infos` VALUES (15294, '郁', '水', 29, 'y', '有草木茂密之意，又表示忧愁。姓氏，郁氏在宋版百家姓一书中列为第一百八十一。');
