INSERT INTO `chinese_infos` VALUES (15406, '卐', '火', 4, 'n', '古代印度宗教的吉祥标志。显现金光，如来佛胸前有卐字。中国唐代武则天定音为“万”。义为“吉祥万德之所集”。佛经中又写作“卍”。');
