INSERT INTO `chinese_infos` VALUES (17051, '飮', '火', 13, 'n', '同“饮”。同“饮”。');
INSERT INTO `chinese_infos` VALUES (17052, '飳', '火', 13, 'n', '1.祭祀。2.一种糕饼。古同“餢”。');
