INSERT INTO `chinese_infos` VALUES (17056, '飹', '火', 13, 'n', '古同“饱”。');
INSERT INTO `chinese_infos` VALUES (17057, '飻', '火', 13, 'n', '古同“餮”。');
INSERT INTO `chinese_infos` VALUES (17058, '饳', '火', 13, 'n', '见  〔馉饳〕');
INSERT INTO `chinese_infos` VALUES (17059, '餀', '火', 13, 'n', '食物腐败发臭。');
INSERT INTO `chinese_infos` VALUES (17060, '馚', '火', 13, 'n', '〔～馧〕香气。');
INSERT INTO `chinese_infos` VALUES (17061, '𫘛', '火', 13, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (17062, '馰', '火', 13, 'n', '〔～颡（sǎng）〕额白色的马。亦作“的颡”。');
INSERT INTO `chinese_infos` VALUES (17063, '馲', '火', 13, 'n', '〔～驼（tuó）〕骆驼，如“自青州以～驼（tuó）驮其种，遂传洛中。”〔～𩢷〕骡，如“牡驴交牛而生者为～𩢷。……今俗通呼为骡矣。”');
INSERT INTO `chinese_infos` VALUES (17064, '馵', '火', 13, 'n', '后左脚白色的马。');
INSERT INTO `chinese_infos` VALUES (17065, '馺', '火', 13, 'n', '（马）奔驰');
INSERT INTO `chinese_infos` VALUES (17066, '髢', '火', 13, 'n', '〔髢髢〕〈方〉假 头发。');
INSERT INTO `chinese_infos` VALUES (17067, '鬾', '火', 13, 'n', '传说中的小儿鬼');
INSERT INTO `chinese_infos` VALUES (17068, '鬿', '火', 13, 'n', '〔鬿堆〕古代神话传说中的奇兽。');
INSERT INTO `chinese_infos` VALUES (17069, '魀', '火', 13, 'n', '见〔𩴌魀〕');
INSERT INTO `chinese_infos` VALUES (17070, '鱽', '火', 13, 'n', '古书上指身体形状像刀的鱼，如带鱼、鲚（jì）鱼。');
INSERT INTO `chinese_infos` VALUES (17071, '魜', '火', 13, 'n', '1.人鱼，即“儒艮”，一种生长在海洋中的哺乳动物，形体像鱼，长约三米，前肢像人手，哺乳时前肢抱仔。2.鲵。');
INSERT INTO `chinese_infos` VALUES (17072, '魝', '火', 13, 'n', '解剖。');
INSERT INTO `chinese_infos` VALUES (17073, '魞', '火', 13, 'n', '日本汉字。捕鱼用的鱼笼子。义未详。');
INSERT INTO `chinese_infos` VALUES (17074, '鳨', '火', 13, 'n', '1.古书上说的一种似凫而比凫小的鸟。2.鸩的别称。');
INSERT INTO `chinese_infos` VALUES (17075, '鳪', '火', 13, 'n', '〔～雉（zhì）〕锦鸡。');
INSERT INTO `chinese_infos` VALUES (17076, '鳫', '火', 13, 'n', '古同“雁”。');
INSERT INTO `chinese_infos` VALUES (17077, '鳭', '火', 13, 'n', '一种青斑色的小鸟，喜欢用嘴啄开芦苇皮吃秆中虫。〔～鷯〕黄鸟。');
INSERT INTO `chinese_infos` VALUES (17078, '鳮', '火', 13, 'n', '同“鸡”。');
INSERT INTO `chinese_infos` VALUES (17079, '鳯', '火', 13, 'n', '古同“凤”。');
INSERT INTO `chinese_infos` VALUES (17080, '鳰', '火', 13, 'n', '1.鸟名。鷿鷈。2.姓氏。日本字。');
INSERT INTO `chinese_infos` VALUES (17081, '麀', '火', 13, 'n', '古书上指母鹿。');
INSERT INTO `chinese_infos` VALUES (17082, '麁', '火', 13, 'n', '同“粗”。');
INSERT INTO `chinese_infos` VALUES (17083, '鼔', '火', 13, 'n', '古同“鼓”。');
INSERT INTO `chinese_infos` VALUES (17084, '鼡', '火', 13, 'n', '同“鼠”（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (17085, '台', '火', 14, 'y', '地名。台州的简称；南发交广东温台。--王安石《和王微之登高斋二首》');
INSERT INTO `chinese_infos` VALUES (17086, '郎', '火', 14, 'y', '根据《说文解字》，在“良”字音义上加以区别；在“良”右边加“阝”，变成“郎”；在“良”左边加“女”，衍成“娘”。“郎”就代表丈夫了。李白有“郎骑竹马来，绕床弄青梅“，义山诗有“刘郎已恨蓬山远，又隔蓬山一万丛”，花间词中有“问郎花好侬颜好”。');
INSERT INTO `chinese_infos` VALUES (17087, '摇', '火', 14, 'n', '指摆动；往上升等。如：摇头摆尾；摇摇欲坠。');
INSERT INTO `chinese_infos` VALUES (17088, '连', '火', 14, 'y', '从辵从车。本义：拉车而行。；会意。从辵,从车。本义:人拉的车。；连,负车也。——《说文》。段注:“连即古文辇也。连车组挽。——《周礼·故书巾车》与其輂连。——《周礼·乡师》');
INSERT INTO `chinese_infos` VALUES (17089, '毓', '火', 14, 'y', '1.生育；养育2.姓。');
INSERT INTO `chinese_infos` VALUES (17090, '缁', '火', 14, 'y', '黑色');
INSERT INTO `chinese_infos` VALUES (17091, '绰', '火', 14, 'y', '形声。从糸，从卓，卓亦声。“糸”指“丝绸服饰”。“卓”意为“高”，转义为“一人高”。“糸”与“卓”联合起来表示“长可拖地的女子服饰”。本义：长可拖地的丝绸仕女服饰。引申义：宽大，舒缓。说明：长可拖地的仕女丝绸服饰可见于中国古画。这种服饰类似于今日常见的西洋婚纱。');
INSERT INTO `chinese_infos` VALUES (17092, '恺', '火', 14, 'y', '快乐；和乐。');
INSERT INTO `chinese_infos` VALUES (17093, '熔', '火', 14, 'y', '从火，容声。本义：用高温使固体物质转变为液态。又有“铸造器物的模子”之意。');
INSERT INTO `chinese_infos` VALUES (17094, '鞅', '火', 14, 'y', '（旧读 yǎng） 古代用马拉车时安在马脖子上的皮套子。见〖牛鞅〗。');
INSERT INTO `chinese_infos` VALUES (17095, '裳', '火', 14, 'y', '通常指代“衣服”。');
INSERT INTO `chinese_infos` VALUES (17096, '通', '火', 14, 'y', '属于常用字。本意是通达，没有障碍。后引申为到达目的地，又引申为互相连接无阻断。也指使知道、传达于对方，或了解、懂得，这两种意思具抽象义。通也可以指精通某一方面的人，用作名词；又由道路无阻塞，转指文句通顺，还引申为普遍适用的、贯通的意思；也引申为全部、从头至尾；也可作副词，表示全部、十分。读“”的音时，作量词用，用于文书，表示一份；用于击鼓等行为，表示一阵，一遍。');
INSERT INTO `chinese_infos` VALUES (17097, '叹', '火', 14, 'y', '因忧闷悲痛而呼出长气：～气。～息。悲～。～惋。长吁短～。2.因高兴、兴奋、激动而发出长声：～赏。～服。赞～。～观止矣（指赞美看到的事物好到极点）。3.吟咏：咏～。一唱三～。');
INSERT INTO `chinese_infos` VALUES (17098, '滕', '火', 14, 'y', '1.周朝国名，在今山东滕州一带。2.姓。');
INSERT INTO `chinese_infos` VALUES (17099, '图', '火', 14, 'y', '从囗从啚。表示啚（艰难）的范围，谋也、〈动〉；会意。从囗,从啚。囗,表示范围。啚,“鄙”的本字,表示艰难。合起来表示规划一件事,需慎重考虑,相当不容易。本义:谋划,反复考虑。；画计难也。左传曰。咨难为谋。画计难者、谋之而苦其难也。国语曰。夫谋必素见成事焉而後履之。谓先规画其事之始终曲折。历歴可见。出於万全。而後行之也。故引伸之义谓绘画为图。聘礼曰。君与卿图事。释诂曰。图、谋也。小雅传曰。虑、图皆谋也。从囗。规画之意。从啚。啚、逗。难意也。说从啚之意。啚者、啬也。啬者、爱濇也。愼难之意。用一定的色彩和线条等绘制出来的形象；画，绘制，绘影图形；谋取，图谋。');
INSERT INTO `chinese_infos` VALUES (17100, '领', '火', 14, 'y', '作名词时可以指代领袖，作动词时可以表示带领。');
INSERT INTO `chinese_infos` VALUES (17101, '赵', '火', 14, 'y', '一般指中国古代赵国；赵姓。');
INSERT INTO `chinese_infos` VALUES (17102, '纶', '火', 14, 'y', '1.较粗的丝线，多指钓鱼的丝线2.化学纤维的商品名称。中国暂行规定合成短纤维一律称作纶。长丝则在末尾加一“丝”字，如锦纶、锦纶丝、涤纶、涤纶丝等。〔纶巾〕古代一种青丝编织成的头巾。');
INSERT INTO `chinese_infos` VALUES (17103, '绫', '火', 14, 'y', '丝织物的 一种。 用桑蚕丝或桑蚕丝 同人造丝交织而成。质地较缎轻薄。');
INSERT INTO `chinese_infos` VALUES (17104, '团', '火', 14, 'y', '从囗从专，形声。本义：圆。《宋玉·九辩》意专专之不可化兮。卽团团也。；把东西揉弄成圆球形。如:团搦(团弄,摆弄);团掿(摆弄);团纸团儿;团药丸；围绕。如:团团转(3)聚集,集合众宾团坐。——《虞初新志·秋声诗自序》(4)又如:团书(通知聚会的请柬)(5)估量;猜度。如:团量(估量)(6)分不开;聚合在一起西园歌舞骤然稀,只有多情蝴蝶作团飞。——宋·石孝友《南歌子》(7)又如:团体(组织起来,团结为一体)(8)调理,解决。如:团弄(处理,解决)');
INSERT INTO `chinese_infos` VALUES (17105, '这', '火', 14, 'n', '从言。迎。走上前去迎接刚刚言语提到（的人）。');
INSERT INTO `chinese_infos` VALUES (17106, '嫡', '火', 14, 'n', '1、 封建宗法制度中指正妻：～庶。2、 正妻所生的：～子。～出（正妻所生，区别于“庶出”）。3、 亲的，血统最近的，封建宗法制度下家庭的正支：～亲。');
INSERT INTO `chinese_infos` VALUES (17107, '荧', '火', 14, 'y', '有微弱的光亮的意思。');
INSERT INTO `chinese_infos` VALUES (17108, '对', '火', 14, 'n', '从丵从口从寸。一只手拿着点燃的蜡烛，有“向着”的意思。对，对或从士。汉文帝以为责对而为言，多非诚对，故去其口以从士也。；(会意。从口,从“丵”(,即,古“丛”字,象草木丛生。),从寸。寸,法度也。汉文帝以为责对而伪,言多非诚,故去其口,以从土。本义:应答；');
INSERT INTO `chinese_infos` VALUES (17109, '端', '火', 14, 'y', '用手平拿着：端饭；端茶；端勺。2规矩；正：端坐；五官端正；品行不端。；3.东西的一头；事情的开头：发端；开端；顶端。④方面；项目：事端；弊端；变化多端。⑤原因；起因：无端；祸端；借端生事。');
INSERT INTO `chinese_infos` VALUES (17110, '畅', '火', 14, 'y', '从申，从昜，昜亦声。《说文》所无。“申”意为“繁殖子孙”，“昜”意为“播散”。“申”与“昜”联合起来表示“子孙繁衍播散”。“子孙繁衍播散”既指纵向的繁殖：父→子→孙，又指横向的繁殖：子女和孙子女的各自婚配。除了纵横两向的人数扩张外，还包括时间的连续和居住地域的扩大的意思。');
INSERT INTO `chinese_infos` VALUES (17111, '种', '火', 14, 'y', '从禾从重，先穜後孰也，後孰曰重：种子：个大的谷物、饱满者用于做种。能够重新种植。');
INSERT INTO `chinese_infos` VALUES (17112, '绿', '火', 14, 'y', '通菉。一种野菜；终朝采绿,不盈一掬。--《诗·小雅·绿竹猗猗》；河出绿图,地出乘黄。--《墨子》');
INSERT INTO `chinese_infos` VALUES (17113, '尔', '火', 14, 'y', '词性为代词、助词或语气词；意为你，你的等。');
INSERT INTO `chinese_infos` VALUES (17114, '雒', '火', 14, 'y', '1.同“洛”2.姓。');
INSERT INTO `chinese_infos` VALUES (17115, '寥', '火', 14, 'y', '姓名学：非姓氏，多用男性 ');
INSERT INTO `chinese_infos` VALUES (17116, '僚', '火', 14, 'n', '官：官～。；旧指同在一起做官的：～属（下属的官吏）。～友。～佐。幕～。(3)古代对一种奴隶或差役的称谓。');
INSERT INTO `chinese_infos` VALUES (17117, '祯', '火', 14, 'y', '吉祥。');
INSERT INTO `chinese_infos` VALUES (17118, '逖', '火', 14, 'y', '远。');
INSERT INTO `chinese_infos` VALUES (17119, '彰', '火', 14, 'y', '又如弥彰（更加显著；越发暴露）；彰明昭著（彰明较著。形容非常显明）；彰著（明显；显著），文采美盛鲜明，圣谟洋洋，嘉言孔彰。——《书·伊训》，；彰显（昭著的事实）；彰施（明施）。');
INSERT INTO `chinese_infos` VALUES (17121, '廖', '火', 14, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (17122, '幛', '火', 14, 'y', '幛子，用作祝贺或吊唁礼物的整幅绸布，上面题有祝贺或哀悼的词句。');
INSERT INTO `chinese_infos` VALUES (17123, '瑙', '火', 14, 'n', '见〖玛瑙〗。');
INSERT INTO `chinese_infos` VALUES (17124, '尘', '火', 14, 'y', '会意。《说文》从三鹿,从土,表示鹿群行扬起尘土的意思。楷书简去重迭的部分,只保留一个鹿。现行简化字尘,也是一个从小从土的会意字。本义；尘土；湫隘嚣尘。--《左传·昭公三年》。注土也。；尘埃也。--《庄子·逍遥游》；而游于尘垢之外。--《庄子·齐物论》；朱尘筵些。--《楚辞·招魂》；满面尘灰烟火色。--唐·白居易《卖炭翁》；渭城朝雨浥轻尘。--唐·王维《送元二使安西》');
INSERT INTO `chinese_infos` VALUES (17125, '屡', '火', 14, 'n', '本义：接连着，不止一次。衍义：又用作姓 ');
INSERT INTO `chinese_infos` VALUES (17126, '绾', '火', 14, 'y', '1.把长条形的东西盘绕起来打成结2.卷（juǎn）');
INSERT INTO `chinese_infos` VALUES (17127, '制', '火', 14, 'n', '有规定：因地～宜；限定，约束，管束：～止；法规，制度：民主集中～；依照规定的标准做的：～钱；古代帝王的命令：～诰；古代父母死亡守丧；守～；造，作：～造的意思。');
INSERT INTO `chinese_infos` VALUES (17128, '搪', '火', 14, 'n', '字义：1.挡，抵拒。2.支吾，敷衍。3.均匀地涂上泥或涂料。4.同“镗”。字意：抵挡、敷衍,应付,马马虎虎干、骗取,混、荡;划');
INSERT INTO `chinese_infos` VALUES (17129, '蜩', '火', 14, 'n', '古书上指蝉。');
INSERT INTO `chinese_infos` VALUES (17130, '尽', '火', 14, 'y', '此字始见于商代甲骨文，其古字形像一只手拿着刷子在刷洗器皿，表示饮食已尽，以此表示终尽之意。本义是器中空无，净尽，泛指竭尽、完尽。引申为终尽、完了、竭尽。由终尽义引申为死亡、达到极限。由竭尽义，即全部用出、使尽义引申为范围副词全部、都。以上各义都读jìn。有时读jǐn。作副词，表示达到最大限度，如言“尽量” “尽早” “尽快”，又可以表示行为的持续。');
INSERT INTO `chinese_infos` VALUES (17131, '奁', '火', 14, 'n', '古代妇女梳妆 用的镜匣');
INSERT INTO `chinese_infos` VALUES (17132, '喏', '火', 14, 'n', '叹词。可以读成不同声调以表示不同的感情和态度古代表示敬意的呼喊');
INSERT INTO `chinese_infos` VALUES (17133, '嫘', '火', 14, 'n', '嫘祖（Léizǔ），传说中黄帝的妻子，发明养蚕。');
INSERT INTO `chinese_infos` VALUES (17134, '瘩', '火', 14, 'n', '中医指生在背部的痈。亦称“搭手”。');
INSERT INTO `chinese_infos` VALUES (17135, '嫪', '火', 14, 'n', '嫪毐（Lào’ǎi），战国时秦国人。');
INSERT INTO `chinese_infos` VALUES (17136, '疟', '火', 14, 'n', '本意为一种按时发冷发烧的急性传染病。');
INSERT INTO `chinese_infos` VALUES (17137, '耥', '火', 14, 'n', '（又）用耥耙弄平田地，清除杂草。[耥耙]清除杂草、弄平田地的农具。（又）用耥耙弄平田地，清除杂草。[耥耙]清除杂草、弄平田地的农具。');
INSERT INTO `chinese_infos` VALUES (17138, '趟', '火', 14, 'n', '多音字，读或者。当读时可表示从有水、草的地方走过去，又可表示用犁、锄等把土翻开，把草锄去。当读时可表示来往的次数，又可表示用于成行的东西。（来自新华字典）');
INSERT INTO `chinese_infos` VALUES (17139, '慆', '火', 14, 'n', '娱悦。');
INSERT INTO `chinese_infos` VALUES (17140, '搌', '火', 14, 'n', '（用松软干燥的东西）轻轻擦抹或按压，吸去湿处的液体');
INSERT INTO `chinese_infos` VALUES (17141, '搦', '火', 14, 'n', '1.握；拿；捏2.挑动；引动');
INSERT INTO `chinese_infos` VALUES (17142, '煽', '火', 14, 'n', '1.同“扇”（shān）2.鼓动（别人做不应该做的事）');
INSERT INTO `chinese_infos` VALUES (17143, '熬', '火', 14, 'n', '是中国的汉字。本义为干煎或干炒。《说文》：“熬，干煎也。”');
