INSERT INTO `chinese_infos` VALUES (17227, '嗻', '火', 14, 'n', '1.厲害；很。2.出色，出眾。1.舊時仆役對主人或賓客的應諾聲。2.招呼聲。语不切要。語氣詞。用於句末，相當於“者”。');
