INSERT INTO `chinese_infos` VALUES (18053, '駏', '火', 14, 'n', '〔～驉（xū）〕古书上说的一种形似骡，可供乘骑的兽');
INSERT INTO `chinese_infos` VALUES (18054, '骲', '火', 14, 'n', '骨制的（也有用木制的）箭头。');
INSERT INTO `chinese_infos` VALUES (18055, '骵', '火', 14, 'n', '古同“體”。');
INSERT INTO `chinese_infos` VALUES (18056, '髚', '火', 14, 'n', '高。');
INSERT INTO `chinese_infos` VALUES (18057, '髣', '火', 14, 'n', '见〖仿佛〗（髣髴）。');
INSERT INTO `chinese_infos` VALUES (18058, '髤', '火', 14, 'n', '同“髹”。');
INSERT INTO `chinese_infos` VALUES (18059, '髥', '火', 14, 'n', '古同“髯”。');
INSERT INTO `chinese_infos` VALUES (18060, '髧', '火', 14, 'n', '头发下垂的样 子。');
INSERT INTO `chinese_infos` VALUES (18061, '髨', '火', 14, 'n', '古同“髡”。');
INSERT INTO `chinese_infos` VALUES (18062, '髩', '火', 14, 'n', '“鬓”的异体字。');
INSERT INTO `chinese_infos` VALUES (18063, '髪', '火', 14, 'n', '同“髮”（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (18064, '鬦', '火', 14, 'n', '同“斗2”。');
INSERT INTO `chinese_infos` VALUES (18065, '魆', '火', 14, 'n', '用于“黑魆魆”（形容黑）。');
INSERT INTO `chinese_infos` VALUES (18066, '𫚉', '火', 14, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (18067, '魠', '火', 14, 'n', '古书上说的一种口大的鱼。');
INSERT INTO `chinese_infos` VALUES (18068, '魡', '火', 14, 'n', '古同“钓”，钓鱼。');
INSERT INTO `chinese_infos` VALUES (18069, '鱾', '火', 14, 'n', '鱼，身体侧扁，略呈椭圆形，头小而钝，口小，绿褐色。生活在海底岩石间。');
INSERT INTO `chinese_infos` VALUES (18070, '𩽹', '火', 14, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (18071, '鳱', '火', 14, 'n', '〔～鹊〕喜鹊。〔～鴠ｄ啵睢澈拍瘛。古同“雁”。');
INSERT INTO `chinese_infos` VALUES (18072, '鸤', '火', 14, 'n', '〔鸤鸠〕 也作尸鸠。 古书上指布谷鸟。');
INSERT INTO `chinese_infos` VALUES (18073, '鳵', '火', 14, 'n', '古同“鸨”。');
INSERT INTO `chinese_infos` VALUES (18074, '麧', '火', 14, 'n', '麦糠里的粗屑，多用以指粗食。');
INSERT INTO `chinese_infos` VALUES (18075, '鼏', '火', 14, 'n', '1.鼎盖2.古通“幂”，覆盖东西的巾');
INSERT INTO `chinese_infos` VALUES (18076, '鼑', '火', 14, 'n', '古同“鼎”。');
INSERT INTO `chinese_infos` VALUES (18077, '董', '火', 15, 'y', '从艸从重。董的本义是鼎蕫，一种比蒲草细小的草；假借为监督、管理。〈动〉；形声。从艸,重声。；本义:草名,即鼎蕫。2督察。；监督;督察董之用威。——《书·大禹谟》。传:“董,督也。”董逋逃。——《左传·文公六年》董之以武师。——《左传·昭公十年》出则监察而董是非。——《后汉书·陈忠传》');
INSERT INTO `chinese_infos` VALUES (18078, '蝶', '火', 15, 'y', '形声。从虫,葉声←蝶；身影);蝶戏(谓蝴蝶飞舞嬉戏);蝶意莺情(比喻爱恋春色的情意);蝶舞(比喻荡子之流嫖妓；蝶蝴蝶。；【蝶泳】游泳姿势之一。俯卧水面，两臂对称前后划水，形似蝴蝶动作，故名。');
INSERT INTO `chinese_infos` VALUES (18079, '逮', '火', 15, 'n', '在汉语词典中的解释：【动】∶捉，特指经过追赶或试图抓。；。如：逮蚊子；逮特务；猫逮老鼠，捉坏人，捉虫子等。1.到，及：力有未～。2.捉拿：～捕。～系。～开创造。');
INSERT INTO `chinese_infos` VALUES (18080, '践', '火', 15, 'y', '从足,戋( jiān)声。( jiàn)本义:农具名,即铁铲。古时候干农活，需要走路下地。');
INSERT INTO `chinese_infos` VALUES (18081, '调', '火', 15, 'y', '1. 安排处置：～令。～用。～度（dù）。～动。～任。～防。～兵遣将。2. 访查了解：～查。～研。3. 音乐上高低长短配合和谐好听的一组音，字音的高低升降：～子。～号。～式。腔～。曲～。大～。小～。南腔北～。');
INSERT INTO `chinese_infos` VALUES (18082, '腰', '火', 15, 'n', '形声字，指东西的中段，中间，也特指身体胯上胁下的部分，或指所穿衣服在身体的腰部的部分。');
INSERT INTO `chinese_infos` VALUES (18083, '阆', '火', 15, 'y', '高大；空旷用于地名，如阆中（在四川）。');
INSERT INTO `chinese_infos` VALUES (18084, '奖', '火', 15, 'y', '形声。字本作妜,从犬,将省声。本义驱使狗猛进；奖,嗾犬厉之也。--《说文》。段玉裁注《口部》‘嗾,使犬声也。’厉之,犹勉之也。；劝勉;鼓励；奖,劝也。--《小尔雅》；以奖善人。--《左传·昭公二十二年》。疏劝也。；奖厉太子。--《汉书·哀帝纪》。注劝使也。；奖率三军。--诸葛亮《出师表》');
INSERT INTO `chinese_infos` VALUES (18085, '褚', '火', 15, 'y', '姓。1.丝绵。2.在衣服里铺丝绵。3.口袋。');
INSERT INTO `chinese_infos` VALUES (18086, '德', '火', 15, 'y', '本意为顺应自然，社会，和人类客观规律去做事。不违背自然规律发展去发展社会，提升自己。');
INSERT INTO `chinese_infos` VALUES (18087, '敌', '火', 15, 'y', '形声。从攴,啇声。本义匹敌;对等；敌,仇也。--《说文》；敌,匹也。--《尔雅》；四国之兵敌。--《战国策·秦策》。注强弱等也。；且夫自敌以下。--《国语·楚语》；敌国宾至。--《国语·周语》；敌则能战之。--《孙子·谋攻》；又如试看天下谁能敌;敌体(彼此地位相等,不分上下);敌礼(平等的礼节);匹敌(谓才艺相当);势均力敌；抵抗;抵挡；(司马懿)乃魏之名将,恐汝不能敌之。--《三国演义》');
INSERT INTO `chinese_infos` VALUES (18088, '辍', '火', 15, 'n', '中间停顿；停止');
INSERT INTO `chinese_infos` VALUES (18089, '质', '火', 15, 'y', '从斦从贝。；形声。从贝,斦声。朱骏声认为“斦”是砧板。从贝,与财富有关。本义:抵押;以…作人质。；质,以物相赘也。——《说文》。按,以钱受物曰赘,以物受钱曰质。本体、本性，朴素、单纯，问明、辨别、责问，抵押或抵押品，同“贽”，礼物。如“质数”，这里的“质”与一般意义上的“质”是有区别的。在台湾“质（大陆）”字有两个；和，音的意思是基本释义里动词的；和名词的（7）的意思；音的意思是基本释义里动词的；和名词的；~（6）的意思。在大陆和音均统一为音，意思也统一，所以请注意！');
INSERT INTO `chinese_infos` VALUES (18090, '缔', '火', 15, 'y', '结合；订立：缔交；缔约；缔盟。2创立；组织：缔造。【缔造】；创立、建立（多指伟大的事业）。；中国海尔集团经过数十年的努力，终于缔造了一个国际知名品牌。');
INSERT INTO `chinese_infos` VALUES (18091, '儋', '火', 15, 'y', '〔～县〕地名，在中国海南省。古同“担”，负荷。');
INSERT INTO `chinese_infos` VALUES (18092, '缓', '火', 15, 'y', '单音字；作形容词时表示慢，与“急”相对，例缓步，缓行；作名词时表示延期，延迟。');
INSERT INTO `chinese_infos` VALUES (18093, '瑶', '火', 15, 'y', '1.美玉2.形容美好、珍贵');
INSERT INTO `chinese_infos` VALUES (18094, '驻', '火', 15, 'n', '是形声字，马为形，主为声。驻的本意是指马停止，泛指短时间内停留，特指军队驻扎，驻防。驻还指工作人员住在执行任务、履行任务的地方。');
INSERT INTO `chinese_infos` VALUES (18095, '弹', '火', 15, 'n', '形声。从弓,单声。本义弹丸；弹,行丸也。--《说文》；长安大道边,挟弹谁家儿?--唐·苏涣《变律》其三；又如枪弹;弹子涡(卵石);枪林弹雨(形容战斗激烈);弹丸黑子(形容地域狭小；形状像弹丸的东西,如圆形的果实;禽鸟的卵等；其法乃以凫弹数十,黄、白各聚一器。--宋·周密《齐东野语》；弹弓；援弹飞丸,应弦而落。--《魏书》');
INSERT INTO `chinese_infos` VALUES (18096, '进', '火', 15, 'y', '向前或向上移动、发展，与“退”相对。');
INSERT INTO `chinese_infos` VALUES (18097, '谈', '火', 15, 'y', '可作名词，意为言论；作动词，谈论，讨论。也是中国姓氏之一。');
INSERT INTO `chinese_infos` VALUES (18098, '彻', '火', 15, 'y', '从彳，甲骨文，从“鬲”（炊具）从“丑”（举手），表示吃罢饭用手撤去炊具的意思。《说文》给古文增加了音符“彳”，变成了形声字。本义：撤除，撤去');
INSERT INTO `chinese_infos` VALUES (18099, '鲁', '火', 15, 'y', '1.古国名；姓氏；2.山东省的简称。');
INSERT INTO `chinese_infos` VALUES (18100, '熠', '火', 15, 'y', '光耀；鲜明。');
INSERT INTO `chinese_infos` VALUES (18101, '征', '火', 15, 'y', '从彳从正。彳，三胫相连，小步也；正，守一以止也。行正为征，召集也。征通徵。徵，召也。行於微而文达者。；。含义：1.由皇帝征聘社会知名人士充任官职。如“公车特征拜郎中”。2.远征。3.召集。4.征求。5.出兵讨伐。');
INSERT INTO `chinese_infos` VALUES (18102, '逯', '火', 15, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (18103, '骀', '火', 15, 'n', '1.使人舒畅（多用来形容春天的景物）2.放荡。劣马');
INSERT INTO `chinese_infos` VALUES (18105, '抟', '火', 15, 'y', '1.同“团”。2.凭借。');
INSERT INTO `chinese_infos` VALUES (18106, '缇', '火', 15, 'y', '橘红色。');
INSERT INTO `chinese_infos` VALUES (18107, '刘', '火', 15, 'y', '形声。从金,从刀,乯声。本义杀,戮；(含有大规模杀戮的意思；胜殷遏刘,耆代尔功。--《诗·周颂·武》；重我民,无尽刘。--《书·盘庚上》。孔传刘,杀也。；秦晋宋卫之间谓杀曰刘,晋之北鄙亦曰刘。--《方言》卷一；芟夷我农功,虔刘我边垂。(虔杀戮)。--《左传·成公十三年》；克,战胜；咸刘商王纣,执矢恶臣百人。--《逸周书·世俘》；刘；兵器名。斧钺；一人冕,执刘,立于东堂。--《书·顾命》');
INSERT INTO `chinese_infos` VALUES (18108, '阵', '火', 15, 'y', '“阵”是古代军队的野战队形，它是人类战争发展到一定历史阶段的产物，盛行于冷兵器时代，消亡于热兵器时代。');
INSERT INTO `chinese_infos` VALUES (18109, '厉', '火', 15, 'y', '严格：～禁（．列队警戒；．严厉的禁令）。～行节约。2.严肃：严～。声色俱～。3.凶猛：～害。雷～风行。色～内荏。4.磨，使锋利：～兵秣马。再接再～。5.古同“疠”、“癞”，恶疮。6.姓。');
INSERT INTO `chinese_infos` VALUES (18110, '侬', '火', 15, 'y', '1.人称代词。你。2.人称代词。我（多见于旧诗文）。3.姓。');
INSERT INTO `chinese_infos` VALUES (18111, '练', '火', 15, 'y', '一是指丝帛；二是指老练；三是指古代小祥之祭；四是指练姓，唐代有练何，河内人。');
INSERT INTO `chinese_infos` VALUES (18112, '鼐', '火', 15, 'y', '大鼎。');
INSERT INTO `chinese_infos` VALUES (18113, '瑭', '火', 15, 'y', '一种玉。');
INSERT INTO `chinese_infos` VALUES (18114, '轮', '火', 15, 'y', '常指轮子，安在车轴上可以转动使车行进的圆形的东西，或安在机器上能旋转并促使机器动作的东西。另指量词，如一轮明月。');
INSERT INTO `chinese_infos` VALUES (18115, '论', '火', 15, 'y', '从言从仑。言明条理。1.分析判断事物的道理：～断。～点。～辩。～据。～者。议～。讨～。辩～。2.分析阐明事物道理的文章、理论和言论：理～。舆～。专～。社～。3.学说，有系统的主张：系统～。4.看待：一概而～。5.衡量，评定：～罪。～功行赏。6.按照：～件。～资排辈。7.姓。分析判断事物的道理：～断。～点。～辩。～据。～者。议～。讨～。辩～。明王铎《太子少保兵部尚书节寰袁公神道碑》：“公（袁可立）疏倧以侄子篡叔，宜加天讨。诚能自托于毛帅，并力罢奴，录其功，贷其前辜，论与廷议合。”');
INSERT INTO `chinese_infos` VALUES (18116, '郯', '火', 15, 'y', '郯城（Tánchéng），地名，在山东。');
INSERT INTO `chinese_infos` VALUES (18117, '诤', '火', 15, 'y', '直爽地劝告');
INSERT INTO `chinese_infos` VALUES (18118, '驼', '火', 15, 'n', '的形状像马，它的头像羊，长颈项，垂耳，脚有三节，背上有两个突出的驼峰，有苍、褐、黄、紫等各种颜色。它的性耐寒，所以夏至褪毛避暑。它的粪烧出的烟就像狼烟一样直冲云宵。古时用它的烟报警。它的力量能负重于千斤，每天可行走二三百里，又能感知泉源水脉和风候。它很耐渴善于在沙漠中行走。');
INSERT INTO `chinese_infos` VALUES (18119, '乐', '火', 15, 'y', '喜悦;愉快；有朋自远方来,不亦乐乎。--《论语·学而》；并怡然自乐。--晋·陶渊明《桃花源记》；游人去而禽鸟乐也。--欧阳修《醉翁亭记》；君游海而乐之,奈臣有图国者何?--《韩非子·十过》；似与游者相乐。--唐·柳宗元《至小丘西小石潭记》；熙熙而乐。--唐·柳宗元《捕蛇者说》');
INSERT INTO `chinese_infos` VALUES (18120, '黎', '火', 15, 'y', '属上下结构汉字。黎字原义是众，众多的意思，常用组词：黎明，黎庶。黎又是中国姓氏。众，众多：～民。～庶。;古通“黧”，黑色。;古国名。;姓。');
INSERT INTO `chinese_infos` VALUES (18121, '谅', '火', 15, 'y', '意思是信实。');
INSERT INTO `chinese_infos` VALUES (18122, '赉', '火', 15, 'n', '赏赐');
INSERT INTO `chinese_infos` VALUES (18123, '皑', '火', 15, 'n', '〔皑皑〕形容洁白（多 用于雪）');
INSERT INTO `chinese_infos` VALUES (18124, '肿', '火', 15, 'n', '部首是月，八画，是个形声字。部首月跟人有关系，中是代表声音的声旁。作名词，本义是身体全部或局部因病而增加份量。其他义是因发炎而突起。作动词，浮胀；肿胀。');
INSERT INTO `chinese_infos` VALUES (18125, '陡', '火', 15, 'n', '坡度很大，近于垂直：陡坡；陡峭；这座山很陡，爬上去很困难。2突然：陡然；天气陡变。');
INSERT INTO `chinese_infos` VALUES (18126, '辎', '火', 15, 'n', '古代 一种有帷子的车。');
INSERT INTO `chinese_infos` VALUES (18127, '憃', '火', 15, 'n', '愚笨。');
INSERT INTO `chinese_infos` VALUES (18128, '缎', '火', 15, 'n', '形声。从糸,段声。本义缝贴于鞋跟的革片、丝绦之类；履舄鞜襞绒缎紃。--《急就篇》；一种质地厚密而有光泽的丝织物；诏赐抚臣名马衣缎。--《聊斋志异·促织》');
INSERT INTO `chinese_infos` VALUES (18129, '腠', '火', 15, 'n', '肌肉上的纹理。');
INSERT INTO `chinese_infos` VALUES (18130, '噘', '火', 15, 'n', '，用于“噘嘴”。');
INSERT INTO `chinese_infos` VALUES (18131, '除', '火', 15, 'n', '字从阜从余，余亦声。“阜”指人工土石堆积物，如城墙。现代有去除，改变等基本含义。另有数学概念除法，下面分别详细介绍。');
INSERT INTO `chinese_infos` VALUES (18132, '摎', '火', 15, 'n', '1.绞洁2.求取3.缚杀；绞死。捋，捋取。物相交。〔～蓼〕搜索。古同“挠”，阻止。');
INSERT INTO `chinese_infos` VALUES (18133, '禚', '火', 15, 'n', '古地名。在今山东。');
INSERT INTO `chinese_infos` VALUES (18134, '逴', '火', 15, 'n', '1.远。2.超越。3.远行。');
INSERT INTO `chinese_infos` VALUES (18135, '骶', '火', 15, 'n', '腰部下面尾骨上面 的部分。');
INSERT INTO `chinese_infos` VALUES (18136, '辋', '火', 15, 'n', '车轮周围的框子。');
INSERT INTO `chinese_infos` VALUES (18137, '缒', '火', 15, 'n', '用绳子拴住人或东西从上往下送');
INSERT INTO `chinese_infos` VALUES (18138, '蜗', '火', 15, 'n', '部首：虫部。〔蜗牛〕，软体动物。〔～居〕喻窄小的住所。');
INSERT INTO `chinese_infos` VALUES (18139, '饵', '火', 15, 'n', '形声。从食,耳声。本义糕饼；饵,粉饼也。--《说文》；鱼胶饵。--《考工记·弓人》；又如果饵(果饼；钓鱼用的鱼食；临川靡芳饵。--《文选·傅长虞诗》；又如鱼饵;钓饵；药饵；得而猎之以为饵,可以已大风、挛踠、瘘、疠。--唐·柳宗元《捕蛇者说》；又如饵治(以药饵治病；食物的总称；筋腱；捶反铡之,去其饵。--《礼记·内则》；饵；吞食；适足以饵大国耳。--《汉书·贾谊传》');
INSERT INTO `chinese_infos` VALUES (18140, '憏', '火', 15, 'n', '见〔㤞憏〕');
INSERT INTO `chinese_infos` VALUES (18141, '踮', '火', 15, 'n', '抬起脚后跟用脚尖站着');
INSERT INTO `chinese_infos` VALUES (18142, '脑', '火', 15, 'n', '形声。本义脑髓；卍,头髓也。从匕,匕,相比著也。巛象发,囟象脑形。--《说文》。按,囟头会卍盖也。字亦作膞。；蹙于(脑)而休于气。--《考工记·弓人》；其荣发也。--《素问·五藏生气篇》。注脑为髓海。；人精在脑。--《春秋元命苞》；晋侯梦与楚子搏,楚子伏己而盐其脑,是以惧。--《左传·僖公二十八年》；形状或颜色像脑髓的东西；头部；云台之高,堕者拆脊碎脑。--《淮南子·俶真》');
INSERT INTO `chinese_infos` VALUES (18143, '铽', '火', 15, 'n', '金属元素，符号Tb（terbium）。是一种稀土元素。银灰色。用来制备高温燃料电池，也用于发光材料等。');
INSERT INTO `chinese_infos` VALUES (18144, '辇', '火', 15, 'n', '古代用人拉的车，后来多指皇帝、皇后坐的车');
INSERT INTO `chinese_infos` VALUES (18145, '馁', '火', 15, 'n', '本意是指饥饿，也指指鱼腐烂变质，孔子提出“七不食”：“食不厌精，脍不厌细。食饐而餲，鱼馁而肉败，不食；色恶，不食；臭恶，不食；失饪，不食；不时，不食；割不正，不食；不得其酱，不食。”（《论语·乡党》）');
INSERT INTO `chinese_infos` VALUES (18146, '獐', '火', 15, 'n', '也叫牙獐。哺乳动物。小型鹿类。雌雄都没有角，雄的有獠牙露在嘴外。多分布于中国长江流域。');
INSERT INTO `chinese_infos` VALUES (18147, '闹', '火', 15, 'n', '会意字，本义是集市讨价还价，嘈杂喧嚣的场景。应从斗（音，对打；争斗。）从市，由于汉字简化原因，简化后的汉字从“门”不符合汉字本意。');
INSERT INTO `chinese_infos` VALUES (18148, '熵', '火', 15, 'n', '1.热力体系中，不能利用来做功的热能可以用热能的变化量除以温度所得的商来表示，这个商叫做熵。2.科学技术上泛指某些物质系统状态的一种量度或者某些物质系统状态可能出现的程度。');
INSERT INTO `chinese_infos` VALUES (18149, '搂', '火', 15, 'n', '两种读法，或。一般做动词用，也可做量词。这里讲述该字基本词义、注解以及字形结构等。');
INSERT INTO `chinese_infos` VALUES (18150, '噢', '火', 15, 'n', '叹词。表示了解');
INSERT INTO `chinese_infos` VALUES (18151, '熨', '火', 15, 'n', '1.烧热后用来烫平衣服的金属器具，称“熨斗（dǒu）”。2.用烙铁、熨斗烫平〔～帖〕a.用字、用词合适，恰当，妥帖；b.心情安宁、舒畅；c.方言，事情完全办妥。均亦作“熨贴”。');
INSERT INTO `chinese_infos` VALUES (18152, '踏', '火', 15, 'n', '该字的基本字义”有用脚踩、亲自到现场去等解释。');
INSERT INTO `chinese_infos` VALUES (18153, '戮', '火', 15, 'n', '1.杀2.并；合');
INSERT INTO `chinese_infos` VALUES (18154, '熛', '火', 15, 'n', '1.火星迸飞。2.迅速。');
INSERT INTO `chinese_infos` VALUES (18155, '鸩', '火', 15, 'n', '1.古代传说中的毒鸟，用它的羽毛泡的酒喝了可以毒死人。2.（用鸩的羽毛泡成的）毒酒3.用毒酒害人。');
INSERT INTO `chinese_infos` VALUES (18156, '僻', '火', 15, 'n', '是中国古代，可作动词、形容词、名词之用，并有多种用法及含义。本词条对僻的用法进行了详细的说明，并进行了相应的词组举例。僻字在中医学上也有特定的含义，在心理学上，则指一种爱好过度的病态。');
INSERT INTO `chinese_infos` VALUES (18157, '挪', '火', 15, 'n', '⑵移动：～动。～移。～借。～用。⑶揉搓：～挲。～绳破篾（搓绳子，破篾片）');
INSERT INTO `chinese_infos` VALUES (18158, '踢', '火', 15, 'n', '用脚特别是足尖触击(打；踢,以足蹴物。--《正字通》');
INSERT INTO `chinese_infos` VALUES (18159, '僾', '火', 15, 'n', '1.如肺与气管堵塞呼吸不畅。2.依稀，模糊。');
INSERT INTO `chinese_infos` VALUES (18160, '寮', '火', 15, 'n', '小屋');
INSERT INTO `chinese_infos` VALUES (18161, '瘤', '火', 15, 'n', '瘤子,身体组织增生形成的赘生物,多由刺激或微生物寄生而起。分癌瘤、肉瘤、淋巴瘤、血管瘤、脂瘤和骨瘤等；瘤,肿也。从疒,留声。--《说文》；肉凸曰瘤。--《通俗文》；瘤,息肉也。--《声类》；瘤,小肿也。--《三苍》');
INSERT INTO `chinese_infos` VALUES (18162, '褪', '火', 15, 'n', '含多种意思的汉字，主要表示褪落、解除等意思。');
INSERT INTO `chinese_infos` VALUES (18163, '摘', '火', 15, 'n', '采取，拿下');
INSERT INTO `chinese_infos` VALUES (18164, '嘹', '火', 15, 'n', '表示声音响亮。');
INSERT INTO `chinese_infos` VALUES (18165, '层', '火', 15, 'n', '重叠起来的东西；重叠起来的东西中的一部分：层次；表层；大气层。2重叠；重复：层峦叠嶂；层出不穷。；3.量词，用于可以分出层次的事物：三层楼；两层意思；擦掉一层灰。');
INSERT INTO `chinese_infos` VALUES (18166, '账', '火', 15, 'n', '关于货币、货物出入的记载：账本。账簿。账号。');
INSERT INTO `chinese_infos` VALUES (18167, '暂', '火', 15, 'n', '字从日，从斩，斩亦声。“斩”意为“执行刑”。“日”为“子”。“斩”与“日”联合起来表示“死期临近”、“来日无长”。本义：时间短。');
INSERT INTO `chinese_infos` VALUES (18168, '噌', '火', 15, 'n', '拟声词。短促的摩擦声或快速行动的声音等〔噌吰〕拟声词。钟鼓声。吰（hóng）。');
INSERT INTO `chinese_infos` VALUES (18169, '齿', '火', 15, 'n', '最早见于甲骨文。本义为人的牙齿，又特指象牙。还引申指齿状物。');
INSERT INTO `chinese_infos` VALUES (18170, '躺', '火', 15, 'n', '字形字义分析解释：躺字从身从尚，尚亦声。“尚”意为“摊开”、“展平”。“身”与“尚”联合起来表示“身体摊平”。本义：身体摊平基本字意：身体（亦指器具、车辆）横倒');
INSERT INTO `chinese_infos` VALUES (18171, '熳', '火', 15, 'n', '色彩艳丽。');
INSERT INTO `chinese_infos` VALUES (18172, '燏', '火', 15, 'n', '火光。多用于人名。');
INSERT INTO `chinese_infos` VALUES (18173, '唠', '火', 15, 'n', '有方言称为说话，闲谈的意思，比如有话慢慢唠。');
INSERT INTO `chinese_infos` VALUES (18174, '脶', '火', 15, 'n', '手指纹。');
INSERT INTO `chinese_infos` VALUES (18175, '蝻', '火', 15, 'n', '蝗虫的若虫。');
INSERT INTO `chinese_infos` VALUES (18176, '踟', '火', 15, 'n', '〔～蹰〕心里犹豫，要走不走的样子，如“～蹰不前”。亦作“踟躇”。');
INSERT INTO `chinese_infos` VALUES (18177, '羰', '火', 15, 'n', '化学用字。是氧和碳组成的合体字。如羰基，有机化合物分子里的一种官能团（ ）。醛类和酮类的分子里都含有这种官能团。');
INSERT INTO `chinese_infos` VALUES (18178, '虑', '火', 15, 'n', '思考；担忧的意思。');
INSERT INTO `chinese_infos` VALUES (18179, '腩', '火', 15, 'n', '肚子上松软的肌肉');
INSERT INTO `chinese_infos` VALUES (18180, '赕', '火', 15, 'n', '奉献');
INSERT INTO `chinese_infos` VALUES (18181, '撂', '火', 15, 'n', '1.放；搁2.弄倒3.抛；扔');
INSERT INTO `chinese_infos` VALUES (18182, '獗', '火', 15, 'n', '见〖猖獗〗。');
INSERT INTO `chinese_infos` VALUES (18183, '踔', '火', 15, 'n', '1.跳跃2.超越。3.疾行。');
INSERT INTO `chinese_infos` VALUES (18184, '辆', '火', 15, 'n', '从车从两。一车两轮，为辆。〈量〉；形声。从车,两声。古代的车一般有两个轮子,故车一乘即称一两,后来写作“辆”,总称为“车辆”。；用于车,指单个数。如:三辆小轿车');
INSERT INTO `chinese_infos` VALUES (18185, '驽', '火', 15, 'n', '1.驽马。2.比喻人没有能力');
INSERT INTO `chinese_infos` VALUES (18186, '髫', '火', 15, 'n', '小儿下垂的短发');
INSERT INTO `chinese_infos` VALUES (18187, '龙', '火', 16, 'y', '象形。甲骨文,象龙形。本义古代传说中一种有鳞有须能兴云作雨的神异动物；龍,鳞虫之长。能幽能明,能细能巨,能短能长。春分而登天,秋分而潜渊。--《说文》。徐铉注象宛转飞动之貌。饶炯注龙之为物,变化无端,说解因着其灵异如此,以；能升天,神其物,而命之曰灵。；飞龙在天。--《易·乾》；麟、凤、龟、龙,谓之四灵。--《礼记·礼运》；甲虫三百有六十,而龙为之长。--《孔子家语·执辔》；叶公子高好龙。--刘向《新序·叶公好龙》');
INSERT INTO `chinese_infos` VALUES (18188, '俦', '火', 16, 'y', '1.伴侣2.等；辈');
INSERT INTO `chinese_infos` VALUES (18189, '陈', '火', 16, 'y', '形声。从阜,从木,申声。本义地名。古宛丘地,春秋时陈国国都,在今河南省淮阳县。引申为姓氏；古国名；陈,宛丘,舜后妫(畧))满之所封。--《说文》；陈,颛顼之族也。--《左传·昭公八年》；陈朝；战阵;行列；前未到匈奴陈二里所。--《史记·李将军列传》；姓；陈；陈设,陈列；陈,列也。--《广雅》；陈,布也。--《玉篇》');
INSERT INTO `chinese_infos` VALUES (18190, '赭', '火', 16, 'y', '红褐色');
INSERT INTO `chinese_infos` VALUES (18191, '谘', '火', 16, 'y', '同“咨”。');
INSERT INTO `chinese_infos` VALUES (18192, '璇', '火', 16, 'y', '美玉。');
INSERT INTO `chinese_infos` VALUES (18193, '陵', '火', 16, 'y', '从阜从夌。阜，大土山；夌，攀越。合起来是“攀越大土山”的意思，引申为“登上、升”义。陵，为“帝王的坟墓”专用，有其”专用的升天通道（之处）“的意思。');
INSERT INTO `chinese_infos` VALUES (18194, '道', '火', 16, 'y', '形声。从辵,首声。本义供行走的道路；道,所行道也。--《说文》；一达谓之道。--《尔雅》；道坦坦。--《易·履》；百夫有洫,洫上有途,千夫有浍,浍上有道,万夫有川,川上有路。--《周礼·地官·遂人》。注途容车一轨道容二轨,路容三轨。；大道甚夷,而民好径。--《老子》五十三章；道听而途说。--《论语·阳货》；从此道至吾军,不过二十里耳。--《史记·项羽本纪》；道渴而死。--《山海经·海外北经》；今天大雨,道不通,度已失期。--《史记·陈涉世家》');
INSERT INTO `chinese_infos` VALUES (18195, '诺', '火', 16, 'y', '答应的声音，表示同意：唯唯～～。答应，允许：诺言。许诺。承诺。一诺千金。《礼记正义》里面注，唯恭于诺。');
INSERT INTO `chinese_infos` VALUES (18196, '灯', '火', 16, 'y', '形声。从火,登声。本写作镫。本义置烛用以照明的器具。镫在古代还作盛熟食的器具解。隶变以后,作照明器具用的写作燈,而镫多作马鞍两旁的铁脚；踏讲；。亦指通电发光的照明器具或做其他用途的发光器具；月夜见渔灯,孤光一点萤。--清·查慎行《舟夜书所见》');
INSERT INTO `chinese_infos` VALUES (18197, '达', '火', 16, 'y', '从辶（辵）从羍、原以羍指代很好的生育能力，后引申为大（羍）道通畅，行人（辵）互不相遇。羊子初生名羍。小曰羔。未成羊曰羜。大曰羊。凡生子始生较难，后稷为姜嫄始生子，乃如达出之易，故曰先生如羍，云如羊子之生（生无不易者）。以羍训逹，凡故训传之通例如此。假借也。达是形声字，辶为形，大为声。达的繁体也是形声字，辶为形，羍为声。达的本意是在大路上行走，有通达、畅通之意。还由畅通的意思引申出了通显、显贵等意。');
INSERT INTO `chinese_infos` VALUES (18198, '燚', '火', 16, 'n', '火燃烧的样子。多用于人名。');
INSERT INTO `chinese_infos` VALUES (18199, '憝', '火', 16, 'y', '1.怨恨。2.坏；恶');
INSERT INTO `chinese_infos` VALUES (18200, '晓', '火', 16, 'y', '象形。从日,尧声。本义天明；。今专指天刚亮；晓,明也。--《说文》；冥冥之中,独见晓焉。--《淮南子·俶真》；晓驾炭车辗冰辙。--唐·白居易《卖炭翁》；晓看红湿处。--唐·杜甫《春夜喜雨》；晓当尽戮之。--《资治通鉴·唐纪》；梳晓鬟也。--唐·杜牧《阿房宫赋》；晓风残月。--宋·柳永《雨霖铃》');
INSERT INTO `chinese_infos` VALUES (18201, '陆', '火', 16, 'y', '数目字六的大写；陆；(会意。从阜,表示与地形地势的高低上下有关,从坴(鑬),土块很大。坴亦兼表字音。本义陆地,高而平的地方；陆,高平地。--《说文》；鸿渐于陆。--《易·渐卦》；陵阜陆墐。--《国语·齐语》；陵衍平陆。--《穆天子传》三；水陆草木。--宋·周敦颐《爱莲说》；水陆俱下。--《资治通鉴》');
INSERT INTO `chinese_infos` VALUES (18202, '都', '火', 16, 'y', '部首为“阝”，有两种读音，分别为“”“”，有7种释义：1.大都市。2.一国的最高行政机关所在的地方。3.美好。4.总。5.居。6.古代称头目、首领。7.古代的姓氏。');
INSERT INTO `chinese_infos` VALUES (18203, '骆', '火', 16, 'y', '本义黑鬃的白马。衍义：又用作姓');
INSERT INTO `chinese_infos` VALUES (18204, '谛', '火', 16, 'y', '1.仔细（听或 看）2.意义；道理');
INSERT INTO `chinese_infos` VALUES (18205, '导', '火', 16, 'y', '从辵（辶，），从首从寸。导的本字，上面是首表示人；下面是止表示脚；外面是行表示路口。合在一起的意思是，人走到路口时，需要得到引导、引领。；形声。从寸,道声。“寸”与“手”意思相近。本义:以手牵引,引导。；导字义：1.、指引，带领：领～。引～。向～（引路的人）。倡～。推～。～引。～游。～向。～师。～言。2、传引，传向：传～。～热。～致（引起）。3、启发：开～。教～。因势利～');
INSERT INTO `chinese_infos` VALUES (18206, '璋', '火', 16, 'y', '古代的一种玉器，形状像半个圭。');
INSERT INTO `chinese_infos` VALUES (18207, '撰', '火', 16, 'y', '形声。字从手，从巽，巽亦声。“巽”义为“辅助”、“翼辅”。“手”与“巽”联合起来表示“动手帮助”。');
INSERT INTO `chinese_infos` VALUES (18208, '录', '火', 16, 'y', '金文像井上辘轳之形，是“辘”的初文。');
INSERT INTO `chinese_infos` VALUES (18209, '瘘', '火', 16, 'y', '1.人和动物体内发生脓肿时生成的管子，也有手术后安装的，管子的开口或在皮肤表面，或与其他内脏相通，病灶内的分泌物等可以从这里流出来2.瘰疬。');
INSERT INTO `chinese_infos` VALUES (18210, '卢', '火', 16, 'y', '释义为：1.饭器。2．黑色：“卢弓一，卢矢百”。3．古同“垆”，酒家安放盛酒器的土墩子。4．姓。卢氏。繁体：卢氏。5．台湾俚语，形容人个性拖泥带水，不干脆，很烦很啰嗦。');
INSERT INTO `chinese_infos` VALUES (18211, '晔', '火', 16, 'y', '1.兴盛；充满生机。2.“曄”，也作“烨”的异体字。');
INSERT INTO `chinese_infos` VALUES (18212, '积', '火', 16, 'y', '繁体字：积；聚集；使逐渐增多：积累；积蓄；日积月累。2长时间逐渐形成的：积习；积劳成疾；积重难返。；3.中医指因某些东西滞留体内而得的病：奶积；食积。④算术乘法的得数：乘积；2乘以2的积是4。');
INSERT INTO `chinese_infos` VALUES (18213, '燃', '火', 16, 'y', '本意是指烧起火焰，引火点着，曹植的《七步诗》和清代薛福成的《观巴黎油画记》均有记载。引申义，比喻花开得红艳鲜明。');
INSERT INTO `chinese_infos` VALUES (18214, '焰', '火', 16, 'y', '常作火焰，气焰词用。火焰、焰火、焰或炎是可燃物质燃烧时，所生成的发光、发热的气体区域。');
INSERT INTO `chinese_infos` VALUES (18215, '陶', '火', 16, 'y', '拼音或，多音汉字，名词时多念，做动词时多念。有多种释义。起先陶字写成没有“阝”的“陶”，后来在其左增加“阝”部，形成陶字，其义为表示制陶时从高坡上取黏土之义，所以说陶字也可以说是形声兼会意，后世便以这个字代替“陶”。陶〈名〉通“窑”。窑灶《集训》:“陶,烧瓦器土室也。”——《一切经音义》平遥，史称古陶，相传为帝尧的封地。春秋时为晋国属地，战国时属赵国。秦置平陶县，西汉置京陵、中都县。北魏始光元年(424），改平陶为平遥至今。');
INSERT INTO `chinese_infos` VALUES (18216, '琏', '火', 16, 'y', '古代祭祀时盛食粮的器皿。');
INSERT INTO `chinese_infos` VALUES (18217, '逎', '火', 16, 'n', '古同“遒”。');
INSERT INTO `chinese_infos` VALUES (18218, '瑾', '火', 16, 'y', '美玉。');
INSERT INTO `chinese_infos` VALUES (18219, '昙', '火', 16, 'y', '云彩密布，多云。');
INSERT INTO `chinese_infos` VALUES (18220, '烨', '火', 16, 'y', '1.火光；日光。2.光盛。');
INSERT INTO `chinese_infos` VALUES (18221, '燊', '火', 16, 'y', '兴盛的样子。');
INSERT INTO `chinese_infos` VALUES (18222, '鸯', '火', 16, 'y', '本义：雌鸟。说明：鸯与鸳相对，鸳是“大口小腹的鸟”、雄鸟。');
INSERT INTO `chinese_infos` VALUES (18223, '赖', '火', 16, 'y', '它的意思有倚靠，仗恃；不承认，刁钻泼辣等，也可以作姓氏。');
INSERT INTO `chinese_infos` VALUES (18224, '臻', '火', 16, 'y', '1.达到（美好的境地）2.来到3.姓。');
INSERT INTO `chinese_infos` VALUES (18225, '励', '火', 16, 'y', '该字的主要字义是劝勉的意思，也是中国姓氏；该字古同“厉”、“砺”，磨炼，振奋。');
INSERT INTO `chinese_infos` VALUES (18226, '历', '火', 16, 'y', '繁体字：歴。词组：；从厂，从秝，从止，谓历史；从厂，从秝，从日，谓日历。1.从厂从秝从止。厂，山石之厓巖，人可居；秝，一行一行的禾苗，谓之农业、农耕；止，一只脚，脚趾朝上，脚后跟朝下，停止。我们的历史，是在一个稳固的山石之下，止于农业社会，周而复始。2、从厂，从秝，从日。以日为单位，记录我们农耕社会的历史。');
INSERT INTO `chinese_infos` VALUES (18227, '熹', '火', 16, 'y', '1.天亮2.明亮');
INSERT INTO `chinese_infos` VALUES (18228, '踱', '火', 16, 'n', '是慢步行走的意思。');
INSERT INTO `chinese_infos` VALUES (18229, '头', '火', 16, 'y', '人身体的最上部分或动物身体的最前的部分：～骨。～脑。～脸（．头和脸；．指面貌；．指面子，体面）。～角（喻青年的气概或才华）。指头发或所留头发的样式：留～。剃～。物体的顶端：山～。笔～。两～尖。指事情的起点或端绪：从～儿说起。～绪。物体的残余部分：布～儿。以前，在前面的：～三天。次序在前，第一：～等。～生。首领：～子。～目。方面：他们是一～的。临，接近：～睡觉先洗脸。量词，多指牲畜：一～牛。表示约计、不定数量的词：三～五百。');
INSERT INTO `chinese_infos` VALUES (18230, '惮', '火', 16, 'n', '1.怕，畏惧2.劳苦，因劳成疾震赫，撼动');
INSERT INTO `chinese_infos` VALUES (18231, '璃', '火', 16, 'y', '形声字，形旁为玉，声旁为离。');
INSERT INTO `chinese_infos` VALUES (18232, '瘴', '火', 16, 'n', '瘴气，热带或亚热带山林中的湿热空气。从前被认为是瘴疠的病源。');
INSERT INTO `chinese_infos` VALUES (18233, '蹄', '火', 16, 'n', '马、牛、猪等生在趾端的保护物，亦指有角质保护物的脚：蹄子。蹄筋。');
INSERT INTO `chinese_infos` VALUES (18234, '猪', '火', 16, 'n', '从豕从诸。豕，象形字，长吻，大腹，四蹄，有尾。本义:猪。诸，表众多。猪一台十余子，为数众多。');
INSERT INTO `chinese_infos` VALUES (18235, '遛', '火', 16, 'n', '见〖逗留〗（逗遛）。1.慢慢走；散步2.牵着牲畜或带着鸟慢慢走');
INSERT INTO `chinese_infos` VALUES (18236, '哝', '火', 16, 'n', '小声说话');
INSERT INTO `chinese_infos` VALUES (18237, '遁', '火', 16, 'n', '1.逃避；逃走2.隐藏；消失');
INSERT INTO `chinese_infos` VALUES (18238, '燔', '火', 16, 'n', '1.焚烧2.烤');
INSERT INTO `chinese_infos` VALUES (18239, '焖', '火', 16, 'n', '一种烹饪方法。把食物放在带有 一定量水的锅中，扣紧锅盖， 用文火慢煮，使物熟汤干');
INSERT INTO `chinese_infos` VALUES (18240, '撑', '火', 16, 'n', '动词，体操动作的基本术语之一。指两手支撑在地面的姿势。在徒手体操中常做如下几种基本撑动作。俯撑：两手和脚撑地，两臂和身体伸直，胸部向地面的姿势。');
INSERT INTO `chinese_infos` VALUES (18241, '廪', '火', 16, 'n', '1.粮仓2.指粮食。');
INSERT INTO `chinese_infos` VALUES (18242, '掸', '火', 16, 'n', '基本字义是用力拂去的意思，另外是中国史书上对傣族的一种称呼。');
INSERT INTO `chinese_infos` VALUES (18243, '赘', '火', 16, 'n', '是形声，读音是。字从敖，从贝，贝亦声。“敖”义为“抬高”、“抬升”。“贝”指“价值”。“敖”与“贝”联合起来表示“价值提升”。');
INSERT INTO `chinese_infos` VALUES (18244, '遖', '火', 16, 'n', '日本汉字。睛；好。');
INSERT INTO `chinese_infos` VALUES (18245, '蹀', '火', 16, 'n', '蹈；顿足。');
INSERT INTO `chinese_infos` VALUES (18246, '赌', '火', 16, 'n', '博，即用斗牌、掷色子等形式，拿有价值的东西做注码来赌输赢的游戏，是人类的一种娱乐方式。任何赌博在不同的文化和历史背景有不同的意义。目前，在西方社会中，它有一个经济的定义，是指“对一个事件与不确定的结果，下注钱或具物质价值的东西，其主要目的为，赢取得更多的金钱或物质价值”。');
INSERT INTO `chinese_infos` VALUES (18247, '踵', '火', 16, 'n', '1.脚后跟2.在后面跟着；追随3.到');
INSERT INTO `chinese_infos` VALUES (18248, '殚', '火', 16, 'n', '尽；竭尽');
INSERT INTO `chinese_infos` VALUES (18249, '吨', '火', 16, 'n', '中文单字，常常用于数学质量单位。；中也使用吨(或者)这个计量词。生活中多用于计量较大物品的重量。');
INSERT INTO `chinese_infos` VALUES (18250, '挠', '火', 16, 'n', '字义：1.搅，搅动：“使水浊者，鱼～之”。2.扰乱，阻止：～乱，阻～。3.弯曲（喻屈服）：～曲，～折，～志（屈节从人），百折不～。4.搔，轻轻抓：～痒。5使动用法，使…屈服。');
INSERT INTO `chinese_infos` VALUES (18251, '醍', '火', 16, 'n', '1.〔～醐〕古代指从牛奶中提炼出来的酥油，佛教喻最高的佛法，如“～醐灌顶”（喻把佛法、智慧、悟性灌输给人，使人彻底醒悟，亦借指听了精辟的言论深受启发教育）。2.较清的浅红色酒');
INSERT INTO `chinese_infos` VALUES (18252, '谑', '火', 16, 'n', '开玩笑');
INSERT INTO `chinese_infos` VALUES (18253, '炖', '火', 16, 'n', '1.把物品盛在 碗或其他器皿中，再放入水中加温2.一种烹饪方法。把原料连同调料放入锅中，加 一定量的水，烧开后用文火久煮使熟烂。');
INSERT INTO `chinese_infos` VALUES (18254, '撤', '火', 16, 'n', '形声字，本字为“彻”，从彳，从育，从攴。甲骨文从鬲，从又，本义为撤除餐具，鬲即食器，又即手。籀文（即大篆，先秦文字）加彳，意为离开、贯通等，小篆（秦始皇“书同文”之后的篆文）加手另造“撤”，意为撤去、消除、撤退等。');
INSERT INTO `chinese_infos` VALUES (18255, '螗', '火', 16, 'n', '古书上指一种较小的蝉。');
INSERT INTO `chinese_infos` VALUES (18256, '糖', '火', 16, 'y', '形声。从米,唐声。本义食用糖及糖制食品的统称；食糖。古代以麦作饴,即麦芽糖◇多用甘蔗、甜菜等制糖；糖,饴也。--《广韵》；更添糖,拼折本,供他几碗。--宋·史浩《粉蝶儿·咏圆子》');
INSERT INTO `chinese_infos` VALUES (18257, '撅', '火', 16, 'n', '1.翘起2.折断1.〔～竖〕眼光浅，才识短，如“～竖小人，无大经略”。2.古同“掘”，控掘。');
INSERT INTO `chinese_infos` VALUES (18258, '炽', '火', 16, 'y', '1.形容火旺2.旺盛；热烈');
INSERT INTO `chinese_infos` VALUES (18259, '缡', '火', 16, 'n', '古代妇女的佩巾');
INSERT INTO `chinese_infos` VALUES (18260, '罹', '火', 16, 'n', '1.遭遇；遭受（灾祸或疾病）2.忧患；苦难。');
INSERT INTO `chinese_infos` VALUES (18261, '擂', '火', 16, 'n', '研磨：擂成细末；擂米粉。2打：擂鼓；自吹自擂；擂了一拳。；3.催促:擂进度、；擂任务。擂；〔擂台〕比武所搭的台子：打擂；守擂；擂主。擂（读音）；〈韩〉农具，擂木。平耙。用以碎土。');
INSERT INTO `chinese_infos` VALUES (18262, '烧', '火', 16, 'n', '形声。从火,尧声。本义使物着火；烧,爇也。--《说文》；雏烧。--《礼记·内则》。注烧烟,于火中也。；齐之北泽烧火。--《管子·轻重甲》。注猎而行火曰烧。；卖炭翁,伐薪烧炭南山中。--唐·白居易《卖炭翁》；烧尽北船,延及岸上营落。--《资治通鉴》');
INSERT INTO `chinese_infos` VALUES (18263, '鸱', '火', 16, 'n', '古书上指鹞鹰。');
INSERT INTO `chinese_infos` VALUES (18264, '鲐', '火', 16, 'n', '也叫鲭、油筒鱼、青花鱼。鱼类。体纺锤形，尾柄细，长可达50厘米，背青色，有深蓝色波状条纹。是海产经济鱼。中国北方沿海产量较高。');
INSERT INTO `chinese_infos` VALUES (18265, '暾', '火', 16, 'n', '刚出来的太阳');
INSERT INTO `chinese_infos` VALUES (18266, '鸵', '火', 16, 'n', '〔～鸟〕现代鸟类中最大的鸟，高两米多，生活在非洲的草原和沙漠地带。肉和卵可食，羽毛可做装饰品。');
INSERT INTO `chinese_infos` VALUES (18267, '绦', '火', 16, 'n', '绦子， 用丝线编织成的花边或扁平的带子。多作衣物边沿的装饰品。');
INSERT INTO `chinese_infos` VALUES (18268, '髭', '火', 16, 'n', '嘴上边的胡子');
INSERT INTO `chinese_infos` VALUES (18269, '辏', '火', 16, 'n', '1.车轮的辐集中到毂上2.聚集');
INSERT INTO `chinese_infos` VALUES (18270, '燠', '火', 16, 'n', '暖；热');
INSERT INTO `chinese_infos` VALUES (18271, '褴', '火', 16, 'n', '（衣服）破烂');
INSERT INTO `chinese_infos` VALUES (18272, '燎', '火', 16, 'y', '常指放火烧草木或火延烧原野。又喻火烧气势旺盛，着火面积大。其他含义：；烹调手法之一。原料剞成花刀片，用高档酒加调味拌匀，下旺火烈油燎炒手勺一推，勺内飞火腾焰，刹时出锅，速度很快称燎。2原料初加工方法之一。如用旺火燎去动物性原料表皮毛茬，或燎糊表皮，再泡软刮净，供烹制。');
INSERT INTO `chinese_infos` VALUES (18273, '撩', '火', 16, 'n', '形声。字从手从尞，尞亦声。“尞”意为“长”、“远”。“手”与“尞”联合起来表示“伸长手”。本义：伸长手（干某事）。');
INSERT INTO `chinese_infos` VALUES (18274, '螂', '火', 16, 'n', '见  〔蟑螂〕 、  〔螳螂〕 、  〔蜣螂〕');
INSERT INTO `chinese_infos` VALUES (18275, '撮', '火', 16, 'n', '字义：取、摘取；用手指捏取细碎的东西；量词；中国市制容量单位，一升的千分之一；撮东西用的器具；聚起，多指用簸箕状的器具铲起东西。');
INSERT INTO `chinese_infos` VALUES (18276, '橐', '火', 16, 'n', '1.一种口袋。2.拟声词。硬物连续撞击地面等的声音');
INSERT INTO `chinese_infos` VALUES (18277, '谍', '火', 16, 'n', '秘密探察军、政及经济等方面的消息；古代字义同“牒”，谱录、“喋”，喋喋。');
INSERT INTO `chinese_infos` VALUES (18278, '捞', '火', 16, 'n', '本义：从水或其它液体里面取东西。衍义：引申指“用不正当的手段取得”。');
INSERT INTO `chinese_infos` VALUES (18279, '獠', '火', 16, 'n', '凶恶。');
INSERT INTO `chinese_infos` VALUES (18280, '膦', '火', 16, 'n', '（俗读lín）磷化氢（PH３）分子里的氢原子被烃基取代后所形成的有机磷化合物的统称。');
INSERT INTO `chinese_infos` VALUES (18281, '鲇', '火', 16, 'n', '鲇鱼，体表多黏液，无鳞，背部苍黑色，头扁口阔，有须两对，尾圆而短，不分叉，背鳍小，臀鳍与尾鳍相连。生活在淡水中，吃小鱼、贝类、蛙等。');
INSERT INTO `chinese_infos` VALUES (18282, '爵', '火', 17, 'y', '象形。甲骨文字形,象古代饮酒器。本义古代酒器。青铜制；盛酒的礼器,形似雀,青铜制,有流、两柱、三足,用以温酒或盛酒,盛行于殷代和西周初期；爵,礼器也。象爵之形,中有鬯酒,又持之也。--《说文》；实二爵。--《仪礼·特牲礼》；殷以斝,周以爵。--《仪礼·明堂位》；洗爵奠斝。--《诗·大雅·行苇》；虢公请器,王与之爵。--《左传·庄公二十一年》；爵位,爵号,官位；王者之制禄爵。--《礼记·王制》');
INSERT INTO `chinese_infos` VALUES (18283, '纵', '火', 17, 'y', '《说文解字》：“纵，缓也。一曰舍也。从糸从声。”');
INSERT INTO `chinese_infos` VALUES (18284, '灿', '火', 17, 'y', '本义：光彩、耀眼。衍义：又用作姓 ；《说文解字》：“灿，灿爤，明瀞皃。从火、粲声 [2]  。”。精凿精洁优质之米是粲之范式。火、粲两范式叠加。光彩鲜明耀眼精凿精洁优质是灿之范式。');
INSERT INTO `chinese_infos` VALUES (18285, '阑', '火', 17, 'y', '1.同“栏”2.同“拦”。3.将尽4.擅自（出入）');
INSERT INTO `chinese_infos` VALUES (18286, '隶', '火', 17, 'y', '从手（又）从尾。隶的本字。柰，一种果木；隶，从事采摘果子的工作的奴隶。本义逮住。');
INSERT INTO `chinese_infos` VALUES (18287, '烛', '火', 17, 'y', '形声。从火,蜀声。本义古代照明用的火炬；烛,庭燎大烛也。--《说文》。按,燋也。未爇曰燋,执之曰日烛,树地曰庭燎。苇薪为之,小者麻蒸为之。；执烛于西阶上。--《仪礼·燕礼》；共坟烛庭燎。--《周礼·司烜氏》。注树于门外,曰大庭燎。；独不见跋。--《礼记·曲礼》；火在地曰燎,执之曰烛。--《仪礼·士丧礼》注；举烛者,尚明也。--《韩非子·外储说左上》；昼短苦夜长,何不秉烛游?--《古诗十九首·生年不满百》；江月亭前桦烛香,龙门阁上驮声长。--陆游《雪夜感旧》');
INSERT INTO `chinese_infos` VALUES (18288, '燮', '火', 17, 'y', '1.调和2.姓。');
INSERT INTO `chinese_infos` VALUES (18289, '丑', '火', 17, 'n', '现在“丑”字多用来形容难看的人或事物。还指戏曲中的丑。在美学中，丑指否定的审美形态。还指十二地支之一，见丑（地支）。');
INSERT INTO `chinese_infos` VALUES (18290, '襄', '火', 17, 'y', '1.帮助2.姓。');
INSERT INTO `chinese_infos` VALUES (18291, '隆', '火', 17, 'y', '该字为左右结构。隆字形声。从生，降省声。本义高；丰大。');
INSERT INTO `chinese_infos` VALUES (18292, '临', '火', 17, 'y', '略说:金文从「臣」从「人」(即「监」省去「皿」)，「品」声。「臣」象竖起的眼睛，全字象人低头俯视之形，有居高临下之意，本义是俯视，引申为监视。；释义：从上向下看，在高处朝向低处。到，来：。遭遇，碰到。挨着，靠近。⒌照样子摹仿字画。⒍旧时指帝王上朝：～朝，～政。⒎姓。');
INSERT INTO `chinese_infos` VALUES (18293, '骋', '火', 17, 'y', '1.奔跑2.放开');
INSERT INTO `chinese_infos` VALUES (18294, '蛰', '火', 17, 'y', '动物在冬天潜伏起来，不食不动');
INSERT INTO `chinese_infos` VALUES (18295, '绩', '火', 17, 'y', '又音，形声。字从纟，从责，责亦声。“责”意为“利息”，引申为“还本付息”。“纟”指“丝帛”。“纟”与“责”联合起来表示“用纺的纱织的布来还本付息”。本义：用家纺产品抵债。引申义：（对家家户户来说）纺织。（对国家来说）功业。');
INSERT INTO `chinese_infos` VALUES (18296, '褶', '火', 17, 'y', '1.衣服折叠后留下的痕迹2.脸上的皱纹');
INSERT INTO `chinese_infos` VALUES (18297, '择', '火', 17, 'y', '字义：挑拣，挑选。义同“择”，用于口语。');
INSERT INTO `chinese_infos` VALUES (18298, '燧', '火', 17, 'y', '1.古时取火的器具。2.古时边防告警的烽火');
INSERT INTO `chinese_infos` VALUES (18299, '繇', '火', 17, 'y', '1.同“徭”。2.同“谣”。古同“由”。古代占卜的文辞。');
INSERT INTO `chinese_infos` VALUES (18300, '队', '火', 17, 'n', '列是一种特殊的线性表，它只允许在表的前端进行删除操作，而在表的后端进行插入操作。进行插入操作的端称为队尾，进行删除操作的端称为队头。队列中没有元素时，称为空队列。');
INSERT INTO `chinese_infos` VALUES (18301, '黛', '火', 17, 'y', '1.青黑色的颜料，古代女子用来画眉2.青黑色的3.姓。');
INSERT INTO `chinese_infos` VALUES (18302, '琎', '火', 17, 'n', '像玉的石头。多 用于人名。');
INSERT INTO `chinese_infos` VALUES (18303, '誊', '火', 17, 'y', '照原稿抄写清楚；謄,迻书也。从言,朕声。--《说文》');
INSERT INTO `chinese_infos` VALUES (18304, '蟑', '火', 17, 'n', '〔蟑螂〕蜚蠊的俗称。');
INSERT INTO `chinese_infos` VALUES (18305, '餲', '火', 17, 'n', '（食物）经久而变味');
INSERT INTO `chinese_infos` VALUES (18306, '蹋', '火', 17, 'n', '详细字义：蹋〈动〉；同“踏”。践踏；踩蹋，践也。——《说文》蹋，履也。——《广雅·释诂一》；字亦作“踏”舞辄数十人相随，蹋地为节。——《后汉书·东夷传》(3)踢王傅萨都喇以足蹋人而死。——《续资治通鉴》(4)另见');
INSERT INTO `chinese_infos` VALUES (18307, '嚏', '火', 17, 'n', '〔嚏喷〕即“喷嚏”喷（pen）。');
INSERT INTO `chinese_infos` VALUES (18308, '遢', '火', 17, 'n', '见“邋遢”。');
INSERT INTO `chinese_infos` VALUES (18309, '麋', '火', 17, 'n', '〔麋鹿〕也叫四不像。哺乳动物。过去认为它角似鹿，头似马，体似驴，蹄似牛，但又不全像以上四种动物中的一种，故名。毛淡褐色，性温驯，食植物。是中国特有珍稀动物。由于历代无节制地猎捕，现已无野生种。');
INSERT INTO `chinese_infos` VALUES (18310, '臀', '火', 17, 'n', '身体部位之一，俗称“屁股”或“腚”。作为，有着悠久的历史。在汉字演变过程中，经历多次演变，在康熙字典中也可查询。');
INSERT INTO `chinese_infos` VALUES (18311, '痨', '火', 17, 'n', '痨病，中医病证名。指结核病');
INSERT INTO `chinese_infos` VALUES (18312, '饧', '火', 17, 'n', '同“糖”。精神不振，眼睛半睁半闭');
INSERT INTO `chinese_infos` VALUES (18313, '螳', '火', 17, 'n', '螳螂');
INSERT INTO `chinese_infos` VALUES (18314, '儡', '火', 17, 'n', '繁体字：儡汉字结构：左右结构造字法：形声简体');
INSERT INTO `chinese_infos` VALUES (18315, '缧', '火', 17, 'n', '捆绑犯人的绳索，借指牢狱');
INSERT INTO `chinese_infos` VALUES (18316, '瞳', '火', 17, 'y', '形声。从目，童声。本义：瞳孔，本义。俗称“瞳仁”。眼珠中心虹膜上一个可收缩的孔。');
INSERT INTO `chinese_infos` VALUES (18317, '谣', '火', 17, 'n', '形声：有章句曰歌,无章曲曰谣。--《韩诗章句》；辨妖祥于谣。--《国语·晋语》；其歌声比余谣。--《书·大传》；西王母为王谣。--《列子·周穆王》；心之忧矣,我歌且谣。--《诗·魏风·园有桃》；谣；流言；众女嫉余之娥眉兮,谣诼谓余以善淫。--《楚辞·离骚》；又如谣诱(花言巧语,诱人上当；行歌,乐曲；吾闻文、成之世,童谣有之。--《左传·昭公二十五年》；好为庐山谣。--李白《庐山谣》');
INSERT INTO `chinese_infos` VALUES (18318, '挡', '火', 17, 'n', '偏旁“当”简化为“当”。依据古人书法省笔简化 [；]  从手、当声。与田相持分别向背而有所积絫加高之面向是当之范式。手、当两范式叠加。与手相持分别向背而有所积絫加高之面向是当之范式。');
INSERT INTO `chinese_infos` VALUES (18319, '醣', '火', 17, 'n', '化学用字。化合物的一大类。现写作糖。');
INSERT INTO `chinese_infos` VALUES (18320, '膛', '火', 17, 'n', '胸腔。');
INSERT INTO `chinese_infos` VALUES (18321, '缕', '火', 17, 'n', '子脍（食品名。以细切的鱼肉制成）;缕肉羹（肉丝羹）;缕金（金丝；以金丝为饰）;缕金百蝶穿花大红洋缎窄裉袄（指在大红洋缎的衣面上用金线绣成百蝶穿花图案的紧身袄。裉，上衣前后两幅在腋下合缝的部分）;缕细（细如丝缕）。');
INSERT INTO `chinese_infos` VALUES (18322, '蹈', '火', 17, 'n', '（形声。从足，从舀，舀亦声。“舀”意为“像捣臼那样向下击打”。“足”与“舀”联合起来表示“用脚击打地面”。本义：顿足。）');
INSERT INTO `chinese_infos` VALUES (18323, '螺', '火', 17, 'y', '又作蠃。形声。从虫,累声。本义凡软体动物腹足类,被有旋线的硬壳都叫螺,种类繁多；蠃,蜾蠃也。--《说文》。俗字作螺。；离…为蠃。--《易·说卦》；其民必移就蒲蠃于东海之滨。--《国语·吴语》。注蚌蛤之属。；冠无觚蠃之理。--《淮南子·本经》；螺,蚌属。--《广韵》；舟形似螺,沈行海底。--王嘉《拾遗记·秦始皇》');
INSERT INTO `chinese_infos` VALUES (18324, '魉', '火', 17, 'n', '见  〔魍魉〕');
INSERT INTO `chinese_infos` VALUES (18325, '疃', '火', 17, 'n', '1.村庄。多用于地名，如王疃（在河北）。2.禽兽践踏的地方。');
INSERT INTO `chinese_infos` VALUES (18326, '殓', '火', 17, 'n', '把死人装进棺材');
INSERT INTO `chinese_infos` VALUES (18327, '暧', '火', 17, 'n', '昏暗，不明朗。');
INSERT INTO `chinese_infos` VALUES (18328, '螽', '火', 17, 'n', '〔螽斯〕昆虫。种类很多。体窄长，绿或褐色，触角细长，雄虫以翅摩擦发声，善跳跃。大多是植物害虫。');
INSERT INTO `chinese_infos` VALUES (18329, '烩', '火', 17, 'n', '1.一种烹饪方法。把原料和调料放入锅中，加一定量的水，用温火煮熟，最后勾芡。2.把饭菜等混合在一起，加水煮熟或煮热');
INSERT INTO `chinese_infos` VALUES (18330, '疗', '火', 17, 'n', '字义：医治：医~，治~，诊~，理~，电~，疗~。');
INSERT INTO `chinese_infos` VALUES (18331, '辗', '火', 17, 'n', '古同“碾”，轧。〔～转（zhuǎn）〕ａ．身体翻来覆去地，如“～转（zhuǎn）反侧”；ｂ．间接，经过曲折，如“～转（zhuǎn）传说”。均亦作“展转”。');
INSERT INTO `chinese_infos` VALUES (18332, '鸸', '火', 17, 'n', '鸟，外形像鸵鸟而稍小，嘴短而扁，羽毛灰色或褐色。翅膀退化，腿长，有三趾，善于走，产在大洋洲草原和开阔的森林中，吃树叶和野果。[英emu]');
INSERT INTO `chinese_infos` VALUES (18333, '鲕', '火', 17, 'n', '鱼苗。');
INSERT INTO `chinese_infos` VALUES (18334, '瞵', '火', 17, 'n', '凝视的样子');
INSERT INTO `chinese_infos` VALUES (18335, '龀', '火', 17, 'n', '小孩子换牙（乳牙脱落，长出恒牙）。');
INSERT INTO `chinese_infos` VALUES (18336, '磷', '火', 17, 'n', '形声。从石,粦声。本义薄石)一种氮族非金属多价元素,原子序数；5,广泛存在于它的各种化合物中；磷；含有磷酸盐的；磷磷,形容岩石、水明净；泛泛东流水,磷磷水中石。--刘桢《赠从弟三首》');
INSERT INTO `chinese_infos` VALUES (18337, '挝', '火', 17, 'n', '老挝（Lǎowō），亚洲国名。同“抓”（多见于早期白话）。');
INSERT INTO `chinese_infos` VALUES (18338, '懔', '火', 17, 'n', '害怕；警惕。');
INSERT INTO `chinese_infos` VALUES (18339, '騃', '火', 17, 'n', '〔𩣚～〕（兽）快跑的样子。');
INSERT INTO `chinese_infos` VALUES (18340, '膣', '火', 17, 'n', '阴道，女性生殖器的一部分。');
INSERT INTO `chinese_infos` VALUES (18341, '瞠', '火', 17, 'n', '直瞪着眼。例¼～目相视。');
INSERT INTO `chinese_infos` VALUES (18342, '燥', '火', 17, 'n', '剁细的肉；燥；快；从此以后,匡超人的肉和豆腐都卖得生意又燥,不到日中就卖完了,把钱拿来家伴着父亲。--《儒林外史》；燥；(形声；(没有水分或水分很少；燥,干也。--《说文》；火就燥。--《易·文言传》；燥,焦也。--《释名》；唇焦口燥呼不得。--唐·杜甫《茅屋为秋风所破歌》；燥荻枯柴。--《资治通鉴》');
INSERT INTO `chinese_infos` VALUES (18343, '瘅', '火', 17, 'n', '中医指热症或湿热症1.劳累造成的病。2.憎恨');
INSERT INTO `chinese_infos` VALUES (18344, '耧', '火', 17, 'n', '播种用的农具，由牲畜牵引，后面有人扶着，可以同时开沟、下种并自行覆土。有的地区叫耩子（jiǎng·zi）。');
INSERT INTO `chinese_infos` VALUES (18345, '担', '火', 17, 'n', '担子：扁担；勇挑重担。2市制重量单位，；担等于50千克。；3.量词：一担水；一担柴。');
INSERT INTO `chinese_infos` VALUES (18346, '黏', '火', 17, 'n', '像糨糊、胶水等所具有的、能使一个物体附着在另一物体上的性质');
INSERT INTO `chinese_infos` VALUES (18347, '裢', '火', 17, 'n', '见  〔褡裢〕');
INSERT INTO `chinese_infos` VALUES (18348, '咛', '火', 17, 'n', '见  〔叮咛〕');
INSERT INTO `chinese_infos` VALUES (18349, '蝼', '火', 17, 'n', '〔蝼蛄〕 也叫蛞蝼。俗称土狗子。通称蝲蝲蛄。昆虫。生活在土中，前足能掘土。是咬食农作物幼苗、根、茎的地下害虫。');
INSERT INTO `chinese_infos` VALUES (18350, '缪', '火', 17, 'y', '1.〔纰～〕错误。2.〔～巧〕计谋，机智，如“岂有他～巧，阴阳不能贼？”3.（繆）〔绸～〕见“绸”。古同“缭”，缭绕。姓。古同“穆”，恭敬。');
INSERT INTO `chinese_infos` VALUES (18351, '焘', '火', 18, 'y', '“焘”dào的又音，多用于人名。同“帱”。');
INSERT INTO `chinese_infos` VALUES (18352, '阗', '火', 18, 'y', '充满');
INSERT INTO `chinese_infos` VALUES (18353, '丛', '火', 18, 'y', '从丵从取。丵，丛生草。本义：聚集。值得注意的是，丛的简体字为”从在一上“，容易使人误导为”人人在地上而普遍之\"的意思，这是曲解，须甄辨。〈动〉；会意。从丵,取声。“丵”,意思是“丛生草”。本义:聚集。；丛,聚也。——《说文》1.聚集，许多事物凑在一起：～生。～聚。～密。2、聚在一起的（人或物）：人～。草～。～刊。为～驱雀（喻不善于团结人，把可以依靠的力量赶到敌人方面去）。～祠。3、丛姓。');
INSERT INTO `chinese_infos` VALUES (18354, '题', '火', 18, 'y', '1、形声。字从页（xié），从是，是亦声。“是”意为“正对”、“对准”，引申为“正面”、“基准面”。“页”指“人头”。“是”与“页”联合起来表示“人头的正面”。本义：人头的正面。特指：额。');
INSERT INTO `chinese_infos` VALUES (18355, '适', '火', 18, 'y', '切合，相合；舒服：适意。舒适；刚巧：适中。适值（恰好遇到）。适可而止；.刚才，方才：适才（刚才）；往，归向：无所适从；旧称女子出嫁：适人。');
INSERT INTO `chinese_infos` VALUES (18356, '曙', '火', 18, 'y', '天刚亮（破晓之光，喻已经在望的美好前景）。');
INSERT INTO `chinese_infos` VALUES (18357, '曛', '火', 18, 'y', '1.日落时的余光2.暮；昏暗。');
INSERT INTO `chinese_infos` VALUES (18358, '戴', '火', 18, 'y', '有如下解释：增益；加在头上或用头顶着；插上、架上或套上；尊奉，推崇，拥护；古国名；姓。');
INSERT INTO `chinese_infos` VALUES (18359, '抬', '火', 18, 'n', '作动词时，基本意思是向上举起、扛起。譬如岳飞《满江红》：“抬望眼，仰天长啸，壮怀激烈。”方言还有争辩的意思；作量词时，指两个人合力抬物，一杠为一抬。如：金银财宝共是几十抬。');
INSERT INTO `chinese_infos` VALUES (18360, '瞻', '火', 18, 'y', '基本信息：往上或往前看：～仰。～望。～拜。～谒。～念。～前顾后。');
INSERT INTO `chinese_infos` VALUES (18361, '贽', '火', 18, 'y', '初次拜见长辈所送的礼物');
INSERT INTO `chinese_infos` VALUES (18362, '遮', '火', 18, 'n', '【名】广东方言（粤语、客家话）里，“遮”就是伞、雨伞的意思。');
INSERT INTO `chinese_infos` VALUES (18363, '鲤', '火', 18, 'y', '鲤鱼；鲤,鱣也。从鱼,里声。--《说文》。按,鱣者大鱼,似鱯而短,即今之寻黄鱼,非鲤也。；必河之鲤。--《诗·陈风·衡门》；一种软鳍淡水鱼,素栖于池塘或缓流中,主要吃植物性食物,可长得相当大,有时能活得很长,原为亚洲产物；书信的代称；鲤庭；鲤庭传事业,鸡树遂翱翔。--刘禹锡《酬郑州权舍人见寄》');
INSERT INTO `chinese_infos` VALUES (18364, '烬', '火', 18, 'y', '物体燃烧后剩下的东西');
INSERT INTO `chinese_infos` VALUES (18365, '粮', '火', 18, 'y', '（粮）；可吃的谷类、豆类等：～食。～仓。～荒。弹尽～绝。粮农。(3)作为农业税的粮食：公～。钱～。');
INSERT INTO `chinese_infos` VALUES (18366, '谪', '火', 18, 'y', '1.封建时代把高级官吏降职并调到边远地方做官2.指神仙受了处罚，降到人间（迷信）3.责备；指摘');
INSERT INTO `chinese_infos` VALUES (18367, '璐', '火', 18, 'y', '美玉。');
INSERT INTO `chinese_infos` VALUES (18368, '丰', '火', 18, 'y', '象形。甲骨文字形,上面象一器物盛有玉形,下面是豆(古代盛器)。故豷本是盛有贵重物品的礼器。这由豱字可以得到证明。古文豷与豱是同一个字,《说文；》豱,行礼之器也。本义古代盛酒器的托盘；中国古代礼器,形状像豆,用以承酒觯；豷,豆之豷满者也。--《说文》；饮酒实于觯,加于丰。--《仪礼·公食大夫礼》；蒲草；东序西向,敷重丰席,画纯。--《书·顾命》');
INSERT INTO `chinese_infos` VALUES (18369, '曜', '火', 18, 'y', '1.日光。2.照耀。3.日、月、星都叫曜。日、月和火、水、木、金、土五星为七曜，旧时分别用来称一个星期的七天，日曜日是星期日，月曜日是星期一，其余依次类推。');
INSERT INTO `chinese_infos` VALUES (18370, '虫', '火', 18, 'n', '虺的本字，原为毒蛇，后指一般的虫类。虫是动物界中无脊椎动物的节肢动物门昆虫纲的动物，所有生物中种类及数量最多的一群，是世界上最繁盛的动物，已发现；00多万种。其基本特点是体躯三段头、胸、腹，2对翅膀三对足；对触角头上生，骨骼包在体外部；一生形态多变化，遍布全球旺家族。');
INSERT INTO `chinese_infos` VALUES (18371, '转', '火', 18, 'y', '“转”简化为“转”。依据古人书法省笔简化 [1]  。《说文解字》：“转，运也。从车、专声。”。手摇动纺锤将散丝集中于一束是专之范式。车、专两范式叠加。车轮往来回复运作行于一途是转之范式。');
INSERT INTO `chinese_infos` VALUES (18373, '礼', '火', 18, 'y', '会意。从示,从豊。“豊”是行礼之器,在字中也兼表字音。本义:举行仪礼,祭神求福。1.社会生活中，由于道德观念和风俗习惯而形成的仪节：婚～。丧～。典～。2.符合社会整体利益的行为准则：～教。～治。克己复～。3.表示尊敬的态度和动作：～让。～遇。～赞。～尚往来。先～后兵。4.表示庆贺、友好或敬意所赠之物：～物。～金。献～。5.古书名，《礼记》的简称。6.姓。');
INSERT INTO `chinese_infos` VALUES (18374, '断', '火', 18, 'n', '形容长形的东西从中间分开等解释。');
INSERT INTO `chinese_infos` VALUES (18375, '懦', '火', 18, 'n', '【形】(形声。从心,需声。本义:胆小,含有软弱之意;弱；懦，驽弱者也。——《说文》少懦于诸侯。——《国语·晋语》而性懦愚,沉于国家大事,开罪于先生。——《战国策·齐策》');
INSERT INTO `chinese_infos` VALUES (18376, '癜', '火', 18, 'n', '皮肤病名。皮肤上出现白色或紫色斑点。常见的是白癜，俗称白癜风。');
INSERT INTO `chinese_infos` VALUES (18377, '障', '火', 18, 'n', '形声。从阜，章声。阜，土山。从阜，与高下、险阻有关。本义：阻塞，阻隔。');
INSERT INTO `chinese_infos` VALUES (18378, '鹈', '火', 18, 'n', '鸟，体长可达2米，翅膀大，嘴长，尖端弯曲，嘴下有一个皮质的囊，可以存食，羽毛大多白色，翅膀上有少数黑色羽毛。喜群居，善于游泳和捕鱼。也叫淘河。');
INSERT INTO `chinese_infos` VALUES (18379, '擢', '火', 18, 'n', '1.拔2.提拔');
INSERT INTO `chinese_infos` VALUES (18380, '耨', '火', 18, 'n', '1.一种用来锄草的农具。2.锄草。');
INSERT INTO `chinese_infos` VALUES (18381, '戳', '火', 18, 'n', '基本释义有以下几种：1.用硬物尖端触击，刺：～穿。2、因猛触硬物而受伤或损坏：～伤。～了手。3、竖立，站立：把棍子～住。4、图章：～记。盖～子。');
INSERT INTO `chinese_infos` VALUES (18382, '蛲', '火', 18, 'n', '寄生虫，身体很小，白色，像线头。寄生在人体的小肠下部和大肠里，雌虫常从肛门爬出来产卵。患者常觉肛门奇痒，并有消瘦、食欲不振等症状。');
INSERT INTO `chinese_infos` VALUES (18383, '餮', '火', 18, 'n', '贪食。见〖饕餮〗。');
INSERT INTO `chinese_infos` VALUES (18384, '怼', '火', 18, 'n', '对，相持也，意为互相对峙，底下加“心”，表示心里抵触，对抗。引申为怨恨，故而怼，怨也。');
INSERT INTO `chinese_infos` VALUES (18385, '辘', '火', 18, 'n', '见下 。');
INSERT INTO `chinese_infos` VALUES (18386, '职', '火', 18, 'y', '管理某种事务，分内应做的事：～业。～务。～守。～权。天～。尽～。');
INSERT INTO `chinese_infos` VALUES (18387, '侓', '火', 18, 'n', '〔～魁〕大的样子。');
INSERT INTO `chinese_infos` VALUES (18388, '魍', '火', 18, 'n', '〔魍魉〕也作蝄。古代传说中的山川精怪。现多用来比喻坏人。魉（liǎng）');
INSERT INTO `chinese_infos` VALUES (18389, '痴', '火', 18, 'n', '本义：胡诌，讲话没有逻辑，思维混乱。引申义：不聪慧；迟钝 ； 对某些事物难以自拔无法自拔，同“瘾”字一样');
INSERT INTO `chinese_infos` VALUES (18390, '噜', '火', 18, 'n', '〔噜苏〕啰唆。苏（su）。');
INSERT INTO `chinese_infos` VALUES (18391, '缭', '火', 18, 'n', '形声字，纟为形，尞为声。读作。缭的本意指缠绕，又指用线斜着缝。');
INSERT INTO `chinese_infos` VALUES (18392, '骓', '火', 18, 'n', '毛色青白相杂的马。');
INSERT INTO `chinese_infos` VALUES (18393, '鞮', '火', 18, 'n', '古代的一种皮鞋。');
INSERT INTO `chinese_infos` VALUES (18394, '谬', '火', 18, 'n', '基本字义谬区分：缪（作为姓氏时读作）错误的，不合情理的：荒～。～论。～传。～误。差错：失之毫厘，～以千里。');
INSERT INTO `chinese_infos` VALUES (18395, '耢', '火', 18, 'n', '1.平整土地用的一种农具，长方形，用藤条或荆条编成。功用和耙差不多，通常在耙过以后用耢进一步平整土地，弄碎土块。也叫耱（mò）或盖。2.用耢平整土地。');
INSERT INTO `chinese_infos` VALUES (18396, '醪', '火', 18, 'n', '1.汁滓混合的酒，即浊酒。2.醇酒。');
INSERT INTO `chinese_infos` VALUES (18397, '韬', '火', 19, 'y', '1.弓或剑的套子。2.隐藏3.用兵的计谋');
INSERT INTO `chinese_infos` VALUES (18398, '辽', '火', 19, 'y', '从辵，尞声。本义为远。也用于地名和朝代名。');
INSERT INTO `chinese_infos` VALUES (18399, '邻', '火', 19, 'y', '住处接近的人家：左邻右舍；街坊四邻；远亲不如近邻。2靠近；紧挨着：邻国；邻家；邻居；邻座。');
INSERT INTO `chinese_infos` VALUES (18400, '庞', '火', 19, 'y', '本义：高屋。汉语词汇“面庞”、“脸庞”指蒙古人种宽大的脸面特征。“庞杂”指胃的内容物数量多种类也多。“庞大”指形体宽大。');
INSERT INTO `chinese_infos` VALUES (18401, '辚', '火', 19, 'n', '形容车行走时的声音');
INSERT INTO `chinese_infos` VALUES (18402, '遴', '火', 19, 'y', '1.谨慎选择2.古又同“吝（lìn）”。');
INSERT INTO `chinese_infos` VALUES (18403, '离', '火', 19, 'y', '大部分以上就是离开、分开的意思。');
INSERT INTO `chinese_infos` VALUES (18404, '丽', '火', 19, 'y', '基本字义1.好看，漂亮：美～。秀～。明～。绚～。富～。～质（女子美好的品貌）。风和日～。2.附着：附～。3.地名：丽水。');
INSERT INTO `chinese_infos` VALUES (18405, '谭', '火', 19, 'y', '形声。字从言从覃，覃亦声。“覃”本义为“深不可测”，转义为“深”。“言”与“覃”联合起来表示“深入交谈”。');
INSERT INTO `chinese_infos` VALUES (18406, '烁', '火', 19, 'y', '指温度极高，能将金石熔化，形容酷热)');
INSERT INTO `chinese_infos` VALUES (18407, '际', '火', 19, 'y', '是形声字，阝（阜）为形，祭为声（在繁体字中），际的本意是两堵墙相合时中间的缝，引申为靠边或交接的地方。由边际反转过来指里面或中间，还引申为遭遇。又转指时间。');
INSERT INTO `chinese_infos` VALUES (18408, '绎', '火', 19, 'y', '肜（音融）也，又祭也（“又祭”者，今日祭，明日再祭，故曰“又祭”），《十三经注疏.毛诗·大雅.丝衣》丝衣，绎宾尸也。高子曰：灵星之尸也。');
INSERT INTO `chinese_infos` VALUES (18409, '帘', '火', 19, 'y', '旧时酒家、茶馆作店招的旗帜,用布做成；白鸟窥鱼网,青帘认酒家。--唐·郑谷《旅寓洛南村舍》；帘；(形声。从竹,廉声。本义门帘；帘子,遮蔽门窗的用具；簾,堂簾也。--《说文》；簾,户蔽也。--《声类》。；严持箧书,置饰室簾南。--《汉书》；草色入簾青。--唐·刘禹錫《陋室銘》；入其舍,则密室垂簾。--《聊斋志异·促織》');
INSERT INTO `chinese_infos` VALUES (18410, '赠', '火', 19, 'y', '形声。从贝,曾声。从贝,表示与财富有关。本义赠送；赠,玩好相送也。--《说文》；何以赠之。--《诗·秦风·渭阳》；凡赠币无常。--《仪礼·既夕礼》。注玩好曰赠。；赠秞所以送死也。--《荀子·大略》；以公命赠孟明。--《左传·僖公三十三年》；赠大珠四枚。--《汉书·李广苏建传》；歌以赠之。--唐·白居易《琵琶行(并序)》；赠谥美显。--明·张溥《五人墓碑记》');
INSERT INTO `chinese_infos` VALUES (18411, '撵', '火', 19, 'y', '（形声。从手，辇声。本义：驱逐）你趁早与我出去，我不着丫头撵你。——明·兰陵笑笑生《金瓶梅》山之高兮无撵，途之泞兮无烛。——明冯梦龙《东周列国志》驱赶：撵逐（驱逐）；撵他下台；撵蹿（赶走）；把猪撵进圈；你不好好干活，老板会撵你；〈方言〉：追赶。如：我撵不上他');
INSERT INTO `chinese_infos` VALUES (18412, '庐', '火', 19, 'y', '形声。从广,廬声。从广,表示与房屋有关。本义特指田中看守庄稼的小屋；庐,寄也。秋冬去,春夏居。--《说文》；庐,舍也。--《广雅》；余二十亩,以为庐舍。--《汉书·食货志》。注田中屋也。；庐井有伍。--《左传·襄公三十年》；中田有庐,疆场有瓜。--《诗·小雅·信南山》；亦泛指简陋居室；屋室庐庾。--《荀子·正名》。注草屋也。；众鸟欣有托,吾亦爱吾庐。--晋·陶潜《读山海经诗》之一；结庐在人境,而无车马喧。--晋·陶潜《饮酒》；南阳诸葛庐。--唐·刘禹锡《陋室铭》');
INSERT INTO `chinese_infos` VALUES (18413, '郸', '火', 19, 'y', '1.郸城（Dānchéng），地名，在河南。2.姓。');
INSERT INTO `chinese_infos` VALUES (18414, '鄣', '火', 19, 'y', '周朝国名，在今山东东平东。');
INSERT INTO `chinese_infos` VALUES (18415, '玺', '火', 19, 'y', '1.帝王的印2.姓。');
INSERT INTO `chinese_infos` VALUES (18416, '邓', '火', 19, 'y', '是中国的一个姓氏。是中国新姓氏排行29的大姓（据公安部治安管理局公布的数据），姓氏发源于今湖北襄阳团山镇邓城（春秋时的古邓国，现存邓城遗址，邓城遗址被湖北省列为省重点文物保护单位；2006年6月25日，被国务院列为国家重点文物保护单位），今天邓姓人最集中的地方是中原地区、长江流域及沿海一带，其中江西、湖南、河南为最，四川、广东、福建、江苏次之。邓姓氏发源地：湖北襄阳团山镇邓城[；]，老百家姓排名第；6；位，新百家姓排名29位。据不完全统计，全国约有800多万邓姓人口，占全国人口总数的0.54%。我们中国的领导人邓小平就是一个邓姓名人。他是我国中国共产党第二代领导核心领导者，马克思主义者，无产阶级革命家、政治家、军事家、外交家，同时也是中国人民解放军、中华人民共和国的主要领导人之一。他是中国社会主义改革开放和现代化建设的总设计师，创立了邓小平理论。他所倡导的“改革开放”及“一国两制”政策理念，改变了20世纪后期的中国，也影响了世界，因此在；978年和；985年，曾两次当选《时代周刊》“年度风云人物”。');
INSERT INTO `chinese_infos` VALUES (18417, '辙', '火', 19, 'y', '车轮压的痕迹：覆辙。车辙。南辕北辙。；车行的一定路线：上下辙。；诗词、歌曲、杂曲所押的韵：辙口。合辙押韵。；办法，主意：没辙了。；途径，门路。');
INSERT INTO `chinese_infos` VALUES (18418, '郑', '火', 19, 'y', '形声。从邑,奠声。从邑,与地名、邦域有关。本义周代诸侯国名,姬姓；郑,京兆县,周厉王子友所封。从邑,奠声。宗周之灭,郑徙缯洧之上,今新郑是也。--《说文》；至于南郑。--《穆天子传》。此旧郑也。；郑穆公使视客馆。--《左传·僖公三十三年》；周西都畿内地。周宣王封季弟友(桓公)于此。在今陕西华县境。其后犬戎杀周幽王,桓公死之,其子武公与晋文侯定平王于东都,武公迁居东都畿内,都新郑,即春秋之郑国。战国；时为韩所灭。又如郑声(郑国音乐);郑风(《诗》');
INSERT INTO `chinese_infos` VALUES (18419, '类', '火', 19, 'y', '为有种类，相似等意思，也可作为姓氏。');
INSERT INTO `chinese_infos` VALUES (18420, '羸', '火', 19, 'n', '1.瘦2.疲劳3.姓。');
INSERT INTO `chinese_infos` VALUES (18421, '摆', '火', 19, 'n', '一是汉字，一是回族中的姓氏，另一个是实验仪器。能够产生摆动的一种机械装置。摆的发展和研究，同钟表计时器的发展有密切的关系。意大利著名力学家伽利略首先研究了单摆，后来荷兰科学家．惠更斯研究了复摆，他们为摆的力学理论奠定了基础。');
INSERT INTO `chinese_infos` VALUES (18422, '齑', '火', 19, 'n', '1.捣碎的姜、蒜或韭菜的细末。2.细；碎');
INSERT INTO `chinese_infos` VALUES (18423, '歠', '火', 19, 'n', '1.吸；喝。2.指可以喝的，如粥、羹汤等。');
INSERT INTO `chinese_infos` VALUES (18424, '骴', '火', 19, 'n', '肉未烂尽的骸骨。');
INSERT INTO `chinese_infos` VALUES (18425, '氇', '火', 19, 'n', '见“氆氇”。');
INSERT INTO `chinese_infos` VALUES (18426, '哒', '火', 19, 'n', '同“嗒”（dā）。');
INSERT INTO `chinese_infos` VALUES (18427, '裣', '火', 19, 'n', '[裣衽]（liǎnrèn）同“敛衽”');
INSERT INTO `chinese_infos` VALUES (18428, '蹻', '火', 19, 'n', '同“跷”。');
INSERT INTO `chinese_infos` VALUES (18429, '曝', '火', 19, 'n', '晒1.使照相底片或感光纸感光。2.比喻隐秘的事（多指不光彩的）显露出来，被众人知道');
INSERT INTO `chinese_infos` VALUES (18430, '鲷', '火', 19, 'n', '鱼，身体侧扁，背部稍微凸起，头大，口小，侧线发达。生活在海里。种类很多，常见的有真鲷、黄鲷、黑鲷等。');
INSERT INTO `chinese_infos` VALUES (18431, '谲', '火', 19, 'n', '1.欺诈；狡诈2.奇异怪诞');
INSERT INTO `chinese_infos` VALUES (18432, '证', '火', 19, 'y', '谏正；证,谏也。从言,正声。--《说文》');
INSERT INTO `chinese_infos` VALUES (18433, '鼗', '火', 19, 'n', '拨浪鼓。');
INSERT INTO `chinese_infos` VALUES (18434, '呖', '火', 19, 'n', '〔呖呖〕拟声词。鸟清脆的叫声');
INSERT INTO `chinese_infos` VALUES (18435, '掷', '火', 19, 'n', '意思是扔，投，抛。');
INSERT INTO `chinese_infos` VALUES (18436, '爆', '火', 19, 'n', '汉语词汇，意思是猛然炸裂或迸出，爆炸；火山爆发。');
INSERT INTO `chinese_infos` VALUES (18437, '鲻', '火', 19, 'n', '鱼，身体长，前部圆，后部侧扁，头短而扁，吻宽而短，眼大，鳞片圆形，没有侧线。生活在浅海或河口咸水和淡水交汇的地方。是常见的食用鱼。');
INSERT INTO `chinese_infos` VALUES (18438, '觯', '火', 19, 'n', '古时饮酒用的器具。');
INSERT INTO `chinese_infos` VALUES (18439, '臁', '火', 19, 'n', '小腿的两侧');
INSERT INTO `chinese_infos` VALUES (18440, '蠊', '火', 19, 'n', '见〖蜚蠊〗。');
INSERT INTO `chinese_infos` VALUES (18441, '脸', '火', 19, 'n', '初指两颊上部，到唐宋时期开始指整个面部，另外又指表情，面子和物体的外部等。脸是一个人外貌特征最显著的标志，是辨认人的身份的重要根据。另有同名歌曲和电影。');
INSERT INTO `chinese_infos` VALUES (18442, '胴', '火', 19, 'n', '1.躯干（gàn）2.大肠。');
INSERT INTO `chinese_infos` VALUES (18443, '撸', '火', 19, 'n', '1.捋（luō）2.撤销（职务）3.训斥；斥责');
INSERT INTO `chinese_infos` VALUES (18444, '鲶', '火', 19, 'n', '同“鲇”。');
INSERT INTO `chinese_infos` VALUES (18445, '鄩', '火', 19, 'n', '1.中国春秋时周邑名，在今河南省巩义市西南。2.古国名，在今中国山东省潍坊市境。3.姓。');
INSERT INTO `chinese_infos` VALUES (18446, '蹲', '火', 19, 'n', '坐。垂足实坐。；虚坐；两腿尽量弯曲，像坐的样子，但臀部不着地：蹲下，蹲伏。蹲踞，蹲腿，〔蹲苗〕在一定时期内控制施肥和灌水，进行中耕和镇压，使幼苗根部下扎，生长健壮，防止多余茎叶生长。喻呆着或闲居：蹲膘。蹲窝。蹲班房（坐牢）。');
INSERT INTO `chinese_infos` VALUES (18447, '胆', '火', 19, 'n', '形声。从肉,詹声。本义动物体内消化器官之一；。通称胆或苦胆；膽,连肝之府也。--《说文》。俗字作胆。；胆者,中正之官,决断出焉。--《素问·灵兰秘典论》。按,形如瓶,长三寸,在肝之短叶间。华佗曰胆者,中清之府,号曰将军。主藏而不泻。；胆为云,肝为风,肾为雨。--《淮南子·精神》。注胆,金也。；剧辛乐毅感恩分,输肝剖胆效英才。--李白《行路难》');
INSERT INTO `chinese_infos` VALUES (18448, '鸫', '火', 19, 'n', '鸟，嘴细长而侧扁，翅膀长而平，善走，叫的声音好听。种类很多，常见的有乌鸫、斑鸫等。');
INSERT INTO `chinese_infos` VALUES (18449, '裆', '火', 19, 'n', '形声。从衣,当声。本义两裤腿相连的地方；两条裤腿缝相接触的接口部分；裆,穷裦也。今以裦有当而旁开者为裆。--《六书故》；独不见群虱之处禈中,…动不敢出禈(裤子)裆。--阮籍《大人先生传》');
INSERT INTO `chinese_infos` VALUES (18450, '薆', '火', 19, 'n', '1.隐蔽2.草木茂盛3.香');
INSERT INTO `chinese_infos` VALUES (18451, '蹭', '火', 19, 'n', '磨；擦：手上蹭破了一块皮。2因接触而沾上：蹭了一身油漆。；3.缓慢向前移动：磨蹭；一步一步往前蹭。');
INSERT INTO `chinese_infos` VALUES (18452, '蠃', '火', 19, 'n', '见〔蜾蠃〕');
INSERT INTO `chinese_infos` VALUES (18453, '蟆', '火', 19, 'n', '本义：白天隐匿，晚上活动的虫。特指：蛙类动物。|旁一蟆。--《聊斋志异·促织》');
INSERT INTO `chinese_infos` VALUES (18454, '嘲', '火', 19, 'n', '意思：嘲:讥笑，拿人取笑；嘲:勾引');
INSERT INTO `chinese_infos` VALUES (18455, '馏', '火', 19, 'n', '通过加热使物质分离或分解；(形声。从食,留声。本义:蒸饭) 把凉了的熟食品再蒸热');
INSERT INTO `chinese_infos` VALUES (18456, '脓', '火', 19, 'n', '的含义是疮口流出来的黄白色汁液，是死亡的白血球、细菌及脂肪等的混合物：～包。～胸。～肿。');
INSERT INTO `chinese_infos` VALUES (18457, '蛏', '火', 19, 'n', '蛏子，软体动物。有两扇贝壳，形状狭长。生活在沿海软泥滩中。');
INSERT INTO `chinese_infos` VALUES (18458, '鲮', '火', 19, 'n', '鱼，体侧扁，头短，口小，背部青灰色，腹部银白色。生活在淡水中，不耐低温，是珠江流域等地区的重要经济鱼类。也叫土鲮鱼。');
INSERT INTO `chinese_infos` VALUES (18459, '咙', '火', 19, 'n', '喉咙，咽部和喉部的统称；咙胡（咽喉）');
INSERT INTO `chinese_infos` VALUES (18460, '蹶', '火', 19, 'n', '见〔尥蹶子〕跌倒。比喻失败或挫折');
INSERT INTO `chinese_infos` VALUES (18461, '醴', '火', 20, 'y', '1.甜酒。2.甘甜的泉水。');
INSERT INTO `chinese_infos` VALUES (18462, '宝', '火', 20, 'y', '本义是指珍贵的东西，即《说文解字》：“宝，珍也。”引申为玉器的总称、玉质的信物、帝后太子的印信，也引申为珍贵的等义');
INSERT INTO `chinese_infos` VALUES (18463, '矍', '火', 20, 'y', '惊慌张望的样子。');
INSERT INTO `chinese_infos` VALUES (18464, '党', '火', 20, 'y', '从尚从黑。本义：非公开、暗地里、私下。《说文》不鲜也。又《周礼·地官·大司徒》五家为比，五比为闾，四闾为族，五族为党。《释名》五百家为党。党，长也。一聚之所尊长也。〈形〉；形声。从黑,尚声。“党”、“黨”在古代是两个字,现在“党”是“黨”的简化字。简化字“党”,从儿(人,尚声)本义:晦暗不明。“党”指集团时,在古代一般只用于贬义,与现代汉语不同。；不鲜明党，不鲜也。——《说文》有政党、偏袒之意，旧时指亲族。党姓的望族居住在冯诩（今天的陕西省境内）。如今随着网民数量的壮大、各类网民已经远离了分散的网络生活状态、各类志同道合的网民走到了一起。这便是“党”形成的基础。同时“党”也是指具有相同志趣或做着相同事情或有相同点的一类人。');
INSERT INTO `chinese_infos` VALUES (18465, '黧', '火', 20, 'y', '黑色');
INSERT INTO `chinese_infos` VALUES (18466, '飘', '火', 20, 'y', '动词，亦可做形容词。可组词飘横，飘扬，飘飞。');
INSERT INTO `chinese_infos` VALUES (18467, '龄', '火', 20, 'y', '名词，意为岁数、 年数。');
INSERT INTO `chinese_infos` VALUES (18468, '罗', '火', 20, 'y', '会意。甲骨文字形,象网中有隹,表示以网捕鸟的意思。小篆增加了糸,表示结网所用的材料。本义用绳线结成的捕鸟网；罗,以丝罟鸟也。--《说文》；有兔爰爰,雉离于罗。--《诗·王风·兔爰》；以天下为这罗,则雀不失矣。--《韩非子·难三》；不见篱间雀,见鹞自投罗。--曹植《野田黄雀行》；又如罗尉(捕鸟的网);罗落(截捕禽兽的用具);罗弋(捕鸟的工具；轻软的丝织品；遍身罗绮。--宋·张俞《蚕妇》；湿罗幕。--唐·岑参《白雪歌送武判官归京》');
INSERT INTO `chinese_infos` VALUES (18469, '耀', '火', 20, 'y', '从光从翟；(形声。从光,翟声。本义:照耀；光线照射：～眼。闪～。照～。光～。～斑。2显扬，显示出来：夸～。炫～。～武扬威。；3.光荣：荣～。“耀”字当选为20；5年度汉字。');
INSERT INTO `chinese_infos` VALUES (18470, '赢', '火', 20, 'y', '注意要区别于“羸弱”中的“羸”。“赢”可以有多种释义，分作形容词、动词、名词，有时还通“盈”，详见正文；“赢”可拆字为“亡”“口”“月”“贝”“凡”，会发现成功必备的品质。');
INSERT INTO `chinese_infos` VALUES (18471, '阐', '火', 20, 'y', '形声。从门,单声。本义开,打开；阐,开也。--《说文》；看山倚前户,待月阐东扉。--唐·白居易诗；开辟;开拓；阐并天下。--《史记·秦始皇本纪》；又如阐辟(开辟,开拓);阐拓(开拓；讲明白；隐则胜阐矣。--《吕氏春秋·决胜》');
INSERT INTO `chinese_infos` VALUES (18472, '赡', '火', 20, 'y', '解释为充足，足够；才情丰富；满足。');
INSERT INTO `chinese_infos` VALUES (18473, '拢', '火', 20, 'y', '汉语词汇，释义为1.凑起，总合：~共。～总。归～。2.靠近，船只靠岸：～岸。拉～。3.使不松散或不离开：收～。～音。把孩子～在怀里。4.梳，用梳子整理头发：～一～头发。5.合上，聚集：她笑得嘴都合不～了。6.方言有收聚力气义。');
INSERT INTO `chinese_infos` VALUES (18474, '胧', '火', 20, 'y', '译为.月光不明；.不清楚，模糊。');
INSERT INTO `chinese_infos` VALUES (18475, '炉', '火', 20, 'y', '是古代焚香烧炭之器，有熏炉、香炉、装饰、手脚炉之分。多用做生活燃香用具或佛前供器。造型多样，西汉时有陶质熏炉和博山炉，两晋时有青瓷熏炉，宋代南北瓷窑烧有鱼耳炉、鼓钉炉、乳钉炉、莲瓣炉、烧制下班香炉，明清时期景德镇窑烧制青花米，以明宣德青花海水纹双耳三足炉为最。');
INSERT INTO `chinese_infos` VALUES (18476, '骘', '火', 20, 'y', '安排；定');
INSERT INTO `chinese_infos` VALUES (18477, '腾', '火', 20, 'y', '本意指马快速奔跑，现拥有多重词性。主要指跳越奔腾之意。在方言中也有其他的解释。');
INSERT INTO `chinese_infos` VALUES (18478, '骝', '火', 20, 'y', '古书上指黑鬣黑尾巴的红马。');
INSERT INTO `chinese_infos` VALUES (18479, '掳', '火', 20, 'y', '把人抢走');
INSERT INTO `chinese_infos` VALUES (18480, '糯', '火', 20, 'y', '稻的一种，米黏性大：～稻。～米（亦称“江米”）。南方称为糯米，北方称为江米。糍粑（́)就是用糯米作成的一种食物；粘性的；。如:糯麦(带粘性的大麦);糯粟(糯高粱);糯高粱，还有糯小米、糯玉米等。');
INSERT INTO `chinese_infos` VALUES (18481, '曦', '火', 20, 'y', '太阳光（多指清晨的）');
INSERT INTO `chinese_infos` VALUES (18482, '窦', '火', 20, 'y', '1.孔；洞2.人体某些器官或组织的内部凹入的部分3.姓。');
INSERT INTO `chinese_infos` VALUES (18483, '挞', '火', 20, 'n', '1.打；用鞭、棍等打人2.旧指征讨；镇压');
INSERT INTO `chinese_infos` VALUES (18484, '嚼', '火', 20, 'n', '动词，本义是以牙磨碎食物。有三个1.，用于“细嚼慢咽”“咬文嚼字”“味同嚼蜡”。2.，用于某些复合词和成语。如“咀嚼”“过屠门而大嚼”。3.，用于“倒嚼”。');
INSERT INTO `chinese_infos` VALUES (18485, '龆', '火', 20, 'n', '儿童换牙');
INSERT INTO `chinese_infos` VALUES (18486, '獭', '火', 20, 'n', '见  〔水獭〕 、  〔旱獭〕 、  〔海獭〕');
INSERT INTO `chinese_infos` VALUES (18487, '躅', '火', 20, 'n', '见〔踯躅〕');
INSERT INTO `chinese_infos` VALUES (18488, '鲽', '火', 20, 'n', '鱼，身体侧扁像薄片，长椭圆形，有细鳞，两眼都在右侧，左侧向下卧在沙底。生活在浅海中。种类很多，常见的有高眼鲽、木叶鲽等。');
INSERT INTO `chinese_infos` VALUES (18489, '鼍', '火', 20, 'n', '扬子鳄。');
INSERT INTO `chinese_infos` VALUES (18490, '懒', '火', 20, 'n', '形声。从心,赖声。本义懒惰；懒,懈也。怠也。一曰卧也。--《说文》。字亦作懒。；吾少懒学问,晚成人。--《宋书·范晔传》');
INSERT INTO `chinese_infos` VALUES (18491, '瓆', '火', 20, 'n', '古人名用字。');
INSERT INTO `chinese_infos` VALUES (18492, '疠', '火', 20, 'n', '1.瘟疫2.恶疮。3.杀。古同“癞”，癞病。');
INSERT INTO `chinese_infos` VALUES (18493, '珑', '火', 21, 'y', '1.古代迷信求雨时所用的玉，上刻龙纹。2.见〔玲珑〕');
INSERT INTO `chinese_infos` VALUES (18494, '览', '火', 21, 'y', '字形上下结构，拼音第三声。可做动词、名词之用。做动词时，一般表示看、会意；也可通“揽”，表采取，采摘之意。做名词时，表景致、景色等。在康熙字典、说文解字中都有详解。此字音韵属于平水韵：上声二十七感。');
INSERT INTO `chinese_infos` VALUES (18495, '斓', '火', 21, 'y', '见  〔斑斓〕');
INSERT INTO `chinese_infos` VALUES (18496, '迩', '火', 21, 'y', '近');
INSERT INTO `chinese_infos` VALUES (18497, '俪', '火', 21, 'y', '1.并列的；对偶的2.指夫妇');
INSERT INTO `chinese_infos` VALUES (18498, '灶', '火', 21, 'y', '用土坯、砖或金属等制成的生火做饭的设备。');
INSERT INTO `chinese_infos` VALUES (18499, '踌', '火', 21, 'n', '见下 。');
INSERT INTO `chinese_infos` VALUES (18500, '髅', '火', 21, 'n', '见  〔髑髅〕 、  〔骷髅〕');
INSERT INTO `chinese_infos` VALUES (18501, '鳎', '火', 21, 'n', '鱼，体侧扁，长椭圆形，口小，两眼生在身体的右侧，有的背鳍、臀鳍与尾鳍相连。生活在海洋中，捕食小鱼、甲壳动物等。种类很多，常见的有条鳎、卵鳎和箬鳎。');
INSERT INTO `chinese_infos` VALUES (18502, '烂', '火', 21, 'n', '可做副词、形容词、动词。共有九种基本字义。');
INSERT INTO `chinese_infos` VALUES (18503, '鳐', '火', 21, 'n', '鱼类的 一科。体扁平，略呈圆形、斜方形或菱形，尾延长或细长呈鞭状。生活在海洋中。有些种类身上有发电器官，能产生电流， 用于拒敌和猎食。中国产五十余种。');
INSERT INTO `chinese_infos` VALUES (18504, '蜡', '火', 21, 'n', '动物、植物或矿物所产生的油质，常温下为固态，具有可塑性，易熔化，不溶于水，可溶于二硫化碳和苯。');
INSERT INTO `chinese_infos` VALUES (18505, '傩', '火', 21, 'n', '古代在腊月举行的 一种驱疫逐鬼的仪式，是原始巫舞之 一。后演变为 一种舞蹈形式。');
INSERT INTO `chinese_infos` VALUES (18506, '癞', '火', 21, 'n', '麻风病。癣疥等皮肤病：～子。～皮狗（喻不要脸的人）。； 表皮凸凹不平或有斑点的：～瓜（即“苦瓜”）。～蛤蟆。');
INSERT INTO `chinese_infos` VALUES (18507, '骡', '火', 21, 'n', '动物名。');
INSERT INTO `chinese_infos` VALUES (18508, '馔', '火', 21, 'n', '饭食');
INSERT INTO `chinese_infos` VALUES (18509, '鹞', '火', 21, 'n', '鸟类。雌雄羽色不 同。体形瘦长，翅、足及尾均长，头略像鸮（xiāo）类，在地上做巢。');
INSERT INTO `chinese_infos` VALUES (18510, '爝', '火', 21, 'n', '（又）火把。（又）火把。');
INSERT INTO `chinese_infos` VALUES (18511, '袜', '火', 21, 'n', '针织或编织的足套,袜子；袜；穿袜子；客初至时,不冠不袜。--清·魏禧《大铁椎传》');
INSERT INTO `chinese_infos` VALUES (18512, '疬', '火', 21, 'n', '1.见〔瘰疬〕2.古又同“癞（lài）”。');
INSERT INTO `chinese_infos` VALUES (18513, '跻', '火', 21, 'n', '登；上升');
INSERT INTO `chinese_infos` VALUES (18514, '啭', '火', 21, 'n', '鸟婉转地叫');
INSERT INTO `chinese_infos` VALUES (18515, '缠', '火', 21, 'n', '形声。从糸,廛)声。本义围绕,缠绕；缠,绕也。--《说文》；臣有所与供儋缠采薪者九方鄄。--《淮南子·道应》；动胃缠缘。--《史记·扁仓传》。正义谓脉缰绕胃也。；野田生葡萄,缠绕一支蒿。--刘禹锡《葡萄歌》');
INSERT INTO `chinese_infos` VALUES (18516, '累', '火', 21, 'y', '本词条介绍的是；累”字的读音、基本信息、基本字义等内容。');
INSERT INTO `chinese_infos` VALUES (18517, '龇', '火', 21, 'n', '（牙齿）暴露在外边');
INSERT INTO `chinese_infos` VALUES (18518, '鲦', '火', 21, 'n', '（cān）。');
INSERT INTO `chinese_infos` VALUES (18519, '腊', '火', 21, 'y', '形声。从肉,巤声。本义年终祭祀)祭名。古代阴历十二月的一种祭祀。冬至后第三个戌日祭祀众神；冬至后三戌,腊祭百神。--《说文》。字亦作臈。；夏曰嘉平,殷曰清祀,周曰大蜡,汉曰腊。--蔡邕《独断》；腊先祖五祀。--《礼记·月令》；虞不腊矣。--《左传·僖公五年》');
INSERT INTO `chinese_infos` VALUES (18520, '曩', '火', 21, 'n', '以往；从前；过去的');
INSERT INTO `chinese_infos` VALUES (18521, '瓐', '火', 21, 'n', '碧玉。');
INSERT INTO `chinese_infos` VALUES (18522, '叠', '火', 22, 'y', '基本字义叠；重复，累积：重～。层见～出。～罗汉。～韵。～嶂。～翠。；折繁体字：摺：摺～。铺床～被。折～。；乐曲的重复演奏：阳关三～。繁体字：曡、叠');
INSERT INTO `chinese_infos` VALUES (18523, '聋', '火', 22, 'y', '本义是老农耳背。引申义为一般人听不清楚。也指人听不见声音。');
INSERT INTO `chinese_infos` VALUES (18524, '弯', '火', 22, 'y', '多指不直，曲折的部分或地方。');
INSERT INTO `chinese_infos` VALUES (18525, '舻', '火', 22, 'y', '见  〔舳舻〕');
INSERT INTO `chinese_infos` VALUES (18526, '读', '火', 22, 'y', '形声。从言,卖声。本义读书；籀书,抽释理解书的意义；读,诵书也。--《说文》；主人习其读而问其传。--《公羊传·定公元年》；读其书。--《孟子》；相随观读,讽述以谈。--《论衡·谈天》；又如读法(诵读法令);读祝(祭祀时宣读祈祷文);读鞫(审判时,宣读起诉理由；阅读；好读书,不求甚解。--晋·陶潜《五柳先生传》；则其读书也必专,而其归书也必速。--清·袁枚《黄生借书说》；楚人贫居,读《淮南方》。--邯郸淳《笑林》');
INSERT INTO `chinese_infos` VALUES (18527, '听', '火', 22, 'y', '从耳从壬（，廷声)从悳。〈动〉；形声。本字从耳从悳，,壬声。从耳德，即耳有所得。今简化为“听”。“听”本义“笑貌”。从口,斤声。本义:用耳朵感受声音。；听,聆也。——《说文》听，形声。本字从旟，ሼ声。从耳德，即耳有所得，今简化为“听”。“听”本义“笑貌”。从口，斤声。本义：用耳朵感受声音。耳内的两个感觉性毛细胞的小区。例如心不在焉，视而不见，听而不闻。——《礼记·大学》。');
INSERT INTO `chinese_infos` VALUES (18528, '颤', '火', 22, 'n', '该字读作时的主要字义是指物体振动。');
INSERT INTO `chinese_infos` VALUES (18529, '囊', '火', 22, 'n', '有两种汉字基本字义读作时是指装有物品的口袋。当读音为时是指猪的乳部肥而松软的肉；或虚弱，懦弱之意用词”囊膪“。');
INSERT INTO `chinese_infos` VALUES (18530, '鲢', '火', 22, 'n', '也叫白鲢。鱼类。体侧扁，长可达1米，银白色，头大、腹缘具棱。主食浮游植物。是中国主要的淡水养殖鱼。');
INSERT INTO `chinese_infos` VALUES (18531, '躐', '火', 22, 'n', '1.超越2.踩；践踏。');
INSERT INTO `chinese_infos` VALUES (18532, '驎', '火', 22, 'n', '〔骐（qí）～〕ａ．古代骏马名；ｂ．古同“麒麟”，传说中的祥兽，形似鹿，独角，全身有鳞甲。');
INSERT INTO `chinese_infos` VALUES (18533, '癃', '火', 22, 'n', '1.衰弱多病。2.中医病证名。指小便不利');
INSERT INTO `chinese_infos` VALUES (18534, '窿', '火', 22, 'n', '在广东地区主要作为洞的意思，主要用来替代“洞”字使用，如鼻窿（鼻孔），裤穿窿（裤子有洞），而地名上则还是使用“洞”字较多。词组：鼻窿，穿窿。在中原河南周边主要作为窟窿之意，如：在河南多把有洞的之称为窟窿。在服装方便，服装的上衣绱袖子于衣身上的窟窿中，称为袖窿；在下装的裤子上有前+后的裆宽，称为裤子的窿门宽。词组：袖窿，窿门。');
