INSERT INTO `chinese_infos` VALUES (18553, '鳓', '火', 22, 'n', '鱼类。中国北方叫鲙（kuài ）鱼、白鳞鱼，南方叫曹白鱼。体侧扁，长可达50厘米。中国沿海均产，是重要食 用鱼。');
INSERT INTO `chinese_infos` VALUES (18554, '恋', '火', 23, 'y', '读：；意思是：想念不忘，爱慕不舍，不忍舍弃，不想分开。');
