INSERT INTO `chinese_infos` VALUES (18790, '鬬', '火', 24, 'n', '同斗（二）。');
INSERT INTO `chinese_infos` VALUES (18791, '鬭', '火', 24, 'n', '同“斗2”。');
INSERT INTO `chinese_infos` VALUES (18792, '鱐', '火', 24, 'n', '干鱼');
INSERT INTO `chinese_infos` VALUES (18793, '鱞', '火', 24, 'n', '古同“鳏”');
INSERT INTO `chinese_infos` VALUES (18794, '鲙', '火', 24, 'n', '见 “鳓” “鱠”， 也作“脍”的异体字。');
INSERT INTO `chinese_infos` VALUES (18795, '鱡', '火', 24, 'n', '古同“鰂”。');
INSERT INTO `chinese_infos` VALUES (18796, '𫚫', '火', 24, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (18797, '鳡', '火', 24, 'n', '鱼，身体长而大，近圆筒形，青黄色，吻尖，尾鳍分叉。性凶猛，捕食其他鱼类，对淡水养殖业有害。也叫黄钻（zuàn）。');
INSERT INTO `chinese_infos` VALUES (18798, '鱥', '火', 24, 'n', '〔～鱼〕体侧扁而较长，银灰色，有黑色斑点，口大，吻尖，喜寒冷。是生活于溪流中的小型鱼类。');
INSERT INTO `chinese_infos` VALUES (18799, '鱦', '火', 24, 'n', '小鱼。');
INSERT INTO `chinese_infos` VALUES (18800, '鱩', '火', 24, 'n', '日本叉牙鱼（海鱼的一种）（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (18801, '鱪', '火', 24, 'n', '1.勒鱼。2.日本和字。');
INSERT INTO `chinese_infos` VALUES (18802, '鱫', '火', 24, 'n', '〔～鱜〕鲇的成年之鱼（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (18803, '𫚈', '火', 24, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (18804, '鳠', '火', 24, 'n', '鱼类。体细而长，灰褐色，有黑色斑点，无鳞，口部有四对须。生活在淡水中。');
INSERT INTO `chinese_infos` VALUES (18805, '鹔', '火', 24, 'n', '〔鹔鹴〕 古书上指 一种雁。');
INSERT INTO `chinese_infos` VALUES (18806, '鸴', '火', 24, 'n', '鸟类。似雀，头部黑色，背青灰色，胸腹赤色。食昆虫等。');
INSERT INTO `chinese_infos` VALUES (18807, '鷾', '火', 24, 'n', '〔～鴯（ér）〕燕子，如“鸟莫知于～鴯（ér）。”');
INSERT INTO `chinese_infos` VALUES (18808, '䴙', '火', 24, 'n', '鸟，外形略像鸭而小，翅膀短，不善飞，生活在河流湖泊上的植物丛中，善于潜水，捕食小鱼、昆虫等。');
INSERT INTO `chinese_infos` VALUES (18809, '鸀', '火', 24, 'n', '一种鸟，即“山乌”，全身羽毛黑色发亮，尾、翼有绿色光泽，嘴鲜红，脚淡红。常结群高飞，叫声响亮。亦称“赤嘴鸟”、“红嘴山鸦”。');
INSERT INTO `chinese_infos` VALUES (18810, '鸁', '火', 24, 'n', '1.〔须～〕一种鸟，即“鸊鷉”。2.〔过～〕桑飞鸟，即“鹪鹩”。');
INSERT INTO `chinese_infos` VALUES (18811, '㶉', '火', 24, 'n', '古书上指像鸳鸯的一种水鸟。');
INSERT INTO `chinese_infos` VALUES (18812, '鸃', '火', 24, 'n', '〔鵔～〕见“鵔”。');
INSERT INTO `chinese_infos` VALUES (18813, '鸄', '火', 24, 'n', '古书上说的一种像乌鸦而苍白色的鸟。亦称“鶶鷵”。');
INSERT INTO `chinese_infos` VALUES (18814, '鸅', '火', 24, 'n', '1.〔～鸆（yú）〕护田鸟。亦作“泽虞”。2.鹈鹕鸟。');
INSERT INTO `chinese_infos` VALUES (18815, '鸆', '火', 24, 'n', '〔鸅～〕见“鸅”。');
INSERT INTO `chinese_infos` VALUES (18816, '鹯', '火', 24, 'n', '古书上说的 一种猛禽。似鹞鹰。');
INSERT INTO `chinese_infos` VALUES (18817, '鸈', '火', 24, 'n', '传说中能知人吉凶的鸟。');
INSERT INTO `chinese_infos` VALUES (18818, '鸉', '火', 24, 'n', '白鹞子。');
INSERT INTO `chinese_infos` VALUES (18819, '鹲', '火', 24, 'n', '也叫热带鸟。鸟类的 一科。中、大型海鸟，体羽大部白色，中央尾羽极长。生活在热带远洋上，主食鱼类。');
INSERT INTO `chinese_infos` VALUES (18820, '鸒', '火', 24, 'n', '寒鸦');
INSERT INTO `chinese_infos` VALUES (18821, '麠', '火', 24, 'n', '水鹿。亦称“马鹿”、“黑鹿”。');
INSERT INTO `chinese_infos` VALUES (18822, '鼞', '火', 24, 'n', '鼓声。');
INSERT INTO `chinese_infos` VALUES (18823, '齅', '火', 24, 'n', '“嗅”的异体字。');
INSERT INTO `chinese_infos` VALUES (18824, '齆', '火', 24, 'n', '鼻道阻塞，发音不清。');
INSERT INTO `chinese_infos` VALUES (18825, '齳', '火', 24, 'n', '老人无牙的样子');
INSERT INTO `chinese_infos` VALUES (18826, '𫜮', '火', 24, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (18827, '齵', '火', 24, 'n', '1.牙齿不整齐。2.泛指参差不齐');
INSERT INTO `chinese_infos` VALUES (18828, '齹', '火', 24, 'n', '牙齿参差不齐。');
INSERT INTO `chinese_infos` VALUES (18829, '粜', '火', 25, 'n', '卖出（粮食）（跟“籴”相对）');
INSERT INTO `chinese_infos` VALUES (18830, '厅', '火', 25, 'y', '在古代园林、宅第中，多具有小型公共建筑的性质，用以会客、宴请、观赏花木。因此，室内空间较大，门窗装饰考究，造型典雅、端庄，前后多置花木、叠石，使人置身厅内就能欣赏园林景色。另指清地方行政单位之一。');
INSERT INTO `chinese_infos` VALUES (18831, '叆', '火', 25, 'n', '1.〔～叇〕云彩很厚的样子。2.（靉）1.〔～。2.〕义同“依稀”，仿佛，不很分明的样子。3.（靉）');
INSERT INTO `chinese_infos` VALUES (18832, '揽', '火', 25, 'n', '把持：～持。独～大权。；拉到自己这方面或自己身上来：包～。招～。承～。～客。～活儿。(3)搂，捆：把孩子～在怀里。(4)取：～秀。～胜。摘星～月。');
INSERT INTO `chinese_infos` VALUES (18833, '鬣', '火', 25, 'n', '某些兽类（如马、狮子等）颈上的长毛。');
INSERT INTO `chinese_infos` VALUES (18834, '囔', '火', 25, 'n', '〔囔囔〕低声絮烦地说。');
INSERT INTO `chinese_infos` VALUES (18835, '颅', '火', 25, 'n', '脑盖，亦指头：头颅，颅骨。颅脑。颅腔。颅：即头骨，首。由脑颅骨与面颅骨组成。(形声。从页,卢声。从“页”,表示与头有关。本义:头盖骨)《素问·气府论》：“足阳明脉气所发者六十八穴：额颅发际傍……”经穴别名。出《针灸甲乙经》。');
INSERT INTO `chinese_infos` VALUES (18836, '脔', '火', 25, 'n', '切成小片的肉');
INSERT INTO `chinese_infos` VALUES (18837, '酃', '火', 25, 'n', '酃县，地名。在湖南东部。今改作炎陵县。');
INSERT INTO `chinese_infos` VALUES (18838, '摞', '火', 25, 'n', '1.把东西重叠地往上放2.用于重叠放置的东西');
INSERT INTO `chinese_infos` VALUES (18839, '纛', '火', 25, 'n', '古代军队里的 大旗。');
INSERT INTO `chinese_infos` VALUES (18840, '逻', '火', 26, 'n', '形声。字从辵，从罗，罗亦声。“罗”意为捕鸟之网。“辵”与“罗”联合起来表示“沿路观察多个捕鸟网的动静。”本义：沿路观察多个捕鸟网的动静。说明：古人一次就沿路张设多个捕鸟网，并撒下诱饵。然后回到营地，等待鸟儿自投罗网。每隔一定时间，就提着鸟笼网兜出去巡行一次，收获一些鸟儿');
INSERT INTO `chinese_infos` VALUES (18841, '郦', '火', 26, 'y', '姓。〔～县〕古地名，在今中国河南省南阳市西北。');
INSERT INTO `chinese_infos` VALUES (18842, '攮', '火', 26, 'n', '（用刀）刺');
INSERT INTO `chinese_infos` VALUES (18843, '瘰', '火', 26, 'n', '〔瘰疬〕俗称鼠疮。中医病证名。指颈淋巴结结核。由结核杆菌侵入颈淋巴结而引起。一般在颈部、锁骨上淋巴结出现单个或成串肿大，严重时化脓向外穿破，形成瘘管。');
INSERT INTO `chinese_infos` VALUES (18844, '驴', '火', 26, 'n', '形声。从马,眗声。本义驴子。家畜名；驴,似马,长耳。--《说文》。段注按,‘驴、骡、駃騠’太史公皆谓为匈奴奇畜,本中国所不用,故字皆不见经传,盖秦人造之耳。；腾驾罢牛,骖蹇驴兮。--贾谊《吊屈原文》；骐骥不能与罢驴为驷。--《史记·日者列传》；黔无驴。--唐·柳宗元《三戒》；又；主将适蝼而麓不闻。--《国语·晋语》');
INSERT INTO `chinese_infos` VALUES (18845, '逦', '火', 26, 'n', '见  〔迤逦〕');
INSERT INTO `chinese_infos` VALUES (18846, '骧', '火', 27, 'y', '马快跑时抬头的样子。多 用于人名。');
INSERT INTO `chinese_infos` VALUES (18847, '鲈', '火', 27, 'n', '鲈鱼，体侧扁，上部青灰色，下部灰白色，背部和背鳍有黑斑，口大，下颌突出。性凶猛，吃鱼虾等。生活在近海，秋末到河口产卵。');
INSERT INTO `chinese_infos` VALUES (18848, '谠', '火', 27, 'n', '正直 的言论');
INSERT INTO `chinese_infos` VALUES (18849, '缆', '火', 27, 'n', '（缆）系船用的粗绳或铁索：缆绳、缆索、缆桩、解缆。泛指许多股拧成的像缆的东西：电缆、钢缆。用绳索栓住：缆舟、一纵不可缆。');
INSERT INTO `chinese_infos` VALUES (18850, '鸬', '火', 27, 'n', '水鸟，羽毛黑色，有绿、蓝、紫色光泽，嘴扁而长，暗黑色，上嘴的尖端有钩。能游泳，善于捕鱼，喉下的皮肤扩大成囊状，捕得鱼可以放在囊内。我国南方多饲养来帮助捕鱼。通称鱼鹰，有的地区叫墨鸦。');
INSERT INTO `chinese_infos` VALUES (18851, '魑', '火', 28, 'n', '〔魑魅〕古代传说中躲在深山密林里害人的妖怪');
INSERT INTO `chinese_infos` VALUES (18852, '跞', '火', 28, 'n', '见〖卓跞〗。走动');
INSERT INTO `chinese_infos` VALUES (18853, '轹', '火', 28, 'n', '1.车轮碾轧。2.欺压');
INSERT INTO `chinese_infos` VALUES (18854, '骊', '火', 29, 'y', '纯黑色的马。');
INSERT INTO `chinese_infos` VALUES (18855, '躏', '火', 29, 'n', '发音为：，现代汉语中一般与“蹂”搭配为“蹂躏”使用。意为“践踏，比喻用暴力欺压、侮辱、侵害。”例：~人权。');
INSERT INTO `chinese_infos` VALUES (18856, '鹂', '火', 30, 'y', '见  〔黄鹂〕');
INSERT INTO `chinese_infos` VALUES (18857, '鸾', '火', 30, 'y', '传说中凤凰 一类的鸟。');
INSERT INTO `chinese_infos` VALUES (18858, '鲡', '火', 30, 'n', '见  〔鳗鲡〕');
INSERT INTO `chinese_infos` VALUES (18859, '馕', '火', 30, 'n', '一种烤制成的面饼，维吾尔、哈萨克等民族当做主食。拼命地往嘴里塞食物。');
INSERT INTO `chinese_infos` VALUES (18860, '爩', '火', 33, 'n', '1.烟出。2.烟气。');
INSERT INTO `chinese_infos` VALUES (18861, '鱻', '火', 33, 'n', '古同“鲜”。');
INSERT INTO `chinese_infos` VALUES (18862, '麤', '火', 33, 'n', '同“粗”。');
INSERT INTO `chinese_infos` VALUES (18863, '龗', '火', 33, 'n', '1.龙。2.古同“灵”，神灵。');
INSERT INTO `chinese_infos` VALUES (18864, '乙', '土', 1, 'y', '是天干的次位，其在方向上指东方，五行里属木，阴阳学说里为阴。在天干中，乙指幼苗或出芽。乙在名次里通常指第二位，但亦有作分类用，如乙类、乙种等，不一定为名次用。');
INSERT INTO `chinese_infos` VALUES (18865, '一', '土', 1, 'y', '是阿拉伯数字中最小的正整数。它广泛应用于很多领域，比如在计算机技术中；与0是计算机储存的基本单位；在音乐领域；代表音阶中的；个基本音级一，汉字的基本笔画，也可单独成为汉字，基本释义是最小的正整数。常用以表示人或事、物的最少数量。');
INSERT INTO `chinese_infos` VALUES (18866, '又', '土', 2, 'y', '象形。甲骨文字形,象右手形,本左右的右。从又的字多与手的动作有关。本义右手；又,手也,象形。--《说文》。段玉裁注此即今之右字。；又；复,再,再一次；敝予又改为兮。--《诗·郑风·缁衣》；天命不又。--《诗·小雅·小宛》；又命之。--《仪礼·燕礼》；又试之鸡。--《聊斋志异·促织》');
INSERT INTO `chinese_infos` VALUES (18867, '于', '土', 8, 'y', '于是迂、纡的本字，本义为曲折。较早的字是在“干”的旁边有一条迂曲的线，行路时要绕着走。“于”；，从古代就有这个字了，只是和现在的意思不同，属古今异义。“于”不是“於”的简体字（《简化字总表》、《通用规范汉字表》都没有规定“於”简化为“于”，《新华字典》对“於”的解释是“同于”），“于”、“於”多数情况相通，但“于”并不能代替“於”的全部意义，“於”也并不能代替“于”的全部意义。同时“于”也是一个姓氏，但不同于“於”姓。常见于文言文中。');
INSERT INTO `chinese_infos` VALUES (18868, '己', '土', 3, 'y', '中宫也。象万物辟藏诎形也。己承戊，象人腹。戊己皆中宫。故中央土。其日戊己。注曰。己之言起也。律历志曰。理纪於己。释名曰。己，皆有定形可纪识也。引申之义为人己。言己以别於人者。己在中。人在外。可纪识也。论语。克己复礼为仁。克己，言自胜也。象万物辟藏诎形也。辟藏者，盤辟收敛。字像其诘诎之形也。此与巳止字绝不同。宋以前分别。自明以来书籍闲大乱。如论语莫己知也。斯己而已矣，唐石经不譌。宋儒乃不能了。居拟切。一部。己承戊。象人腹。；自己：先人后己；知己；舍己为人。2天干的第六位。也表示次序第六。');
INSERT INTO `chinese_infos` VALUES (18869, '兀', '土', 3, 'y', '此字始见于商代甲骨文及商代金文，“兀”最初与“元”是同一个字，都是突出头部的人形，本义指人的头。后来为了突出“人”上之“首”，就在上面加了一横，成为“元”字，分化成了两个字。头是人体最高之处，所以“兀”有高耸突出的意思。');
INSERT INTO `chinese_infos` VALUES (18870, '也', '土', 3, 'y', '亦表示同样；叠用,强调两事并列或对等；表示转折或让步；表示委婉。如我也只好如此；表示强调；表示选择,还是；你见我府里那个门子,却是多少年纪?或是黑瘦也白净肥胖?--《水浒传》；又如也是(还是；也语气；用在句末表示判断或肯定语气。相当于啊、呀；少时之岁月为可惜也。--清·袁枚《黄生借书说》');
INSERT INTO `chinese_infos` VALUES (18871, '丸', '土', 3, 'y', '丸（元）目前又有了新的含义，即短小而强大的事物。特征是具有顽强的生命力，以及具有美丽的外观。是未来工具制造的一个重要方向。');
INSERT INTO `chinese_infos` VALUES (18872, '土', '土', 3, 'y', '是尚未固结成岩的松、软堆积物。主要为第四纪时的产物。土与岩石的根本区别是土不具有刚性的联结，物理状态多变，力学强度低等。土由各类岩石经风化作用而成。土位于地壳的表层，是人类工程经济活动的主要地质环境。土与岩石一起是工程岩土学的研究对象。');
INSERT INTO `chinese_infos` VALUES (18873, '丫', '土', 3, 'y', '本义指物体上端分叉的部分；“丫”是分叉的部位，表示次要的、不受重视的，因此，“丫”也借用来指丫头，指女孩子或旧时供使役的女孩儿。');
INSERT INTO `chinese_infos` VALUES (18874, '山', '土', 3, 'y', '象形。甲骨文和金文字形,象山峰并立的形状。山是汉字的一个部首。本义地面上由土石构成的隆起部分；山,土有石而高。--《说文》；山,土之聚也。--《国语·周语》；山人取之。--《左传·昭公四年》；夏后代山。--《礼记·明堂位》；太行、王屋二山,方七百里,高万仞。--《列子·汤问》；如山如阜,如冈如陵。--《诗·小雅·天保》；生于山阜,处于室堂。--《荀子·赋》；为山九仞,功亏一篑。--《书·旅獒》；山重水复疑无路,柳暗花明又一村。--陆游《游山西村》');
INSERT INTO `chinese_infos` VALUES (18875, '尢', '土', 3, 'n', '同“尪”。1.同“尤”。2.姓。');
INSERT INTO `chinese_infos` VALUES (18876, '允', '土', 4, 'y', '会意。甲骨文字形，上为“以”（吕）字，下为“儿”（人）字。以是任用，用人不贰就是“允”。本义：诚信。');
INSERT INTO `chinese_infos` VALUES (18877, '夭', '土', 4, 'n', '屈也。从大。象形。象首夭屈之形也。夭；，它的基本意思是草木茂盛美丽，它还是一个多音字，也有早死的意思。');
INSERT INTO `chinese_infos` VALUES (18878, '引', '土', 4, 'y', '大致有两种含义：；指引回被敌方切断的棋子。近人徐去疾《围棋入门》：“自远应援围中之子，使其乘机得出曰‘引’。”或相当于现代的“渡”。；即“退”。徐润周《围棋术语图说》：“引，也是引回之意。”');
INSERT INTO `chinese_infos` VALUES (18879, '尹', '土', 4, 'y', '此字初文始见于商代甲骨文及商代金文，会意字，其古字形像以手持杖，表示有权力；或以为像手拿笔，以表示治事。 [2]  尹由治理引申指官名，用作名词。尹也用作姓氏。');
INSERT INTO `chinese_infos` VALUES (18880, '友', '土', 4, 'y', '从二又（右手）。相交友也。同志为友。；会意。甲骨文字形，象顺着一个方向的两只手，表示以手相助。本义：朋友。；。友，同志为友。——《说文》友是汉字，其意思为彼此有交情的人或有亲近和睦关系。本意为朋友、友好。');
INSERT INTO `chinese_infos` VALUES (18881, '曰', '土', 4, 'y', '1.说：故～：“知彼知己，百战不殆。”2.叫做：哲学上名之～同一性（哲学上给它一个名称叫做同一性）。');
INSERT INTO `chinese_infos` VALUES (18882, '予', '土', 4, 'y', '夏朝第七代君主，名字一作杼或作季杼。');
INSERT INTO `chinese_infos` VALUES (18883, '切', '土', 4, 'y', '从七从刀。；形声。从刀，七声。本义:用刀把物品分成若干部分。；。如:切花(从植株上剪下的花枝或枝叶);切心(比喻痛切);切肺(祭祀所用的肺);切象(切,割取。取象以作比喻);切腹(剖腹自杀);切菜;切肉(3)治骨。加工珠宝骨器的工艺名称。如:切错(治理器物);切玉(割玉);切镂(镂刻;雕刻)(4)切磋。指学行上切磋相正。如:切切(相互敬重切磋勉励的样子);切正(切磋相正);切直(切磋相正);切摩(切磋相正);切磨(切磋相正);切责(互相切磋责勉);切厉(切磋砥砺)(5)直线、曲线或面等与圆、弧或球相交于一点。如:切面;切球;切圆。；摩擦;接触切,摩也。——《广雅》不待切脉。——《史记·扁鹊仓公传》可切循把握。——《淮南子·原道》；又如:切齿(咬紧牙齿。形容痛恨);切磨(摩擦;摩搓);切摩(摩擦);切循(抚摩)(3)契合。与…相一致。如:不切实际;译文不切原意;切正(恰切平正);切用(切合实用);切至(犹切当);切事(切合情事);切病(切中弊病)(4)一定要');
INSERT INTO `chinese_infos` VALUES (18884, '王', '土', 4, 'y', '会意。本作士,是能独立任事的人,后加一横,表示在士之上,即人间的最高统治者,而帝是天上的最高统治者◇帝、王同步降职,帝成了人间的皇帝,而王；成了对臣子的最高封爵。本义天子、君主；殷周时代对帝王的称呼；溥天之下,莫非王土。--《诗·小雅·北土》；厉王虐,国人谤王。--《国语·周语上》；王,天下所归往也。董仲舒曰古之造文者,三画而连其中谓之王。三者,天、地、人也;而参通之者,王也。--《说文》；王,天子也。--《释名》');
INSERT INTO `chinese_infos` VALUES (18885, '尤', '土', 4, 'y', '。疣和多指在人体是多余的、不正常的，故引申为过错，由过错又引申为指责。疣和多指是突出于皮肤的，所以又引申为突出、异常、特别，后世多用此义。');
INSERT INTO `chinese_infos` VALUES (18886, '卬', '土', 4, 'n', '1.人称代词。我。2.同“昂”。同“仰”。');
INSERT INTO `chinese_infos` VALUES (18887, '厄', '土', 4, 'n', '1.险要的地方2.灾难；困苦3.受困');
INSERT INTO `chinese_infos` VALUES (18888, '以', '土', 5, 'y', '用在单纯的方位词前，表示时间、方位、数量的界限，如以前、二十岁以下。表示用、拿，如以少胜多、以毒攻毒。表示因如何以知之，不以人废言。表示目的，如以待时机。表示于、在，如中华人民共和国以；949年；0月；日宣告成立。也作连词，同“而”，如城高以厚。');
INSERT INTO `chinese_infos` VALUES (18889, '瓦', '土', 5, 'y', '象形。象屋瓦俯仰相承的样子。瓦是汉字的一个部首。本义已烧土器的总称；瓦,土器已烧之总名。--《说文》；神农作瓦器。--《周书》；夏时昆吾作瓦。--《礼记·有虞上陶世本云》；载弄之瓦。--《诗·小雅·斯干》');
INSERT INTO `chinese_infos` VALUES (18890, '永', '土', 5, 'y', '象形。小篆字形,象水流曲曲折折的样子。本义:水流长。；永,水长也。象水巠理之长。——《说文》江之永矣,不可方思。——《诗·周南·汉广》。毛传:“永,长。”(3)泛指长。兼指时间和空间');
INSERT INTO `chinese_infos` VALUES (18891, '由', '土', 5, 'y', '字义：1.原因：原～。事～。理～。～于（介词，表示原因或理由）。2、自，从：～表及里。～衷（出于本心）。3、顺随，听从，归属：～不得。信马～缰。4、经过，经历：必～之路。～来已久。5、凭借：～此可知。6、古同“犹”，尚且，还。7、古同“犹”，犹如，好像。8、姓。');
INSERT INTO `chinese_infos` VALUES (18892, '硅', '土', 11, 'y', '一种非金属元素，是一种半导体材料，太阳能电池片的主要原材料。亦可用于制作半导体器件和集成电路。旧称“矽”。');
INSERT INTO `chinese_infos` VALUES (18893, '戊', '土', 5, 'y', '1.天干的第五位。见〖干支〗。2.（Wù）姓。');
INSERT INTO `chinese_infos` VALUES (18894, '用', '土', 5, 'y', '从卜从中。可施行也。〈动〉；象形。甲骨文字形，象桶形。桶可用，故引申为用。本义：使用，采用。；用，可施行也。——《说文》最常见的用法是动词和名词，作动词时可以表示使用，利用，作名词时则一般表示用品。');
INSERT INTO `chinese_infos` VALUES (18895, '幼', '土', 5, 'y', '年纪小，初出生的：～儿。～虫。～苗。～年。2.小孩儿：～教。～师。扶老携～。3.对儿童爱护：“～吾幼，以及人之幼。”');
INSERT INTO `chinese_infos` VALUES (18896, '凹', '土', 5, 'n', '在字典中清除) 凹有两个意思，即周围高，中间低，与“凸”相对； 意同“洼”（多用于地名）。');
INSERT INTO `chinese_infos` VALUES (18897, '右', '土', 5, 'y', '会意。从口,从又(手)。口手并用帮助别人。本义右助。见《说文》。这个意义后来写作佑；助;帮助◇多作佑；右,手口相助也。--《说文》。字亦作佑。；右者,助也。--《易·系辞》；以左右刑罚。--《周礼·士师》。注左右,助也。；王右伯舆。--《左传·襄公十年》。注助也。；保右命之。--《诗·大雅·嘉乐》；又如右援(援助;引荐);右序(辅助;佑助);右饷(谓享受祭献,佑助降福);右与(佑助,帮助；尊崇;崇尚；兼爱,尚贤,右鬼,非命,墨子之所立也。--《淮南子》');
INSERT INTO `chinese_infos` VALUES (18898, '仔', '土', 5, 'y', '是一个多音字，意思是指有某些特征或从事某种职业的年轻人或幼小的动物。');
INSERT INTO `chinese_infos` VALUES (18899, '央', '土', 5, 'y', '属于央字族。在央字族里，央字都是声符兼义符。央字族汉字都与萌芽之义有关，所以央的本义是萌芽。');
