INSERT INTO `chinese_infos` VALUES (19021, '羽', '土', 6, 'y', '其古字形像鸟类的羽毛，引申指代鸟类。又转指昆虫的翅膀。羽也是古代五音之一。古代的箭尾上常用羽毛作装饰，故“羽”也作箭的代称。');
INSERT INTO `chinese_infos` VALUES (19022, '伢', '土', 6, 'y', '本意是小孩儿。');
