INSERT INTO `chinese_infos` VALUES (19046, '吐', '土', 6, 'n', '念时表示使东西从口里出来。另外可以表示放出，说出。也用作姓氏。念时表示内脏里的东西从口里涌出或者把吞没的东西退出来。');
