INSERT INTO `chinese_infos` VALUES (19268, '甬', '土', 7, 'y', '1.甬江，水名。在浙江。流经宁波。2.浙江宁波的别称。');
INSERT INTO `chinese_infos` VALUES (19269, '坎', '土', 7, 'y', '既可以作为名词，又可以作为动词使用，指低洼的地方或困窘的处境。');
INSERT INTO `chinese_infos` VALUES (19270, '圻', '土', 7, 'y', '1.边界。2.姓。同“垠”。');
INSERT INTO `chinese_infos` VALUES (19271, '均', '土', 7, 'y', '平均，均摊，势均力敌；还有都，全的意思。');
INSERT INTO `chinese_infos` VALUES (19272, '牡', '土', 7, 'y', '基本含义1.雄性的鸟或兽，亦指植物的雄株，与“牝”相对：～牛。2.锁匙：～钥。3.丘陵。');
INSERT INTO `chinese_infos` VALUES (19273, '坊', '土', 7, 'y', '里巷（多用于街巷的名称）。街市，市中店铺：坊间。街坊（邻居）。手工制作的地方：作坊。原生态的加工场所。');
INSERT INTO `chinese_infos` VALUES (19274, '岘', '土', 7, 'y', '岘山，山名，在湖北。');
INSERT INTO `chinese_infos` VALUES (19275, '坂', '土', 7, 'n', '山坡；斜坡');
INSERT INTO `chinese_infos` VALUES (19276, '延', '土', 7, 'y', '是汉字中的单字，有延长、伸展之意，亦可是个姓。');
INSERT INTO `chinese_infos` VALUES (19277, '位', '土', 7, 'y', '是会意字。字从人，从立。“立”本义“站立”，引申含义指“独立”。姓氏，位姓来源于西汉于阗王位侍之后。史称位侍被长兄匈奴王封为于阗王，子孙为了纪念他，便以他的名字为氏，姓位。');
INSERT INTO `chinese_infos` VALUES (19278, '冶', '土', 7, 'y', '以冰融化解释金属销熔，用焙烧，熔炼等方法提取矿石中所需的金属。');
INSERT INTO `chinese_infos` VALUES (19279, '役', '土', 7, 'n', '基本字义役，服兵务，从军：从役。现役。战事：战役。服劳力之事：劳役。徭役。使唤：役使。奴役。被役使的人：役夫。役徒。仆役。杂役。');
INSERT INTO `chinese_infos` VALUES (19280, '完', '土', 7, 'y', '（1）全：完备。完全。完整。完善。完好。完璧归赵。覆巢无完卵。（2）尽，无：用完了。煤烧完了。（3）做成，了结：完毕。完成。完结。（4）交纳：完粮。完税。（5）姓。');
INSERT INTO `chinese_infos` VALUES (19281, '卣', '土', 7, 'n', '古代盛酒的器具，口小腹大。');
INSERT INTO `chinese_infos` VALUES (19282, '妑', '土', 7, 'n', '1.古女子人名用字。2.少女发式，梳成双髻');
INSERT INTO `chinese_infos` VALUES (19283, '岍', '土', 7, 'n', '岍山，山名，在陕西。今作千山。');
INSERT INTO `chinese_infos` VALUES (19284, '坑', '土', 7, 'y', '形声。从土,亢声。本义地面上凹下去的地方；坑,堑也,壑也。--《玉篇》；或被坑水冲淹。--《广东军务记》；又如刨个坑;挖坑;坑阱(泛指深坑);坑坎(指地面高低不平;洼地;坑穴；矿穴,矿场；其天下自五岭以北,见捋银坑,并宜禁断。--《旧唐书·食货志上》');
INSERT INTO `chinese_infos` VALUES (19285, '秃', '土', 7, 'n', '（人）没有头发；（鸟兽头或尾）没有毛；（树木）没有叶子，（山）没有树木（3）物体失去尖端（4）首位结构不完整。');
INSERT INTO `chinese_infos` VALUES (19286, '吲', '土', 7, 'n', '1.〔～哚〕有机化合物。亦称“氮杂茚”。2.〔～唑〕有机化合物。亦称“二氮杂茚”。古同“哂”，微笑。');
INSERT INTO `chinese_infos` VALUES (19287, '呀', '土', 7, 'n', '形声。从口,牙声。本义张口的样子)表示惊异；呀；吱吱嘎嘎的声音；呀；表示语气。啊受前一字韵母的影响而发生变音。如他从小就没有了家呀!群众的力量真大呀!老张,快去呀；呀叹词。〈表〉惊疑～！你说怎么办？像声词～的一声，门开了。；呀助词。\"啊\"的变音好好学习～（啊）！努力工作～（啊）！；呀1.空旷貌，敞开貌。2.张口;张开。3.高耸。参见\"呀呀\"﹑\"呀空\"');
INSERT INTO `chinese_infos` VALUES (19288, '岈', '土', 7, 'n', '嵖岈（Cháyá），山名，在河南。');
INSERT INTO `chinese_infos` VALUES (19289, '址', '土', 7, 'n', '意为建筑物的地基。');
INSERT INTO `chinese_infos` VALUES (19290, '氙', '土', 7, 'n', '气体元素，符号Xe（xenonum）。无色无臭无味，大气中含量极少，化学性质不活泼。用来填充光电管、闪光灯等。');
INSERT INTO `chinese_infos` VALUES (19291, '岜', '土', 7, 'n', '地名用字');
INSERT INTO `chinese_infos` VALUES (19292, '圬', '土', 7, 'n', '1.泥瓦工用的抹（mǒ）子。2.抹（mò）墙。');
INSERT INTO `chinese_infos` VALUES (19293, '釫', '土', 7, 'n', '古同“铧”，耕地起土的农具。古同“圬”。');
INSERT INTO `chinese_infos` VALUES (19294, '岙', '土', 7, 'y', '浙江、福建等沿海一带称山间平地（多用于地名）');
INSERT INTO `chinese_infos` VALUES (19295, '坩', '土', 7, 'n', '盛东西的陶器。');
INSERT INTO `chinese_infos` VALUES (19296, '坍', '土', 7, 'n', '倒塌');
INSERT INTO `chinese_infos` VALUES (19297, '佚', '土', 7, 'n', '1.同“逸”。2.放荡3.美4.姓。古同“迭”，轮流，更替。');
INSERT INTO `chinese_infos` VALUES (19298, '坌', '土', 7, 'n', '1.翻（土）；刨2.尘埃3.聚4.粗劣。5.用细末撒在物体上面。6.笨；不灵巧。');
INSERT INTO `chinese_infos` VALUES (19299, '矣', '土', 7, 'n', '1.用在句末，跟“了”相同2.表示感叹');
INSERT INTO `chinese_infos` VALUES (19300, '岫', '土', 8, 'y', '1.山穴2.山');
INSERT INTO `chinese_infos` VALUES (19301, '夜', '土', 8, 'y', '形声。金文字形。从月,亦声。本义从天黑到天亮的时间；夜,舍也。天下休舍也。--《说文》；夜,暮也。--《广雅》；夜呼旦。--《周礼·鸡人》。注夜,夜漏未尽,鸡鸣时也。；辛卯夜,恒星不见。--《左传·庄公七年》。疏;夜者自昏至旦之总名。；夜者阴。--《周髀算经》；夙兴夜寐,靡有朝矣。--《诗·卫风·氓》；昨夜见军帖。--《乐府诗集·木兰诗》；三日三夜。--《世说新语·自新》');
INSERT INTO `chinese_infos` VALUES (19302, '佾', '土', 8, 'y', '古代乐舞的行列。');
INSERT INTO `chinese_infos` VALUES (19303, '坳', '土', 8, 'y', '山间的平地');
INSERT INTO `chinese_infos` VALUES (19304, '垇', '土', 8, 'n', '用于地名，如黄垇 （在江西）。');
INSERT INTO `chinese_infos` VALUES (19305, '坪', '土', 8, 'y', '源于日本传统计量系统尺贯法的面积单位，主要用于计算房屋、建筑用地之面积。主要应用于日本、台湾和朝鲜半岛。；坪等于；日亩的三十分之一，合3.3057平方米。；坪是6日尺*6日尺也就是2块榻榻米的面积，；日尺约为30.3公分。坪，也指平坦的场地。');
INSERT INTO `chinese_infos` VALUES (19306, '岷', '土', 8, 'y', '1.岷山，山名，在四川、甘肃交界的地方。2.岷江，水名，在四川。3.岷县，地名，在甘肃。');
INSERT INTO `chinese_infos` VALUES (19307, '亚', '土', 8, 'y', '（亚）意为：较差，次一等的，或原子价较低，酸根或化合物中少含一个氢原子或氧原子的物质。同时，亚还是中国稀有姓氏之一。');
INSERT INTO `chinese_infos` VALUES (19308, '帕', '土', 8, 'y', '字属于白字族。在白字族里，白字都是声符兼义符。白字族汉字都与“空无一物”之义有关。帕的本义是“空白的布巾”、“包裹布”。');
INSERT INTO `chinese_infos` VALUES (19309, '旺', '土', 8, 'y', '篆文写作“暀”是形声字，日为形往为声。后来由王代替往，写作“旺”。旺本意指光明旺盛，引申指火势剧烈，也形容事业兴隆、发达、繁盛。');
INSERT INTO `chinese_infos` VALUES (19310, '委', '土', 8, 'y', '指任，派，把事交给人办。《说文》：“委，委随也。从女，从禾。”');
INSERT INTO `chinese_infos` VALUES (19311, '宛', '土', 8, 'y', '属于多音字；另有南阳市简称和姓氏等义项名。“宛”是南阳最早的地名之一。宛，即反映了“盆地”的地貌特征，又反映了它的生态环境。《说文解字》：宛，屈草自履也，从宀、夗声。其义：；“四方高中央下”，符合南阳西、北、东三面环山，当中低平的盆地地貌特征；2“屈草自覆”为芳草盖地，植被葱绿貌。春秋初期，南方的楚国日益强大起来，先后向北吞并了汉水流域诸国，吕、申两国也被楚文王所灭。楚国占据这片既有沃野美壤，又有江河之便的土地之后，便在这里建置宛邑，作为问鼎中原的基地。宛之名，即自此而始。其后，宛由地域名，演变成为“宛城”、“宛县”、“宛州”、“上宛县”等，时至今天，“南阳市”和“宛城区”均以“宛”为其约定俗成的简称。');
INSERT INTO `chinese_infos` VALUES (19312, '坡', '土', 8, 'y', '意为倾斜、倾斜的地方。坡是一外国名字，美国诗人、作家。又译名爱伦·坡。小说主要有：《厄舍古厦的倒塌》、《红色死亡假面舞会》《马里·罗盖特的秘密》、《金甲虫》、《被窃的信件》。');
INSERT INTO `chinese_infos` VALUES (19313, '奄', '土', 8, 'y', '商朝古地名，通“匽”，即“郾”，河南郾城（今属河南省漯河市郾城区，古城村一带）。');
INSERT INTO `chinese_infos` VALUES (19314, '坦', '土', 8, 'y', '地名：高坦乡，位于安徽省池州市贵池区，现于梅村乡合并为梅村镇。可以从《新华字典》、《汉字辞典》、《汉字文化》中找到这个字的意思，这个字曾经被很多古代文学家拿来作诗和词。“坦”在古文中通常用来表示坦白事实。');
INSERT INTO `chinese_infos` VALUES (19315, '依', '土', 8, 'y', '形声。从人,衣声。甲骨文字形。人在衣中。本义靠着；依,倚也。--《说文》；依,恃也。--《广雅》；白日依山尽,黄河入海流。--王之涣《登鹳雀楼》；依柱而笑。--《史记·刺客列传》；空游无所依。--唐·柳宗元《至小丘西小石潭记》；极贫无所依。--清·方苞《狱中杂记》；又如依山之榭(靠山建造的房屋);依栖(依傍栖息);依山傍水;依着电线杆;偎依(亲密地靠着);依倚(倚靠;依傍);依门卖笑(以色媚人；倚仗,仗恃,仰赖；无依势作威,无依法以削。--《书·君陈》');
INSERT INTO `chinese_infos` VALUES (19316, '抑', '土', 8, 'y', '本写作归。会意。小篆字形,象用手抑人使跽。本义按,向下压；抑之欲其奥。--唐·柳宗元《柳河东集》；又如抑按(按压);抑首(按其首);抑搔(按摩抓搔);抑扬(按下与上举；抑制；抑其兼并。--清·洪亮吉《治平篇》；不可遏抑。--孙文《黄花冈七十二烈士事略序》；又如抑勒(压制被扣);抑情(克制感情);抑引(抑制情欲而导之以善);抑末(抑制商贾);抑志(抑制自己的志向);抑枉(抑制邪辟);抑削(抑制削弱);抑挫(抑制折挫；贬；教之春秋,而为之耸善而抑恶焉。--《国语·楚语上》');
INSERT INTO `chinese_infos` VALUES (19317, '肴', '土', 8, 'y', '根据隶定字形解释，形声。字从乂，从有，有亦声。“有”为“以手持肉”之形，意为“生鲜肉食”。“乂”即“又（右手）”。“乂”与“有”联合起来表示“把肉食拿起两次，第一次是生肉，第二次是熟肉”。本义，做熟的鱼肉和猪牛羊肉。');
INSERT INTO `chinese_infos` VALUES (19318, '往', '土', 8, 'n', '意为从这个地方走向目的地。');
INSERT INTO `chinese_infos` VALUES (19319, '盂', '土', 8, 'y', '盛液体的敞口器具');
INSERT INTO `chinese_infos` VALUES (19320, '侑', '土', 8, 'n', '劝人（吃、喝）');
INSERT INTO `chinese_infos` VALUES (19321, '岢', '土', 8, 'y', '〔岢岚〕地名。在山西西北部。岚（lán）。');
INSERT INTO `chinese_infos` VALUES (19323, '坤', '土', 8, 'y', '异体字为堃，汉字基本含义：称女性的：坤造。坤宅（旧时婚礼称女家）。坤表。坤车。坤鞋。坤角儿（唕）。八卦之一：乾坤。坤舆。');
INSERT INTO `chinese_infos` VALUES (19324, '昀', '土', 8, 'y', '日光。');
INSERT INTO `chinese_infos` VALUES (19325, '艾', '土', 8, 'y', '形声。本义:草名即艾蒿。一种菊科的多年生草本植物,叶制成艾绒,供艾灸用。');
INSERT INTO `chinese_infos` VALUES (19326, '於', '土', 8, 'y', '文言叹词。[於乎][於戏]文言叹词，旧时祭文常用“呜呼”表示叹息。姓。同“于”。');
INSERT INTO `chinese_infos` VALUES (19327, '忤', '土', 8, 'n', '不顺从；不和睦');
INSERT INTO `chinese_infos` VALUES (19328, '盱', '土', 8, 'n', '1.张目。2.大。');
INSERT INTO `chinese_infos` VALUES (19329, '峁', '土', 8, 'n', '中国西北地区一种黄土丘陵。顶部平缓，斜坡陡峭。');
INSERT INTO `chinese_infos` VALUES (19330, '坶', '土', 8, 'n', '〔坶野〕古地名。即牧野，在今河南淇县。见〔垆坶〕');
INSERT INTO `chinese_infos` VALUES (19331, '坭', '土', 8, 'n', '同“泥（ní）”。如红毛坭（方言中称水泥）。用于地名，如白坭（在广东）。');
INSERT INTO `chinese_infos` VALUES (19332, '坨', '土', 8, 'n', '1.坨子，成块或成堆的东西2.面食煮熟后粘在一块儿3.特指露天的盐堆');
INSERT INTO `chinese_infos` VALUES (19333, '爬', '土', 8, 'n', '手和脚一齐着地走路，虫类行走：～行。～虫（爬行动物）。～泳。2.攀登：～高。～升。～山。往上～（讽刺追求功名的人）。3.搔：～痒。～搔（“搔”读轻声）。');
INSERT INTO `chinese_infos` VALUES (19334, '坢', '土', 8, 'n', '1.平坦。2.发地。3.方言，山坡粪肥');
INSERT INTO `chinese_infos` VALUES (19335, '坯', '土', 8, 'n', '指没有烧过的砖瓦、陶器等，泛指半成品，特指土坯，用以指未成年的人等意思。');
INSERT INTO `chinese_infos` VALUES (19336, '狁', '土', 8, 'n', '见〖猃狁〗。');
INSERT INTO `chinese_infos` VALUES (19337, '附', '土', 8, 'n', '该字的主要字义是另外加上，随带着的意思。也有：同意，赞同：捎带：靠近：依从的意思。');
INSERT INTO `chinese_infos` VALUES (19338, '坫', '土', 8, 'n', '古时室内放置食物、酒器等的土台子。');
INSERT INTO `chinese_infos` VALUES (19339, '矸', '土', 8, 'n', '〔矸石〕也叫矸子。通 常指煤生产过程中含在煤中的碎石。可从中回收少量煤炭，也可用作制砖、水泥等的原料。');
INSERT INTO `chinese_infos` VALUES (19340, '坷', '土', 8, 'n', '字从土从可。“可”意为“肩挑、担荷”。“土”与“可”联合起来表示“挑运的土块”。');
INSERT INTO `chinese_infos` VALUES (19341, '臾', '土', 8, 'n', '见〖须臾〗。');
INSERT INTO `chinese_infos` VALUES (19342, '岬', '土', 8, 'n', '1.岬角，突入海中的尖形陆地。多用于地名，如成山岬（在山东）。2.两山之间。');
INSERT INTO `chinese_infos` VALUES (19343, '坻', '土', 8, 'n', '山坡。也用于地名，如宝坻（在天津）。江河中的小洲或高地。');
INSERT INTO `chinese_infos` VALUES (19344, '岵', '土', 8, 'n', '多草木的山。');
INSERT INTO `chinese_infos` VALUES (19345, '岣', '土', 8, 'n', '岣嵝（Gǒulǒu），山名，衡山主峰，也指衡山，在湖南。');
INSERT INTO `chinese_infos` VALUES (19346, '垃', '土', 8, 'n', '会意字。“立”意为“独立”。“土”指“土块”。“土”与“立”联合起来表示“独立土块”。本义：独立的土块。多余的土块。突兀的土块。辨析：垃和圾。“垃”指平整地面上散落的多余土块，它们不是地面的一部分，应当拿掉，以保持地面的整洁。“圾”指手头边的土块，可以轻易搬动。“垃圾”一词原意是“手头边多余的土块”。');
INSERT INTO `chinese_infos` VALUES (19347, '呦', '土', 8, 'n', '叹词。表示惊异');
INSERT INTO `chinese_infos` VALUES (19348, '矽', '土', 8, 'n', '硅的旧称。');
INSERT INTO `chinese_infos` VALUES (19349, '胃', '土', 9, 'n', '是食道的扩大部分,位于膈下,上接食道,下通小肠。本词条主要介绍人体的胃。通过蠕动搅磨食物，使食物与胃液充分混合。动物的胃，一般俗称“肚子”，如：猪肚子、牛肚子。');
INSERT INTO `chinese_infos` VALUES (19350, '匽', '土', 9, 'n', '1.隐藏。2.古同“偃”（ａ．倒伏。ｂ．停止）。3.古兵器名，戟的一种。4.古通“燕（yān）”，古国名。1.排污水的阴沟2.厕所3.古通“宴”，安。');
INSERT INTO `chinese_infos` VALUES (19351, '怡', '土', 9, 'y', '1.快乐；愉快2.姓。');
INSERT INTO `chinese_infos` VALUES (19352, '姨', '土', 9, 'n', '人物称谓：母亲的姊姊称为姨妈。母亲的妹妹称为姨娘。母亲的姊兄称为姨爸。母亲的妹弟称为姨爹。妻子的姊姊称为大姨姐。妻子的妹妹称为小姨子。妻子的姊兄称为大姨姐夫。妻子的妹弟称为小姨夫。姨是一个汉语形声词，从女，夷声，指的是妻子的姐妹，出自《说文》：“妻之女弟同出为姨。从女，夷声。”');
INSERT INTO `chinese_infos` VALUES (19353, '姻', '土', 9, 'n', '结亲的男家，指夫或夫之父');
INSERT INTO `chinese_infos` VALUES (19354, '音', '土', 9, 'y', '从言含一。声生於心，有节於外，谓之音。丝竹金石匏土革木，音也。有节之意也。；指事。从言含一。甲骨文“言、音”互用，金文、小篆在“言中加一横，表示所发之音。本义：声音。；字义：1.声，亦特指有节奏的声：声～。～乐。～律。～色。～量。～区。～韵。～像。～容（声音、容貌）。弦外之～。2.信息，消息：～信。佳～。～讯。');
INSERT INTO `chinese_infos` VALUES (19355, '威', '土', 9, 'y', '威力;威风；威,畏也。--《说文》；威也者,力也。--《吕氏春秋·荡兵》；有而可畏谓之威《史记·平原君虞卿列传》；严大国之威。--《史记·廉颇蔺相如列传》；秦王之威。；威天下不以兵革。--《孟子·公孙丑下》；威棱威乎邻国。--《汉书·李广传》；威振四海。--汉·贾谊《过秦论》；为敛威去。--《明史》');
INSERT INTO `chinese_infos` VALUES (19356, '屋', '土', 9, 'y', '从尸从至。尸所主也。居也，舍也。又车盖也。同幄。一曰尸象屋形，从至，至所至止也。夫三为屋，屋三为井，出地贡者，三三相任。——《注》诸侯素锦以为屋，士輤苇席以为屋。——《礼·杂记》小帐覆棺者。——《注》意思是房，房间。屋的本义是幄，后来“屋”指房屋，另造“幄”字。会意。从尸，从至。尸，与房屋有关，“至”表示来到。本义：帐幕。“幄”的本字。');
INSERT INTO `chinese_infos` VALUES (19357, '盆', '土', 9, 'y', '专指用来盛放物品的钵状容器，因其形状要比钵大，故而称为盆。盆与碗的本质区别在于，盆的边缘比内壁要厚，而碗的边缘较内壁要薄。');
INSERT INTO `chinese_infos` VALUES (19358, '要', '土', 9, 'y', '解字：会意字。从西，夕阳西下，两只鸟儿归巢依卧；从女，妇女。以“夕阳西下鸟儿归巢依卧”作比喻，表示日暮就寝时丈夫搂着妻子的腰，以表示之需要。借古音“夭”，表示妻子屈腰仰就。后借“约”表示男女性欲时限不同，夫妻双方都有责任与义务满足对方的需求；要是腰的初文。选自《中华字通》。有索取、希望或想、请求、重大值得重视的、纲要、应该、必须、将、如果等意。');
INSERT INTO `chinese_infos` VALUES (19359, '禹', '土', 9, 'y', '古人名。传说是夏朝第一个王，鲧之子。因治水有功，舜让位给他。他死后，子启即位，开始了世袭制度。');
INSERT INTO `chinese_infos` VALUES (19360, '耶', '土', 9, 'y', '音译用字。如耶路撒冷。1.文言助词。相当于“吗”“呢”2.同“爷”。');
INSERT INTO `chinese_infos` VALUES (19361, '韦', '土', 9, 'y', '1.皮革。2.姓。');
INSERT INTO `chinese_infos` VALUES (19362, '垚', '土', 9, 'n', '山高。');
INSERT INTO `chinese_infos` VALUES (19363, '垣', '土', 9, 'y', '1.墙2.城3.姓。');
INSERT INTO `chinese_infos` VALUES (19364, '畏', '土', 9, 'y', '“畏”基本义指恐惧、害怕。古书中亦可指使……害怕。由本义又引申指敬服、佩服。');
INSERT INTO `chinese_infos` VALUES (19365, '姚', '土', 9, 'y', '一般指美好的样子。汉语言文字“姚”组合来讲，“兆”应为其本意，女旁则为姚姓从母系氏族社会传到今的痕迹。姚，从女兆声。或为姚，娆也。中国在母系氏族社会时期，当时的妇女处于社会的核心地位。姚，姓氏。');
INSERT INTO `chinese_infos` VALUES (19366, '约', '土', 9, 'y', '形声。从糸,勺声。糸是细丝,有缠束作用。本义绳索；人寻约。--《左传》注；又如寻约(八尺长的绳子；共同商定的事,共同议定要遵守的条文；盟约;相期的诺言；负约不偿城。--《史记·廉颇蔺相如列传》；又如约酬(契约,文券；姓；约；缠束;环束；约之阁阁。--《诗·小雅·斯干》；约,缠束也。--《说文》；凡任索约。--《考工记·匠人》');
INSERT INTO `chinese_infos` VALUES (19367, '垠', '土', 9, 'y', '界限；边际');
INSERT INTO `chinese_infos` VALUES (19368, '娃', '土', 9, 'y', '字最初的意思是成熟美丽的女人。《说文解字》中对于“娃”的解释是“圜深目皃（貌）。或曰，吴楚之闲（间）谓好曰娃。从女，圭声。”“好”的本义也是美貌。从这里我们可以推测得知，在汉代，大约是最初使用“娃”这个字的时候，它的意思是“美女”，是形容成熟的女人。元、明以下的“娃”字常常随北方地方语之义以应用、流传，“娃”字的年龄降得更低。到了今天的俗语中，除了亲昵的小名，“娃”字则更多地用于婴幼儿。');
INSERT INTO `chinese_infos` VALUES (19369, '玥', '土', 9, 'y', '古代传说中的一种神珠。');
INSERT INTO `chinese_infos` VALUES (19370, '奎', '土', 9, 'y', '1.二十八宿之一。2.姓。');
INSERT INTO `chinese_infos` VALUES (19371, '砭', '土', 9, 'y', '1.古代一种治病的石针。2.用石针扎穴位治病3.刺');
INSERT INTO `chinese_infos` VALUES (19372, '幽', '土', 9, 'y', '隐藏，不公开的；沉浸而安闲；迷信的人指阴间。');
INSERT INTO `chinese_infos` VALUES (19373, '昶', '土', 9, 'y', '1.白天时间长。2.舒畅；畅通。3.姓。');
INSERT INTO `chinese_infos` VALUES (19374, '勇', '土', 9, 'y', '从甬从力。力及所至，生命勃发甬甬然也。勇者，气也。气之所至。力亦至焉。心之所至。气乃至焉。故古文勇从心，恿1.有胆量，敢做：～敢。～毅。～气。～士。英～。奋～。2.中国清代称战争时期临时招募的兵士：兵～。劲～。募～。3.姓。4.儒家的伦理范畴。指果断、勇敢。孔子把“勇”作为施“仁”的条件之一。“勇”必须符合“仁、义、礼、智”，而且不能“疾贫”，才能成其为勇。《论语·宪问》：“仁者必有勇。”又《阳货》：“君子有勇而无义为乱。”又《子罕》：“知者不惑，仁者不忧，勇者不惧。”');
INSERT INTO `chinese_infos` VALUES (19375, '爰', '土', 9, 'y', '1.疑问代词。何处；哪里2.于是3.姓。');
INSERT INTO `chinese_infos` VALUES (19376, '垌', '土', 9, 'y', '田地（多用于地名）垌冢（Tóngzhǒng），地名，在湖北。');
INSERT INTO `chinese_infos` VALUES (19377, '肚', '土', 9, 'n', '形声，从肉，土声。本义是人或动物的腹部。');
INSERT INTO `chinese_infos` VALUES (19378, '砉', '土', 9, 'y', '形容迅速动作的声音皮骨相离的声音');
INSERT INTO `chinese_infos` VALUES (19379, '羑', '土', 9, 'n', '羑里（Yǒulǐ），古地名，在今河南汤阴一带。');
INSERT INTO `chinese_infos` VALUES (19380, '峒', '土', 9, 'y', '山洞（多用于地名）崆峒（Kōngtóng），山名，在甘肃。又岛名，在山东。');
INSERT INTO `chinese_infos` VALUES (19381, '怕', '土', 9, 'n', '形声。从心,白声。怕就是怖,怕是中古的新字。本义畏惧；怕,怕惧。--《广韵》；鬼神怕嘲咏。--韩愈《双鸟诗》；粉骨碎身浑不怕。--明·于谦《石灰吟》');
INSERT INTO `chinese_infos` VALUES (19382, '垧', '土', 9, 'n', '旧时土地面积单位，各地不同，东北地区多数地方1垧合15亩，西北地区1垧合3亩或5亩。');
INSERT INTO `chinese_infos` VALUES (19383, '垛', '土', 9, 'n', '墙或某些建筑物突出的部分，有支撑或掩蔽作用：垛子、垛堞、城垛。土筑的箭靶子：箭垛。射垛。中垛2整齐地堆积起来：垛积。堆垛。整齐地堆积成的堆：麦垛。草垛。量词，用于堆砌起来的东西：一垛墙。');
INSERT INTO `chinese_infos` VALUES (19384, '纡', '土', 9, 'n', '1.弯曲；曲折2.系；结');
INSERT INTO `chinese_infos` VALUES (19385, '瓮', '土', 9, 'n', '一种盛水或酒等的陶器：水瓮。酒瓮。菜瓮。瓮城（围绕在城门外的小城）。瓮牖绳枢（以破瓮作窗户，以草绳系户枢。形容家里穷）。');
INSERT INTO `chinese_infos` VALUES (19386, '垤', '土', 9, 'n', '小土堆');
INSERT INTO `chinese_infos` VALUES (19387, '舁', '土', 9, 'n', '共同用手抬');
INSERT INTO `chinese_infos` VALUES (19388, '哀', '土', 9, 'n', '悲伤；悲痛：悲哀、哀鸣2悼念：哀悼、默哀；3.怜悯：哀怜、哀其不幸');
INSERT INTO `chinese_infos` VALUES (19389, '拗', '土', 9, 'n', '弄弯；使……断，如“把钢丝拗断”。；，不顺、不顺从，如“违拗”“拗口”。(3)，固执、不驯服，如“执拗”“脾气拗”。');
INSERT INTO `chinese_infos` VALUES (19390, '怏', '土', 9, 'n', '不高兴或不满意的神情');
INSERT INTO `chinese_infos` VALUES (19391, '垡', '土', 9, 'n', '1.翻耕过的土块2.地名用字');
INSERT INTO `chinese_infos` VALUES (19392, '咦', '土', 9, 'n', '表示惊异');
INSERT INTO `chinese_infos` VALUES (19393, '哇', '土', 9, 'n', '形容呕吐声、大哭声等“啊”受到前一字收音u或ao的影响而发生的变音');
INSERT INTO `chinese_infos` VALUES (19394, '疣', '土', 9, 'n', '皮肤病，病原体是乳头状瘤病毒，常见的有扁平疣、寻常疣等。扁平疣是针头至绿豆大小的丘疹，数目较多；寻常疣是针头至黄豆大小的丘疹，表面粗糙，数目较少。不痛不痒，多长在面部、头部或手背等处。也叫赘疣。寻常疣通称瘊子。');
INSERT INTO `chinese_infos` VALUES (19395, '咿', '土', 9, 'n', '见下 。');
INSERT INTO `chinese_infos` VALUES (19396, '峋', '土', 9, 'n', '见〖嶙峋〗。');
INSERT INTO `chinese_infos` VALUES (19397, '趴', '土', 9, 'n', '通常指肚子向下卧倒或身体向前靠在东西上。');
INSERT INTO `chinese_infos` VALUES (19398, '垞', '土', 9, 'n', '小土山（多用于地名）');
INSERT INTO `chinese_infos` VALUES (19399, '衩', '土', 9, 'n', '裤衩短裤；衩；衩衣裙旁边开口的部位～口。');
INSERT INTO `chinese_infos` VALUES (19400, '窀', '土', 9, 'n', '〔窀穸〕墓穴。穸（xī）。');
INSERT INTO `chinese_infos` VALUES (19401, '俑', '土', 9, 'n', '古代殉葬的偶像');
INSERT INTO `chinese_infos` VALUES (19402, '垢', '土', 9, 'n', '字义：污秽，脏东西：污～。泥～。牙～。～腻。～秽。～敝（又脏又破）。；同“诟”。；姓氏，音同寇。');
INSERT INTO `chinese_infos` VALUES (19403, '押', '土', 9, 'n', '字义：在文书、契约上签名或画记号；把财物交给人作保证；拘留；跟随看管；同“压”；姓。');
INSERT INTO `chinese_infos` VALUES (19404, '咽', '土', 9, 'n', '（英文名：）是一前后略扁的漏斗形肌性管道，位于第；～6颈椎前方，上端附于颅底，向下于第6颈椎下缘或环状软骨的高度续于食管。咽有前壁、后壁及侧壁，其前壁不完整，故咽向前分别与鼻腔、口腔及喉腔相通。咽腔分别以软腭与会厌上缘为界，分为鼻咽、口咽和喉咽3部。咽具有吞咽功能，呼吸功能，保护和防御功能以及共鸣作用。此外，咽也是一个重要的发音共振器，对发音起辅助作用。');
INSERT INTO `chinese_infos` VALUES (19405, '垲', '土', 9, 'n', '地势高而且干燥');
INSERT INTO `chinese_infos` VALUES (19406, '砍', '土', 9, 'n', '形声。从石,欠声。本义猛劈；刀砍不入。--《广东军务记》；又如砍柴禾;砍树;砍削(用刀、斧劈或削);砍杀(用刀斧杀人;拼杀);砍头(杀头);砍脍(细切鱼肉；镢头举得挺高,可是落到地上,砍不了两寸深。--《吕梁英雄传》');
INSERT INTO `chinese_infos` VALUES (19407, '砒', '土', 9, 'n', '1.砷的旧称。2.砒霜。');
INSERT INTO `chinese_infos` VALUES (19408, '垓', '土', 9, 'n', '1.古代指一万万。2.垓下（Gāixià），古地名，在今安徽灵璧东南。项羽在这里被围失败。');
INSERT INTO `chinese_infos` VALUES (19409, '宥', '土', 9, 'y', '宽恕；原谅');
INSERT INTO `chinese_infos` VALUES (19410, '砘', '土', 9, 'n', '播种后，用石砘子把松土压实');
INSERT INTO `chinese_infos` VALUES (19411, '侮', '土', 9, 'n', '欺负，轻慢：～辱（使对方人格或名誉受到损害，蒙受耻辱）。～蔑（轻视，轻蔑）。欺～。不可～。[2]古代奴婢的贱称。');
INSERT INTO `chinese_infos` VALUES (19412, '型', '土', 9, 'n', '形声。从土，刑声。本义：铸造器物的模子。用木做的叫模，用竹做的叫范，用泥做的叫型');
INSERT INTO `chinese_infos` VALUES (19413, '砑', '土', 9, 'n', '用卵石或弧形的石块碾压或摩擦皮革、布匹等，使密实而光亮');
INSERT INTO `chinese_infos` VALUES (19414, '埯', '土', 11, 'n', '1.挖小坑点种瓜、豆等。2.点种时挖的小坑。3.量词。用于点种的瓜、豆等植物');
INSERT INTO `chinese_infos` VALUES (19415, '囿', '土', 9, 'n', '1.养动物的园地2.局限；拘泥');
INSERT INTO `chinese_infos` VALUES (19416, '祐', '土', 9, 'n', '同“佑”。');
INSERT INTO `chinese_infos` VALUES (19417, '翁', '土', 10, 'y', '汉字的基本字义是老头儿，如“老翁”，“渔翁，“塞翁失马，焉知非福”。明王明敏《烟客题跋·题自画关使君袁环中》：“环（袁枢，号环中）翁使君，既工盘礴，又富收藏。”');
INSERT INTO `chinese_infos` VALUES (19418, '轩', '土', 10, 'y', '其含义有：指古代一种有围棚或帷幕的车；有窗的长廊或小屋等。《说文》记载有：轩，曲輈藩车。');
INSERT INTO `chinese_infos` VALUES (19419, '准', '土', 10, 'y', '从水、隼声。谓水之平也。天下莫平於水。水平谓之准。因之制平物之器亦谓之准。；准本为凖的俗字,现为凖的简化字。但在未简化前,准习用的意义,与凖字有别。；公文用语。始于唐·五代；表示允许,认可');
INSERT INTO `chinese_infos` VALUES (19420, '峡', '土', 10, 'y', '韵部【平水韵】：属入声十七洽部。【中华新韵】：属一麻阳平（二声）部。【词林正韵】：属第十九部十五合十七洽通用。【词韵简编】：属十九部，与词林正韵同。参考：百度或下载均可。');
INSERT INTO `chinese_infos` VALUES (19421, '峨', '土', 10, 'n', '高');
INSERT INTO `chinese_infos` VALUES (19422, '埃', '土', 10, 'n', '形声。从土,矣声。本义尘土;灰尘；埃,尘也。--《说文》；埃,谓风扬尘也。--《苍颉篇》；灰尘曰埃。--《通俗文》；野马也,尘埃也,生物之以息相吹也。--《庄子·逍遥游》；埃不漫。--《列子·黄帝》；溘埃风余上征。--《离骚》；风淫所胜,则太虚埃昏。--《素问·至真要大论》；上食埃土,下饮黄泉。--《荀子·劝学》');
INSERT INTO `chinese_infos` VALUES (19423, '宴', '土', 10, 'y', '指太阳下山，月亮尚未升起的时段，但是宴指的的正午或者下午·“宀”为“家”或者“宅”。“宀”与“妟”联合起来表示“在正午与妻子共进餐”。本义：与妻子安逸的共进午餐。转义：在正午时段安逸聚餐。');
INSERT INTO `chinese_infos` VALUES (19424, '晏', '土', 10, 'y', '1.迟2.同“宴”。');
INSERT INTO `chinese_infos` VALUES (19425, '恩', '土', 10, 'y', '从因从心。用心感受到的最大的范围；好处，深厚的情谊：～爱。～赐。～宠（指帝王对臣下的优遇和宠幸）。～德。～典。～惠。～仇。感～。开～。');
INSERT INTO `chinese_infos` VALUES (19426, '娓', '土', 10, 'y', '1.〔～～〕形容谈话不倦或说话具有吸引力，如“～～而谈”、“～～动听”。2.顺从。');
INSERT INTO `chinese_infos` VALUES (19427, '敖', '土', 10, 'y', '1.同“遨”。2.（Áo）同“隞”。3.姓。');
INSERT INTO `chinese_infos` VALUES (19428, '盎', '土', 10, 'y', '1.古代一种腹大口小的器皿。2.洋溢；盛（shèng）');
INSERT INTO `chinese_infos` VALUES (19429, '育', '土', 10, 'y', '是含义是养子使作善也。同毓。甲骨文字形,象妇女生孩子。');
INSERT INTO `chinese_infos` VALUES (19430, '容', '土', 10, 'y', '包容，宽容；打扮，装饰之意。');
INSERT INTO `chinese_infos` VALUES (19431, '益', '土', 10, 'y', '会意。小篆字形。象器皿中有水漫出。从皿,从水。水已隶变。本义溢的本字。水漫出；益,饶也。--《说文》。按,从水,浮于四,会意。字亦作溢。；引申为水涨；澭水暴益。--《吕氏春秋》；在某些方面增加(如体积、数量、程度等；损益,盛衰之始也。--《易·杂卦传》；人或益之,人或损之,胡可得而法?--《吕氏春秋·察今》；有所广益。--诸葛亮《出师表》；斟酌损益。；大有所益。--《资治通鉴·汉纪》');
INSERT INTO `chinese_infos` VALUES (19432, '彧', '土', 10, 'y', '有文采。');
INSERT INTO `chinese_infos` VALUES (19433, '殷', '土', 10, 'y', '有三个读音、；是会意字，由㐆（反身的人）和殳左右组合而成，㐆也表音，表示乐舞的人拿着舞蹈器具舞动。本意为盛大、多。引申表示人数众多、情感深厚、生活富足等。此外殷还表示热情、周到。另外殷是商朝迁都后的都城，所以商朝也称殷商。亦可作为姓氏用字。殷又形容雷声，读作；殷还；指红黑色。');
INSERT INTO `chinese_infos` VALUES (19434, '埔', '土', 10, 'n', '地名用字大埔（Dàbù），地名，在广东。');
INSERT INTO `chinese_infos` VALUES (19436, '员', '土', 10, 'y', '作姓氏时；作增加、表示语气或者在古人名字之中时，常；');
INSERT INTO `chinese_infos` VALUES (19437, '城', '土', 10, 'y', '会意。从土,从成,成亦声。本义城墙；城,所以盛民也。--《说文》；城郭沟池以为固。--《礼记·礼运》；使老弱女子乘城。--《史记·田单列传》；百雉而城。--《公羊传·定公十二年》。注天子周城,诸侯轩城。；城为保民为之也。--《谷梁传·隐公七年》；城者,可以自守也。--《墨子·七患》；青山横北郭,白水绕东城。--唐·李白《送友人》');
INSERT INTO `chinese_infos` VALUES (19438, '袁', '土', 10, 'y', '汉字\"袁\"有三个意思，一个是作为名词姓氏讲，就是\"袁姓\"的意思；二是作为地理名词讲，如\"袁州\"、\"袁河\"；此外，袁还可以作形容词讲，意思为\"长衣的样子\"');
INSERT INTO `chinese_infos` VALUES (19439, '砩', '土', 10, 'n', '〔～石〕又作“氟石”，一种矿物，即萤石。1.用石头拦水。2.堤坝');
INSERT INTO `chinese_infos` VALUES (19440, '啊', '土', 10, 'y', '在句末，表示感叹，惊叹，惊讶，疼痛的语气（常因前面字音不同而发生变音，可用不同的字来表示）。');
INSERT INTO `chinese_infos` VALUES (19441, '肫', '土', 10, 'n', '1.禽类的胃（亦称“胗”）2.诚恳、真挚1.古代祭祀所用牲畜后体的一部分。2.古同“纯”，整体，全。3.精细致密');
INSERT INTO `chinese_infos` VALUES (19442, '砥', '土', 10, 'n', '1.细的磨刀石。2.磨炼');
INSERT INTO `chinese_infos` VALUES (19443, '蚜', '土', 10, 'n', '一种害虫，生在豆类、棉花、菜类、稻、麦等的幼苗上，吸食嫩芽的汁液，能分泌一种甜液，古名竹虫，今称木虱，通称“蚜虫”。');
INSERT INTO `chinese_infos` VALUES (19444, '阢', '土', 10, 'n', '〔阢陧〕危险和不安的样子。陧（niè）。');
INSERT INTO `chinese_infos` VALUES (19445, '砧', '土', 10, 'n', '捶或砸东西时垫在底下的器具，有铁的（砸钢铁材料时用）、石头的（捶衣物时用）、木头的（即砧板）。');
INSERT INTO `chinese_infos` VALUES (19446, '胭', '土', 10, 'n', '〔胭脂〕 一种红色颜料。多作化妆 用品， 也作国画的颜料。');
INSERT INTO `chinese_infos` VALUES (19447, '砟', '土', 10, 'n', '某些坚硬的块状物');
INSERT INTO `chinese_infos` VALUES (19448, '砸', '土', 10, 'n', '打，捣。指重物掉落在物体上，或用重物撞击或者指敲打、为某物投入大量资源等等。');
INSERT INTO `chinese_infos` VALUES (19450, '軎', '土', 10, 'n', '古代车上的零件，青铜制，形如圆筒，套在车轴的两端。軎上有孔，用以纳辖。亦作“轊”。');
INSERT INTO `chinese_infos` VALUES (19451, '芺', '土', 10, 'n', '古书上说的一种草，亦称“苦芺”，嫩苗可食用。');
INSERT INTO `chinese_infos` VALUES (19452, '砝', '土', 10, 'n', '〔砝码〕在天平、磅秤上用作称量物品时衡定重量的标准。用金属制成。');
INSERT INTO `chinese_infos` VALUES (19453, '唉', '土', 10, 'n', '是一个常见叹词，也可指叹息的声音，组词有唉声叹气。基本释义唉1.叹词，应人声。2.叹息的声音：～声叹气。唉叹词，表示伤感或惋惜：～，病了几天，把事都耽误了。');
INSERT INTO `chinese_infos` VALUES (19454, '氧', '土', 10, 'n', '属于常用字。指一种气体元素，无色、无味、无臭，分布于大气中。在本义项名作为汉字描述。');
INSERT INTO `chinese_infos` VALUES (19455, '恙', '土', 10, 'n', '病');
INSERT INTO `chinese_infos` VALUES (19456, '唔', '土', 10, 'n', '象声词。1.叹词。表示疑问。2.方言。我。3.方言。你。同“嗯”');
INSERT INTO `chinese_infos` VALUES (19457, '砰', '土', 10, 'n', '象声词，形容撞击或重物落地的声音，例如砰地一声，一块陨石坠落在地。');
INSERT INTO `chinese_infos` VALUES (19458, '鹌', '土', 10, 'n', '〔鹌鹑〕鸟类。形似小鸡，头小，尾短秃。背褐色，杂有棕白色条纹，腹白色。雄性好斗。');
INSERT INTO `chinese_infos` VALUES (19459, '按', '土', 10, 'n', '字义：用手或手指压、止住、依照、考查、研求等意思。');
INSERT INTO `chinese_infos` VALUES (19460, '砹', '土', 10, 'n', '非金属元素，符号At（astatium）。有放射性，自然界分布极少。可用作示踪剂。');
INSERT INTO `chinese_infos` VALUES (19461, '峭', '土', 10, 'y', '山又高又陡：陡峭。峭壁。峻峭。峭立。峭丽（.陡峭而多姿；.文辞遒劲华美）。峭拔。形容严峻：峭寒。峭急。峭劲。峭厉。');
INSERT INTO `chinese_infos` VALUES (19462, '埏', '土', 10, 'n', '用水和（huó）土；和泥。1.边际；边远的地方。2.墓道。');
INSERT INTO `chinese_infos` VALUES (19463, '俺', '土', 10, 'n', '北方方言，意思为“我、我们”（不包括听说话的一方）。向对方说“俺”，则指自己或自己这边，但不包括对方，不完全等同于“我们”。这个词在北方普遍存在，并且在全国范围内被广泛应用，流行区域以北方方言区为主，声调高低略有不同。');
INSERT INTO `chinese_infos` VALUES (19464, '邘', '土', 10, 'n', '周朝国名，在今河南沁阳西北。');
INSERT INTO `chinese_infos` VALUES (19465, '迂', '土', 10, 'n', '有两个读音，词语组合多样同时意义也较多。常用词语有：迂腐、迂缓、迂回、迂见等。');
INSERT INTO `chinese_infos` VALUES (19466, '氤', '土', 10, 'n', '〔氤氲〕也作 缊。气体（云烟等）很盛的样子。');
INSERT INTO `chinese_infos` VALUES (19467, '破', '土', 10, 'n', '形声。从石,皮声。本义石头开裂;破碎,碎裂；破,石碎也。--《说文》；石破天惊逗秋雨。--李贺《李凭箜篌引》；沉船、破甑。--《史记·项羽本纪》；卵破子死。--《荀子·劝学》；秦王恐其破璧。--《史记·廉颇蔺相如列传》；又如爆破(用炸药炸毁);破瓦(古代一种占卜方法。使瓦碎裂,观其纹理以定吉凶祸福);破卵倾巢(碎裂鸟卵,倾覆鸟巢);破剥(破裂剥落);破坠(破裂坠落);破溃(破裂崩溃);破；块(敲碎土块；破坏;损坏；破,坏也。--《广雅》；淫破义疏。--《左传·隐公三年》');
INSERT INTO `chinese_infos` VALUES (19468, '蚓', '土', 10, 'n', '该字与蚯组词”蚯蚓”，蚯蚓环节动物，身体细长柔软，生活于土中，以带有机物的土壤为食，使土壤疏松，对农作物有益。可作鱼和家禽的食饵，可入药。亦称“曲蟮”、“地龙”。');
INSERT INTO `chinese_infos` VALUES (19469, '案', '土', 10, 'n', '古称案几，本义是指木制的盛食物的矮脚托盘，亦指长形的桌子或架起来代替桌子用的长木板。');
INSERT INTO `chinese_infos` VALUES (19470, '砌', '土', 10, 'n', '本义：建筑时垒砖石，用泥灰粘合。 衍义：引申指“台阶”。 ');
INSERT INTO `chinese_infos` VALUES (19471, '酏', '土', 10, 'n', '酏剂');
INSERT INTO `chinese_infos` VALUES (19472, '埕', '土', 10, 'n', '1.指蛏（chēng）田。2.坛子。');
INSERT INTO `chinese_infos` VALUES (19473, '堍', '土', 10, 'n', '桥两头靠近平地的地方');
INSERT INTO `chinese_infos` VALUES (19474, '眙', '土', 10, 'n', '用于地名，如盱（xū）眙（在江苏）。1.〈书〉2.直视；注视。3.惊视。');
INSERT INTO `chinese_infos` VALUES (19475, '砣', '土', 10, 'n', '1.秤砣。2.碾砣。3.用砣子打磨玉器');
INSERT INTO `chinese_infos` VALUES (19476, '峬', '土', 10, 'n', '〔峬峭〕山石玲珑奇巧。也形容人物或文笔有风致。');
INSERT INTO `chinese_infos` VALUES (19477, '砬', '土', 10, 'n', '〔～子〕方言，岩石，如“石～子”。石药。石头碰撞声。');
INSERT INTO `chinese_infos` VALUES (19478, '埒', '土', 10, 'n', '1.同等；（相）等2.指矮墙、田埂、堤防等');
INSERT INTO `chinese_infos` VALUES (19479, '眢', '土', 10, 'n', '1.眼球枯陷无光。2.干枯');
INSERT INTO `chinese_infos` VALUES (19480, '埋', '土', 10, 'n', '把东西放在坑里用土、雪、落叶盖上。或指隐藏。如“掩埋”“埋葬”。指因为事情不如意而对人或事物表示不满、责怪。如“埋怨”。');
INSERT INTO `chinese_infos` VALUES (19481, '砷', '土', 10, 'n', '非金属元素，符号As，原子序数33。由于晶体结构不同呈现黄、灰、黑褐三种颜色。砷加在铅或黄铜中可改变它们的硬度。砷的化合物均有毒，可用于杀菌、杀虫等。砷与镓、铟等元素组成的化合物是半导体材料。');
INSERT INTO `chinese_infos` VALUES (19482, '埂', '土', 10, 'n', '田间的土垄');
INSERT INTO `chinese_infos` VALUES (19483, '窕', '土', 11, 'n', '1.细2.有空隙3.美好妖艳，轻挑');
INSERT INTO `chinese_infos` VALUES (19484, '埭', '土', 11, 'y', '坝（多用于地名）');
INSERT INTO `chinese_infos` VALUES (19485, '岗', '土', 11, 'y', '高起的土坡：黄土岗。2值勤、守卫的地方，也指职位：岗位；站岗；下岗职工。【岗位】；原指军警守卫的处所，现泛指职位：坚守工作岗位等之意。');
INSERT INTO `chinese_infos` VALUES (19486, '婉', '土', 11, 'y', '屈伸貌；卷曲貌和顺貌柔美；美好；委婉，有形容女子温柔美好之意。服注：“婉,顺也。”；婉软（柔和的样子）；婉艳（柔顺华美）；婉嫕（婉静。温顺娴静）；婉容（和顺的仪容）；婉悦（恭顺和悦）；婉弱（和顺谦恭）；');
INSERT INTO `chinese_infos` VALUES (19487, '堂', '土', 11, 'y', '形声。从土,尚声。尚有高义。本义殿堂。高于一般房屋,用于祭献神灵、祈求丰年；堂,殿也。--《说文》。段注古曰堂,汉以后曰殿。古上下皆称堂,汉上下皆称殿。至唐以后,人臣无有称殿者矣。；殿,堂之高大者也。--《说文古本考》；立于西堂。--《书·顾命》；吾见封之若堂者矣。--礼记·檀弓》。注堂形四方而高。；王坐于堂上。--《孟子·梁惠王上》；燕坐于华堂之上。--明·刘基《诚意伯刘文成公文集》；观其坐高堂。--明·刘基《卖柑者言》');
INSERT INTO `chinese_infos` VALUES (19488, '唯', '土', 11, 'y', '本义：同“惟”，表示独一无二、独此。衍义：又用作姓');
INSERT INTO `chinese_infos` VALUES (19489, '帷', '土', 11, 'n', '围起来作遮挡用的帐子');
INSERT INTO `chinese_infos` VALUES (19491, '崞', '土', 11, 'y', '〔崞阳镇〕地名。在山西北部。原为崞县，1958年撤销。');
INSERT INTO `chinese_infos` VALUES (19492, '培', '土', 11, 'y', '有为保护植物或墙堤等，在根基部分加土等意思。');
INSERT INTO `chinese_infos` VALUES (19493, '崎', '土', 11, 'y', '形容山路不平，形声。从山，奇声。本义：崎岖，地面高低不平的样子。');
INSERT INTO `chinese_infos` VALUES (19494, '伟', '土', 11, 'y', '从人从韦，形容“与凡人相背，遵循大规则行动”的人。伟，本义是“高大”、“壮美”、“奇特”。《说文》：“伟，奇也。”《三国志·蜀书·诸葛亮传》：“身长八尺，容貌甚伟。”引申为“伟大”、“才识卓越”。“伟”也可形容事物。如“伟服”、“伟辞”。');
INSERT INTO `chinese_infos` VALUES (19495, '基', '土', 11, 'y', '形声。字从土，从其，其亦声。“其”意为“一系列等距排列的直线条”。“土”指夯土层。“土”与“其”联合起来表示“夯土层剖面像一系列等距排列的直线条”。本义：叠加的夯土层。承重用的夯土地面。（申明：的译音字见词条“基（网络用语）”）');
INSERT INTO `chinese_infos` VALUES (19496, '尉', '土', 11, 'y', '读时，有尉（单姓），尉迟（复姓），尉犁（新疆县名）。读时，古官名，如太尉。也有尉官的意思。');
INSERT INTO `chinese_infos` VALUES (19497, '胡', '土', 11, 'y', '牛顄垂也。从肉古声。中国古代称北边的或西域的民族：胡人。胡服。胡姬（西域出生的少女）。胡越（“胡”在北方；“越”在南方，喻疏远、隔绝）。胡本是匈奴的自称。匈奴使者递单于书：“南有大汉，北有强胡”');
INSERT INTO `chinese_infos` VALUES (19498, '娅', '土', 11, 'y', '见〖姻娅〗。');
INSERT INTO `chinese_infos` VALUES (19499, '悠', '土', 11, 'y', '◎ 久，远，长：～久。～远。～扬。田字格中的“悠”字田字格中的“悠”字◎ 在空中摆动：～荡。晃～。转～。◎ 稳住，控制：～着点劲。◎ 闲适，闲散：～闲。～然。～忽（形容悠闲懒散）。～缓。～～（a．闲适，自由自在，如“白云～～”；b．忧郁，如“～～我思”；c．长久，遥远，如“～～长夜”；d．众多；e．荒谬，如“～～之谈”）。');
INSERT INTO `chinese_infos` VALUES (19500, '庸', '土', 11, 'y', '从庚从用。以秋之实作变动去用、而达到（可以、行）的结果。先秦对某些地位较低的劳动者的一种称呼。战国之间，与社会政治、经济的变革适应，“庸”所指的对象也发生了变化。在战国时期的史料里，“庸”有时作“佣”（佣），多指雇佣劳动者。功绩，《新唐书；02卷令狐德棻传》中“先烈世庸”一词，指先祖世世代代的功绩。平常；平凡、能力低。如平庸。无所作为的需要：无庸细说，无庸讳言。庸人自扰，自己搅乱自己。形容原本没事，却要自找麻烦。庸俗，平庸鄙俗。厚有中庸说法，乃指不偏之谓中,不易之谓庸，庸在此作为“不改变保持原样，延续之前”之意');
INSERT INTO `chinese_infos` VALUES (19501, '迎', '土', 11, 'y', '接：迎接；迎送；欢迎；辞旧迎新。2面向着；正对着：迎面；迎头赶上；迎风招展。');
INSERT INTO `chinese_infos` VALUES (19502, '闫', '土', 11, 'y', '姓。注');
INSERT INTO `chinese_infos` VALUES (19503, '寅', '土', 11, 'y', '1.地支的第三位。2.寅时，旧式记时法，相当于三点到五点。');
INSERT INTO `chinese_infos` VALUES (19504, '翌', '土', 11, 'y', '次于今日、今年的');
INSERT INTO `chinese_infos` VALUES (19505, '异', '土', 11, 'y', '指有不同的、特别的意思。');
INSERT INTO `chinese_infos` VALUES (19506, '移', '土', 11, 'y', '释义：；挪动：移动；移居；迁移；转移；愚公移山。2改变；变动：移风易俗；潜移默化；坚定不移。移动：改变原来的位置。成语：移风易俗：改变旧的风俗习惯。移花接木：把一种花木嫁接到另一种花木上，比喻暗中更换人或物，以假乱真。移植：；把幼苗从一处移到另一处栽种，使幼苗获得更好的生长环境。2将身体的某一器官（或某一部分）补在同一机体或另一机体的缺陷部分上，使它逐渐长好。');
INSERT INTO `chinese_infos` VALUES (19507, '野', '土', 11, 'y', '形声。从里,予声。《尔雅·释言》里,邑也。本义郊外;野外；野,郊外也。--《说文》；邑外谓之郊,郊外谓之野。--《说文》。段注；龙战于野。--《易·坤》；王朝至于商郊牧野。--《书·牧誓》；七月在野。--《诗·豳风·七月》；体国经野。--《周礼》。注谓一百里以外三等采地之中。；永州之野产异蛇。--柳宗元《捕蛇者说》；捐金于野。--《后汉书·列女传》；野茫茫。--《乐府诗集·杂歌谣辞·敕勒歌》；光照旷野。--明·魏禧《大铁椎传》');
INSERT INTO `chinese_infos` VALUES (19508, '焉', '土', 11, 'y', '1.跟介词“于”加代词“是”相当2.疑问代词。哪里；怎么（多用于反问）3.乃；于是4.表示肯定的语气5.姓。');
INSERT INTO `chinese_infos` VALUES (19509, '欲', '土', 11, 'y', '从谷从欠。取慕液、虚受之意，同浴。感於物而动，性之欲也。欲而当於理，则为天理。欲而不当於理，则为人欲。欲求适可斯已矣，非欲之外有理也。古有欲字，无欲字，後人分别之，制欲字，殊乖古义也。今贪下作欲物也，亦是浅人增字所为。');
INSERT INTO `chinese_infos` VALUES (19510, '勖', '土', 11, 'y', '勉励');
INSERT INTO `chinese_infos` VALUES (19511, '峥', '土', 11, 'y', '〔峥嵘〕形容高峻。比喻超越寻常，不平凡');
INSERT INTO `chinese_infos` VALUES (19512, '牾', '土', 11, 'n', '违背；不顺从');
INSERT INTO `chinese_infos` VALUES (19513, '捂', '土', 11, 'n', '用双手遮住脸，或者是重要部位');
INSERT INTO `chinese_infos` VALUES (19514, '崃', '土', 11, 'n', '邛崃（Qiónglái），山名，又地名，都在四川。');
INSERT INTO `chinese_infos` VALUES (19515, '埠', '土', 11, 'y', '指停船的码头，靠近水的地方（古时亦作“步”）；旧与外国通商的城市：开～、商～。');
INSERT INTO `chinese_infos` VALUES (19516, '啪', '土', 11, 'n', '形容放枪、拍掌或东西撞击等声音');
INSERT INTO `chinese_infos` VALUES (19517, '硐', '土', 11, 'n', '山洞、窑 洞或矿坑。');
INSERT INTO `chinese_infos` VALUES (19518, '鸟', '土', 11, 'n', '象形。甲骨文字形,小篆作字形,都象鸟形。本义飞禽总名；鸟,长尾禽总名也。象形。--《说文》；飞鸟遗之音。--《易·小过》；我则鸣鸟不闻。--《书·君奭》；有鸟高飞。--《诗·小雅·菀柳》；鸟乃去矣。--《诗·大雅·生民》；兽奔鸟窜。--宋·苏轼《教战守》');
INSERT INTO `chinese_infos` VALUES (19519, '硭', '土', 11, 'n', '〔硭硝〕同“芒硝”');
INSERT INTO `chinese_infos` VALUES (19520, '埝', '土', 11, 'n', '田里或浅水里用来挡水的土埂');
INSERT INTO `chinese_infos` VALUES (19521, '岽', '土', 11, 'n', '岽罗（Dōngluó），地名，在广西。');
INSERT INTO `chinese_infos` VALUES (19522, '硒', '土', 11, 'n', '非金属元素，符号Se（selenium）。灰色、红色晶体或红色无定形粉末。晶体硒能够导电，导电能力随光照强度的增减而改变。是一种半导体材料，可用来制造光电池等。');
INSERT INTO `chinese_infos` VALUES (19523, '硎', '土', 11, 'n', '1.磨刀石2.磨制。');
INSERT INTO `chinese_infos` VALUES (19524, '堀', '土', 11, 'n', '1.古同“窟”，洞穴。2.穿穴3.冲起');
INSERT INTO `chinese_infos` VALUES (19525, '眺', '土', 11, 'n', '眺望');
INSERT INTO `chinese_infos` VALUES (19526, '崤', '土', 11, 'n', '崤山，也叫崤陵。山名。在河南西部。');
INSERT INTO `chinese_infos` VALUES (19527, '堾', '土', 11, 'n', '地边上用石块垒起来的挡土的墙。');
INSERT INTO `chinese_infos` VALUES (19528, '崆', '土', 11, 'n', '崆峒（Kōngtóng），山名，在甘肃。又岛名，在山东。');
INSERT INTO `chinese_infos` VALUES (19529, '埽', '土', 11, 'n', '1.治河时用来护堤堵口的器材，用树枝、秫秸、石头等捆扎而成。2.用秫秸修成的堤坝或护堤。古同“扫”，打扫。');
INSERT INTO `chinese_infos` VALUES (19530, '崮', '土', 11, 'n', '四周陡峭，顶上较平的山（多用于地名）');
INSERT INTO `chinese_infos` VALUES (19531, '堋', '土', 11, 'n', '1.分水堤（中国战国时李冰修建都江堰时所创建）。2.射击瞄准用的土墙。丧葬下土。');
INSERT INTO `chinese_infos` VALUES (19532, '眭', '土', 11, 'n', '1.姓。2.目光深注的样子');
INSERT INTO `chinese_infos` VALUES (19533, '迕', '土', 11, 'n', '1.相遇2.违背；不顺从');
INSERT INTO `chinese_infos` VALUES (19534, '垩', '土', 11, 'n', '1.白垩。2.用白垩涂饰。3.施（肥）。');
INSERT INTO `chinese_infos` VALUES (19535, '崩', '土', 11, 'n', '形声。从山,朋声。本义山倒塌；梁山崩。--《左传·成公五年》；又如山崩地裂；崩裂;倒塌；中间力拉崩倒之声,火爆声,呼呼风声,百千齐作。--《虞初新志·秋声诗自序》；又如崩拆(倒塌断裂);崩陷(倒塌陷落);崩陨(塌陷);崩损(崩塌损坏);崩坠(倒塌坠落；古代把天子的死看得很重,常用山塌下来比喻,由此从周代开始帝王死称崩；故临崩寄臣以大事也。--诸葛亮《出师表》；越二月,帝崩。--《明史·海瑞传》');
INSERT INTO `chinese_infos` VALUES (19536, '牵', '土', 11, 'y', '指可牵走的牲畜如牛羊猪等。');
INSERT INTO `chinese_infos` VALUES (19537, '崨', '土', 11, 'n', '〔～嶫（yè）〕（山）高峻。');
INSERT INTO `chinese_infos` VALUES (19538, '崛', '土', 11, 'n', '1.突起；兴起2.〔崛峍〕形容山崖突起。峍（lù）。');
INSERT INTO `chinese_infos` VALUES (19539, '畦', '土', 11, 'n', '田园中分成的小区：畦田、菜畦。古代称田五十亩为一畦。');
INSERT INTO `chinese_infos` VALUES (19540, '硌', '土', 11, 'n', '触着凸起的东西觉得不舒服或受到损伤山上的大石。');
INSERT INTO `chinese_infos` VALUES (19541, '埤', '土', 11, 'n', '增加。〔埤堄〕城墙上的矮墙，即女墙。');
INSERT INTO `chinese_infos` VALUES (19542, '堆', '土', 11, 'n', '土垖成堆。〈名〉；象形。从土,隹声。本义:土堆。；土墩,沙墩或水中聚集的礁石累积在一起的东西；累积在一起；聚积在一起；量词，用于成堆的物或成群的人.一般指2个以上。在计算机科学中，堆是一种数据结构。');
INSERT INTO `chinese_infos` VALUES (19543, '茔', '土', 11, 'n', '坟地');
INSERT INTO `chinese_infos` VALUES (19544, '蚰', '土', 11, 'n', '〔蚰蜒〕节肢动物。像蜈蚣而小，全身分15节，每节有一对细长的足，末对足特别长。生活在阴湿的地方。蜒（yán）。');
INSERT INTO `chinese_infos` VALUES (19545, '哑', '土', 11, 'n', '形声。从口，亚声。本义：笑声；乌鸦之类的叫声；婴儿学说话的声音。受某些生理缺陷或疾病的影响而不能说话。常和聋联系在一起，称为聋哑。这类患者不一定一点残余听力都不剩，但是，凡语言紧要听力区的听力完全损失者，即不能接受一般人类语言频率的声响刺激，都可能发展为哑。');
INSERT INTO `chinese_infos` VALUES (19546, '痖', '土', 11, 'n', '同“哑（yǎ）”。');
INSERT INTO `chinese_infos` VALUES (19547, '讶', '土', 11, 'n', '讶⑵（形声。从言，从牙，牙亦声。“牙”指人的上下前排齿，与人类语言功能密切相关。“言”与“牙”联合起来表示“开口说话”、“露齿而笑”。本义：边说边笑地迎接客人。引申义：迎接。）⑶讶，相迎也。从言，牙声。周礼曰，诸侯有卿讶发。按，乍接必以言，故从言，俗字作迓。周官有讶士，有掌讶。――东汉·许慎《说文》跛者讶跛者。――《公羊传·成公二年》从者讶受皮。――《仪礼·公食大夫礼》。注：“迎也。”厥明，讶宾于馆。――《仪礼·聘礼》');
INSERT INTO `chinese_infos` VALUES (19548, '迓', '土', 11, 'n', '迎接');
INSERT INTO `chinese_infos` VALUES (19549, '垭', '土', 11, 'n', '（又）两山之间的狭窄地方。多用于地名（又）两山之间的狭窄地方。多用于地名');
INSERT INTO `chinese_infos` VALUES (19550, '庵', '土', 11, 'y', '闇的俗字作庵或萩；草圆屋谓之庵。--《释名·释宫室》；庵,舍也。--《广雅》；亲人萩庐。--《后汉书·皇甫规传》；居河之湄,结划为庵。--《神仙传·焦先传》。；又如庵舍(设在墓旁的草屋);庵庐(草舍；尼姑的住所；一定另外或村庄或尼庵寻个下处。--《红楼梦》');
INSERT INTO `chinese_infos` VALUES (19551, '唵', '土', 11, 'n', '1.把手里握着的颗粒状或粉末状的东西塞进嘴里2.表示疑问3.佛教咒语用字。');
INSERT INTO `chinese_infos` VALUES (19552, '崦', '土', 11, 'n', '〔崦嵫〕山名。在甘肃。嵫（zī）。');
INSERT INTO `chinese_infos` VALUES (19553, '蚴', '土', 11, 'n', '某些寄生蠕虫（如绦虫、血吸虫等）的幼体');
INSERT INTO `chinese_infos` VALUES (19554, '恿', '土', 11, 'n', '见  〔怂恿〕');
INSERT INTO `chinese_infos` VALUES (19556, '狺', '土', 11, 'n', '〔狺狺〕拟声词。狗叫声。');
INSERT INTO `chinese_infos` VALUES (19557, '埸', '土', 11, 'n', '1.田间的界限。2.边境');
INSERT INTO `chinese_infos` VALUES (19558, '偃', '土', 11, 'n', '1.仰面倒下；放倒2.停止');
INSERT INTO `chinese_infos` VALUES (19559, '痍', '土', 11, 'n', '创伤');
INSERT INTO `chinese_infos` VALUES (19560, '崖', '土', 11, 'y', '即（崖姓）中国少数民族姓氏之一主要分布于：山东、广西、上海、云南等地。“崖”姓注释：“崖”乃僻姓也，自古有之，至早追溯及唐宋。清通志氏族略》：“西夏、吐蕃有厓氏；蜀有厓氏。”《中华万姓渊源》：“厓”氏当为吐蕃之后。《中国姓氏大全》记载：“崖”姓,罕见姓,原为吐蕃（唐朝时）和西夏（宋代时）姓氏,后裔流入内地。今南北均有此姓。山或高地陡立的侧面，另有崖州古城，即现海南三亚市崖城镇，位于三亚市西40多公里处，自南北朝起建制崖州，宋朝以来历代的州、郡、县治均设在这里。今天的崖城，以其悠久的历史和繁多的名胜古迹而成为海南旅游胜地。及崖州孔庙。崖（大陆旧读音，台湾正读音，大陆旧又读）；：山崖；悬崖。');
INSERT INTO `chinese_infos` VALUES (19561, '挨', '土', 11, 'n', '本义为以手击背。字义引申为靠近、拥挤、依次等。');
INSERT INTO `chinese_infos` VALUES (19562, '埴', '土', 11, 'n', '黏土。');
INSERT INTO `chinese_infos` VALUES (19563, '琊', '土', 11, 'n', '同“玡”。');
INSERT INTO `chinese_infos` VALUES (19564, '蛭', '土', 11, 'n', '环节动物的一大类，体一般长而扁平，无刚毛，前后各有一个吸盘。生活在淡水中或湿润的地方，大多为半寄生，如水蛭、医蛭、山蛭等。有的吸食人或动物的血液。通称蚂蟥。');
INSERT INTO `chinese_infos` VALUES (19565, '狳', '土', 11, 'n', '见〔犰狳〕');
INSERT INTO `chinese_infos` VALUES (19566, '域', '土', 11, 'y', '形声。从土,或声。土,表示疆土。“域”本作“或”,与“国”同义。“从口,从戈,以守一。一,地也。”为会意字。本义:疆界,疆域。；域之原义指范围，是语境可以随着认知不断扩展的词。后逐渐演变为数学、生物、科技等学科的某类单位的分类词语。');
INSERT INTO `chinese_infos` VALUES (19567, '堤', '土', 12, 'y', '用土石等材料修筑的挡水的高岸。');
INSERT INTO `chinese_infos` VALUES (19568, '诒', '土', 12, 'y', '1.传给2.赠与，给与欺诈');
INSERT INTO `chinese_infos` VALUES (19569, '胰', '土', 12, 'y', '是人体的第二大消化腺，在胃的后方，横行于腹后壁，横跨在第1.2腰椎的前面。（如图：十二指肠和胰），胰腺呈长条状，质地柔软，呈灰红或淡红色，可分为头、体、尾三部。胰头膨大位于右侧，位于第2腰椎体的右前方，被十二指肠环抱，胰腺管的末端穿入十二指肠壁，会合胆总管，开口于十二指肠乳头。内分泌腺是由散在于外分泌腺之间大小不同的细胞团──胰岛所组成。胰岛分泌的激素叫胰岛素，直接进入血液和淋巴，主要参与调节糖代谢。胰岛素分泌不足，可引起糖尿病。');
INSERT INTO `chinese_infos` VALUES (19570, '贻', '土', 12, 'y', '1.赠送2.遗留');
INSERT INTO `chinese_infos` VALUES (19571, '费', '土', 12, 'y', '形声。字从贝，从弗，弗亦声。“弗”意为“不平”。“贝”指钱币。“贝”与“弗”联合起来表示“因物价高企而多耗用钱财”。');
INSERT INTO `chinese_infos` VALUES (19573, '壹', '土', 12, 'y', '“一”的大写。常用于支票、银行票据等，以避免差错或涂改。壹的哲学本义指：专一纯粹、专精。《中华心法》阐述一：一者，谓专精也，用心一也，专于一境也。谓之不偏、不散、不杂、独不变也，道之用也。故君子执一而不失，人能一则心纯正，其气专精也；人贵取其一，至精、至专、至纯，大道成矣。此自然界生产力之不二法则。');
INSERT INTO `chinese_infos` VALUES (19574, '崴', '土', 12, 'y', '1.山路不平。2.崴子，山、水弯曲处。多用于地名，如海参崴（在俄罗斯）。3.同“𨂿”。〔崴嵬〕山高的样子。嵬（wéi）。');
INSERT INTO `chinese_infos` VALUES (19575, '为', '土', 12, 'y', '多音字，一般表示做（作为），表示为某人做某事。有时也表示被动。');
INSERT INTO `chinese_infos` VALUES (19576, '围', '土', 12, 'y', '有多种解释，常见意义为，环绕，四周拦挡起来；圈起来作拦阻或遮挡的东西。');
INSERT INTO `chinese_infos` VALUES (19577, '迤', '土', 12, 'y', '往；向（表示在某一方向上的延伸）');
INSERT INTO `chinese_infos` VALUES (19578, '奠', '土', 12, 'y', '象形。金文字形,上面是酋(即酒),下面象放东西的基物。本义设酒食以祭；。特指初死时的备供品敬礼；奠,置祭也。--《说文》；于以奠之。--《诗·召南·采蘯》；共其奠牛。--《周礼·牛人》。注丧所荐馈曰奠。；舍奠于其庙。--《礼记·祭统》。注非时不而祭曰奠。；故既奠。--《礼记·郊特牲》。注谓荐熟时也。；有司以几宴舍奠于其墓。--《礼记·檀弓》；奠汝又不见汝食。--清·袁牧《祭妹文》');
INSERT INTO `chinese_infos` VALUES (19579, '黄', '土', 12, 'y', '象形字。甲骨文象佩璜形。上为系，下为垂穗，中间为双璜并联状。黄姓是现今中国大陆第七大姓氏、台湾岛第三大姓氏，在《百家姓》中排名第96位。黄姓人口在广东省最多，占全国黄姓人口总数约；9%。根据；977年中国史学家李栋明在《东方杂志》发表的一篇有关“姓”的论文上指出，黄姓是全球华人十大姓之一。');
INSERT INTO `chinese_infos` VALUES (19580, '画', '土', 12, 'y', '界也,象田四界,聿所以画之。--《说文》；画为九州。--《左传·襄公四年》；画壄分州。--《汉书·地理志上》；又如画野分疆(划分疆域而治);画土分贡(中国古代实行分封制,天子划分封地给诸侯,诸侯定期向天子朝贡);画地(在地上画界线);画界(划定疆界；绘画;作画；为蛇画足。--清·全祖望《梅花岭记》；善画者多工书。--蔡元培《图画》');
INSERT INTO `chinese_infos` VALUES (19581, '岚', '土', 12, 'y', '山里的雾气');
INSERT INTO `chinese_infos` VALUES (19582, '咏', '土', 12, 'y', '咏的异体字，从言从永。经过时间与空间沉淀、源远流长的、值得称道的。');
INSERT INTO `chinese_infos` VALUES (19583, '嵋', '土', 12, 'y', '峨嵋（Éméi），山名，在四川。今作峨眉。');
INSERT INTO `chinese_infos` VALUES (19584, '粤', '土', 12, 'y', '最早出自甲骨文 [2]  ，本义不明 [3]  ，古与“聿”“越”“曰”通用，用于句首或句中，无意义，后用于广东的简称');
INSERT INTO `chinese_infos` VALUES (19585, '越', '土', 12, 'y', '一字词，姓，多义多用等。');
INSERT INTO `chinese_infos` VALUES (19586, '翔', '土', 12, 'y', '本义为翅膀平直不动盘旋地飞，字义解释：；（翔实）详细而确实。⑵盘旋地飞而不扇动翅膀。');
INSERT INTO `chinese_infos` VALUES (19587, '惟', '土', 12, 'y', '1.同“唯”。2.思想；思考3.文言助词。常用在年、月、日之前');
INSERT INTO `chinese_infos` VALUES (19588, '傲', '土', 12, 'y', '形声。从人,敖声。本义骄傲;傲慢无理；傲,倨也。--《说文》；经传以敖为之；凡视上于面则敖。--《礼记·曲礼》；彼交匪敖。--《诗·小雅·桑柔》；不吴不敖。--《诗·周颂·丝衣》；敖而无足数者。--《史记·游侠传》；执币,傲。--《左传·文公九年》；齐音傲辟乔志。--《礼记·乐记》；图傲救世之士哉。--《庄子·天下》；弟敬爱兄谓之悌,反悌为傲。--《贾子道术》；令尹甚傲而好兵,子必谨敬。--《韩非子·内储说下》；傲不可长,欲不可纵。--魏征《十三斩不克终疏》');
INSERT INTO `chinese_infos` VALUES (19589, '婺', '土', 12, 'n', '1.婺江，水名，就是乐安江的上游，在江西。2.指婺州（旧州名，在今浙江金华一带）');
INSERT INTO `chinese_infos` VALUES (19590, '砚', '土', 12, 'y', '形声。从石,见声。本义砚台,磨墨器；光滑的石头；砚,石滑也。--《说文》。段玉裁注谓石性滑利也。；砚,研也。研墨使和濡也。--《释名》；砚台；一砚。--清·周容《芋老人传》；砚冰坚。--明·宋濂《送东阳马生序》');
INSERT INTO `chinese_infos` VALUES (19591, '堡', '土', 12, 'y', '通常指军事上防守用的建筑物：堡垒、暗堡、地堡、城堡堡见于地名。通“铺”，驿站（今用于地名）：十里～，马家～。堡见于地名。古已有之，为转音：凤凰～。堡有城墙的村镇，泛指村庄（多用于地名）：～子，吴～，柴沟～，瓦窑～');
INSERT INTO `chinese_infos` VALUES (19592, '崽', '土', 12, 'n', '1.〈方〉儿子；小孩子。2.幼小的动物');
INSERT INTO `chinese_infos` VALUES (19593, '喑', '土', 12, 'n', '1.嗓子哑，不能出声；失音2.缄默，不做声');
INSERT INTO `chinese_infos` VALUES (19594, '堛', '土', 12, 'n', '土块。');
INSERT INTO `chinese_infos` VALUES (19595, '堙', '土', 12, 'n', '1.土山。2.堵塞。3.埋没。');
INSERT INTO `chinese_infos` VALUES (19596, '恶', '土', 12, 'n', '为中国常用词汇，经常表示不好的，凶狠的，对人和事的厌恶态度等意思。');
INSERT INTO `chinese_infos` VALUES (19597, '跖', '土', 12, 'n', '1.脚面上接近脚趾的部分。2.脚掌。3.践；踏。');
INSERT INTO `chinese_infos` VALUES (19598, '蛙', '土', 12, 'n', '形声。从虫,圭声。本义田鸡类动物。青蛙；…苏俗谓之田鸡,扬州谓之水鸡,亦曰吠蛤。--《说文》；听取蛙声一片。--宋·辛弃疾《西江月·夜行黄沙道中》；又如蛙市(夜晚蛙群聚集而鸣,称为蛙市);蛙吹(蛙鸣);蛙角(将头发束起,形状宛如青蛙头上左右凸出而对称的部分);蛙怒(形容青蛙叫声很大,有如鼓气发怒);蛙蛤(蛙的一种；蛙；淫邪；凹隐。扳着脸的样子；那陈经济把脸儿蛙着不言语。--《金瓶梅》');
INSERT INTO `chinese_infos` VALUES (19599, '猗', '土', 12, 'n', '1.美好盛大的样子。晉•陸雲•《高岡詩四首之一》2.助词。犹“兮”。相当于“啊”，用于句末，表示语气。《詩經•魏風•伐檀》3.叹词。常用于句首，表示赞叹。相当於“啊”。《詩經•齊風•猗嗟》4.姓。如春秋時魯有猗頓。1.加，超越。《詩經•小雅•巷伯》2.依靠。通“倚”。《詩經•小雅•車攻》3.牵引，束而採之。通“掎”。《詩經•豳風•七月》');
INSERT INTO `chinese_infos` VALUES (19600, '掖', '土', 12, 'n', '塞进；你来把我这边的被掖掖罢。--《红楼梦》；又如把被角掖好；藏；黛玉微微的点头,便掖在袖里。--《红楼梦》；插入腰带；说着,掖上那把刀,迈步出门,往外就走。--《儿女英雄传》；掖咕；你把我的围巾掖咕在哪儿了；掖；用手扶着别人的胳膊；掖,以手持人臂投地也。从手,夜声。--《说文》；二礼从国子巡城掖以赴外。--《左传·僖公二十五年》');
INSERT INTO `chinese_infos` VALUES (19601, '帏', '土', 12, 'n', '1.帐子；幔幕。2.古人佩带的香囊。');
INSERT INTO `chinese_infos` VALUES (19602, '喁', '土', 12, 'n', '鱼口向上，露出水面。应和的声音。');
INSERT INTO `chinese_infos` VALUES (19603, '堪', '土', 12, 'y', '能，可以，足以：不堪设想，堪当重任，堪以告慰，忍受，能支持：难堪。不堪一击。');
INSERT INTO `chinese_infos` VALUES (19604, '喹', '土', 12, 'n', '〔喹啉〕有机化合物。无色液体，有特殊臭味。用以制药品、染料等。啉（lín）。');
INSERT INTO `chinese_infos` VALUES (19605, '嵛', '土', 12, 'n', '昆嵛（Kūnyú），山名，在山东。');
INSERT INTO `chinese_infos` VALUES (19606, '堞', '土', 12, 'n', '城墙上形的矮墙');
INSERT INTO `chinese_infos` VALUES (19607, '塄', '土', 12, 'n', '田地边上的坡儿');
INSERT INTO `chinese_infos` VALUES (19608, '堠', '土', 12, 'n', '古代瞭望敌方情况的土堡。');
INSERT INTO `chinese_infos` VALUES (19609, '崾', '土', 12, 'n', '[崾崄]两山之间像马鞍子的地方，多用于地名');
INSERT INTO `chinese_infos` VALUES (19610, '晻', '土', 12, 'n', '〔～蔼〕ａ．昏暗不明，如“日～蔼以西迈。”ｂ．繁盛，如“华盖纷～蔼。”古同“暗”〔～～〕昏暗不明，如“日～～而下颓。”');
INSERT INTO `chinese_infos` VALUES (19611, '胺', '土', 12, 'n', '（俗读ān）有机化合物的一类，通式R—NH２。为氨分子里的氢原子被烃基取代后所形成的碱性物质。');
INSERT INTO `chinese_infos` VALUES (19612, '嵖', '土', 12, 'n', '嵖岈（Cháyá），山名，在河南。');
INSERT INTO `chinese_infos` VALUES (19613, '媕', '土', 12, 'n', '〔媕婀〕随声附和，没有主见的样子。');
INSERT INTO `chinese_infos` VALUES (19614, '埚', '土', 12, 'n', '见  〔坩埚〕');
INSERT INTO `chinese_infos` VALUES (19615, '垴', '土', 12, 'n', '山岗、丘陵较平的顶部（多用于地名）');
INSERT INTO `chinese_infos` VALUES (19616, '捱', '土', 12, 'n', '同“挨（ái）”。');
INSERT INTO `chinese_infos` VALUES (19617, '啽', '土', 12, 'n', '〔～呓〕说梦话。闭口不言。');
INSERT INTO `chinese_infos` VALUES (19618, '釉', '土', 12, 'n', '涂在陶瓷表面烧制而成的玻璃质薄层。能增加制品的光泽、机械强度，防止腐蚀。');
INSERT INTO `chinese_infos` VALUES (19619, '砗', '土', 12, 'n', '〔砗磲〕软体动物。两个贝壳很厚，略呈三角形，体长可达1.8米，重可达250千克。生活在热带海底。');
INSERT INTO `chinese_infos` VALUES (19620, '黑', '土', 12, 'y', '从囱从炎。火烧（炎）烟熏（囱）之色。〈形〉；会意。小篆字形,上面是古“囱”字,即烟囱;下面是“炎”(火)字,表示焚烧出烟之盛。合起来表示烟火熏黑之意。本义:黑色。；黑,火所熏之色也。——《说文》当作姓氏时，。小篆字形，上面是古“囱”字，即烟囱；下面是“炎”（火）字，表示焚烧出烟之意。合起来表示烟火熏黑之意。本义：黑色）。');
INSERT INTO `chinese_infos` VALUES (19621, '硪', '土', 12, 'n', '砸实地基或打桩等用的一种工具，通常是一块圆形石头或铁制的饼状物，周围系着几根绳子');
INSERT INTO `chinese_infos` VALUES (19622, '硖', '土', 12, 'n', '硖石（Xiáshí），地名，在浙江。');
INSERT INTO `chinese_infos` VALUES (19623, '硝', '土', 12, 'n', '是一种其体积（经过化学变化）能变小变细的矿石，作为火药的原料矿石。');
INSERT INTO `chinese_infos` VALUES (19624, '氩', '土', 12, 'n', '气体元素，符号Ar（argonium）。无色无臭无味，是大气中含量最多的稀有气体，化学性质很不活泼。放电时发出蓝色的光，在电弧焊接不锈钢、镁、铝等时用作保护气体，也用来填充灯管和灯泡。');
INSERT INTO `chinese_infos` VALUES (19625, '堰', '土', 12, 'y', '形声。字从土，从匽，匽亦声。“匽”意为“帝王退休”。“土”与“匽”联合起来表示“让水结束流淌，停下来休息的土坝”。本义：拦河蓄水大坝');
INSERT INTO `chinese_infos` VALUES (19626, '崵', '土', 12, 'n', '〔首～〕即“首阳山”。有多处，分别在今中国河北省卢龙县（今名阳山）、山西省永济县等。古同“砀”，山名。');
INSERT INTO `chinese_infos` VALUES (19627, '翕', '土', 12, 'n', '1.相合；和顺。2.合拢；收敛');
INSERT INTO `chinese_infos` VALUES (19628, '媪', '土', 12, 'n', '年老的妇女。');
INSERT INTO `chinese_infos` VALUES (19629, '奡', '土', 12, 'n', '1.同“傲”。2.〔排奡〕矫健有力。');
INSERT INTO `chinese_infos` VALUES (19630, '痦', '土', 12, 'n', '〔痦子〕皮肤上微微突起的黑痣。参见“痣”');
INSERT INTO `chinese_infos` VALUES (19631, '喂', '土', 12, 'n', '基本意思是把食物送进嘴里。');
INSERT INTO `chinese_infos` VALUES (19632, '犹', '土', 13, 'y', '意为相似，如同，尚且仍然，还等。此外还有犹姓，历史名人有犹道明，犹泳等。');
INSERT INTO `chinese_infos` VALUES (19633, '雍', '土', 13, 'y', '1.和谐。2.姓。');
INSERT INTO `chinese_infos` VALUES (19634, '块', '土', 13, 'n', '形声。从土，鬼声。字本作“凷”,是个会意字，表示土块装在筐器之中。后来写作“块”,变成了形声字，现在简化为“块”。本义：土块');
INSERT INTO `chinese_infos` VALUES (19635, '衙', '土', 13, 'n', '形声。从行,吾声。本义列队行进的样子；旧时官署之称；诸州县长官在公衙亦准此。--张昭远《旧唐书·仪卫志》；衙,衙府也。--《广韵》；衙门,本牙门之讹,《周礼》谓之旌门,郑氏司常注所云,巡狩兵车之会,皆建太常是也,其旗两边刻绘如牙状,故亦曰牙旗,后时因谓营门曰雅门。--《陔余丛考》；每日衙门虚寂,无复诉讼者。--《北齐书·宋世良传》');
INSERT INTO `chinese_infos` VALUES (19636, '握', '土', 13, 'n', '形声。从手,屋声。本义攥在手里,执持；握,搤持也。--《说文》；握,持也。--《广韵》；长尺有握。--《仪礼·乡射礼》；私握臣手。--《楚辞·九章·怀少》；怀瑾握瑜。--《楚辞·九章·怀沙》；手握刀。--清·邵长蘅《青门剩稿》；又如握君(如意的别称);握篆(手握官印);握算(执算筹以计数。亦指谋划);握镜(执持明镜);握节(持守符节。不辱君命);握炭流汤(握炽炭,蹈沸汤。不畏危难,敢于用命；屈指成拳；终日握而手不掜。--《庄子·庚桑楚》');
INSERT INTO `chinese_infos` VALUES (19637, '饮', '土', 13, 'n', '会意。甲骨文字形。右边是人形,左上边是人伸着舌头,左下边是酒坛(酉)。象人伸舌头向酒坛饮酒。小篆演变为飲,隶书作饮。本义喝；。有时特指喝酒；飮,飮也。--《说文》。今隶作饮。；君子以饮食宴乐。--《易·需》。虞注水流入口为饮。；饮酒浆饮,俟于东房。--《仪礼·公食礼》；冬日则饮汤,夏日则饮水。--《孟子·告子上》；子反受而饮之。--《韩非子·十过》；项王即日因留沛公与饮。--《史记》');
INSERT INTO `chinese_infos` VALUES (19638, '嵩', '土', 13, 'y', '1.山大而高。2.高。3.姓。');
INSERT INTO `chinese_infos` VALUES (19639, '诩', '土', 13, 'y', '说大话；夸耀');
INSERT INTO `chinese_infos` VALUES (19640, '阿', '土', 13, 'y', '作前缀或译音时；如“阿哥”“阿爸”；意为迎合、偏袒时,如“阿谀”“阿其所好”“刚正不阿”。');
INSERT INTO `chinese_infos` VALUES (19641, '晕', '土', 13, 'n', '昏倒；魏老儿上了一夹棍,贾魏氏上了一拶子,两个人都晕绝过去。--《老残游记》；又如晕绝(昏厥);头晕(头脑昏乱);晕昏(昏迷);晕迷(昏眩；晕；(形声。从日,军声。本义日月周围形成光圈；日月周围的光圈；晕,日月气也。--《说文新附》；晕,卷也。气在外卷结之也,日月俱然。--《释名》；晕适背穴。--《汉书·天文志》；日月晕适,云风,此天之客气,其发见亦有大运。--《史记》');
INSERT INTO `chinese_infos` VALUES (19642, '恽', '土', 13, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (19643, '裔', '土', 13, 'y', '1.后代子孙2.边远的地区');
INSERT INTO `chinese_infos` VALUES (19644, '奥', '土', 13, 'y', '室内的西南角，泛指房屋及其他深处隐蔽的地方：堂奥，经堂入奥。');
INSERT INTO `chinese_infos` VALUES (19645, '园', '土', 13, 'y', '形声。从囗,袁声。形符为囗,表示范围。本义种蔬菜、花果、树木的地方；园,所以树果也。--《说文》；种树曰园。--《三苍》；园圃毓草木。--《周礼·大宰》；以场圃任园地。--《周礼·载师》。注樊圃谓之园。；于丘园。--《易·贲》；园有桃。--《诗·魏风·园有桃》；青青园中葵。--《乐府诗集·长歌行》；田园将芜。--晋·陶渊明《归园田居》');
INSERT INTO `chinese_infos` VALUES (19646, '圆', '土', 13, 'y', '是一种几何图形，指的是平面中到一个定点距离为定值的所有点的集合。这个给定的点称为圆的圆心。作为定值的距离称为圆的半径。当一条线段绕着它的一个端点在平面内旋转一周时，它的另一个端点的轨迹就是一个圆。圆的直径有无数条；圆的对称轴有无数条。圆的直径是半径的2倍，圆的半径是直径的一半。用圆规画圆时，针尖所在的点叫做圆心，一般用字母表示。连接圆心和圆上任意一点的线段叫做半径，一般用字母表示，半径的长度就是圆规两个角之间的距离。通过圆心并且两端都在圆上的线段叫做直径，一般用字母表示。圆是平面上的曲线图形，是一个轴对称图形，它的对称轴是指经所在的直线，圆有无数条对称轴。20；5年；2月，中国印钞总公司回应新版百元钞上\"圆\"是错字。');
INSERT INTO `chinese_infos` VALUES (19647, '援', '土', 13, 'y', '有拉、引、援助之意。');
INSERT INTO `chinese_infos` VALUES (19648, '爱', '土', 13, 'y', '形声。从心,旡声。本义亲爱;喜爱；对人或事物有深厚真挚的感情；惠,爱也。--《尔雅》；爱施者,仁之端也。--《说苑·说丛》；君子自爱,仁之至也。--《法言》；爱亲者不敢恶于人。--《孝经》。沈宏曰亲至结心为爱。；爱共叔段,欲立之。--《左传·隐公元年》；停车坐爱枫林晚,霜叶红于二月花。--杜牧《山行》；父母之爱子也,则为之计深远。--《战国策》；公夫人甚爱女。--明·崔铣《记王忠肃公翱三事》；爱其子,择师而教之。--唐·韩愈《师说》；非疏骨肉爱过客。--《韩非子·五蠹》');
INSERT INTO `chinese_infos` VALUES (19649, '诣', '土', 13, 'y', '1.到某人所在的地方；到某个地方去看人（多用于所尊敬的人）2.（学业、技术等）所达到的程度');
INSERT INTO `chinese_infos` VALUES (19650, '嵊', '土', 13, 'y', '嵊州（Shèngzhōu），嵊泗（Shèngsì），地名，都在浙江。');
INSERT INTO `chinese_infos` VALUES (19651, '圣', '土', 13, 'y', '形声。从耳,呈声。甲骨文字形。左边是耳朵,右边是口字。即善用耳,又会用口。本义通达事理；圣,通也。--《说文》。按耳顺之谓圣。彼教所言耳根圆通亦此意。；听信之谓圣。--《管子·四时》；睿作圣。--《书·洪范》。传于事无不通谓之圣。；母氏圣善。--《诗·邶风·凯风》；而圣君治国累世而不见者,其所谓忠者不忠,而所谓贤者不贤也。--《史记·屈原贾生列传》');
INSERT INTO `chinese_infos` VALUES (19652, '嵬', '土', 13, 'y', '高大耸立');
INSERT INTO `chinese_infos` VALUES (19653, '塘', '土', 13, 'y', '意思是面积不大的池子。基本解释：堤岸，堤防：塘坝。塘堰。原意是挡水的土坝。水被“塘”挡住后形成了一个“池”，通称为池塘，有些地方简称“塘”，也称“水塘”等。');
INSERT INTO `chinese_infos` VALUES (19654, '琬', '土', 13, 'y', '一种无棱角的圭。');
INSERT INTO `chinese_infos` VALUES (19655, '意', '土', 13, 'y', '儒家谓人对事物的思想与情态和对事物的态度。先秦儒家非常重视意对外界事物的看法，认为人对事物与行为好坏的看法都是由意造成的。故汉刘向《说苑·修文》：“检其邪心，守其正意。”');
INSERT INTO `chinese_infos` VALUES (19656, '碑', '土', 13, 'n', '刻上文字纪念事业、功勋或作为标记的石头：石～。丰～。墓～。口～。～文。～碣。～刻（刻在碑上的文字或图画）。～拓。～帖。～林。里程～（．设于道路旁边用以记载里数的标志；．喻在历史发展进程中可以作为象征或标志的大事）。有口皆～。');
INSERT INTO `chinese_infos` VALUES (19657, '鄘', '土', 13, 'n', '周朝国名，在今河南新乡西南。');
INSERT INTO `chinese_infos` VALUES (19658, '猷', '土', 13, 'y', '计划；谋划');
INSERT INTO `chinese_infos` VALUES (19659, '揠', '土', 13, 'n', '拔');
INSERT INTO `chinese_infos` VALUES (19660, '蛹', '土', 13, 'n', '是指一些昆虫从幼虫变化到成虫的一种过渡形态。这个阶段只会在完全变态的昆虫出现，如蝴蝶及蛾（鳞翅目）、甲虫（鞘翅目）、苍蝇（双翅目）与蜂、黄蜂及蚂蚁（膜翅目）。这个阶段是在幼虫后及成虫前出现，成年昆虫的体形会在这个阶段生成，而幼虫的体形结构则会瓦解。大部份的蛹都是固定的（但蚊的蛹会游泳），且有着坚硬的保护外壳。蛹含有丰富的蛋白质，可食用。');
INSERT INTO `chinese_infos` VALUES (19661, '跬', '土', 13, 'n', '古称一举足（一脚向前迈出后着地）的距离为跬，两举足的距离为步');
INSERT INTO `chinese_infos` VALUES (19662, '瘀', '土', 13, 'n', '1.积血。2.血液不流通');
INSERT INTO `chinese_infos` VALUES (19663, '蜈', '土', 13, 'n', '形声。字从虫从吴，吴亦声。“吴”本义为“街头说唱艺人”，转义为“杂耍演员”、“杂耍表演”。“虫”与“吴”联合起来表示“一种像杂技团演员排着队伍一齐走路的虫”。本义：多脚虫。');
INSERT INTO `chinese_infos` VALUES (19664, '呜', '土', 13, 'n', '口中舒发而出的声音,叹息声。悲叹；及与公卿言国家事,未尝不噫呜流涕。--《后汉书·袁安传》');
INSERT INTO `chinese_infos` VALUES (19665, '佣', '土', 13, 'n', '为多音字，有和两种作时，多指雇用、受雇用，如佣耕；作时，多指买卖时给中间人的报酬，如佣金。');
INSERT INTO `chinese_infos` VALUES (19666, '袅', '土', 13, 'n', '柔软细长的样子');
INSERT INTO `chinese_infos` VALUES (19667, '嗡', '土', 13, 'n', '飞虫特有的持续刺耳声或很快的不规则的震动产生的声音；嗡像声词～～～飞机声。蚊子太多～～音不停。；嗡1.声音通过鼻腔发出，含糊不清。');
INSERT INTO `chinese_infos` VALUES (19668, '揞', '土', 13, 'n', '用手把药面儿或其他粉末敷在伤口上');
INSERT INTO `chinese_infos` VALUES (19669, '愔', '土', 13, 'n', '1.〔～～〕ａ．形容安静和悦；ｂ．形容静寂，深沉。2.〔～然〕形容沉默无声或安静。');
INSERT INTO `chinese_infos` VALUES (19670, '猬', '土', 13, 'n', '兽名。即刺猬；矢如猬毛。--宋·司马光《资治通鉴·唐纪》');
INSERT INTO `chinese_infos` VALUES (19671, '暗', '土', 13, 'n', '1.不亮，没有光，与“明”相对 2.不公开的，隐藏不露的 3.愚昧，糊涂');
INSERT INTO `chinese_infos` VALUES (19672, '坞', '土', 13, 'y', '小障蔽物，防卫用的小堡；水边建筑的停船或修造船只的地方；地势周围高中间凹的地方');
INSERT INTO `chinese_infos` VALUES (19673, '塬', '土', 13, 'y', '我国西北黄土高原地区因流水冲刷而形成的一种地貌，呈台状，四周陡峭，顶上平坦。');
INSERT INTO `chinese_infos` VALUES (19674, '稚', '土', 13, 'y', '表示幼小的意思，孩童、幼苗的意思。');
INSERT INTO `chinese_infos` VALUES (19675, '轾', '土', 13, 'n', '见  〔轩轾〕');
INSERT INTO `chinese_infos` VALUES (19676, '碇', '土', 13, 'n', '系船的石礅（现 用铁锚）');
INSERT INTO `chinese_infos` VALUES (19677, '碓', '土', 13, 'n', '舂米用具，用柱子架起一根木杠，杠的一端装一块圆形的石头，用脚连续踏另一端，石头就连续起落，去掉下面石臼中的糙米的皮。简单的碓只是一个石臼，用杵捣米。');
INSERT INTO `chinese_infos` VALUES (19678, '埙', '土', 13, 'n', '吹奏乐器。多用陶土烧制而成，也有木、骨或石制的，多为上小下大的鸡蛋形，有一至十几个音孔。');
INSERT INTO `chinese_infos` VALUES (19679, '硼', '土', 13, 'n', '三价高熔点非金属元素。硼非金属元素之一。有非结晶和结晶的两种。非结晶的为褐色粉末，结晶的为有光泽的灰色晶体，硬度与金刚石相近。硼的化合物在医药﹑农业﹑玻璃工业等方面用途很广。');
INSERT INTO `chinese_infos` VALUES (19680, '氲', '土', 13, 'n', '见〔氤氲〕');
INSERT INTO `chinese_infos` VALUES (19681, '痱', '土', 13, 'n', '〔～子〕由于暑天出汗过多，引起汗腺发炎，皮肤表面生出来的小红疹，很痒。古代称偏瘫症');
INSERT INTO `chinese_infos` VALUES (19682, '碘', '土', 13, 'n', '非金属元素，元素符号I，元素周期表53号元素，在化学元素周期表中位于第5周期，系ⅦA族，卤族元素之一。');
INSERT INTO `chinese_infos` VALUES (19683, '话', '土', 13, 'n', '本义：说出来的能表达思想感情的声音，亦指把这种声音记录下来的文字。衍义：引申指“说，谈论” ');
INSERT INTO `chinese_infos` VALUES (19684, '碱', '土', 13, 'n', '1、含在土里的一种物质，化学成分是碳酸钠，用做洗涤剂，亦用来中和发面中的酸味：碱土。碱化。碱荒。2、化学上称能在水溶液中电离而生成氢氧离子（ＯＨ－）的化合物，如氢氧化钠等。溶液具有涩味，能使石蕊试纸变蓝，能跟酸中和而生成盐：碱金属。碱式盐。碱性岩。');
INSERT INTO `chinese_infos` VALUES (19685, '嵴', '土', 13, 'n', '山脊。');
INSERT INTO `chinese_infos` VALUES (19686, '睚', '土', 13, 'n', '眼角。');
INSERT INTO `chinese_infos` VALUES (19687, '碉', '土', 13, 'n', '军事上防守用的建筑物，多用砖、石、钢筋混凝土等建成。');
INSERT INTO `chinese_infos` VALUES (19688, '觟', '土', 13, 'n', '1.有角的母羊。2.姓。〔～𧣾〕古同“獬豸”，传说中的神羊。');
INSERT INTO `chinese_infos` VALUES (19689, '塑', '土', 13, 'n', '指的是柔软、非流质、可任意变形的性质，也可指呆愣的模样。');
INSERT INTO `chinese_infos` VALUES (19690, '琙', '土', 13, 'n', '古人名用字。');
INSERT INTO `chinese_infos` VALUES (19691, '矮', '土', 13, 'n', '从矢从委。形声，从矢,委声。本义是身材短。(古汉语多用短,少用矮)英文是。');
INSERT INTO `chinese_infos` VALUES (19692, '嗌', '土', 13, 'n', '咽喉被食物等塞住。咽喉。');
INSERT INTO `chinese_infos` VALUES (19693, '蜒', '土', 13, 'n', '【名】(形声。从虫,延声。本义:蜒蚰,虫名),又名“蛞蝓”、“鼻涕虫”；。蛞蝓科。形似去壳蜗牛,有两对触角,身体分泌粘液,爬行后常留下银白色的条痕。是农作物的害虫。如:蚰蜒(节肢动物,像蜈蚣而略小)【动】蜿蜒,龙蛇爬行；南有炎火千里,蝮蛇蜒只。——《楚辞》又如:蜒蜒(龙蛇之类曲折爬行的样子);蜒蜿(蜿蜒。龙蛇曲折爬行的样子)');
INSERT INTO `chinese_infos` VALUES (19694, '揶', '土', 13, 'n', '〔揶揄〕耍笑；嘲弄。揄（yú）。');
INSERT INTO `chinese_infos` VALUES (19695, '琟', '土', 13, 'n', '鸟名。似玉的美石。');
INSERT INTO `chinese_infos` VALUES (19696, '嗷', '土', 13, 'n', '拟声词。哀号或喊叫的声音');
INSERT INTO `chinese_infos` VALUES (19697, '碚', '土', 13, 'n', '用于地名，如北碚（在重庆）。');
INSERT INTO `chinese_infos` VALUES (19698, '塌', '土', 13, 'n', '因凹陷而倒塌；塌,堕也。--《广雅》；天崩地塌壮士死。--吴士玉《玉带生歌奉和漫堂先生》；忽忆雨时秋井塌。--杜甫《苏端薛复筵简薛华醉歌》');
INSERT INTO `chinese_infos` VALUES (19699, '廒', '土', 13, 'n', '收藏粮食的仓库');
INSERT INTO `chinese_infos` VALUES (19700, '嶅', '土', 13, 'n', '〔嶅阳〕地名。在山东。形容山高。');
INSERT INTO `chinese_infos` VALUES (19701, '爷', '土', 13, 'n', '今义两种：全国大多数地方指祖父或祖父级男子，有些地方指父亲或父亲级男子。在粤语中可以指伯爷。');
INSERT INTO `chinese_infos` VALUES (19702, '嵫', '土', 13, 'n', '见〔崦嵫〕');
INSERT INTO `chinese_infos` VALUES (19703, '碎', '土', 13, 'n', '指完整的东西破坏成零片或零块。');
INSERT INTO `chinese_infos` VALUES (19704, '填', '土', 13, 'n', '一般是指把空缺的地方塞满或补满，如填塞，填充。');
INSERT INTO `chinese_infos` VALUES (19705, '碗', '土', 13, 'n', '一种圆形凹心盛食品的器具,在形式上通常接近半球形,一般比盘子深,比茶杯大和重；芙蓉玉碗,莲子金杯。--北周·庾信《春赋》；又如饭碗;瓷碗;搪瓷碗;摆碗筷;碗泥(烧制瓷器的粘土);碗窑(烧制瓷器的窑；像碗的东西；碗；用来计量用碗装的饮食；滑出出,水冷冷,两碗来素匾食。--明·朱有《豹子和尚自还俗》；宋朝以来灯笼一盏叫一碗；正劝不开,只见两三碗灯笼飞也似来。--《水浒全传》');
INSERT INTO `chinese_infos` VALUES (19706, '揖', '土', 13, 'n', '古代的拱手礼。如：揖让（宾主相见的礼节）、揖别（作揖告别）等，不同于抱拳。');
INSERT INTO `chinese_infos` VALUES (19707, '饴', '土', 13, 'n', '饴糖，以含有淀粉的物质为原料经糖化和加工制得。饴糖含有麦芽糖、葡萄糖及糊精，味甜爽口，可供制糕点、糖果等。');
INSERT INTO `chinese_infos` VALUES (19708, '猥', '土', 13, 'n', '1.卑鄙；下流2.杂');
INSERT INTO `chinese_infos` VALUES (19709, '艉', '土', 13, 'n', '船体的尾部。');
INSERT INTO `chinese_infos` VALUES (19710, '痿', '土', 13, 'n', '中医指身体某一部分萎缩或失去功能的病，例如下痿、阳痿等。');
INSERT INTO `chinese_infos` VALUES (19711, '塾', '土', 14, 'y', '旧时私人设立的教学的地方');
INSERT INTO `chinese_infos` VALUES (19712, '踊', '土', 14, 'y', '汉字。有往上跳的意思，出自《广雅》。');
INSERT INTO `chinese_infos` VALUES (19713, '与', '土', 14, 'y', '赐予也。一勺为与。”会意字，以一、勺示意，勺即酌，以物与人不问所欲，当斟酌而与之。与的本义为“赐予”，为实词。虚词“与”是假借字，可作介词、连词和副词。作连词的“与”，从先秦至现在没有大的变化。介词“与”，唐宋以后又产生了新义，如“使”“将”等义，但这些新义在现代汉语中又消失了。副词“与”是“举”的通假字，也是多音字。');
INSERT INTO `chinese_infos` VALUES (19714, '诱', '土', 14, 'n', '字义是劝导，教导；使用手段引人：也做称美之辞：“诱然与日月争光”。');
INSERT INTO `chinese_infos` VALUES (19715, '监', '土', 14, 'y', '象形字。象人俯身低头向盘中水面照看状。甲金文「监」象人在水盤裏照看自己的容颜，故「监」的本义是察看，这种盤子後来叫做「鉴」。本义是临水照看自己的容颜，又表示器名，指盛水映照面颜的容器。後期金文「目」形和人身分别被改为「臣」和「人」旁，并排写在盛水的「皿」的上面，为小篆字形所本。楷书「人」旁变为「一短撇连接一横」，下面保留象水的一点。古代官署名称(其主管亦称“监”或“少监”)。如：牧马监；钦天监；国子监。');
INSERT INTO `chinese_infos` VALUES (19716, '瑕', '土', 14, 'y', '玉上面的斑点，比喻缺点');
INSERT INTO `chinese_infos` VALUES (19717, '境', '土', 14, 'y', '境字可解释为疆界，地方，状况的意思。可组词为境界，处境，境况。');
INSERT INTO `chinese_infos` VALUES (19718, '玮', '土', 14, 'y', '1.玉名。2.珍奇；贵重');
INSERT INTO `chinese_infos` VALUES (19719, '鸢', '土', 14, 'y', '老鹰');
INSERT INTO `chinese_infos` VALUES (19720, '祎', '土', 14, 'y', '美好（多用于人名）。');
INSERT INTO `chinese_infos` VALUES (19721, '硕', '土', 14, 'y', '是形声字，页为形，石为声；旧。本意指头大，后泛指为大。');
INSERT INTO `chinese_infos` VALUES (19722, '逵', '土', 14, 'y', '四通八达的大路。');
INSERT INTO `chinese_infos` VALUES (19723, '误', '土', 14, 'y', '谬误;错误；误,谬也。--《说文》；群臣议皆误。--《史记·萧相国世家》；兴尽晚归舟,误入藕花深处。--李清照《如梦令》；误落尘网中。--晋·陶渊明《归田居》；土木之误。--清·张廷玉《明史》；又如笔误;误错(失误差错);误证(错误的证明);误本(有错字的版本);误谬(谬误;差错；误；耽误；郑以救公误之,遂失秦伯。--《左传·僖公十五年》；又如误却(耽误掉;失掉；妨害；专欲误将军。--宋·司马光《资治通鉴》');
INSERT INTO `chinese_infos` VALUES (19724, '维', '土', 14, 'y', '此字始见于西周金文，形声字，古字形从糸隹声。维的本义是系物的大绳。作动词表示系、联结，由系、联结引申为保全、保持。古代又常用作语气词，放于句首或居中。');
INSERT INTO `chinese_infos` VALUES (19725, '崭', '土', 14, 'y', '形声。从山,斩声。双音词崭岩。本义山高而险峻的样子；崭岩嵾嵯。--《史记·司马相如传》；豨谷崭岩兮水曾波。--《楚辞·招隐士》；仰视所登之处,崭然在云汉。--《徐霞客游记》');
INSERT INTO `chinese_infos` VALUES (19726, '翟', '土', 14, 'y', '姓。1.古书上指长尾的野鸡。2.古代用作舞具的野鸡的羽毛。3.姓。');
INSERT INTO `chinese_infos` VALUES (19727, '瑛', '土', 14, 'y', '1.玉的光彩。2.像玉的美石。');
INSERT INTO `chinese_infos` VALUES (19728, '嶂', '土', 14, 'y', '直立像屏障的山峰');
INSERT INTO `chinese_infos` VALUES (19729, '墅', '土', 14, 'y', '指的是田庐；村舍，田间土舍，例如剧哉边海民，寄身于草墅——曹植《泰山梁甫行》。');
INSERT INTO `chinese_infos` VALUES (19730, '夤', '土', 14, 'y', '1.深2.攀附');
INSERT INTO `chinese_infos` VALUES (19731, '温', '土', 14, 'y', '为汉语言法定规范简化字，基本字音读：，一声。基本意义有不冷不热、性情柔和等。');
INSERT INTO `chinese_infos` VALUES (19732, '嫣', '土', 14, 'y', '鲜艳；美好');
INSERT INTO `chinese_infos` VALUES (19733, '墉', '土', 18, 'y', '1.城墙。2.高墙。');
INSERT INTO `chinese_infos` VALUES (19734, '旖', '土', 14, 'y', '〔旖旎〕柔和美丽。旎（nǐ）。');
INSERT INTO `chinese_infos` VALUES (19735, '壸', '土', 14, 'n', '古时皇宫里的路。');
INSERT INTO `chinese_infos` VALUES (19736, '墋', '土', 14, 'n', '1.沙土。2.混浊不清3.食品中混入沙土。');
INSERT INTO `chinese_infos` VALUES (19737, '腋', '土', 14, 'n', '指胳肢窝。');
INSERT INTO `chinese_infos` VALUES (19738, '砜', '土', 14, 'n', '硫酰基（）与烃基结合而成的有机化合物。用作制塑料的原料。[英sulfone]');
INSERT INTO `chinese_infos` VALUES (19739, '墒', '土', 14, 'n', '土壤适合种子发芽和作物生长的湿度');
INSERT INTO `chinese_infos` VALUES (19740, '墚', '土', 14, 'n', '我国西北地区称条状的黄土山岗。');
INSERT INTO `chinese_infos` VALUES (19741, '瑀', '土', 14, 'y', '像玉的石头。');
INSERT INTO `chinese_infos` VALUES (19742, '獒', '土', 14, 'n', '狗的一种，身体大，尾巴长，四肢较短，毛黄褐色。凶猛善斗，可做猎狗。');
INSERT INTO `chinese_infos` VALUES (19743, '垫', '土', 14, 'n', '《说文解字》：“下也。《春秋传》曰：“垫隘。”从土、执声。”。”。抓捕了罪人避免灾难是执之范式。土、执两范式叠加。执土以填陷是垫范式。');
INSERT INTO `chinese_infos` VALUES (19744, '碳', '土', 14, 'n', '一种主要呈四价的非金属元素,存在于自然界中(如以金刚石和石墨形式),是煤、石油、沥青、石灰石和其他碳酸盐以及一切有机化合物的成分；碳化学元素之一。有三种同素异形体，即金刚石﹑石墨和非结晶碳。碳的化学性质在常温下很穏定。碳是构成有机物的主要成分，在工业上和医药上有广泛的用途。');
INSERT INTO `chinese_infos` VALUES (19745, '闺', '土', 14, 'n', '形声。字从门，从圭，圭亦声。“圭”本指“土圭”、“圭表”，是古代中央王朝的标准计时器，位于王宫附近。引申为“土中”。再引申为“内部”。“门”与“圭”联合起来表示“位于宅院中央的门”。本义：位于宅院中央的门。内门。转义：上圆下方的小门。小门。说明：古代“六瑞”之一的“玉圭”上圆下方，“闺”可能模仿了玉圭的形状。');
INSERT INTO `chinese_infos` VALUES (19746, '碟', '土', 14, 'n', '盛食物等的器具，扁而浅，比盘子小。');
INSERT INTO `chinese_infos` VALUES (19747, '猿', '土', 14, 'n', '形声。从犬,袁声。猿似犬,故从犬。本义灵长类动物,形态与猴相似；蝯善援,禺属。--《说文》。通常写作猨、猿。；貉逾汶则死。--《考工记·总目》。注貉或为猨。谓善缘木之猨也。；毋教猱升木。--《诗·小雅·角弓》。传猱,猨属。；猿狙之便自山林来。--《庄子·天地》；猿之所以寿者,好引其末,是故气四越。--汉·董仲舒《春秋繁露》');
INSERT INTO `chinese_infos` VALUES (19748, '冤', '土', 14, 'n', '词语。常用于表示“有冤屈”');
INSERT INTO `chinese_infos` VALUES (19749, '碣', '土', 14, 'n', '圆顶的石碑〔～磍〕猛兽盛怒的样子。');
INSERT INTO `chinese_infos` VALUES (19750, '寤', '土', 14, 'n', '1.睡醒。2.同“悟”。');
INSERT INTO `chinese_infos` VALUES (19751, '碥', '土', 14, 'n', '1.在水旁斜着伸出来的山石。2.山崖险峻地方的登山石级。');
INSERT INTO `chinese_infos` VALUES (19753, '呕', '土', 14, 'n', '口为形，区为声，区也表意。“区”本指“装满食物的容器”，转指“肚子”。“口”与“区”联合起来表示“食物装得太多，满到了嘴里”意思是吐。有女子方跪，据树而呕丝北海外也。——《博物志·异人》，也指饮食、痰涎从胃中上涌，自口而出。古代文献多以有声无物为呕，有物无声为吐，有物有声为呕。现一般不区分，而将有声无物者，称为是“干呕”。');
INSERT INTO `chinese_infos` VALUES (19754, '碲', '土', 14, 'n', '非金属元素，符号 Te （tellurium）。银白色晶体或棕灰色粉末。碲和一些碲的化合物是半导体材料，金属或合金中加入碲，可以改善它们的性能。');
INSERT INTO `chinese_infos` VALUES (19755, '岖', '土', 14, 'n', '是形声字，山为形，区为声。。“崎岖”是双声连绵词，形容山路不平的样子。');
INSERT INTO `chinese_infos` VALUES (19756, '顼', '土', 14, 'n', '见  〔颛顼〕');
INSERT INTO `chinese_infos` VALUES (19757, '墟', '土', 14, 'n', '1.原来许多人家居住过而现在已经荒废了的地方2.村庄3.同“圩（xū）”。');
INSERT INTO `chinese_infos` VALUES (19758, '碰', '土', 14, 'n', '1.撞击2、相遇3、试探，如碰运气。(形声。从石,并声。本义:两物相触或相撞)');
INSERT INTO `chinese_infos` VALUES (19759, '诞', '土', 14, 'n', '其本义为说大话，即《说文解字》：“诞，词诞也”。后引申为说谎话、欺诈、荒唐、放荡等，又引申为出生、生日');
INSERT INTO `chinese_infos` VALUES (19760, '堑', '土', 14, 'n', '1.防御用的壕沟，护城河2.陷坑，亦喻挫折3.挖掘古同“錾”。古通“渐”，平缓。');
INSERT INTO `chinese_infos` VALUES (19761, '肮', '土', 14, 'n', '形声字。“骨”与“亢”联合起来表示“管状骨”。本义指管状骨。骨管。');
INSERT INTO `chinese_infos` VALUES (19762, '嘘', '土', 14, 'n', '1.慢慢地吐气2.叹气3.火或蒸气的热力接触到物体4.表示制止、驱逐等，一般用shī，也写作嘘。5.发出“嘘”（xū）的声音来制止或驱逐表示制止、驱逐等');
INSERT INTO `chinese_infos` VALUES (19763, '殒', '土', 14, 'n', '丧失（生命）；死亡');
INSERT INTO `chinese_infos` VALUES (19764, '塝', '土', 14, 'n', '田边土坡；沟渠或土埂的边 （多用于地名）');
INSERT INTO `chinese_infos` VALUES (19765, '碴', '土', 14, 'n', '剃后残余或复生的短毛发;胡子丛生的样子；碴；小碎块；器物破口上的残缺部分；崭新黑漆的车,把头折了一段,秃碴碴的露着两块白木碴儿,非常的不调和,难看。--老舍《骆驼祥子》');
INSERT INTO `chinese_infos` VALUES (19766, '腌', '土', 14, 'n', '本义：用盐擦抹肉块的办法制作咸肉。引申义：用盐、糖、酱液浸渍蔬菜、肉类的办法制作咸菜、咸肉）');
INSERT INTO `chinese_infos` VALUES (19767, '诬', '土', 14, 'n', '基本释义是说假话冤枉别人。');
INSERT INTO `chinese_infos` VALUES (19768, '嵝', '土', 14, 'n', '岣嵝（Gǒulǒu），山名，衡山主峰，也指衡山，在湖南。');
INSERT INTO `chinese_infos` VALUES (19769, '墁', '土', 14, 'n', '用砖或石块等铺地面');
INSERT INTO `chinese_infos` VALUES (19770, '愠', '土', 14, 'n', '含怒；怨恨');
INSERT INTO `chinese_infos` VALUES (19771, '叶', '土', 15, 'y', '形声。从苃,枽声。植物的叶子。叶,同协,会意字,从十从口。本是两个字。本义草木之叶；叶,草木之叶也。--《说》；其叶为胡蝶。--《列子·天瑞》。注散也。；枝叶未有害。--《诗·大雅·荡》；又如叶尖(植物学名词。叶片的尖端部位);叶序(植物学名词,叶在茎上的排列方式称为叶序);叶芽(植物学名词。与花芽相对。发育后成为茎、枝及叶的芽);叶基(植物学名词；。叶片的下端靠近叶柄的部分；比喻轻小、轻飘像叶子的东西；万里风波一叶舟。--李商隐《无题》');
INSERT INTO `chinese_infos` VALUES (19772, '卫', '土', 15, 'y', '卫的古字形像四足环绕着城邑，本义为守卫。引申指守卫者，如门卫、侍卫。另引申指边远的地方。卫也指古代九服之一，或五服之一，等等。又为周朝国名，又作姓氏用字。');
INSERT INTO `chinese_infos` VALUES (19773, '磁', '土', 15, 'n', '形声。从石,兹声。本义磁石。也指磁性；。也为石名,即磁石,俗称吸铁石；瓷的俗字。指瓷器；地名；磁；紧密');
INSERT INTO `chinese_infos` VALUES (19774, '废', '土', 15, 'n', '停止，不再使用。荒芜，衰败等');
INSERT INTO `chinese_infos` VALUES (19775, '墩', '土', 15, 'y', '释义：；土堆：土墩。2厚而粗的石块、木头或建筑物基础等：石墩；桥墩。；3.量词，用于丛生的或几棵合在一起的植物：几墩草；一墩稻秧。④姓氏。');
INSERT INTO `chinese_infos` VALUES (19776, '缘', '土', 15, 'y', '原因：缘故；缘由；无缘无故；只缘身在此山中。2命运：喻为命运的丝线。；3.边：边缘；外缘。④沿着，顺着：缘江而下；缘溪而行；缘木求鱼。⑤发生联系的机会：缘分；人缘；血缘。');
INSERT INTO `chinese_infos` VALUES (19777, '增', '土', 15, 'y', '基本词义为加多，如：增添，增加。');
INSERT INTO `chinese_infos` VALUES (19778, '娴', '土', 15, 'y', '1.熟练2.文静；稳重');
INSERT INTO `chinese_infos` VALUES (19779, '纬', '土', 15, 'y', '编织：～萧（用蒿草编成帘子）。');
INSERT INTO `chinese_infos` VALUES (19780, '坟', '土', 15, 'n', '形声。从土,贲声。本义:大防。；指坟墓坟土的意思，来源于“坟，墓也。――《说文》。按，此字本训大防也。坟，冢。秦晋之间谓之坟。――《方言十三》。注：“坟取名于大防也。”古也，墓而不坟。――《礼记·檀弓》。注：“土之高者谓之坟。”');
INSERT INTO `chinese_infos` VALUES (19782, '院', '土', 15, 'y', '是形声字，阝（阜）为形，完为声。；本意指围墙。后指有围墙的院子、房屋。此外，院在旧时也指官署。在现代有些组织、政府机构也叫院，如“法院”。');
INSERT INTO `chinese_infos` VALUES (19783, '阅', '土', 15, 'y', '是个简化字，门部，音同“越”、“月”，意和读相近，但内含审核之意，也作经历讲。');
INSERT INTO `chinese_infos` VALUES (19784, '养', '土', 15, 'y', '本义：抚育，供给生活品。衍义：引申指“饲养动物，培植花草”。');
INSERT INTO `chinese_infos` VALUES (19785, '影', '土', 15, 'y', '形声。从彡,景声。彡,毛饰花边形。本义影子;因挡住光线而投射的暗影；影,形影。--《广韵》；犹表之与影。--《吕氏春秋·功名》；吾日悠悠惭于影。--《淮南子·修务》；字本作景；二子乘舟,汛汛其影。--《诗·邶风·二子乘舟》。疏景,指舟影。；罔两问于景。--《淮南子·道应》；正日景以求地中。--《周礼·大司徒》。释文景,本或作影。；影布石上,佁然不动。--唐·柳宗元《至小丘西小石潭记》');
INSERT INTO `chinese_infos` VALUES (19786, '嶙', '土', 15, 'n', '〔～峋〕a.山石一层层的重叠不平，如“～峋的巨石”；b.形容人瘦削，如“瘦骨～峋”。');
INSERT INTO `chinese_infos` VALUES (19787, '欧', '土', 15, 'y', '形声字，欠为形，区为声。欧的本意指呕吐；此意后用“呕”表示。在现代汉语中，欧主要用于作姓氏，也做音译字用，也可以指代欧洲。');
INSERT INTO `chinese_infos` VALUES (19788, '邮', '土', 15, 'n', '指传递文书的人在路上的停宿处，引申指递送函件者，又引申为递送，用作动词。简体“邮”亦见于战国文字，古字形从邑、由声。据《说文》，古代有个亭子叫邮亭，故址在今陕西高陵，这个“邮”繁体文本仍作“邮”，');
INSERT INTO `chinese_infos` VALUES (19789, '鞍', '土', 15, 'n', '形声。从革,安声。本义马鞍),放在骡马等背上供人骑坐或载物的器具。；鞌,鞁具也。--《说文》；令皆下马解鞍。--《汉书·李广传》；投鞌高如城者数所。--《汉书·韩安国传》；汉王下马踞鞍而问。--《史记·留侯世家》');
INSERT INTO `chinese_infos` VALUES (19790, '亿', '土', 15, 'y', '指数目，一万万，古代指十万。亿万（泛指极大的数目），如亿万斯年（形容无限长远的年代）。');
INSERT INTO `chinese_infos` VALUES (19791, '确', '土', 15, 'y', '1.真实，实在 2.坚固，固定');
INSERT INTO `chinese_infos` VALUES (19792, '峤', '土', 15, 'y', '1.尖而高的山。2.山道。1.尖而高的山。2.山道。');
INSERT INTO `chinese_infos` VALUES (19793, '逸', '土', 15, 'y', '一般是指超凡脱俗，卓尔不群：～轩，～志。也可以指安闲，安乐：安～。逸，过也。');
INSERT INTO `chinese_infos` VALUES (19794, '磊', '土', 15, 'y', '石头多。');
INSERT INTO `chinese_infos` VALUES (19795, '余', '土', 15, 'y', '上下结构，形声；从人、舍省声。有很多种意思，作为字义解释，有我；剩下来的，多出来的；农历四月的别称；姓氏等义项。');
INSERT INTO `chinese_infos` VALUES (19796, '慰', '土', 15, 'y', '使人心里安适；心安。');
INSERT INTO `chinese_infos` VALUES (19798, '暮', '土', 15, 'y', '形声。从日,莫声。古字作莫,像太阳落到草丛中,表示天将晚。暮为后起字。本义日落时,傍晚；暮者,晚也。--《论衡·明雩》；朝三而暮四。--《庄子·齐物论》；恐美人之迟暮。--《楚辞·离骚》；旦辞爷娘去,暮宿黄河边。--《乐府诗集·木兰诗》；今朝至,暮变。--《韩非子·难一》；至莫夜月光明。--宋·苏轼《石钟山记》；暮投石壕村,有吏夜捉人。--唐·杜甫《石壕吏》');
INSERT INTO `chinese_infos` VALUES (19799, '热', '土', 15, 'y', '形声。本义温度高；。跟冷相对；肺甚畏热。--《素问·五常变大论》；清暖寒热,不得不救。--《韩非子·有度》；如水益深,如火益热--《孟子·梁惠王下》；立厩中仆马之间,……即饥寒毒热不可忍,不去也。--明·宗臣《报刘一丈书》；此不为近者热而远者凉乎。--《列子·汤问》');
INSERT INTO `chinese_infos` VALUES (19800, '磐', '土', 15, 'y', '大石头');
