INSERT INTO `chinese_infos` VALUES (20749, '鸳', '土', 16, 'y', '（～鸯），水鸟，比鸭小，栖息于池沼之上，雌雄常在一起。汉族民间传说和文学上用来喻夫妻；又用来称成偶的东西，如“～～剑”。简称“鸳”，如“～侣”（喻夫妻）。');
INSERT INTO `chinese_infos` VALUES (20750, '碜', '土', 16, 'n', '1.食物中杂有沙子。见〖牙碜〗。2.丑；难看。见〖寒碜〗。');
