INSERT INTO `chinese_infos` VALUES (21484, '鮈', '土', 16, 'n', '〔～鱼〕体小而侧扁或呈圆筒形，背鳍一般无硬刺，生活于淡水下层。种类很多。');
INSERT INTO `chinese_infos` VALUES (21485, '鮉', '土', 16, 'n', '古同“鲷”。');
INSERT INTO `chinese_infos` VALUES (21486, '鲉', '土', 16, 'n', '鱼，体侧扁；头部有许多棘状突起，口大，生活在近海岩礁间。种类很多。');
INSERT INTO `chinese_infos` VALUES (21487, '鮌', '土', 16, 'n', '同“鲧”。');
INSERT INTO `chinese_infos` VALUES (21488, '鲏', '土', 16, 'n', '见  〔鳑鲏〕');
INSERT INTO `chinese_infos` VALUES (21489, '鮏', '土', 16, 'n', '同“鯹”。');
INSERT INTO `chinese_infos` VALUES (21490, '鲊', '土', 16, 'n', '1.一种用盐和红曲腌的鱼2.用米粉、面粉等加盐和其他作料拌制的切碎的菜，可以贮存海蜇，水母的一种。');
INSERT INTO `chinese_infos` VALUES (21491, '𩾀', '土', 16, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21492, '鮖', '土', 16, 'n', '◎ 日本地名用字。');
INSERT INTO `chinese_infos` VALUES (21493, '鮗', '土', 16, 'n', '〔～鱼〕体中长而侧扁，被栉鳞，侧线中断，尾鳍圆形。');
INSERT INTO `chinese_infos` VALUES (21494, '鮘', '土', 16, 'n', '义未详。〈方〉鲤鱼。闽语。');
INSERT INTO `chinese_infos` VALUES (21495, '䲟', '土', 16, 'n', '也叫印头鱼。鱼类。体略呈圆筒形，长可达80厘米。头平扁，头顶有吸盘，常吸附于鲨、鲸、海豚等身上或船底而移徙远方。中国沿海均产。');
INSERT INTO `chinese_infos` VALUES (21496, '鴊', '土', 16, 'n', '1.鶗鵳鸟，即“鹞”。2.鸡。');
INSERT INTO `chinese_infos` VALUES (21497, '鴏', '土', 16, 'n', '古同“玳”。');
INSERT INTO `chinese_infos` VALUES (21498, '𫛤', '土', 16, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21499, '鴑', '土', 16, 'n', '古同“鴽”。');
INSERT INTO `chinese_infos` VALUES (21500, '鸰', '土', 16, 'n', '见  〔鹡鸰〕');
INSERT INTO `chinese_infos` VALUES (21501, '鴓', '土', 16, 'n', '鸟名，冕柳莺的旧称。又为莺科某些鸟的旧称，如褐头鹪莺旧称为“竿鴓”，斑鸫为“红麦鴓”，蚁鴷为“地啄鴓”，树鹨为“树鲁鴓”。');
INSERT INTO `chinese_infos` VALUES (21502, '鴖', '土', 16, 'n', '古书上说的一种红嘴鸟，形状像翠鸟。');
INSERT INTO `chinese_infos` VALUES (21503, '𫁡', '土', 16, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21504, '鴘', '土', 16, 'n', '1.苍鹰。2.姓。');
INSERT INTO `chinese_infos` VALUES (21505, '鴙', '土', 16, 'n', '古同“雉”，野鸡。');
INSERT INTO `chinese_infos` VALUES (21506, '鴚', '土', 16, 'n', '鸿雁');
INSERT INTO `chinese_infos` VALUES (21507, '鸮', '土', 16, 'n', '见  〔鸱鸮〕');
INSERT INTO `chinese_infos` VALUES (21508, '鴠', '土', 16, 'n', '〔渴（hé）～〕寒号鸟。亦作“鹖鴠”。');
INSERT INTO `chinese_infos` VALUES (21509, '鴡', '土', 16, 'n', '〔～鸠〕鱼鹰。亦作“雎鸠”。');
INSERT INTO `chinese_infos` VALUES (21510, '鴢', '土', 16, 'n', '一种鸟，即“鱼鵁”。');
INSERT INTO `chinese_infos` VALUES (21511, '鴤', '土', 16, 'n', '古书上说的一种水鸟。');
INSERT INTO `chinese_infos` VALUES (21512, '𫛣', '土', 16, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21513, '鴧', '土', 16, 'n', '古同“鴥”。');
INSERT INTO `chinese_infos` VALUES (21514, '鴩', '土', 16, 'n', '古书上说的一种鸟。');
INSERT INTO `chinese_infos` VALUES (21515, '鴪', '土', 16, 'n', '古同“鴥”。');
INSERT INTO `chinese_infos` VALUES (21516, '鴫', '土', 16, 'n', '日本地名用字。');
INSERT INTO `chinese_infos` VALUES (21517, '鴬', '土', 16, 'n', '同“莺”（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (21518, '鹷', '土', 16, 'n', '盐。');
INSERT INTO `chinese_infos` VALUES (21519, '麅', '土', 16, 'n', '同“狍”。');
INSERT INTO `chinese_infos` VALUES (21520, '麆', '土', 16, 'n', '幼獐。古通“粗”。');
INSERT INTO `chinese_infos` VALUES (21521, '𤿲', '土', 16, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21522, '麭', '土', 16, 'n', '糕饼。');
INSERT INTO `chinese_infos` VALUES (21523, '麮', '土', 16, 'n', '大麦粥');
INSERT INTO `chinese_infos` VALUES (21524, '黇', '土', 16, 'n', '〔黇鹿〕哺乳动物。鹿的一种。角的上部扁平，呈掌状，尾略长。性温顺。');
INSERT INTO `chinese_infos` VALUES (21525, '黈', '土', 16, 'n', '1.黄色。2.古人堵塞耳朵的黄绵。');
INSERT INTO `chinese_infos` VALUES (21526, '黕', '土', 16, 'n', '1.污垢。2.乌 黑。');
INSERT INTO `chinese_infos` VALUES (21527, '黖', '土', 16, 'n', '〔～～〕昏暗不明，如“（万物蠢生），芒芒～～。”');
INSERT INTO `chinese_infos` VALUES (21528, '黗', '土', 16, 'n', '黄黑色。');
INSERT INTO `chinese_infos` VALUES (21529, '黺', '土', 16, 'n', '彩色花纹。');
INSERT INTO `chinese_infos` VALUES (21530, '鼼', '土', 16, 'n', '1.仰鼻。2.鼻折。');
INSERT INTO `chinese_infos` VALUES (21531, '齓', '土', 16, 'n', '古同“龀”。');
INSERT INTO `chinese_infos` VALUES (21532, '隘', '土', 17, 'n', '属于常用字，本意是狭窄，引申指险要处。');
INSERT INTO `chinese_infos` VALUES (21533, '嶷', '土', 17, 'y', '九嶷（Jiǔyí），山名，又地名，都在湖南。也作九疑。');
INSERT INTO `chinese_infos` VALUES (21534, '拥', '土', 17, 'y', '从手从雍，收谓敛持在手，拥谓抱之於怀也。本意多指用手去抱，或者具有，拥有之意，也指人或车船等紧紧地挤在一起。引申意多指拥护：领袖、党派、政策、措施等表示赞成并全力支持。');
INSERT INTO `chinese_infos` VALUES (21535, '膺', '土', 17, 'y', '1.胸2.接受；承受3.伐；打击');
INSERT INTO `chinese_infos` VALUES (21536, '岭', '土', 17, 'y', '意思是山，山脉。|岭，山道也。——《说文新附》 |末涧增波，云生岭逾叠。——南朝 宋· 谢灵运《登上戍石鼓山》');
INSERT INTO `chinese_infos` VALUES (21537, '应', '土', 17, 'y', '一般表示该，当，如应该；也可以表示回答，如应答；作连词时可以表示随，即；也表示姓。');
INSERT INTO `chinese_infos` VALUES (21538, '优', '土', 17, 'y', '形声。从人,尤声。本义古代表演乐舞、杂戏的艺人。宋元以后,亦泛称戏曲艺人、演员；优,饶也。一曰倡也。--《说文》。按,倡者本训,饶者假借。；陈氏鲍氏之圉人为优。--《左传·襄公二十八年》；公之优曰施。--《国语·晋语》；信谗喜优。--《国语·越语》；优笑在前。--《国语·齐语》。注倡俳也。；优孟。--《史记·滑稽列传》；所爱倡优巧匠之属。--《汉书·灌夫传》。注谐戏者也。；优施曰…我优也,言无邮。--《国语》');
INSERT INTO `chinese_infos` VALUES (21539, '忆', '土', 17, 'y', '形声。从心,意声。本义思念,回想；问女何所忆。--《乐府诗集·木兰诗》；还思建邺水,终忆武昌鱼。--庾信《奉和永丰殿下言志》；又如忆想(回想；留在记忆中;记住；读书数行并下,过目皆忆。--《梁书》；汝忆否。--清·林觉民《与妻书》；又如忆记(记忆);忆识(记忆；回忆；能不忆江南。--唐·白居易《忆江南》');
INSERT INTO `chinese_infos` VALUES (21540, '婴', '土', 17, 'y', '历史上取名为婴的人都有：晏婴、赢子婴、郑子婴、仲婴齐、太子婴、闾丘婴。还有：程婴、灌婴、窦婴、夏侯婴。');
INSERT INTO `chinese_infos` VALUES (21541, '阳', '土', 17, 'y', '从阝(阜)从昜。高明也。——《说文》形声。从阜，昜声。从阜，与山有关。本义：山南水北。');
INSERT INTO `chinese_infos` VALUES (21542, '嵘', '土', 17, 'y', '见  〔峥嵘〕');
INSERT INTO `chinese_infos` VALUES (21543, '闱', '土', 17, 'y', '1.古代宫殿的旁门2.科举时代称考场');
INSERT INTO `chinese_infos` VALUES (21544, '邬', '土', 17, 'y', '姓。');
INSERT INTO `chinese_infos` VALUES (21545, '怿', '土', 17, 'y', '欢喜；高兴。');
INSERT INTO `chinese_infos` VALUES (21546, '壕', '土', 17, 'n', '指护城河，也指壕沟，防空洞等。');
INSERT INTO `chinese_infos` VALUES (21547, '矶', '土', 17, 'y', '水边突出的岩石或江河当中的石滩');
INSERT INTO `chinese_infos` VALUES (21548, '屿', '土', 17, 'y', '形声。从山,与声。本义小岛；为屿,为嵁,为岩。--唐·柳宗元《至小丘西小石潭记》；平地小山；屿,平地小山也。--《六书故》；中央之山宜平,则为坻为屿,若以供吾布席置酒之用也。--明·袁宏道《嵩游第五》');
INSERT INTO `chinese_infos` VALUES (21549, '辕', '土', 17, 'y', '与猿同音，指车前驾牲畜的两根直木；旧时指军营、官署的外门，借指衙署。');
INSERT INTO `chinese_infos` VALUES (21550, '远', '土', 17, 'y', '是形声字，辶为形，袁为声（在繁体字中）。；用作距离长，与“近”相对、远见（远大的眼光）、时间长、关系疏，不亲密、深奥，旨远等。另外有同名漫画角色信息。');
INSERT INTO `chinese_infos` VALUES (21551, '癌', '土', 17, 'n', '来源于上皮组织的恶性肿瘤胃～。');
INSERT INTO `chinese_infos` VALUES (21552, '闇', '土', 17, 'n', '同“暗”');
INSERT INTO `chinese_infos` VALUES (21553, '遨', '土', 17, 'n', '漫游');
INSERT INTO `chinese_infos` VALUES (21554, '醠', '土', 17, 'n', '酒');
INSERT INTO `chinese_infos` VALUES (21555, '謷', '土', 17, 'n', '1.诋毁2.高；高超3.古同“嗷”，哀叹声');
INSERT INTO `chinese_infos` VALUES (21556, '鮟', '土', 17, 'n', '〔～𩾌〕鱼，头大而扁平，体软无鳞，口宽牙锐，尾细小，能发出像老人咳嗽的声音。栖息海底，慢慢匍行。');
INSERT INTO `chinese_infos` VALUES (21557, '郧', '土', 17, 'n', '1.郧县，地名，在湖北。2.姓。');
INSERT INTO `chinese_infos` VALUES (21558, '酝', '土', 17, 'n', '基本字义酝（酝）；酿酒，亦指酒：～酿。；古同“蕴”，包含。；,,;');
INSERT INTO `chinese_infos` VALUES (21559, '翳', '土', 17, 'n', '1.遮掩2.白翳，眼球上生的障蔽视线的白膜。');
INSERT INTO `chinese_infos` VALUES (21560, '硗', '土', 17, 'n', '土质硬，不肥沃');
INSERT INTO `chinese_infos` VALUES (21561, '龠', '土', 17, 'n', '1.古代容量单位，一龠等于半合（gě）。2.古代一种乐器，形状像箫。');
INSERT INTO `chinese_infos` VALUES (21562, '鲔', '土', 17, 'n', '古书上指鲟（xún ）鱼。');
INSERT INTO `chinese_infos` VALUES (21563, '压', '土', 17, 'n', '2.压压从上面加力：～住。～碎。～缩。泰山～顶。用威力制服、镇服：镇～。～服。～迫。控制，使稳定，使平静：～价。～住阵脚。搁置：积～。逼近：大兵～境。赌博时在某一门上下注：～宝（亦作“押宝”）。压〔～根儿〕根本，从来，如“我～～～就不理解这件事”。（压）【平水韵】：属入声十七洽部。');
INSERT INTO `chinese_infos` VALUES (21564, '鼋', '土', 17, 'n', '俗称癞头鼋。爬行动物。背甲近圆形，暗绿色，有小疣。生活在河中。是中国国家重点保护动物。');
INSERT INTO `chinese_infos` VALUES (21565, '黝', '土', 17, 'n', '青黑色');
INSERT INTO `chinese_infos` VALUES (21566, '磺', '土', 17, 'n', '澹水在磺山之下，日出磺气上腾，东风一发，感触易病，雨则磺水入河，食之往往得病以死。――《台海使槎录》');
INSERT INTO `chinese_infos` VALUES (21567, '隅', '土', 17, 'n', '屋角的洞穴，角落');
INSERT INTO `chinese_infos` VALUES (21568, '壑', '土', 17, 'n', '山沟或大水坑');
INSERT INTO `chinese_infos` VALUES (21569, '鲑', '土', 17, 'n', '鱼，身体大，略呈纺锤形，鳞细而圆，有些生活在海洋中，有些生活在淡水中，是重要的食用鱼类。种类很多，常见的有大麻哈鱼。古书上指鱼类的菜肴。');
INSERT INTO `chinese_infos` VALUES (21570, '礁', '土', 17, 'n', '指在河、海里的由岩石或钙质珊瑚堆积成的接近水面的岩状物，可露出也可不露出水面：～石、暗～、珊瑚～、触～。我国沿海渔民又称之为“线”、“沙”、“铲”等等。又称环礁湖、珊瑚湖。环礁内的水域或堡礁与大陆间的水域。现在已经有重要的军事意义。');
INSERT INTO `chinese_infos` VALUES (21571, '圹', '土', 17, 'n', '1.墓穴2.原野。');
INSERT INTO `chinese_infos` VALUES (21572, '礅', '土', 17, 'n', '略具方形或圆 形的整块的大石头');
INSERT INTO `chinese_infos` VALUES (21573, '磴', '土', 17, 'n', '1.石头台阶。2.量词。用于台阶或楼梯的层级');
INSERT INTO `chinese_infos` VALUES (21574, '础', '土', 18, 'y', '形声。从石,楚声。本义柱脚石,垫在房屋柱子下的石头；云蒸柱础润。--《淮南子·说林》');
INSERT INTO `chinese_infos` VALUES (21575, '鄢', '土', 18, 'y', '1.鄢陵（Yānlíng），地名，在河南。2.姓。');
INSERT INTO `chinese_infos` VALUES (21576, '韪', '土', 18, 'y', '是；对（常与“不”字连用，指过失或谬误）');
INSERT INTO `chinese_infos` VALUES (21577, '彝', '土', 18, 'y', '1.古代盛酒的器具。也泛指古代宗庙祭器。2.法度；常规3.彝族。');
INSERT INTO `chinese_infos` VALUES (21578, '鄞', '土', 18, 'y', '1.鄞州（Yínzhōu），地名，在浙江。2.姓。');
INSERT INTO `chinese_infos` VALUES (21579, '瑷', '土', 18, 'n', '1.美玉。2.〔瑷珲〕地名。在黑龙江省。今作爱辉。');
INSERT INTO `chinese_infos` VALUES (21581, '医', '土', 18, 'y', '本义：治病的人。医。会意兼形声。从匸(）从矢,矢亦声。《说文》:“盛弓弩矢器也。”“医”,会意,从“殹”,从酉。“殹”,治病时的扣击声。“酉”,用以医疗的酒。二字各有本义,今用“医”为简体字。');
INSERT INTO `chinese_infos` VALUES (21582, '璧', '土', 18, 'y', '形声。从玉,辟声。本义平而圆,中心有孔的玉；璧,瑞玉环也。--《说文》；肉倍好谓之璧。--《尔雅·释器》。注肉边好孔。玉人,璧好三寸。；璧者,方中圆外。--《白虎通》；宏璧。--《书·顾命》。郑注大璧,度尺二寸。；以苍璧礼天。--《周礼·大宗伯》；如圭如璧。--《诗·卫风·淇奥》；愿以十五城请易璧。--《史记·廉颇蔺相如列传》');
INSERT INTO `chinese_infos` VALUES (21583, '讴', '土', 18, 'n', '1.歌唱2.民歌');
INSERT INTO `chinese_infos` VALUES (21584, '垒', '土', 18, 'y', '用砖、石等堆砌起来；军营的围墙或防御工事。');
INSERT INTO `chinese_infos` VALUES (21585, '袄', '土', 18, 'n', '形声。从衣,夭声。本义有衬里的上衣；皮衣之类的御寒衣服；身上穿着银红撇花半旧大袄。--《红楼梦》');
INSERT INTO `chinese_infos` VALUES (21586, '陨', '土', 18, 'n', '释义为坠落，从高下也；又有毁坏之意。古同“殒”，为死亡之意。');
INSERT INTO `chinese_infos` VALUES (21587, '盫', '土', 18, 'n', '古同“盦”。');
INSERT INTO `chinese_infos` VALUES (21588, '黟', '土', 18, 'n', '黟县，地名，在安徽。');
INSERT INTO `chinese_infos` VALUES (21589, '癔', '土', 18, 'n', '〔癔症〕也叫歇斯底里。神经官能症的一种。多由精神刺激而急剧发病。主要症状是运动、感觉和精神意识等障碍。');
INSERT INTO `chinese_infos` VALUES (21590, '欤', '土', 18, 'n', '1.表示疑问或反问，跟“吗”或“呢”相同2.表示感叹，跟“啊”相同');
INSERT INTO `chinese_infos` VALUES (21591, '鼬', '土', 18, 'n', '哺乳动物，身体细长，四肢短小，头狭长，耳一般短而圆，唇有须，肛门附近大多有臭腺，可以驱敌自卫。毛有黄褐、棕、灰棕等色。种类很多，如黄鼬、紫貂。');
INSERT INTO `chinese_infos` VALUES (21592, '恹', '土', 18, 'n', '形容患病而精神疲乏');
INSERT INTO `chinese_infos` VALUES (21593, '碍', '土', 18, 'n', '从石从疑，止也。妨碍别人做事，使人感到不方便；障碍（挡住道路，使不能顺利通过；也指阻挡物）;碍着脸（难为情，怕伤情面）遮蔽；掩盖');
INSERT INTO `chinese_infos` VALUES (21594, '礓', '土', 18, 'n', '砂礓，砂姜的旧称。砂土中的不规则姜形钙质结核体。');
INSERT INTO `chinese_infos` VALUES (21595, '硷', '土', 18, 'n', '同“碱”。');
INSERT INTO `chinese_infos` VALUES (21596, '蹦', '土', 18, 'n', '两脚并着跳：～跳。～高。欢～乱跳（亦称“活蹦乱跳”）。');
INSERT INTO `chinese_infos` VALUES (21597, '垄', '土', 19, 'y', '意为：埂子，田埂，指乡村田间种植作物的土行，中间以土高高填起来，高于两侧的土地。');
INSERT INTO `chinese_infos` VALUES (21598, '垆', '土', 19, 'y', '1.黑色的土壤2.酒店里安放酒瓮的土台子，借指酒店');
INSERT INTO `chinese_infos` VALUES (21599, '臆', '土', 19, 'n', '1.胸。2.无根据的；主观的');
INSERT INTO `chinese_infos` VALUES (21600, '舣', '土', 19, 'y', '使船靠岸。');
INSERT INTO `chinese_infos` VALUES (21601, '礤', '土', 19, 'y', '粗石。');
INSERT INTO `chinese_infos` VALUES (21602, '疆', '土', 19, 'y', '从弓从土从畺，；指事。从土,从弓,从畺(,田界)。从“弓”,表示以弓记步,即以弓来丈量土地。本义:田界。；畺,界也。从田,三其界画也。指事。——《说文》地域，领域，边界：～土。～宇（国土）。～界。～场（战场）。～陲（边境）。边～。海～。2.极限：万寿无～。划分界限：“楚子～之”。');
INSERT INTO `chinese_infos` VALUES (21603, '韵', '土', 19, 'y', '是形声字，音为形，匀为声。旧多写作均。韵的本意指和谐的声音，也指风度、气质、情趣。韵也指一个音节中声母、介音以外的部分，如、、是三个韵母，但同是韵。在古代，声调不同，又分别属于不同的韵。现代韵又指韵母。');
INSERT INTO `chinese_infos` VALUES (21604, '韫', '土', 19, 'y', '收藏，蕴藏，包含1.赤黄色；赤色。2.弓袋；韣（dú）也。');
INSERT INTO `chinese_infos` VALUES (21605, '稳', '土', 19, 'y', '由禾和急组成。其意为象禾苗生长一样，不知不觉，不急不燥。');
INSERT INTO `chinese_infos` VALUES (21606, '爊', '土', 19, 'n', '1.放在微火上煨熟。2.同“熬”（āo）。');
INSERT INTO `chinese_infos` VALUES (21607, '垅', '土', 19, 'n', '“垄”的异体字。');
INSERT INTO `chinese_infos` VALUES (21608, '坏', '土', 19, 'n', '从土从褱，；(形声。从土、褱声。古代建筑物主要以土建造,故从“土”。本义:倒塌(指建筑物遭到破坏)。按:古代“坏”与“坏”是音义都不相同的两个字。“坏”,本读作,是个从土、不声的形声字,指“未烧过的砖瓦、陶器”。现在“坏”简化为“坏”；');
INSERT INTO `chinese_infos` VALUES (21609, '鹉', '土', 19, 'n', '部首：鸟。《广韵》文甫切《集韵》《韵会》罔甫切，$音武。鹦鹉，能言鸟也。');
INSERT INTO `chinese_infos` VALUES (21610, '玙', '土', 19, 'n', '美玉。');
INSERT INTO `chinese_infos` VALUES (21611, '坜', '土', 19, 'n', '中坜（Zhōnglì），地名，在台湾省。');
INSERT INTO `chinese_infos` VALUES (21612, '臃', '土', 19, 'n', '〔臃肿〕身体过于肥胖或衣服穿得太多，动作不灵活。也比喻机构庞大，工作效率低。');
INSERT INTO `chinese_infos` VALUES (21613, '繶', '土', 19, 'n', '1.用丝线编织成的带子。2.系束。3.古代酒器口与足底之间的篆文装饰。');
INSERT INTO `chinese_infos` VALUES (21614, '骛', '土', 19, 'n', '1.奔驰；乱跑2.同“务”');
INSERT INTO `chinese_infos` VALUES (21615, '嬿', '土', 19, 'n', '1.美好；和美2.安闲；安乐。');
INSERT INTO `chinese_infos` VALUES (21616, '岿', '土', 20, 'y', '1.高大屹立的样子2.小山丛列。');
INSERT INTO `chinese_infos` VALUES (21617, '壤', '土', 20, 'y', '是由一层层厚度各异的矿物质成分所组成大自然主体。');
INSERT INTO `chinese_infos` VALUES (21618, '邺', '土', 20, 'y', '古地名，在今河北临漳。');
INSERT INTO `chinese_infos` VALUES (21619, '巉', '土', 20, 'n', '山势高险的样子。');
INSERT INTO `chinese_infos` VALUES (21620, '矿', '土', 20, 'y', '即蕴藏在地层中可供开采利用的物质。');
INSERT INTO `chinese_infos` VALUES (21621, '鹜', '土', 20, 'n', '鸭子');
INSERT INTO `chinese_infos` VALUES (21622, '砾', '土', 20, 'n', '是形声字，石为形，乐为声。基本意思是小石，碎石。如：～石。砂～。瓦～。～漠（地表几乎全为砾石所覆盖，没有土壤，植物稀少的地区）。');
INSERT INTO `chinese_infos` VALUES (21623, '骜', '土', 20, 'n', '1.骏马。2.马不驯顺，喻指傲慢倔强');
INSERT INTO `chinese_infos` VALUES (21624, '罂', '土', 20, 'n', '小口大肚的瓶子。');
INSERT INTO `chinese_infos` VALUES (21625, '鼯', '土', 20, 'n', '〔鼯鼠〕也叫大飞鼠。哺乳动物。前后肢间有宽而多毛的飞膜，能在树间滑翔。夜间活动。食树芽、果实等。是森林害兽。');
INSERT INTO `chinese_infos` VALUES (21626, '嘤', '土', 20, 'n', '拟声词。鸟叫声。');
INSERT INTO `chinese_infos` VALUES (21627, '痒', '土', 20, 'n', '是指皮肤或黏膜受刺激需要抓挠的一种感觉。');
INSERT INTO `chinese_infos` VALUES (21628, '矾', '土', 20, 'n', '各种金属(如铜、铁、锌)的硫酸盐,尤指具有玻璃质状态表面或光泽的该种硫酸盐的水合物；矾山);矾水(溶明矾的水);矾粉(脱胶的明矾粉末);矾书(用明矾水写的保密书信；铝的氧化物23；矾（礬）某些金属硫酸盐的含水复盐结晶，有明～。铬钾～。铁铵～等。明～也叫\"白矾\"带涩味，呈酸性反应，可供造纸、制革及制颜料、染料等用。');
INSERT INTO `chinese_infos` VALUES (21629, '碌', '土', 21, 'y', '用来表示圆柱形或者是类圆柱形的东西的数量。');
INSERT INTO `chinese_infos` VALUES (21630, '蠡', '土', 21, 'y', '1.瓢。2.贝壳。1.用于人名，范蠡，春秋时人。2.（Lǐ）蠡县，地名，在河北。');
INSERT INTO `chinese_infos` VALUES (21631, '砻', '土', 21, 'y', '1.去掉稻壳的工具，形状像磨，多用木料制成。2.用砻去掉稻壳');
INSERT INTO `chinese_infos` VALUES (21632, '巍', '土', 21, 'y', '高大：～峨。～焕（高大壮观）。～然。～～。崔～。形声。从山，从魏，魏亦声。“魏”意为“壮汉”、“强人”。“山”与“魏”联合起来表示“壮汉一般的山”。本义：山体强壮高大。');
INSERT INTO `chinese_infos` VALUES (21633, '跃', '土', 21, 'y', '从足从翟。翟，长尾的野鸡。；形声。从足,翟声。本义:跳跃。');
INSERT INTO `chinese_infos` VALUES (21634, '撄', '土', 21, 'y', '1.接触；触犯2.纠缠；扰乱。');
INSERT INTO `chinese_infos` VALUES (21635, '誉', '土', 21, 'y', '该字的基本字义是名声的意思，如：荣誉。名誉。信誉。沽名钓誉。又作称扬，赞美之意：古同“豫”，欢乐。');
INSERT INTO `chinese_infos` VALUES (21636, '礴', '土', 21, 'n', '见〔磅礴〕');
INSERT INTO `chinese_infos` VALUES (21637, '礞', '土', 21, 'n', '〔礞石〕药石名。分青礞石、金礞石两种。');
INSERT INTO `chinese_infos` VALUES (21638, '砺', '土', 21, 'y', '1.磨刀石。2.磨（刀）');
INSERT INTO `chinese_infos` VALUES (21639, '鳌', '土', 21, 'y', '传说中指海里的大龟或大鳖。');
INSERT INTO `chinese_infos` VALUES (21640, '巅', '土', 22, 'y', '山顶');
INSERT INTO `chinese_infos` VALUES (21641, '鳙', '土', 22, 'n', '也叫花鲢、胖头鱼。鱼类。长可达1米多，背暗黑色，有小黑斑，头大，腹缘后部有棱。是中国主要淡水养殖鱼，分布于全国各大水系。');
INSERT INTO `chinese_infos` VALUES (21642, '璎', '土', 22, 'y', '似玉的石头。');
INSERT INTO `chinese_infos` VALUES (21643, '隐', '土', 22, 'y', '从阝（阜）从爪从工从又（手）从心。阜,山崖边的石磴；双手做工，稳定下来，隐藏。藏匿、伤痛、怜悯、凭倚的意思。');
INSERT INTO `chinese_infos` VALUES (21644, '懿', '土', 22, 'y', '美好（多指德行）');
INSERT INTO `chinese_infos` VALUES (21645, '峦', '土', 22, 'y', '《唐韵》落官切《集韵》《韵会》《正韵》卢官切，音銮。《说文》山小而锐。《六书故》圜峰也。《楚辞·九章》登石峦以远望兮。又山纡回绵连曰峦。《徐悱登琅邪城诗》襟带尽巖峦。《正字通》又盐韵，音廉。引苏轼谢欧阳晦夫遗琴枕诗：我怀汝隂六一老，眉宇秀发如春峦。按苏轼俱入寒韵引用，譌。');
INSERT INTO `chinese_infos` VALUES (21646, '鸥', '土', 22, 'y', '形声。从鸟,区声。《说文》水鸮也。本义水鸮；水鸟名；惊起一滩鸥鹭。--宋·李清照《如梦令》；鸥鹭灭没。--宋·陆游《过小孤山大孤山》');
INSERT INTO `chinese_infos` VALUES (21647, '饔', '土', 22, 'n', '熟食，有时专指早饭。');
INSERT INTO `chinese_infos` VALUES (21648, '呓', '土', 22, 'n', '呓语');
INSERT INTO `chinese_infos` VALUES (21649, '窃', '土', 22, 'n', '它一般的意思从米，以米为穴，意为虫在穴中偷米吃，是会意字。');
INSERT INTO `chinese_infos` VALUES (21650, '瘾', '土', 22, 'n', '由于中枢神经经常受到刺激而形成的习惯性；泛指浓厚的兴趣；');
INSERT INTO `chinese_infos` VALUES (21651, '缨', '土', 23, 'y', '指彩带，是古代汉族女子许嫁时所系的一种彩色带子。新娘进洞房后，新郎要亲自为她脱去缨。亦用以系香囊。如：缨徽（妇女所佩的香囊）。');
INSERT INTO `chinese_infos` VALUES (21652, '岩', '土', 23, 'y', '岩石，矿物的集合体，是地壳的主要成分：～层火成～花岗～2，凸起的岩石构成的山峰。');
INSERT INTO `chinese_infos` VALUES (21653, '娈', '土', 23, 'n', '相貌美。');
INSERT INTO `chinese_infos` VALUES (21654, '靥', '土', 23, 'n', '酒窝 儿 ');
INSERT INTO `chinese_infos` VALUES (21655, '罐', '土', 24, 'n', '又叫罐子，是盛东西用的大口器皿，多为陶瓷制品。现在也多用玻璃制成，具有密封效果，用于储藏食物的罐子。');
INSERT INTO `chinese_infos` VALUES (21656, '盐', '土', 24, 'n', '形声。从卤,监声。本义食盐；若作和羹,尔惟盐梅。--《书·说命下》；鲁盐漆丝。--《史记·货殖列传》');
INSERT INTO `chinese_infos` VALUES (21657, '坝', '土', 24, 'y', '形声。从土,霸声。本义堤坝；截河拦水的堤堰。堤坝；其河自西坝至东坝十六里有余。--单锷《吴中水利书》；又如队;坝埽;顺坝；堤岸；平地；君家水茄白银色,殊胜坝里紫彭亨。--黄庭坚《谢杨履道》');
INSERT INTO `chinese_infos` VALUES (21658, '塆', '土', 25, 'n', '小沟里的小块平地。多 用于地名。');
INSERT INTO `chinese_infos` VALUES (21659, '彟', '土', 25, 'n', '1.尺度。2.用秤称（今口语说yāo，写作“约”）。');
INSERT INTO `chinese_infos` VALUES (21660, '𣃁', '土', 25, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21661, '曯', '土', 25, 'n', '古同“烛”，照。');
INSERT INTO `chinese_infos` VALUES (21662, '𣚚', '土', 25, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21663, '欙', '土', 25, 'n', '古代走山路乘坐的器具。');
INSERT INTO `chinese_infos` VALUES (21664, '欚', '土', 25, 'n', '1.船。2.捕鸟兽的网。');
INSERT INTO `chinese_infos` VALUES (21665, '欛', '土', 25, 'n', '同“把（bà）”。');
INSERT INTO `chinese_infos` VALUES (21666, '欝', '土', 25, 'n', '同“郁”。');
INSERT INTO `chinese_infos` VALUES (21667, '灢', '土', 25, 'n', '〔泱（yāng）～〕水浊。');
INSERT INTO `chinese_infos` VALUES (21668, '𪹳', '土', 25, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21669, '爦', '土', 25, 'n', '火乱。1.焚烧；延烧。2.烤炙。');
INSERT INTO `chinese_infos` VALUES (21670, '犪', '土', 25, 'n', '〔～牛〕即“犩”。');
INSERT INTO `chinese_infos` VALUES (21671, '矘', '土', 25, 'n', '1.眼睛无神，茫然直视。2.失志的样子。');
INSERT INTO `chinese_infos` VALUES (21672, '矡', '土', 25, 'n', '矛一类的兵器。');
INSERT INTO `chinese_infos` VALUES (21673, '籫', '土', 25, 'n', '1.盛筷、勺的竹笼。2.丛。');
INSERT INTO `chinese_infos` VALUES (21674, '籭', '土', 25, 'n', '古同“筛”。');
INSERT INTO `chinese_infos` VALUES (21675, '纙', '土', 25, 'n', '穿铜钱的绳子。');
INSERT INTO `chinese_infos` VALUES (21676, '𫄥', '土', 25, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21677, '臡', '土', 25, 'n', '带骨的肉酱。古同“脔”。');
INSERT INTO `chinese_infos` VALUES (21678, '襽', '土', 25, 'n', '古同“襴”。');
INSERT INTO `chinese_infos` VALUES (21679, '覊', '土', 25, 'n', '同“羁”。');
INSERT INTO `chinese_infos` VALUES (21680, '觿', '土', 25, 'n', '古代用骨头制的解绳结的锥子。');
INSERT INTO `chinese_infos` VALUES (21681, '讗', '土', 25, 'n', '1.言壮；自夸。2.数相怒。3.疾言。');
INSERT INTO `chinese_infos` VALUES (21682, '讘', '土', 25, 'n', '〔～䛟〕多言，妄语。');
INSERT INTO `chinese_infos` VALUES (21683, '讙', '土', 25, 'n', '同“欢”。');
INSERT INTO `chinese_infos` VALUES (21684, '讛', '土', 25, 'n', '同“呓”。');
INSERT INTO `chinese_infos` VALUES (21685, '豒', '土', 25, 'n', '古同“秩”');
INSERT INTO `chinese_infos` VALUES (21686, '躢', '土', 25, 'n', '古同“蹋”。');
INSERT INTO `chinese_infos` VALUES (21687, '躣', '土', 25, 'n', '〔～～〕（蛟龙）游动的样子，如“左朱雀之茇茇兮，右苍龙之～～。”');
INSERT INTO `chinese_infos` VALUES (21688, '鑯', '土', 25, 'n', '1.铁器。2.古同“尖”，尖锐。');
INSERT INTO `chinese_infos` VALUES (21689, '鑳', '土', 25, 'n', '古同“键”。');
INSERT INTO `chinese_infos` VALUES (21690, '鑵', '土', 25, 'n', '古同“罐”。');
INSERT INTO `chinese_infos` VALUES (21691, '顲', '土', 25, 'n', '面黄肌瘦。');
INSERT INTO `chinese_infos` VALUES (21692, '𫗵', '土', 25, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21693, '髗', '土', 25, 'n', '古同“颅”。');
INSERT INTO `chinese_infos` VALUES (21694, '鱬', '土', 25, 'n', '古书中记载的一种人面鱼身的动物');
INSERT INTO `chinese_infos` VALUES (21695, '鱰', '土', 25, 'n', '同“鱪”（日本汉字）。');
INSERT INTO `chinese_infos` VALUES (21696, '鱴', '土', 25, 'n', '鮆鱼。');
INSERT INTO `chinese_infos` VALUES (21697, '鱶', '土', 25, 'n', '古同“鮝”。');
INSERT INTO `chinese_infos` VALUES (21698, '𫛢', '土', 25, 'n', NULL);
INSERT INTO `chinese_infos` VALUES (21699, '鸍', '土', 25, 'n', '1.〔鸩（zhèn）～〕古书上说的一种能吃蛇的鸟。2.沉凫，即“野鸭”。');
INSERT INTO `chinese_infos` VALUES (21700, '鸎', '土', 25, 'n', '同“莺”。');
INSERT INTO `chinese_infos` VALUES (21701, '鸐', '土', 25, 'n', '〔～雉（zhì）〕山雉，野鸡。');
INSERT INTO `chinese_infos` VALUES (21702, '鸑', '土', 25, 'n', '〔～鷟（zhuó）〕ａ．凤的别称，如“～鷟（zhuó）鸣于岐山。”ｂ．古书上说的一种水禽，似野鸭而稍大。');
INSERT INTO `chinese_infos` VALUES (21703, '麡', '土', 25, 'n', '〔～狼〕古书上说的一种像鹿的动物。');
INSERT INTO `chinese_infos` VALUES (21704, '黵', '土', 25, 'n', '1.黑，黑色2.黥，古代在犯人或奴婢、士兵脸上刺字3.用笔墨涂改文字方言，污染；弄脏');
INSERT INTO `chinese_infos` VALUES (21705, '鼈', '土', 25, 'n', '同“鳖”。');
INSERT INTO `chinese_infos` VALUES (21706, '鼝', '土', 25, 'n', '古同“鼘”');
INSERT INTO `chinese_infos` VALUES (21707, '鼟', '土', 25, 'n', '拟声词。敲鼓声。');
INSERT INTO `chinese_infos` VALUES (21708, '齸', '土', 25, 'n', '1.麋鹿反刍。2.麋鹿的胃。');
INSERT INTO `chinese_infos` VALUES (21709, '齺', '土', 25, 'n', '牙齿咬物时上下交切的样子，喻上下相向。');
INSERT INTO `chinese_infos` VALUES (21710, '齻', '土', 25, 'n', '牙床末端最后长出的两对磨牙。');
