INSERT INTO `chinese_infos` VALUES (21738, '鱵', '土', 26, 'n', '〔～鱼〕体细长，亚圆筒形，眼大，口小，下颌细长如针，故亦称“针鱼”，背鳍生于身体后部，臀鳍与之相等。是生活于浅海河口的小型鱼类，有的也能进入淡水生活。');
INSERT INTO `chinese_infos` VALUES (21739, '鸓', '土', 26, 'n', '〔～鼠〕小飞鼠，形似鼯鼠，前后肢之间有宽大多毛的膜，尾长，能在大树间滑翔，常在夜间活动。');
